	StoryDialog = {
		["663703"] = "Ja",
		["994281"] = "Hahaha, meine Plünderer werden ihre Jagd erst dann beenden, wenn Handels Ritter in ganz Ichthyosauria ausgelöscht sind ...",
		["661152"] = "Komm, ich beginne, mich selbst zu verlieren.",
		["111121"] = "Wer ist Lord Ramones?",
		["771198"] = "Was heißt hier, keine andere Wahl?! Ihr habt Teresa umsonst geopfert?! Ich muss sofort in dieses verdammte Grab der Giganten und sie zurückholen!",
		["111134"] = "Ha-ha, schlauer Mensch.",
		["661322"] = "Ganz recht ... die Losung ... Wenn du nicht weißt, wovon ich spreche, rate ich dir, die Beine in die Hand zu nehmen!",
		["100210022202"] = "Ja",
		["997013"] = "Ich kann immer noch den Odem des Lebens irgendwo auf dieser Insel spüren, nur halt extrem schwach.",
		["551268"] = "Beeil dich, geh ins Seligenland, tu, was du tun musst ... Ich muss mich jetzt ausruhen ... Keine Sorge ... Ich hoffe, es geht alles gut.",
		["771230"] = "Den Terrence, der voller Selbstzweifel in den Dunklen Nebel ging, gibt es nicht mehr.",
		["333092"] = "Warum bist du hier festgenagelt?",
		["552329"] = "Kosten: 1 Kondensierter Honig und 30 Blumentierknochen. Weiter?",
		["771224"] = "Teresa würde vermutlich versuchen, dich davon abzubringen.",
		["663504"] = "Nein",
		["1006100084"] = "Er hat das Yekub-Siegel ...",
		["9971340"] = "Dieser Armreif bedeutet mir die Welt. Ich suche seinen Besitzer.",
		["663706"] = "Nein",
		["991431"] = "Heggiestein",
		["331163"] = "Das ist unmöglich!",
		["884043"] = "Es gehört mir!",
		["221293"] = "Der Segen des Heiligen Vaters ist unbestritten! Wenn du deinen Weg sicher fortsetzen willst, rate ich dir, keinen Ärger zu machen.",
		["9971364"] = "Doch im Licht des Seligenlandes wird das die Trübnis in meinem Herzen nicht auflösen.",
		["663196"] = "Nein",
		["553255"] = "Ja",
		["771092"] = "Geh einfach, ich kümmere mich darum.",
		["331098"] = "...",
		["771077"] = "Norwood hat sie zu den Ruinen nahe der Höhle gebracht.",
		["100610064168"] = "Kann ohne Schlüssel nicht geöffnet werden.",
		["9931005"] = "Links oder rechts, Dunkelheit oder Licht, richtig oder falsch ... Alles in dieser Welt hängt irgendwie zusammen. Alles läuft auf ein zum Scheitern verurteiltes Ende hinaus, ungeachtet der Unsicherheiten.",
		["773075"] = "Gehen",
		["552180"] = "",
		["221104"] = "Ich weiß, dass dir meine Maske nicht geheuer ist, aber ich bin Norwood. Und, genau wie du, ein Diener der Kirche.",
		["661310"] = "Offenbar bist du ... noch nicht von den Sendril kontaminiert worden, du Glücklicher.",
		["667124"] = "Noch ein Fragment.",
		["221262"] = "Was ist mit diesem Boden, auf dem die Hinrichtungen durchgeführt werden?",
		["9934003"] = "/t0",
		["991152"] = "Ich hab vor langer Zeit mal gehört, wie meine Eltern darüber geredet haben ... Früher gab's hier mal einen Aufstand, viele Leute sind gestorben.",
		["551132"] = "Ach, du bist's, mein Freund. Wie läuft's denn so? Hast du die Sanduhr gefunden?",
		["552341"] = "Kosten: 1 Kondensierter Honig und 60 Blumentierknochen. Weiter?",
		["772003"] = "Zurück zur Kutsche?",
		["791319"] = "Mir scheint, der Fluch der Marcion wird früher oder später jeden einholen, der damit zu tun hatte. Vielleicht ist das die Strafe für die Beleidigung der Marcion.",
		["441243"] = "Den weiße Staub. Er versucht, das Geschenk von Edith, der letzten großen Hoffnung der Demiurgen, zu zerstören.",
		["332100"] = "Jenny ... könnte ich dich doch nur wiedersehen.",
		["661802"] = "Geh einfach. Du wirst hier nichts Nützliches für dich finden.",
		["661801"] = "Bitte nicht. Bitte! Suche Schutz in der Dreifaltigkeitshalle. Es hat keinen Sinn, hierzubleiben!",
		["9924033"] = "/t0",
		["111046"] = "Das sollten wir uns so schnell wie möglich ansehen!",
		["661702"] = "Geh einfach. Du wirst hier nichts Nützliches für dich finden.",
		["111088"] = "Jerry ist fort, weil die John-Brüder ... zu Monstern wurden.",
		["663602"] = "Nein",
		["663505"] = "Ja",
		["553154"] = "Gehen",
		["663503"] = "Ja",
		["558302"] = "",
		["662503"] = "Die Tür öffnen?",
		["111069"] = "Jerry ... Mein Jerry ist fort ... Monster, alle werden zu Monster ...",
		["663502"] = "Nein",
		["9931004"] = "Terrence! Das kann nicht dein Ernst sein!",
		["662501"] = "Die Tür öffnen?",
		["661405"] = "Gott, ich kann nichts mehr tun. Jeder verdient die Chance, besser zu leben als ich.",
		["551379"] = "Die verborgene Wahrheit fördert unwissende böse Absichten.",
		["441186"] = "Ist das der Preis, den diese Marcion-Frau erwähnt hat?",
		["9932034"] = "",
		["663323"] = "Azath",
		["558301"] = "",
		["663322"] = "Losung?",
		["777046"] = "Komm sofort zurück! Das rote Licht zerfrisst deinen Geist! Schnell!",
		["441120"] = "Danke, Fremder, jetzt geht es mir schon besser.",
		["441300"] = "Mensch, willst du mit mir handeln? Ich besitze viele Schätze.",
		["441144"] = "Haha! Ein Kurier, der die Fakten nicht kennt. Anstatt hier deine Zeit zu vertrödeln, solltest du dich auf den Weg machen, bevor etwas Schreckliches passiert. ",
		["792382"] = "",
		["667126"] = "Nach dem Zusammensetzen der Teile erscheint die Form ähnlich der Nut auf der Statue des Heiligen Schwertes im Hof der Dreifaltigkeit zu sein ... Da könnte es doch einen Zusammenhang geben, nicht wahr?",
		["661325"] = "Warte, vielleicht gibt es noch mehr Hinweise.",
		["9961044"] = "Falls mir etwas zustoßen sollte, schnapp dir Viola und verschwindet.",
		["663241"] = "Nein",
		["333906"] = "Nein",
		["9963027"] = "Gehen",
		["661505"] = "Gott, ich kann nichts mehr tun. Jeder verdient die Chance, besser zu leben als ich.",
		["441125"] = "Niemand interessiert sich für einen alten Sack wie mich ... Wie sehr ich mir wünschte, ins Seligenland zurückzukehren.",
		["9961020"] = "Aber glaubst du wirklich, du könntest ihre Meinung ändern?",
		["552165"] = "Vielleicht fielen sie alle zusammen in einen tiefen Schlaf …",
		["661223"] = "Es gibt so viele Unmenschen da draußen, also reiß dich bitte zusammen.",
		["661214"] = "Dann finde einen Weg, dieses verdammte Tor zu öffnen!",
		["552116"] = "",
		["111105"] = "Geh zu der Statue in der Mine und finde den Tränenstein. Nur mit seiner Hilfe lässt sich dieser harte Panzer auflösen.",
		["221227"] = "Ich bin dir noch immer sehr dankbar für alles, was du für mich getan hast. Aber sieh dich doch nur einmal an. Endlose Gier hat dich in eine lebende Leiche verwandelt, die in einen dunklen Abgrund hinabsteigt. Dharam, ich kann es dir nicht mehr überlassen ... Du bist durch und durch ... verderbt.",
		["791279"] = "Hast du wieder Ton gefunden?",
		["665189"] = "Gott, was sonst noch verlangst du von mir? Bitte vergib unsere Übertretungen.",
		["9956021"] = "",
		["551266"] = "Ihr großes Ziel ist nicht, das Grab der Giganten zu betreten ... Sie will Rache an der Kirche üben und das Seligenland vernichten ...",
		["111086"] = "Fremder, wenn du meinen Jerry findest, wäre ich dir sehr dankbar.",
		["551410"] = "...",
		["551153"] = "Haha ... Ich überlebe einfach in dieser Welt, aber auf andere Art und Weise.",
		["663403"] = "Ja",
		["667136"] = "Sie widmete ihr Leben dem Glauben, aber leider wurde sie von ihm vereinnahmt.",
		["554180"] = "/t0",
		["117124"] = "Uh-huh, ich nehm's zurück. Heggem liegt bereits in Trümmern.",
		["331192"] = "Aber aus welchem Grund?",
		["221117"] = "Es ist nicht weit von hier, aber sein Tor ist mit einer Art Ritual versiegelt. Niemand kommt hinein.",
		["771107"] = "Agnes ... Agnes ... Warum spukt mir dieser Name im Kopf herum?",
		["661115"] = "Ähem, der faulige Geruch der Sendril ... er macht mich nervös.",
		["555251"] = "Barmherziger Aufseher, ich hoffe, du kannst mein Gebet hören ...",
		["661106"] = "Ich will die Wahrheit finden, aber sieh mich an ... Die Suche danach würde meinen Tod bedeuten.",
		["661105"] = "Alle im Seligenland scheinen das zu glauben, aber die Macht der Heiligen Säulen begann bereits vor mehr als zehn Jahren zu verblassen ... Ich, ein Blinder, war der Einzige, der den Unterschied bemerkte. Die anderen ließen sich durch die falschen Illusionen der Kirche in die Irre führen.",
		["991201"] = "Hahaha ... ich hab das Schiff der Irren zerstört ... jetzt kommen die hier nicht mehr weg ... Setzt den Willen von Lord Ebbinghaus durch ... sollen die doch für ihre Arroganz büßen ...",
		["664160"] = "/t0",
		["661155"] = "Es hat nichts mit mir zu tun ...",
		["663153"] = "<color=#DBBA7FFF>Den Gesegneten Basilisken einsetzen</color>",
		["662152"] = "",
		["551179"] = "Die Dunkelheit unter dem Heiligen Licht, die Sendril, von denen du sprichst, sind sie wirklich die Schuldigen?",
		["9933021"] = "Gehen",
		["30135237"] = "Bitte! Aufhören, die Sendril hat es hinfort geweht.",
		["331112"] = "Sie war meine Gefährtin ... Wir sind an diesen verlassenen Ort gekommen, um diesen Feigling zu finden. Wir konnten ja nicht ahnen, was für Katastrophen uns erwarteten.",
		["9936039"] = "",
		["662139"] = "",
		["331156"] = "Die hier?",
		["9941023"] = "Ein einziger Funke kann ein Feuer entfachen.",
		["552281"] = "",
		["334186"] = "/t0",
		["551438"] = "Ichthyosauria?",
		["442320"] = "Zum Seligenland gehen?",
		["991066"] = "Was hat es mit diesem Schiff der Irren auf sich, von dem du geredet hast?",
		["9966053"] = "",
		["9951047"] = "Hoffentlich sucht uns der Geist der Marcion nicht heim ...",
		["111057"] = "Sei vorsichtig.",
		["9961045"] = "Na na, Terrence, jetzt nur nicht schwarzmalen. Du weißt doch, dass ich mit solchen Situationen nicht gut umgehen kann, weil man Gefühle nicht in Geld messen kann.",
		["777031"] = "Leise ... und ganz vorsichtig. Wir wollen doch keine schlafenden Hunde wecken.",
		["9961042"] = "Glenn, ich brauche vielleicht deine Hilfe.",
		["441293"] = "Das weiß niemand ... Hinter Geheimnissen verbergen sich noch mehr Geheimnisse, und die meisten Mitwisser sind jetzt tot.",
		["9966039"] = "",
		["334188"] = "/t0",
		["441148"] = "Lügen werden immer schmackhaft an den Mann gebracht, haha ...",
		["9961037"] = "Und ich stelle Gott infrage. Warum stürzt er alle in Ungemach?",
		["9961035"] = "Seit ich dich kenne, muss ich immer an meine Tage mit Teresa denken.",
		["9963036"] = "Gehen",
		["772264"] = "",
		["551398"] = "Trotzdem danke, Kurier. Ich respektiere deine Entscheidung, aber ich muss sagen, dass ich sie bedauere ... Offenbar bleibt mir bis zur Entwerdung dieser Welt keine andere Wahl, als hierzubleiben und herauszufinden, ob es einen anderen Weg gibt. Aber bitte vergiss nicht, dass das Fragment noch immer der Schlüssel zu allem ist.",
		["222294"] = "",
		["664230"] = "/t0",
		["996464"] = "",
		["114142"] = "/t1",
		["668302"] = "",
		["221130"] = "Hat der Heilige Vater dich hier eingesperrt?",
		["111047"] = "Übrigens, das hier habe ich auf der Brücke dahinten gefunden.",
		["444149"] = "Die Tür scheint festzusitzen.",
		["551387"] = "Die verborgene Wahrheit fördert unwissende böse Absichten.",
		["554777"] = "Kann von dieser Seite aus nicht geöffnet werden.",
		["9943013"] = "Gehen",
		["991125"] = "Woher weißt du das alles?",
		["331065"] = "Wir haben keine Zeit für Sentimentalitäten ... Lass uns zuerst die Kapelle aufsuchen.",
		["441116"] = "Es ist vorbei. Unser Schicksal ist besiegelt. Dieser falsche Glaube zögert die unweigerliche Zerstörung nur hinaus.",
		["553288"] = "Gehen",
		["992001"] = "Die Ichthyosauria verlassen?",
		["221249"] = "Sieh dich um, ich bin es, der sie alle gerettet hat! Ich bin es, der die Blutlinie Adaminas weiterträgt!",
		["221129"] = "Der Heilige Vater befahl uns, jeden zu fangen, der an Adamina vorbeikommt. Ich wusste, dass es nicht richtig war, und am Anfang war auch niemand bereit, es zu tun, aber nach und nach haben wir uns daran gewöhnt, und jetzt ist niemand mehr dagegen.",
		["9922035"] = "",
		["663321"] = "...",
		["551282"] = "Leider kann ich dir keinen Rabatt gewähren, aber meine Sachen sind ihren Preis wert.",
		["461003"] = "Wir sitzen jetzt alle im selben Boot. Ich überbringe dir nur seine Nachricht ...",
		["221108"] = "Soll das heißen, die Krankheit der Giganten hängt mit dieser rothaarigen Hexe zusammen?",
		["991017"] = "Hust ... Hust ... Schnell ... Hust ... In die Höhle.",
		["554001"] = "Betreten nicht möglich.",
		["221116"] = "Am Orakel?",
		["331078"] = "Gehen wir ... zur Kapelle",
		["221254"] = "闭上你的嘴！",
		["225187"] = "Es ist der Heilige Vater, der die Blutlinie Adaminas fortführt ...",
		["551310"] = "Charakterattribute zurücksetzen?",
		["555170"] = "/t0",
		["551433"] = "Die verborgene Wahrheit fördert unwissende böse Absichten.",
		["773193"] = "Rosa schimmernde Knospe geben.",
		["9973451"] = "Reden",
		["991432"] = "Heggiestein",
		["554165"] = "/t0",
		["552154"] = "",
		["551386"] = "Nachsichtige Rache ... leidenschaftliche Konvergenz ... gefährlicher Versuch ...",
		["774033"] = "Wenn du mich nicht rauslässt, schwöre ich, dass ich dich irgendwann erwische!",
		["551383"] = "Ah ... gierige Obsession ... Sind wir nicht alle von ihr geblendet?",
		["9931008"] = "Lass diese sinnlosen Spielereien ... Sag mir lieber, ob es in Exilium irgendwelche Spuren gibt.",
		["221056"] = "Wenn sie bereits tot ist, können wir nichts mehr tun.",
		["551123"] = "Ist es das, worüber wir gesprochen haben?",
		["661224"] = "Teresa war direkt vor mir, und ich habe meine Chance wieder nicht nutzen können. Ob ich wohl noch eine bekomme ...?",
		["551374"] = "Die Schwachen und Kleinen zweifeln nicht einmal offensichtliche Lügen an.",
		["111101"] = "Ist da jemand? Hilfe! In bin in diesem Ei gefangen! Hilfe! Hilfe!",
		["441206"] = "Du scheinst klug zu sein, darum vertraue ich darauf, dass du ein vernünftiges Urteil treffen wirst.",
		["991451"] = "Gigantenstein",
		["9911020"] = "无论做出什么样的选择，我们的灵魂还是会重归黑暗……",
		["551408"] = "...",
		["773244"] = "Reden",
		["771216"] = "Wenn es eines Tages so weit sein sollte, wirst du bestimmt von mir hören. Aber versprich mir, dass du mit Teresa zurückkehren wirst.",
		["112907"] = "Zurück zur Kutsche?",
		["9921004"] = "Wer weiß ...? Ich habe in dieser endlosen Dunkelheit allen Sinn für die Zeit verloren.",
		["551393"] = "Ich hätte nie gedacht, dir hier zu begegnen. Du bist es doch, oder? Hat sich dein Herzenswunsch erfüllt?",
		["773076"] = "Wo ist Teresa?",
		["773204"] = "Reden",
		["441203"] = "Kurier, dieses Mal wird es für die Demiurgen keine zweite Chance geben. Um Vaillant vor dem Seligenland zu retten, haben wir bereits alles auf eine Karte gesetzt.",
		["551281"] = "Großzügiger Kurier, ich wünsche dir viel Erfolg.",
		["551355"] = "Die verborgene Wahrheit fördert unwissende böse Absichten.",
		["444228"] = "Kann im Augenblick nicht geöffnet werden.",
		["551350"] = "Die Schwachen und Kleinen zweifeln nicht einmal offensichtliche Lügen an.",
		["223113"] = "Gehen",
		["771199"] = "Das wäre dein sicherer Tod. Nichts anderes wäre damit gewonnen.",
		["221171"] = "Hast du meine Frau Mama gefunden?",
		["551396"] = "Das Fragment des purpurroten Meteors in deinem Besitz ist der Schlüssel zu allem. Du wirst es brauchen, wenn die Zeit gekommen ist. Hab Dank für dein Versprechen, aber du musst noch etwas warten. Ich werde dein Vertrauen nicht enttäuschen.",
		["114146"] = "/t1",
		["551234"] = "Was genau ist dir passiert?",
		["443188"] = "Gehen",
		["441225"] = "Das geht dich nichts an.",
		["113077"] = "<color=#DBBA7FFF>Warum hast du die Mine verlassen?</color>",
		["551188"] = "Hör mich an. Tu, was immer du tun kannst. Mehr als das würde nur qualvolle Schmerzen und den ewigen Kreislauf der Sünde mit sich bringen.",
		["4994993019"] = "Materialien übergeben",
		["333902"] = "Nein",
		["1001100015"] = "Mach endlich deinen Kopf frei! Es ist Zeit zu tanzen!",
		["331119"] = "Meine Dame, es tut mir leid, Euch im Stich zu lassen ... Ich habe nicht viel Zeit ... Albertus ... Albertus ... Euer Name kann nicht vergeben werden ... All Eure Sünden werden in diesem Land der Verstoßenen begraben!",
		["331074"] = "Deine Augen scheinen voller Zweifel zu sein.",
		["9999291"] = "Aber einer Obsession nachzugehen führt nicht immer zu einem guten Ende ...",
		["441331"] = "Alles in Ordnung?",
		["991117"] = "Weil du schon zu lange nicht mehr vom Seelenfluss getrunken hast. Du hast die Vergangenheit komplett vergessen.",
		["224170"] = "/t0",
		["222189"] = "",
		["551313"] = "Wer bist du?",
		["775118"] = "Nein ... Hilfe ... Hilft mir denn niemand ...?",
		["771140"] = "Du warst es, der Viola dazu gebracht hat, diese Kugel auf Teresa abzufeuern! Du warst es, der verzweifelt versucht hat, das Grab der Giganten zu öffnen, und der dieses Chaos verursacht hat! Und jetzt sagst du mir, du willst Teresa retten?",
		["1004100076"] = "Aber du hast sie benutzt ...",
		["9923043"] = "Outfit wechseln",
		["551293"] = "Haha ... Ich wusste, dass heute mein Glückstag ist! Auf unsere neue Freundschaft!",
		["551289"] = "Hörst du das ...? Die Geister der Marcion ... Sie wandern umher und flüstern!",
		["551275"] = "Endlich kann ich ins Seligenland zurückkehren ...",
		["9951031"] = "Du solltest froh sein, der Kirche für ein neues Leben den Rücken gekehrt zu haben.",
		["332905"] = "Zurück zur Kutsche?",
		["551273"] = "Haha ... Ich wusste, dass heute mein Glückstag ist! Auf unsere neue Freundschaft!",
		["661240"] = "Was denn? Kein Grund zur Aufregung, ich mag nun mal kein Licht ...",
		["331144"] = "Das ist es auch, aber die Kirche hat ihre Untersuchung in Katib nie eingestellt. Gerüchten zufolge hat man dort den Eingang zum Grab der Giganten gefunden.",
		["551363"] = "Die verborgene Wahrheit fördert unwissende böse Absichten.",
		["221303"] = "Wessen morbide Idee war das denn?",
		["228231"] = "",
		["9963039"] = "Segnen",
		["884045"] = "Ahh! Etwas ist in mir!",
		["334071"] = "/t0",
		["4994993007"] = "Nein danke.",
		["791340"] = "Dieser Armreif bedeutet mir die Welt. Ich suche seinen Besitzer.",
		["9961013"] = "Wenn wir im Seligenland ankommen, bleib mit Glenn in der Kutsche. Es gibt keinen Grund, dich da mit reinzuziehen.",
		["551392"] = "Kurier, würdest du bitte kurz warten, ich möchte dich um einen Gefallen bitten.",
		["447084"] = "Nun ... Natürlich können wir nicht einfach tatenlos zusehen und auf unseren eigenen Untergang warten. Das Risiko einzugehen, diesen Kerl zu finden, könnte der einfachste Weg sein, ins Seligenland zu kommen.",
		["9971308"] = "Was für einen Wunsch?",
		["661101"] = "Wohin soll ich denn sonst gehen? Kurier, an diesem Ort gibt es nichts weiter als Sendril und Gemetzel. Wo kann man noch sicher sein?",
		["30134240"] = "Teresa, wo bist du?",
		["551262"] = "Wenn wir sie ins Seligenland zurückkehren lassen, werden sich sämtliche Tragödien, die sich hier ereignet haben, wiederholen ... oder Schlimmeres ...",
		["554258"] = "/t0",
		["791303"] = "Freut mich für dich.",
		["663704"] = "Nein",
		["991411"] = "Keine Reaktion",
		["551244"] = "Ich habe etwas gesehen ... Teresa ... sie hielt das Siegel von Yekub ... und Ritter knieten vor ihr nieder ...",
		["441228"] = "Ich werde die letzte Glocke zum Läuten bringen. Danach weiß du, wo du mich finden kannst.",
		["991074"] = "Hab Dank, gütiger Fremder. Du hast mir geholfen, sie zu finden, bevor ich aufgeben konnte. Das müssen diese Irren von dem Schiff gewesen sein ... Die haben ihr einen so üblen Streich gespielt ...",
		["662401"] = "Die Tür öffnen?",
		["551126"] = "Endlich kann ich ins Seligenland zurückkehren ...",
		["112077"] = "",
		["331157"] = "Ihr werdet sie brauchen, um da draußen eine klaren Kopf zu behalten.",
		["555253"] = "Bitte hör auf ... Lass mich gehen ...",
		["447304"] = "Das müsste das hintere Tor zum Dorf sein, das Frollo erwähnt hat. Mit dem Schlüssel lässt es sich öffnen.",
		["551116"] = "So sieht man sich wieder, Kurier. Entschuldige, ich war mit den Gedanken bei meiner Frau im Seligenland. Und obwohl ich mich nicht mehr an ihr Gesicht erinnern kann, fehlt sie mir noch immer ... Ah ja, hast du die Sanduhr gefunden?",
		["9971341"] = "Da ist eine seltsame Inschrift eingraviert ...",
		["223106"] = "Warum riechen alle Jünger hier genau wie die Sendril?",
		["551276"] = "Diese Sifler nutzen jede Gelegenheit, um etwas Geld zu verdienen.",
		["1006100083"] = "In diesem Moment hast du etwas verloren ...",
		["221138"] = "Herr Kurier? Lebst du noch? Nein, nein, nein ... du musst tot sein! Ich kann meine Frau Mama nur sehen, wenn du tot bist! Wo ist Elma? Wo ist sie?!",
		["551337"] = "Die verborgene Wahrheit fördert unwissende böse Absichten.",
		["9971365"] = "Alle Anstrengungen werden sich irgendwann auszahlen. Das gilt auch für die Trübnis in deinem Herzen.",
		["553366"] = "Ja",
		["551270"] = "...",
		["995015"] = "Hust ... Hust ...",
		["9999295"] = "Im Seligenland sind die Kinder der Entstellten als \"Nachtwanderer\" bekannt ... Doch das ist lange her. Ich möchte nicht darüber sprechen.",
		["661151"] = "Kurier, ich hoffe, was ich getan habe, könnte eine neue Hoffnung auf Überleben aufkeimen lassen ... Mein ganzes Leben lang habe ich gegen das Schicksal gekämpft ... Meine Herkunft habe ich mir nicht aussuchen können, aber zumindest kann ich entscheiden, wie ich aus dem Leben treten möchte.",
		["554178"] = "/t0",
		["551160"] = "Als der Gigant begann, in den Heiligen Turm hineinzuwachsen, ließen die Marcion-Hexen alles einschlafen ... und es war nicht leicht für sie, aus diesem Traum zu erwachen.",
		["441196"] = "Wenn du noch immer ins Seligenland willst, ist dies deine letzte Chance. Eine Hand wäscht die andere, Kurier.",
		["557169"] = "Eigenartig. Hier steht: \"Der Stein der Vergangenheit\".",
		["113207"] = "Nein",
		["661311"] = "Klopf-klopf (Klopfen an der Tür)",
		["334224"] = "/t1",
		["551165"] = "Sie entschieden sich jedoch, dieses Geheimnis für immer im Turm zu bewahren, sodass sie für immer dort blieben …",
		["9932012"] = "",
		["111056"] = "Teilen wir uns auf. Du untersuchst den Gipfel, ich sehe mich in Heggem um.",
		["991042"] = "Du bist zu spät. Es ist nichts mehr übrig ... wie auch Erinnerungen und Hoffnung ... Hahaha ... Hä?",
		["441245"] = "Es geht um ein uraltes Ritual der Marcion-Hexe. Es schürt die unermessliche Macht in uns allen und wird die Demiurgen mit Edith vereinen.",
		["554129"] = "/t0",
		["445090"] = "Licht! Mehr Licht! Nimm es nicht fort!",
		["221101"] = "那个小女孩是个彻头彻尾的骗子！我早有预感！",
		["551154"] = "Ich war einst ein Priester der Kirche, aus Fleisch und Blut, aber das ist lange her. Inzwischen ist es ein seltsames Gefühl, die Form eines Menschen anzunehmen.",
		["662196"] = "Möchtest du fortfahren?",
		["771229"] = "Inwiefern?",
		["553138"] = "Gefunden.",
		["551345"] = "Das Mittelmaß ist stets besessen davon, aufzusteigen.",
		["551137"] = "Das solltest du lieber ganz schnell vergessen ... Falls nicht, wird dir das, was als Nächstes passiert, ganz und gar nicht gefallen, mein Freund.",
		["9931006"] = "Tut mir leid, aber ich habe gewonnen. Ich habe dir doch gesagt, der Ausgang wäre vorherbestimmt. Ich hoffe, du bist jetzt bereit, der Wahrheit ins Auge zu sehen.",
		["553135"] = "Nein.",
		["662320"] = "",
		["551131"] = "...",
		["111119"] = "Ist sie noch hier?",
		["551127"] = "Nur Geduld, mein kluger Freund, nur Geduld ...",
		["113071"] = "<color=#DBBA7FFF>Was ist hier los?</color>",
		["9933033"] = "Hast du vielleicht ein paar neue Items?",
		["331145"] = "Sie sucht also nach dem Grab der Giganten. Wozu?",
		["441187"] = "Marcion-Frau?! Was hat sie dir erzählt?!",
		["993257"] = "Gehen",
		["442063"] = "",
		["441260"] = "Kennen die Demiurgen das Ergebnis?",
		["331223"] = "Kein Umtausch, keine Erstattung. Gute Reise.",
		["9963032"] = "Handeln",
		["4994991010"] = "Geh schutzlos durch die Nacht, und du wirst in Stücke zerrissen ... Warum suchst du nicht etwas, um Licht ins Dunkel zu bringen?",
		["9921039"] = "Wer ist eigentlich dieser der Maskierte?",
		["554086"] = "/t0",
		["221261"] = "Geh nach Exilium, Terrence. Dort wirst du den Hinweis finden, den du suchst.",
		["771205"] = "Du hast getan, was du konntest, und mehr.",
		["551315"] = "Was willst du hier im Dunklen Nebel?",
		["441298"] = "Wie lange wird das dauern?",
		["991003"] = "Hrmph ... ich dachte schon, ihr seid Fischfutter.",
		["553903"] = "Ja",
		["791316"] = "Die Zeiten haben sich geändert, Terrence. Die Ketzerei ist für die Kirche vielleicht nicht die größte Bedrohung. Der Meister wollte mir seine Hand nicht geben ... bis ich meine eigene abschnitt ...",
		["664083"] = "/t0",
		["225280"] = "Oh, zu wenig, bei weitem zu wenig ... Hör auf zu schreien. Bald ... wird der Schmerz vergehen.",
		["9951052"] = "Für die Kirche ist Katib ein unermesslicher Schatz.",
		["225081"] = "Ich habe die Blutlinie unseres Heiligen Vaters fortgesetzt.",
		["9924034"] = "/t0",
		["441250"] = "Nein, das ist nur der Anfang ...",
		["9951038"] = "Ich bin keine bloße Schachfigur der Kirche, ich gehorche nur meinem Herrn. Meine Lippen sind versiegelt, bis wir die Wahrheit finden.",
		["337049"] = "Wie erwartet ist dieser Ort zu einem Paradies für Tod und Sendril verkommen.",
		["9951030"] = "So funktioniert die Kirche. Sie manipuliert Menschen mit ihrem Glauben und macht sie zu Schachfiguren.",
		["441270"] = "Du enttäuscht mich, Kurier. Du hattest deine Chance, aber hast die dümmsten Entscheidungen getroffen. Das Ritual ist abgeschlossen. Bist du jetzt bereit, dich dem gemeinsamen Zorn der Demiurgen zu stellen?",
		["9951029"] = "...",
		["113903"] = "Ja",
		["331160"] = "Und wenn sie noch lebt?",
		["9951018"] = "Wenn du von mir erwartest, dass ich meine Beichte ablege, vergeudest du deine Zeit.",
		["441099"] = "...",
		["9951017"] = "Terrence, obwohl ich nun schon so lange blind bin, sehe ich noch immer dein Gesicht voller Trauer vor mir. Es ist noch immer dasselbe.",
		["441278"] = "Verflucht. Diese Frau hat das Ritual vorangetrieben. Edit wird wiedergeboren werden!",
		["111087"] = "Oh, das ist Jerrys Mantel. Mein Jerry, du bist wieder da.",
		["115089"] = "Geliebter Jerry, Mutter bringt dich nach Hause. Mutter wird für immer bei dir sein.",
		["991456"] = "Heggiestein",
		["338230"] = "",
		["444269"] = "/t0",
		["448312"] = "",
		["448311"] = "",
		["331063"] = "Wunderschön, würde ich meinen ...",
		["9911018"] = "咳……咳……它还在等待，它总是在等待……",
		["117127"] = "Da! Da ist er! Sifler durchforsten das Dorf. Offenbar haben sie schon einiges an Wert zusammengetragen.",
		["224047"] = "/t1",
		["551425"] = "...",
		["661219"] = "Du bist zu weit gegangen!",
		["771108"] = "Ah ... Da bist du ja wieder. Hast du es dir anders überlegt? Wenn du von hier weg möchtest, nimm mir die Fesseln ab.",
		["331193"] = "Wenn das, was du sagst, wahr ist, dann liegt der Eingang zum Grab der Giganten in Katib. Dahin wird Teresa als Nächstes gehen.",
		["447158"] = "Der Kerl auf dem Glockenturm könnte unser Schlüssel zum Seligenland sein.",
		["552287"] = "",
		["9931021"] = "Nein, im Gegenteil.",
		["551150"] = "Oh, das ist aber merkwürdig ... Gib es immer noch normale Menschen in Katib, oder wirken sie einfach nur normal?",
		["118150"] = "",
		["221245"] = "Was genau hat sie an sich genommen?",
		["665194"] = "Vergib mir, Vaillant ... Aber dieses Opfer muss gebracht werden.",
		["114023"] = "Glenn, ich nehme dich nur mit, wenn du deinen Schnabel hältst.",
		["221054"] = "Keine Sorge, damit ist jetzt Schluss.",
		["665083"] = "Die Kirche ... muss ... den Preis ... bezahlen ...",
		["445089"] = "Das matte Licht wird ewig werden und uns ins Land des Nichts führen ...",
		["441290"] = "Soll das heißen, das Vaillant zu Unrecht beschuldigt wurde?",
		["4994992004"] = "Erfordert ranziges Walöl und 100 Geisterpilze.",
		["551099"] = "Ihr Vorrat ist begrenzt ... Es war schwer genug, die eine zu bekommen. Wenn du sie für mich finden könntest, hätten wir beide etwas davon.",
		["221193"] = "Bitte gib ihr das hier. Es ist ihr Lieblingsspielzeug. Ich möchte nicht, dass sie traurig ist ... Sobald ich das Kind des Heiligen Vaters geboren habe, kommt ihre Frau Mama zurück.",
		["443080"] = "Gehen",
		["991515"] = "Wir können nur geduldig warten und schließlich alles vergessen. Zum Glück leisten mir immer noch diese Bücher Gesellschaft.",
		["661116"] = "Wieso zeigt sich der Delegat nicht? Keiner weiß mehr, wann er seine Residenz das letzte Mal verlassen hat ...",
		["221106"] = "Warum riechen alle Jünger hier genau wie die Sendril?",
		["337081"] = "Er lebt noch!",
		["663404"] = "Nein",
		["1007100095"] = "Wem gehörte diese Stimme?",
		["331224"] = "Ich habe schon schwächere Kuriere als dich gesehen, und leider muss ich sagen, sind sie inzwischen alle tot.",
		["994888"] = "Kann im Augenblick nicht geöffnet werden.",
		["991085"] = "Das liegt bereits in ferner Vergangenheit ... Es ist längst in Vergessenheit geraten ... Alles hier ist verfallen. Vielleicht kennen nur die Leute da oben die wahre Antwort ...",
		["221306"] = "Dieses \"Wunder\" hatte eine seltsame Nebenwirkung. Es kam zu einem massiven Anstieg der Sterblichkeit der männlichen Bevölkerung. Aber das muss für den Heiligen Vater nichts unbedingt Schlechtes sein ...",
		["224900"] = "/t0",
		["9933036"] = "Gehen",
		["221309"] = "Du bist jetzt frei ...",
		["441165"] = "Wenn er alle Glocken läutet, wird die Dämmerung der Demiurgen in die Dunkelheit zurückgeworfen ...",
		["541002"] = "Was zum Teufel hat sie vor? Verheimlichst du mir nach all dieser Zeit noch immer irgendetwas?",
		["552132"] = "",
		["441109"] = "Es wird dauern, bis sie zurückkehren. Ich werde lange auf sie warten müssen. Ich hoffe nur, sie enden nicht wie ihr Vater und lassen mich ein Leben lang warten.",
		["9941035"] = "Nach so vielen Jahren ist es das erste Mal, dass ich Gott um Hilfe bitte.",
		["991104"] = "Ich fühle mich so ohne Hoffnung. Vielleicht mögen das mal meine Leute gewesen sein, aber ich kann mich nicht an sie erinnern.",
		["662701"] = "Die Tür öffnen?",
		["9972455"] = "",
		["225183"] = "Heiliger Vater, ich bin willens, für dich ins Fegefeuer zu gehen.",
		["441141"] = "Elena?",
		["111082"] = "Ich habe den Schatz gestohlen ... um meinen Jerry zu finden. Ich habe mir geschworen, ihn zurückbringen, sobald ich Jerry finde, aber ich habe überall gesucht. Fremder, würdest du mir bitte helfen?",
		["668303"] = "",
		["9971470"] = "Wie auch immer, unter dem Heiligen Turm in Katib, hinter dem Tor, das mit der Macht der Sendril gefüllt ist, liegt die verborgene Wahrheit.",
		["9953042"] = "Handeln",
		["551120"] = "In der Tat, die Sachei ist nicht ohne, denn, gelinde gesagt, auch der größte Glückspilz kann einfach mal Pech haben. Man kann einfach nie wissen, wann man hier auf diese verfluchte Marcion-Hexerei trifft.",
		["9971388"] = "Ein Gott wird erschaffen, als unsichtbares Leitbild, und so geben die Menschen ihrem Wahnsinn einen Sinn. Hat jemand ernsthaft darüber nachgedacht, warum Gott eine verfluchte Welt erschaffen hat? Es gibt immer einen Grund, aber manche Dinge sind nicht mehr zu verstehen.",
		["799315"] = "Vielleicht. Aber ich mache mir keine zu großen Hoffnungen. Beeil dich, Kurier. Gehe entschieden in die finstere Nacht und stemme dich energisch gegen das Sterben des Lichts.",
		["9956058"] = "",
		["111098"] = "(Das riesige Ei spürt deine Schritte und erzittert.)",
		["991421"] = "Stein der Menschlichen Natur",
		["9961021"] = "Ich weiß es nicht, aber ich muss es versuchen.",
		["791359"] = "Als ich das Waisenhaus der Kirche verließ, hielt ich mich für mutig, doch nun wird mir klar, dass ich meine Vergangenheit nicht hinter mir lassen kann.",
		["664161"] = "/t0",
		["9933035"] = "Hast du vielleicht ein paar neue Items?",
		["461004"] = "Ich weiß, was ich tue.",
		["115066"] = "Jerry ... Wo ist mein Jerry ...? Mein Jerry ...",
		["228233"] = "",
		["661180"] = "Niemand hat ihn seit der Verurteilung Vaillants gesehen. Lediglich ein schmerzhaftes Stöhnen aus den Tiefen des Seligenlandes ist zu hören. Es dauert bereits seit über einem Jahrzehnt an.",
		["333158"] = "Wieso bist du noch hier?",
		["441071"] = "Hmpf ... Er hat geglaubt, die Demiurgen würden sich aus Angst zurückziehe. Unsinn!",
		["771147"] = "Terrence, ich weiß, es ist alles zu viel für dich ... Aber das Wichtigste im Augenblick ist, auf den Grund dieser Höhle zu kommen und Teresa zu retten. Danach werde ich dir alles erklären.",
		["441204"] = "Ich muss zurück in den Gerichtssaal, wo eine wichtigere Angelegenheit mich erwartet.",
		["112111"] = "",
		["224888"] = "Kann im Augenblick nicht geöffnet werden.",
		["9941020"] = "Ob sie uns helfen können?",
		["554138"] = "/t0",
		["331099"] = "Kurier, ob du mir einen Gefallen tun könntest? Offenbar habe ich meinen Hochzeitsring in der Kapelle verloren. Wenn meine Verlobte zurückkehrt, möchte ich ihr meine ewige Treue schwören, mit ihr die Ringe tauschen und ihr für ihre Hilfe danken. Würdest du den Ring für mich finden?",
		["111053"] = "Die Person, die du suchst?",
		["551136"] = "Machst du Witze? So viel Pech hat kein Mensch! Oder willst du die Sanduhr etwa für dich behalten?",
		["9961048"] = "Was glaubst du wohl, warum ich noch am Leben bin? Niemand kennt das Seligenland so gut wie ich. Wenn dir dein Leben also lieb ist, solltest du mich mitnehmen.",
		["221069"] = "Noch ein Feigling.",
		["221206"] = "Der gesegnete Heilige Vater! Danke, dass ich deine Blutlinie fortführen durfte ...",
		["9921023"] = "Was ist zwischen euch beiden passiert?",
		["111052"] = "...",
		["225232"] = "看看你们都干了什么好事！",
		["333221"] = "Gehen",
		["551164"] = "Sie gingen alle zurück zum Heiligen Turm, und das furchtbare Geheimnis darunter wurde entdeckt.",
		["443105"] = "Ja, habe ich.",
		["991219"] = "Ich hab dafür jetzt keine Zeit. Du solltest dir eine sichere Bleibe suchen.",
		["551397"] = "Danke für deine Hilfe, ich weiß sie sehr zu schätzen. Entschuldige, aber ich bin nicht in der Stimmung, eine gute Tat zu tun.",
		["553105"] = "Gefunden.",
		["551271"] = "Ach, du bist's, mein Freund. Wie läuft's denn so? Hast du die Sanduhr gefunden?",
		["791325"] = "Keine Sorge, er ist nun eine \"richtige\" Waffe geworden. Der Hass versorgt ihn mit einem steten Strom aus Energie.",
		["112067"] = "",
		["9943027"] = "Gehen",
		["441285"] = "Aber warum kannst du mir nicht schon jetzt alles sagen?",
		["9971282"] = "Ich habe etwas Ton gefunden ... Ich hoffe, es hilft.",
		["551280"] = "Mein Stand ist schon lange verwaist. Vielleicht kann ich dir einen guten Preis machen.",
		["111099"] = "(Das riesige Ei spürt deine Schritte und erzittert.)",
		["1004100112"] = "Doch wir wissen, dass alle Demiurgen nur eine einzige Stimme brauchen.",
		["112103"] = "",
		["994478"] = "/t0",
		["9932020"] = "",
		["551323"] = "Diejenigen, die der Besessenheit nachjagen, können die Verschwörung von Licht und Dunkelheit nur beenden, wenn man sie im Boden verscharrt.",
		["881080"] = "Unwissenheit und Gier fließen hier zusammen ... Lange lebte ich erhobenen Hauptes, und das Verneigen ist mir nun fremd.",
		["441332"] = "Vergib mir ... Der beruhigende Klang der Wellen ließ mich in Gedanken versinken. Ich hatte nicht erwartet, den purpurroten Meteor von Ichthyosauria in nur wenigen Monaten zu finden ... All unsere Anstrengungen haben endlich Früchte getragen.",
		["227120"] = "Ich konnte Viola finden, wurde aber von einer überirdischen Macht zurückgehalten ... Ich kam nicht einmal in ihre Nähe ...",
		["551236"] = "So redet man aber nicht mit jemandem, der sich Sorgen macht.",
		["557242"] = "Irgendetwas wurde hier bewahrt und angebetet.",
		["551138"] = "Ist es das, worüber wir gesprochen haben?",
		["665131"] = "Du kannst uns nicht aufhalten ... Die Demiurgen werden sich alles zurückholen, was ihnen genommen wurde ...",
		["331148"] = "Das kommt mir bekannt vor ...",
		["441074"] = "Kurier, der Kerl ist meine Beute. Überlass ihn mir.",
		["446304"] = "",
		["223315"] = "Ihr die Aktiven Sendril geben",
		["551243"] = "Alles in Ordnung?",
		["9971354"] = "Es beschreibt den Tag meiner Geburt.",
		["223191"] = "Gehen",
		["221190"] = "Grüner Mantel? Ich habe deine Tochter gesehen.",
		["461005"] = "Es ist besser so. Ich habe das Gefühl, dass du in letzter Zeit etwas vergesslicher geworden bist.",
		["111071"] = "Was ist hier los?",
		["1001100016"] = "Er sieht unglücklich aus ...",
		["551263"] = "Wieso?",
		["115140"] = "Die Trauer hält mich nüchtern ...",
		["9999296"] = "Könntest du nicht irgendwo neu anfangen?",
		["661264"] = "Ha-ha, dummer Kurier.",
		["111074"] = "Ein Kurier hat die überlebenden Jünger in die Mine gebracht. Angeblich gibt es dort einen Schatz, der Heggem retten kann.",
		["773225"] = "Gehen",
		["224169"] = "/t0",
		["222249"] = "",
		["221126"] = "Der Heilige Vater sagte, solange ich Fremde nach Adamina bringe und die Jagd beende, könnte ich meine Frau Mama wiedersehen.",
		["9923044"] = "Gehen",
		["113206"] = "Ja",
		["551417"] = "Die Schwachen und Kleinen zweifeln nicht einmal offensichtliche Lügen an.",
		["111080"] = "Mein lieber Jerry, er wollte draußen mit den John-Brüdern spielen, aber er ist seit zwei Tagen nicht zurückgekommen. Er spielt einfach zu gern.",
		["553395"] = "Ablehnen",
		["111081"] = "Wie hast du hier draußen auf dich aufpassen können?",
		["551440"] = "...",
		["552238"] = "",
		["9953046"] = "Gehen",
		["553272"] = "Gefunden.",
		["661170"] = "Was zum Teufel ist vor fünfzehn Jahren passiert?",
		["551360"] = "Die Liebe ... Sie ist es, die den Wahnsinn bringt.",
		["991038"] = "Was auch immer passiert, ich hoffe nur, ich lebe lange genug, um diesen Lord Ebbinghaus kennenzulernen.",
		["228232"] = "",
		["111076"] = "Ja, das Licht, das wir gefunden haben, war hell genug, um die Sendril in der Mine zu zerstreuen. Dann haben wir uns einfach dort versteckt und abgewartet, dass die Kirche uns retten kommt.",
		["551274"] = "Alles ist bereit, also nichts wie los. Heute ist das Glück auf meiner Seite, und wir werden das Siegel von Yekub unter diesem Heiligen Turm finden.",
		["441205"] = "Pioniere der Demiurgen haben sich bereits auf den Weg ins Seligenland gemacht. Wenn es uns nicht gelingt, sie zu unterstützen, wird ihr Opfer umsonst gewesen sein.",
		["9951024"] = "Was in Katib passiert ist ... Das Massaker der Marcion ... Ist es das, an dem du dich festklammerst?",
		["9971309"] = "Als ich diese Hand von meinem Meister bekam, sagte er mir, dass außer diesem Arm noch drei weitere alte Relikte in diesen Sarg gelegt wurden, doch sie gingen verloren oder wurden gestohlen. Nur durch die anderen Artefakte kann die Hand beruhigt werden, andernfalls könnte sie sämtliches Leben verschlingen.",
		["551110"] = "Deine Grübeleien machen die Sache nicht besser.",
		["994016"] = "/t0",
		["661261"] = "Ha-ha, dummer Kurier.",
		["113133"] = "Gehen",
		["661102"] = "...",
		["115142"] = "Die Trauer hält mich nüchtern ...",
		["555271"] = "Ahh ... ich bin so dumm ... Ich habe gegeben, was ich konnte ... Um ihn zu jagen ... Vergeblich ... Nun bleiben mir nur noch Lügen und Spott ...",
		["111049"] = "Das sind Worte aus dem Seligenland, und nun findet man sie schon in Heggem.",
		["9961014"] = "Ich werde nicht einfach dasitzen und Däumchen drehen.",
		["775034"] = "/t0",
		["4994993006"] = "Aufgeben",
		["221111"] = "Der Heilige Vater? Ist er auch ein Jünger Adaminas?",
		["994036"] = "/t0",
		["791338"] = "Ich suche etwas Bestimmtes.",
		["441061"] = "...",
		["114127"] = "/t0",
		["552284"] = "",
		["223294"] = "Noch nicht.",
		["664888"] = "Kann im Augenblick nicht geöffnet werden.",
		["662901"] = "Zurück zur Kutsche?",
		["111126"] = "Seid Ihr das, warmherziger Herr? Seid ihr zurück, um mich zu retten?",
		["113901"] = "Ja",
		["441156"] = "Wir können ihn nicht alle Glocken läuten lassen, ansonsten wird der Plan der Demiurgen ernsthafte Probleme bekommen.",
		["991135"] = "So wie wir alle geglaubt haben, dass der Seelenfluss niemals austrocknen würde, bis er eines Tages plötzlich weg war.",
		["9953021"] = "Segnen",
		["117095"] = "Heggems Licht verblasst, und der Schatz ist verloren. Diese Armen Jünger sind zu Monstern geworden.",
		["771253"] = "Ich werde an deine Worte denken.",
		["991512"] = "Es wird immer schwieriger, reines Seelenfluss-Wasser zu finden.",
		["661175"] = "Tote erzählen keine Geschichten, aber die Marcion sind untrennbar mit der ganzen Sache verbunden. Niemand ist unschuldig. Wer sich darauf einlässt, die Wahrheit zu erkunden, wird darin gefangen.",
		["331058"] = "Hier ist alles so gut wie tot.",
		["9931007"] = "Terrence, du musst verrückt geworden sein!",
		["221200"] = "Das Baby kommt! Tut das weh!",
		["553359"] = "Ja",
		["9922041"] = "",
		["9961049"] = "...",
		["221163"] = "Du wirst dich freuen, es zu sehen.",
		["771239"] = "Gib auf Viola acht.",
		["221172"] = "Es war ein Abschied für immer ...",
		["221228"] = "Dann lässt mir keine Wahl! Gib sie sofort her!",
		["111044"] = "Was in aller Welt lässt ihn so schwach werden?",
		["222268"] = "",
		["111073"] = "Hat einer der Jünger überlebt?",
		["443901"] = "Ja",
		["112205"] = "Das Ei des Trägers zurückgeben?",
		["553375"] = "Ja",
		["9942032"] = "",
		["111048"] = "Das ist einfach nur ein Hexenvermerk mit einfacher Alchemie, sonst nichts.",
		["551375"] = "Ah ... gierige Obsession ... Sind wir nicht alle von ihr geblendet?",
		["9973381"] = "Vertrag",
		["9921036"] = "Die Antwort darauf findest du nur in Adamina.",
		["993218"] = "Ablehnen",
		["9933017"] = "Handeln",
		["221223"] = "Teresa ...",
		["9921018"] = "Gib schon her!",
		["9931031"] = "Gut, dann vergiss es.",
		["334050"] = "Kirchengarde. Ihre Leichen sind verkümmert, und ihre Kehlen weisen tödliche Schnitte auf.",
		["553426"] = "Die Melosflasche füllen.",
		["226269"] = "",
		["991446"] = "Siflerstein",
		["221314"] = "Ich hoffe, du bist nicht verletzt, gütiger Kurier. Ansonsten hätte ich jetzt ein schlechtes Gewissen.",
		["551343"] = "Ah ... gierige Obsession ... Sind wir nicht alle von ihr geblendet?",
		["333901"] = "Ja",
		["225082"] = "Bleibt mir vom Leib, ihr dreckigen Fremden!",
		["331179"] = "Wenn das so ist, möchte ich zurück in die gute alte Zeit.",
		["441114"] = "Ah, wie sehr habe ich mich danach gesehnt, im strahlenden Licht des Seligenlandes zu baden …",
		["552310"] = "",
		["778301"] = "",
		["551190"] = "Ist nichts Ernstes. Ich muss mich nur ein wenig ausruhen.",
		["9951040"] = "Das könnte man sagen, mein Freund. Keine Sorge, ich weiß, was und was nicht gesagt werden kann.",
		["771206"] = "Ach ja? Ich habe mich nicht einmal richtig von ihm verabschiedet. Ich weiß nicht einmal, ob er ein guter Mensch war oder nicht.",
		["551151"] = "Moment mal ... einer von euch ... versucht etwas zu verbergen, aber ich rieche das Blut ... du warst einst ein Handlanger der Kirche ... und hast eine lange Vorgeschichte mit diesem Ort ...",
		["553351"] = "Ja",
		["441246"] = "Frollos Glocke eliminiert nicht nur den weißen Staub, sondern treibt auch noch alle Menschen, die das Ritual empfangen haben, in den Wahnsinn.",
		["774035"] = "Rühr ihn ja nicht an, sonst kannst du was erleben!",
		["334058"] = "/t0",
		["224123"] = "/t0",
		["553285"] = "Gehen",
		["441303"] = "Mensch, willst du mit mir handeln? Ich besitze viele Schätze.",
		["991150"] = "Natürlich kann sich in Ichthyosauria niemand allzu sehr auf sein Gedächtnis verlassen, also keine Sorge.",
		["222288"] = "",
		["9933034"] = "Was weißt du über Exilium?",
		["9961022"] = "Und wenn es dir nicht gelingt?",
		["774041"] = "/t0",
		["443104"] = "Nein, tut mir leid.",
		["331087"] = "Oh, entschuldige bitte vielmals, Kurier. Ich hoffe, mein abscheuliches Äußeres hat dich nicht erschreckt. Keine Angst, auch ich war einst Ritter der Kirche.",
		["9941013"] = "Als Kurier komme ich nicht einmal in die Nähe vom Seligenland.",
		["551312"] = "Kein Sternenwein",
		["441271"] = "Wie gesagt, alles was sich wagt, sich den Demiurgen auf ihrer Suche nach dem Licht entgegenzustellen, wird vernichtet.",
		["221308"] = "Ansonsten sind uns die Hände gebunden, und wir könnten Hilfe gebrauchen.",
		["443302"] = "Gehen",
		["665070"] = "Wenn die Marcion-Hexe den Delegaten findet, wird Vaillant endlich frei sein.",
		["9963014"] = "Gehen",
		["661216"] = "Herrgott noch mal, du willst Teresa wohl nicht lebend wiedersehen, oder?",
		["331154"] = "Benita, wenn wir die restliche Teile des Meteors finden, könntest du sie dann wieder zusammensetzen?",
		["997208"] = "Diebe? Schau sich mal einer das an! Mir fällt nichts ein, was mal stehlen könnte ... vergeuden wir nicht unsere Zeit — keiner dieser Leute scheint mir normal zu sein.",
		["9921047"] = "Ich bin ein wenig müde und muss mich ausruhen.",
		["9963026"] = "Reden",
		["553311"] = "Nein",
		["441070"] = "Das mit dieser Ratte scheint doch nicht so einfach zu sein.",
		["999282"] = "Hey! Hast du den Kontrollhebel und das Zahnrad schon gefunden?",
		["9961039"] = "Aber das Leid wird ein Ende haben. Ganz gleich, was aus Teresa geworden ist, ich gehe mit ihr durch Dick und Dünn.",
		["447137"] = "Die Glocke auf dem Turm läutet. Wir müssen so schnell wie möglich dort hinauf, solange er noch dort ist!",
		["793312"] = "Ich habe ein paar alte Relikte gefunden.",
		["791282"] = "Ich habe etwas Ton gefunden ... Ich hoffe, es hilft.",
		["991092"] = "Ich kann dir nicht helfen ... Nimm das hier, vielleicht hilft es dir ja.",
		["227119"] = "Gott sei Dank, Terrence. Du lebst ... Hier stinkt es übrigens ganz schön ...",
		["991044"] = "Verschwinde, es ist nicht mehr genug für uns alle da. Du kriegst nichts, bis jemand etwas Wasser bringt ...",
		["447159"] = "Der Kerl auf dem Glockenturm könnte unser Schlüssel zum Seligenland sein.",
		["331189"] = "Das ist ... das Siegel von Yekub. Angeblich ist es der Schlüssel zum Grab der Giganten.",
		["771187"] = "Ah ... hast du sie gefunden? Dieses Licht, dieser Geschmack ...",
		["663332"] = "Gehen",
		["552373"] = "Kosten: 1 Kondensierter Honig und 180 Blumentierknochen. Weiter?",
		["991190"] = "Lord Handel, es tut mir leid, dass ich den Ruhm der Ritter nicht erben kann.",
		["331168"] = "Ahh ... Ich weiß nicht, wie ich dir danken soll, Kurier.",
		["113104"] = "<color=#DBBA7FFF>Wie kann ich dir helfen?</color>",
		["991086"] = "Worauf wartest du?",
		["221290"] = "Vielen Dank, Herr Kurier.",
		["334051"] = "Kirchenritter mit Speeren. Ihre rostigen Rüstungen quietschen im Wind – sie sind mausetot.",
		["100510052203"] = "Ja",
		["333220"] = "Handeln",
		["331221"] = "Ich habe schon schwächere Kuriere als dich gesehen, und leider muss ich sagen, sind sie inzwischen alle tot.",
		["221107"] = "Aus den Hinweisen über die Krankheit der Giganten folgerte der Heilige Vater, dass eine böse rothaarige Hexe Adamina ins Verderben führen würde.",
		["443065"] = "Die Revolte der Demiurgen ...",
		["771196"] = "Du wusstest, dass es passieren würde?! Und dennoch hast du sie in Gefahr gebracht?",
		["331184"] = "Wenn Teresa letztendlich zum Monster würde, würdest du ...?",
		["993070"] = "Gehen",
		["773086"] = "Dein Vater ...",
		["551358"] = "Die Schwachen und Kleinen zweifeln nicht einmal offensichtliche Lügen an.",
		["9952017"] = "",
		["9931022"] = "Du scheinst etwas gegen Norwood zu haben.",
		["9931025"] = "Das ist was anderes.",
		["994415"] = "",
		["331142"] = "Sie in einen verbotenes Gebiet von Katib eingedrungen.",
		["551403"] = "...",
		["551238"] = "Offenbar wurde er von den Sendril kontaminiert.",
		["113113"] = "Eine komische Hexe?",
		["9953018"] = "Gehen",
		["224049"] = "Dieser Eingang scheint verschlossen zu sein.",
		["441295"] = "Mehr weiß ich nicht. Geh ins Seligenland, stell deine Fragen der Marcion-Hexe, die du nun schon so lange suchst. Sie wird dir deine Fragen beantworten.",
		["661179"] = "Der Delegat ist nicht erschienen?",
		["331177"] = "Aber zumindest hat er sein Versprechen gehalten.",
		["113202"] = "Ja",
		["9963053"] = "Handeln",
		["991467"] = "Großzügiger Kurier, ich wünsche dir viel Erfolg.",
		["9943040"] = "Die Vergangenheit zwischen den Demiurgen und der Kirche?",
		["111041"] = "Der alten Legende nach ist dies das Omen, dass der Gigant sich auf seine Reise zu seinem Grab vorbereitet.",
		["661228"] = "Aber der Delegat ist tot. Wir kommen nicht mehr hinein.",
		["441162"] = "Bist du verletzt?",
		["661117"] = "Sei gesegnet, Kurier. Ich hoffe, wir alle werden dich wiedersehen.",
		["441142"] = "Diese verrückte Frau, die hinter mir her war. Sie wurde zum Vize-Pontifex gewählt. Die Demiurgen beschwören ihren eigenen Untergang herauf.",
		["441283"] = "Vergiss nicht unsere Geheimwaffe. Schnell, dies ist meine letzte Bitte. Wenn du mir hilfst, werde ich dir alles sagen.",
		["9971389"] = "Die Menschen glauben, dass Gott ihnen die Fähigkeit gegeben hat, alles zu verstehen, aber bedeutet alles wirklich \"alles\"? Der Mensch hasst Ungewissheit, also sucht er Schutz im Glauben. Das ist seine Sünde. Das Licht des Giganten ist nicht die Fackel der Vernunft. Ohne nachzudenken ist der Mensch nur eine Hülle.",
		["223902"] = "Nein",
		["991105"] = "Suchen wir zuerst nach jenem Jungen. Er könnte etwas wissen.",
		["444320"] = "/t0",
		["117029"] = "Heggem ist nicht der erste Gigant, der sein Licht verliert. Jetzt kann er den eindringenden Dunklen Nebel nicht mehr aufhalten. Wo sind Heggems Jünger?",
		["668301"] = "",
		["331181"] = "Es geht mir gut ...",
		["551183"] = "Ich möchte einen Weg finden, diesen ewigen Traum zu beenden, also hat es mich hierhergezogen ...",
		["441077"] = "Was hat es mit dieser Glocke auf sich? Sie scheint dir große Schmerzen zu bereiten.",
		["663405"] = "Ja",
		["225080"] = "Kleines! Meine liebe Kleine!",
		["337084"] = "Knapp daneben. Es ist etwas anderes.",
		["991094"] = "Haha, endlich！Das ist ein Wunder！",
		["447085"] = "Edith ist wieder zu Licht gekommen? Wie ist das möglich?",
		["991240"] = "Genug ... ich verstehe ... Könntest du bitte ... mir sagen, wie es da draußen ist ... sag mir, wie sieht der Himmel aus?",
		["884057"] = "Du bist nicht für die Wahrheit bereit.",
		["773393"] = "Nein",
		["9971471"] = "So viele Hinweise. Welchem soll ich nachgehen?",
		["551394"] = "Wenn wir beide aus diesem Albtraum befreit werden können, würdest du mir dann zur Hand gehen?",
		["772244"] = "",
		["9943012"] = "Reden",
		["447092"] = "Offenbar verwirrt die Glocke hier jedem den Verstand ...",
		["442188"] = "",
		["441100"] = "Immer noch ängstlich? Haha ... Darf ich dich um einen Gefallen bitten, bevor du gehst?",
		["221148"] = "Aber bitte sei vorsichtig. Sie hat sich zuletzt ein wenig ... merkwürdig benommen. Verärgere sie nicht, ich weiß nicht, zu was sie in der Lage ist.",
		["793382"] = "Vertrag",
		["114038"] = "/t0",
		["9971455"] = "Denkst du noch immer an das, was gerade geschehen ist?",
		["9931040"] = "Danke für deinen Einkauf!",
		["9946046"] = "",
		["334227"] = "/t0",
		["441107"] = "Ich habe meinen Lebenszweck erfüllt und drei Soldaten für die Demiurgen großgezogen. Nun kann ich mich endlich zur Ruhe setzen.",
		["551241"] = "Irgendetwas wurde hier bewahrt und angebetet.",
		["225233"] = "吼！",
		["991423"] = "Siflerstein",
		["221135"] = "Es war Elma in Adamina, und sie war neben meiner Frau Mama der netteste Mensch. Sie hat mich hier eingesperrt, um mich vor Gefahren zu schützen.",
		["9971307"] = "Keine Sorge, sie wirkt nur rastlos, weil sie einen unerfüllten Wunsch hat.",
		["443120"] = "Eine Fettkerze geben.",
		["9961047"] = "Aber ich muss mich auf das Schlimmste vorbereiten.",
		["334180"] = "/t0",
		["661181"] = "Du meinst ... dieses Stöhnen ist der Delegat?",
		["9941041"] = "Das haben sie sich selbst zuzuschreiben. Denn anstatt im Heiligen Kloster die alten Schriften zu studieren, bestanden sie darauf, mit Pontifex Vaillant den Giganten zu studieren.",
		["9973378"] = "Vertrag",
		["227222"] = "Das ist das Versteck des Heiligen Vaters. Das Tor scheint durch ein uraltes Ritual versiegelt worden zu sein.",
		["9962035"] = "",
		["9999304"] = "Möchtest du wirklich das Schicksal von ganz Solas aufs Spiel setzen?",
		["551163"] = "Was ist mit den anderen in der Kirche? Wo sind sie jetzt?",
		["773008"] = "Nein",
		["668304"] = "",
		["221059"] = "In Adamina!",
		["793290"] = "Tut mir leid.",
		["221057"] = "Sie lebt, aber es ist nicht mehr viel Zeit.",
		["551320"] = "Warte, auch du besitzt offenbar eine Obsession, aber sie ist noch nicht stark genug ... Vielleicht eines Tages, wenn du zurückkommst ...",
		["441155"] = "Kurier, ich warne dich erneut. Dies ist Sache der Demiurgen und geht dich nichts an.",
		["114140"] = "/t1",
		["441259"] = "Doch diese Menschen wurden Teil des Bewusstseins des Giganten – und danach seine Marionetten.",
		["118148"] = "",
		["551325"] = "Diejenigen, die der Besessenheit nachjagen, können die Verschwörung von Licht und Dunkelheit nur beenden, wenn man sie im Boden verscharrt.",
		["551362"] = "Nachsichtige Rache ... leidenschaftliche Konvergenz ... gefährlicher Versuch ...",
		["771089"] = "Angesichts dessen, was Norwood getan hat, bleibt meinem Vater nicht mehr viel Zeit.",
		["444179"] = "Kann ohne Schlüssel nicht geöffnet werden.",
		["9971283"] = "Danke. Schön, dass du daran gedacht hast.",
		["441244"] = "Was tut dieser weiße Staub?",
		["661803"] = "Halt! Nicht aufmachen!",
		["461006"] = "Du solltest dich um deinen eigenen Kram kümmern.",
		["553415"] = "Die Melosflasche füllen.",
		["667073"] = "Sie ist dem rachsüchtigen Willen der Demiurgen anheim gefallen. Das gestürzte Seligenland, die verrückte Frau, es ist ein absoluter Albtraum.",
		["777040"] = "Was um alles in der Welt ist hier passiert?!",
		["991470"] = "Großzügiger Kurier, ich wünsche dir viel Erfolg.",
		["991216"] = "Danke! Die Leute von Ichthyosauria werden dankbar sein. Und da wir jetzt Freunde sind, kannst du das hier haben. Viel Glück.",
		["9971363"] = "Diese Worte sind dir gewidmet. Diese Person hat dich nie vergessen.",
		["9971331"] = "Ich werde diese Hand in ihrer Heimatstadt begraben und sie in Frieden ruhen lassen. Vielleicht folgen die Marcion bald dem Weg ihrer Ahnen und existieren nur noch in Mythen, bevor sie irgendwann völlig in Vergessenheit geraten und spurlos aus der Geschichte verschwinden.",
		["441286"] = "Glaubst du an Zufälle, Kurier?",
		["9999293"] = "Genug. Nachtwanderer können nicht mehr wie menschliche Wesen existieren. Wir verlieren das Recht auf freie Entscheidungen mit der Geburt.",
		["441337"] = "Doch Ichthyosauria kann nicht länger warten.",
		["663705"] = "Ja",
		["551338"] = "Das Streben nach Erhabenheit ist die ultimative Bedrohung des Menschen.",
		["331107"] = "Kurier? Wie bist du hier hereingekommen? Hat der Kerl an der Tür dich nicht gesehen? Ich möchte keinen Ärger, also belästige mich bitte nicht!",
		["884006"] = "Die Angst, sie zu verlieren? Oder etwas anderes? Etwas Dunkleres? Geheimeres? Ich muss dir die Wahrheit verraten ... eine schockierende Wahrheit ...",
		["9973458"] = "An den Delegaten",
		["9971462"] = "Sie kennt dich einfach viel zu gut.",
		["9971465"] = "Wenn es nur Norwood wäre, würde es mich nicht wundern. Aber Viola ... Ich weiß nicht, warum sie da mitgemacht hat.",
		["9971466"] = "Ich kann es auch nicht glauben.",
		["9971467"] = "Dann ist das also die Wahrheit, die das Seligenland die ganze Zeit zu begraben versucht? Ein von den Sendril vollständig kontaminierter Delegat?",
		["114001"] = "Betreten nicht möglich.",
		["442119"] = "Fremder, kannst du mir zur Hand gehen? Mir ist ... so kalt. Hast du vielleicht Kerzen dabei?",
		["444092"] = "/t0",
		["9973388"] = "Gehen",
		["9951045"] = "Hast du von diesem Massaker in Katib gehört?",
		["662240"] = "Bereit, zu gehen?",
		["9976379"] = "",
		["111075"] = "Habt ihr ihn gefunden?",
		["771260"] = "Mein Stand ist schon lange verwaist. Vielleicht kann ich dir einen guten Preis machen.",
		["227053"] = "Das Pech scheint dir an den Fußsohlen zu kleben, Terrence, ebenso wie die Monster.",
		["773261"] = "Handeln",
		["661103"] = "Kurier, findest du das nicht auch seltsam? Dieser Ort wird von dem ewigen Licht der Säulen geschützt, und dennoch ist es zu schwach, um dem Eindringen der Sendril zu widerstehen.",
		["552104"] = "",
		["771262"] = "Leider kann ich dir keinen Rabatt gewähren, aber meine Sachen sind ihren Preis wert.",
		["551414"] = "...",
		["221170"] = "Herr Kurier, du bist wieder da.",
		["791304"] = "Ich habe auch das hier gemacht. Hoffentlich gefällt es dir.",
		["551346"] = "Nachsichtige Rache ... leidenschaftliche Konvergenz ... gefährlicher Versuch ...",
		["771233"] = "Ich hoffe, dass es wieder so wird wie in den guten alten Zeiten ...",
		["777022"] = "Endlich sind wir da! Aber was denn ...? Noch mehr Treppenstufen ... Manchmal weiß ich wirklich zu schätzen, wie methodisch ihr Streben ist ...",
		["116131"] = "",
		["776264"] = "",
		["771264"] = "Großzügiger Kurier, ich wünsche dir viel Erfolg.",
		["991115"] = "Jerold, warum hast du uns davon nichts gesagt?",
		["775035"] = "/t1",
		["774777"] = "Kann von dieser Seite aus nicht geöffnet werden.",
		["9973275"] = "Vertrag",
		["9953024"] = "Reden",
		["221478"] = "Außerdem gibt es hier noch viele andere Leute, die wie meine Frau Mama Hilfe brauchen. Ich möchte für sie alle etwas tun.",
		["663331"] = "<color=#DBBA7FFF>Das Fragment einsetzen</color>",
		["9971278"] = "Aber ich will es immer noch vollenden. Falls du Ton übrig hast, gib ihn mir bitte ... Hat natürlich keine Eile, es gibt wichtigere Dinge.",
		["771200"] = "Ich habe nichts zu verlieren ...",
		["771220"] = "Ich werde diesen Tag herbeisehnen ...",
		["221225"] = "Ich will nur zurück, was mir gehört.",
		["9971279"] = "Hast du wieder Ton gefunden?",
		["9972279"] = "",
		["9973280"] = "Ich habe etwas Ton gefunden.",
		["9973281"] = "Tut mir leid.",
		["331203"] = "Tapferer Kurier, ich bin dir wirklich dankbar für alles, was du für mich getan hast. Bitte erlaube mir, dich als Ritter herauszufordern. Ich warte dort auf dich, wo sie gestorben ist. Im Namen meiner Liebe!",
		["771101"] = "Moment, du bist es nicht ... Die Lebenden sind nicht mehr da ... Hast du das Monster gesehen?!",
		["9923035"] = "Reden",
		["333904"] = "Nein",
		["9971284"] = "Entschuldige meine Neugier, aber wen stellt die Figur dar, die du da machst?",
		["994037"] = "/t0",
		["30135238"] = "Versteck dich, na los, sie dürfen dich nicht finden!",
		["9971287"] = "Meine Sorge ist, dass ich ihr Gesicht vergesse, bevor die Figur fertig ist. Ich muss sie vorher vollenden ... Nur so kann ich meine Schuld begleichen.",
		["557176"] = "Es gibt Verbindungen zwischen Vergangenheit, Gegenwart und Zukunft.",
		["4994993029"] = "Inneres des Dunklen Nebels",
		["9971291"] = "Ich glaube, ich kann die Verbannten im Dunklen Nebel \"sehen\", und sie wollen mich hineinziehen.",
		["9971377"] = "Hast du wieder Ton gefunden?",
		["221263"] = "Beachte ihn nicht weiter, Terrence. Er hat üble Absichten.",
		["9973295"] = "Ich habe noch etwas Ton gefunden.",
		["9972311"] = "",
		["9971295"] = "Fast fertig?",
		["553358"] = "Ja",
		["551388"] = "Das Streben nach Erhabenheit ist die ultimative Bedrohung des Menschen.",
		["9971297"] = "Man muss stark sein, um die Wahrheit durch den \"Glauben\" zu sehen.",
		["771250"] = "Terrence, ich habe dich niemals angelogen ...",
		["9971298"] = "Wird es irgendwann ein Gleichgewicht geben?",
		["9973301"] = "Vertrag",
		["9953045"] = "Reden",
		["554269"] = "/t0",
		["9971305"] = "Danke ... Das ist das erste Mal ...",
		["9961026"] = "Du siehst nicht wie ein Mann aus, der seine Frau treffen will. Was willst du ihr sagen? Hast du daran überhaupt schon mal gedacht?",
		["111125"] = "Du stinkst nach Hexe! Du bist auf ihrer Seite!",
		["778302"] = "",
		["9971306"] = "Und vielleicht nicht das letzte Mal ...",
		["9951041"] = "Wer ist dein Herr?",
		["551272"] = "Ist es das, worüber wir gesprochen haben?",
		["331182"] = "Sie erinnert dich an Teresa?",
		["221416"] = "Schon bald werden sie am Orakel getraut werden, und dann wird sie dem Heiligen Vater neues Leben gebären.",
		["551267"] = "Wir müssen sie aufhalten, oder Solas wird dasselbe Schicksal erleiden wie meine Heimatstadt ...",
		["117143"] = "Der Asket nutzt Schmerz und Willen, um den Dunklen Nebel zu vertreiben, aber offenbar wurde er in einen Entstellten verwandelt.",
		["552271"] = "",
		["667086"] = "Geh, bevor Teresa den Delegaten findet.",
		["331162"] = "Ich habe sie in Adamina getroffen ...",
		["9973296"] = "Tut mir leid.",
		["9971313"] = "Erkläre es mir mit einfachen Worten: Warum ist diese Hand ein Relikt der Marcion?",
		["9971314"] = "Diese Hand gehörte einem mächtigen Soldaten der Marcion. Als er im Sterben lag, verschmolz sie jemand durch ein verbotenes Ritual mit den Sendril, um sie zu einer Waffe zu machen.",
		["9971316"] = "Die Zeiten haben sich geändert, Terrence. Die Ketzerei ist für die Kirche vielleicht nicht die größte Bedrohung. Der Meister wollte mir seine Hand nicht geben ... bis ich meine eigene abschnitt ...",
		["9961023"] = "...",
		["771213"] = "Vergib mir, Viola ... Ich kann Teresa nicht einfach dort lassen.",
		["9971317"] = "Du Wahnsinniger.",
		["221199"] = "Du siehst so blass aus!",
		["9971318"] = "Nur Wahnsinnige können in dieser Welt überleben.",
		["662403"] = "Die Tür öffnen?",
		["441272"] = "Die Demiurgen erkennen dich an und gemäß unserer Abmachung, werde ich das Tor zum Seligenland für dich öffnen.",
		["222901"] = "Zurück zur Kutsche?",
		["774036"] = "Pass du lieber auf. Norwood, ich überlasse dir das.",
		["221159"] = "Das hier ist von Elma. Damit kannst du jetzt zum Orakel. Der Heilige Vater wird sicher dort sein.",
		["9971378"] = "Wie läuft es denn? Hast du etwas Interessantes gefunden?",
		["9971319"] = "Mir scheint, der Fluch der Marcion wird früher oder später jeden einholen, der damit zu tun hatte. Vielleicht ist das die Strafe für die Beleidigung der Marcion.",
		["9971383"] = "...",
		["9971321"] = "Ich möchte einfach nur diese verborgene Gefahr loswerden.",
		["9973379"] = "Vertrag",
		["9973324"] = "Tut mir leid.",
		["9971324"] = "Wenn du außer Kontrolle gerätst, werde ich keine Gnade walten lassen.",
		["9972382"] = "",
		["9971330"] = "Und wo willst du als Nächstes hin?",
		["221168"] = "Mutter ... Mutter ... Wo bist du? Gott, bitte sag es mir!",
		["9971333"] = "Hoffentlich hat sie dieser Feigling nicht enttäuscht.",
		["9973335"] = "Vertrag",
		["30135239"] = "Lass ihn gehen, er ist nur ein Kind!",
		["771106"] = "(räusper) Ich bin unglaublich durstig, aber ich kann nicht trinken ... Schon ein Tropfen Wasser würde meine Kehle verbrennen ... Das Einzige, woran ich mich deutlich erinnern kann, ist dieser ständige Schmerz, den mein Durst verursacht.",
		["9971387"] = "Das Seligenland wird also langsam zur Vergangenheit werden, einer Vergangenheit, die sich in der Zukunft ständig wiederholen wird. Die Kirche ist nur ein törichter Anhänger, diese leuchtenden Köpfe um den Delegaten sind der beste Beweis ...",
		["665071"] = "Du bist nichts weiter als ein Narr, der meine Zeit nicht wert ist!",
		["553110"] = "...",
		["9973380"] = "Vertrag",
		["111068"] = "Bist du noch anderen Fremden begegnet?",
		["9971380"] = "Ich suche etwas Bestimmtes.",
		["9973345"] = "Ich habe eine zerbrochene Tafel gefunden.",
		["9971299"] = "Ich glaube nicht.",
		["1002100024"] = "Willst du den Demiurgen nicht retten?",
		["664055"] = "/t0",
		["551370"] = "Nachsichtige Rache ... leidenschaftliche Konvergenz ... gefährlicher Versuch ...",
		["992140"] = "",
		["991080"] = "Wieso?",
		["9931029"] = "Mein Vater hatte sich auch auf seinen Instinkt verlassen, doch am Ende tat er ein paar bedauerliche Dinge, die nun leider nicht mehr zu ändern sind.",
		["9971381"] = "Ich suche etwas Bestimmtes.",
		["551351"] = "Ah ... gierige Obsession ... Sind wir nicht alle von ihr geblendet?",
		["9931023"] = "In diesem Dunklen Nebel sollte man niemandem über den Weg trauen.",
		["221060"] = "Mein Bauch sagt mir, das wird kein Kinderspiel.",
		["9972381"] = "",
		["9943036"] = "Segnen",
		["9971352"] = "Wenn ich vom Inhalt der Tafel ausgehe, bedeutet die Inschrift des Armreifs \"... Feier der Flamme ... das ewige Licht\"",
		["9946029"] = "",
		["555254"] = "Aha ... Es ist Zeit, aufzuwachen. Endlich werde ich für all die Demütigungen Rache nehmen, die ich ertragen musste.",
		["9931027"] = "Sagt dir das wieder dein Bauchgefühl?",
		["331155"] = "Ich weiß nicht, ob das möglich ist, aber ich will es gern versuchen. Ach übrigens, bevor ihr geht, achtet darauf, die hier dabei zu haben.",
		["9971355"] = "Das ist schon mal ein Fortschritt. Lässt sich noch etwas daraus ableiten?",
		["332219"] = "",
		["9971356"] = "Leider nicht genug, um das gesamte Rätsel zu entschlüsseln.",
		["441297"] = "Ich werde hierbleiben und mein Bestes versuchen, die Auswirkungen des weißen Staubs zu beseitigen. Die Demiurgen sind noch nicht am Ende.",
		["991045"] = "Tropf, tropf ... Der wunderbare Klang des Wassers schenkt der Seele Frieden. Viel Glück! Ich hoffe, wenn du wieder zurück bist, erkennen wir uns noch wieder ... Hahaha!",
		["551265"] = "Du hast bereits gesehen, was passiert ist, als sie eines von ihnen geöffnet hat ...",
		["791350"] = "Nach Relikten einer Zivilisation zu suchen, von der nie jemand etwas gehört hat, klingt nach einer wirren Fantasie.",
		["661148"] = "...",
		["441292"] = "Ich verstehe nicht ... Wie konnte ein Pontifex des Heiligen Klosters so etwas tun?",
		["991464"] = "Großzügiger Kurier, ich wünsche dir viel Erfolg.",
		["9971360"] = "Das kann vermutlich niemand.",
		["991021"] = "Dieser Fluch wird meine Stärke sein. Solange Ichthyosauria noch zu retten ist, bereue ich nichts — selbst wenn ich zum Monster werden sollte. ",
		["441127"] = "Danke, Kurier.",
		["558305"] = "",
		["993467"] = "Handeln",
		["551404"] = "...",
		["9971362"] = "\"Die Geburt der Feier der Flamme, die Wärme aus der Tiefe meines Herzens, das ewige Licht\"",
		["664163"] = "/t0",
		["331219"] = "Pah, du bist der neue Kurier?! Dann habe ich genau das Richtige für dich, wenn du im Dunklen Nebel überleben willst.",
		["664166"] = "/t0",
		["551187"] = "Kurier, lass die Vergangenheit ruhen und halte dich an der Lüge fest, die du aufrichtig glaubst. Verneige dich und galoppiere in die Dunkelheit. Ins blendende Licht zu starren lässt einen nur blind werden.",
		["9971366"] = "Aber wenn die Welt zu Ende geht, ist das doch bedeutungslos.",
		["9973367"] = "Vertrag",
		["9971367"] = "Da das Grab der Giganten nun gefunden wurde, sind die Kuriere nicht länger von Bedeutung.",
		["225083"] = "Heiliger Vater, bitte verlasse uns nicht!",
		["9971368"] = "Vielleicht brauchen sie einfach eine neue Aufgabe? Zum Beispiel den Menschen des Seligenlandes helfen.",
		["9971369"] = "Ich werde dich niemals davon abhalten, etwas zu tun, aber um eines muss ich dich bitten: Geh keine sinnlosen Risiken ein.",
		["441143"] = "Aber die Glocke scheint doch der Ursprung von Ediths Wahnsinn zu sein.",
		["791275"] = "Arbeitest du ... noch immer daran?",
		["771385"] = "Terrence, ich muss dir leider sagen, unser Vertrag endet hier. Du hast das Grab der Giganten gefunden. Meine Arbeit ist getan.",
		["551407"] = "...",
		["791276"] = "Ähm ...",
		["332222"] = "",
		["793279"] = "Vertrag",
		["664193"] = "/t2",
		["793280"] = "Ich habe etwas Ton gefunden.",
		["793281"] = "Tut mir leid.",
		["335225"] = "Beende mein bedeutungsloses Leben, Kurier, ich flehe dich an.",
		["9973323"] = "Ich habe ein paar alte Relikte gefunden.",
		["664162"] = "/t0",
		["9971275"] = "Arbeitest du ... noch immer daran?",
		["772184"] = "",
		["221141"] = "Adamina wird immer unheimlicher. Ich will mit meiner Frau Mama einfach nur hier weg, so schnell wie möglich ...",
		["551122"] = "Nur Geduld, mein kluger Freund, nur Geduld ...",
		["791287"] = "Meine Sorge ist, dass ich ihr Gesicht vergesse, bevor die Figur fertig ist. Ich muss sie vorher vollenden ... Nur so kann ich meine Schuld begleichen.",
		["991404"] = "Ebbinghaus' Zitate: III",
		["661114"] = "Hier ist es nicht sonderlich sicher ... aber wenn du immer noch wissen möchtest, was passiert ist, findest du mich in der Kammer der Dreifaltigkeitshalle",
		["793288"] = "Vertrag",
		["792288"] = "",
		["331150"] = "Bist du sicher?",
		["221295"] = "Herr Kurier, kannst für mich ein paar Adamina-Kräuter pflücken? Denn ich darf nicht aufhören mit Umrühren.",
		["791290"] = "Was ist denn mit dir los?",
		["9941022"] = "Ich glaube nicht, dass sie für ein paar Niemande ein solches Risiko eingehen würden.",
		["100310032203"] = "/t0",
		["791377"] = "Hast du wieder Ton gefunden?",
		["793296"] = "Tut mir leid.",
		["441179"] = "Nimm diesen Schlüssel und öffne das hintere Tor zum Dorf, dann wirst du wissen, wem du vertrauen kannst.",
		["9971276"] = "Ähm ...",
		["444777"] = "Kann von dieser Seite aus nicht geöffnet werden.",
		["791296"] = "Ja. Ich frage mich immer: Wenn ich sie wäre, würde ich den \"Glauben\" jetzt noch gutheißen?",
		["995278"] = "Wir waren zu unvorsichtig. Diesmal werden wir nicht zulassen, dass du Lord Ebbinghaus störst ...",
		["331199"] = "Wieso ...? Wieso spüre ich sie nicht?",
		["993118"] = "Nach den Kreaturen fragen, die gerade erst vertrieben wurden.",
		["551245"] = "Das muss ein Zeichen sein ... Ist Teresa irgendwie mit der Kirche verbunden? Da, ein Schlüssel ist auf dem Altar erschienen.",
		["773264"] = "Handeln",
		["791306"] = "Und vielleicht nicht das letzte Mal ...",
		["791307"] = "Keine Sorge, sie wirkt nur rastlos, weil sie einen unerfüllten Wunsch hat.",
		["791311"] = "Wie läuft es denn? Hast du etwas Interessantes gefunden?",
		["333147"] = "<color=#DBBA7FFF>Warum sind die Sendril so schwach hier?</color>",
		["9971304"] = "Ich habe auch das hier gemacht. Hoffentlich gefällt es dir.",
		["792311"] = "",
		["9963042"] = "Reden",
		["9962042"] = "",
		["793313"] = "Tut mir leid.",
		["791313"] = "Erkläre es mir mit einfachen Worten: Warum ist diese Hand ein Relikt der Marcion?",
		["552901"] = "Zurück zur Kutsche?",
		["773184"] = "...",
		["793378"] = "Vertrag",
		["1004100077"] = "Sie haben es einstimmig beschlossen.",
		["793320"] = "Tut mir leid.",
		["991016"] = "Jerold！Jerold！",
		["441287"] = "...",
		["221128"] = "Die Jagd?",
		["791320"] = "Oh, entschuldige. Ich wollte dich nicht beleidigen. Ihre Gier hat sie zu diesem Ende geführt, du hingegen wurdest gezwungen ...",
		["551237"] = "Der Altar da oben scheint mit etwas unter dem Heiligen Turm verbunden zu sein ... Es neckt der Verlangen der Dunkelheit ... Geh, bevor es zu spät ist.",
		["791321"] = "Ich möchte einfach nur diese verborgene Gefahr loswerden.",
		["221258"] = "Aufwachen. Genug geschlafen.",
		["991031"] = "Wir müssen so bald wie möglich Ebbinghaus sprechen, um uns ein paar Antworten zu holen.",
		["791379"] = "Wie läuft es denn? Hast du etwas Interessantes gefunden?",
		["117136"] = "Wer sich hinter einer Maske versteckt, hat etwas zu verbergen.",
		["791333"] = "Hoffentlich hat sie dieser Feigling nicht enttäuscht.",
		["441330"] = "...",
		["9956042"] = "",
		["793335"] = "Vertrag",
		["791335"] = "Hast du es jemals bereut?",
		["791337"] = "Die meisten Menschen würden lieber sterben, als den Pfad des Kuriers zu wählen.",
		["662703"] = "Die Tür öffnen?",
		["551322"] = "Hahaha, interessanter Kerl. Nimm das hier, und fülle es mit starker Obsession. Du könntest etwas anderes finden.",
		["441302"] = "Ha-ha, schlauer Mensch.",
		["791343"] = "Dem legendären Ersten?",
		["791344"] = "Ich weiß nicht, welche Verbindung zwischen ihnen besteht, aber wenn du etwas findest, das auch nur im Entferntesten mit Samarosa zusammenhängt, dann bring es mir bitte.",
		["668305"] = "",
		["774113"] = "Es ist blockiert.",
		["793380"] = "Vertrag",
		["793345"] = "Ich habe eine zerbrochene Tafel gefunden.",
		["793346"] = "Ich habe noch nichts gefunden.",
		["991418"] = "Die Tür gibt nicht nach. An ihr befinden sich fünf Einfassungen.",
		["224048"] = "/t0",
		["791346"] = "Diese Tafel enthält die Legenden der Samarosianer ... \"Das heilige Totem von Samarosa ... führt uns zum Haus Gottes ...\"",
		["661182"] = "Es heißt, um den Frieden im Seligenland zu bewahren, bewacht der Delegat tagein, tagaus das Siegel von Yekub, um zu verhindern, dass der Fluch in Erfüllung geht.",
		["9999290"] = "Für Teresa ... Ich hatte keine andere Wahl.",
		["221136"] = "Sagst du mir, wie ich den Heiligen Vater finden kann?",
		["791349"] = "Wenn du mehr Tafeln wie diese findest, gelingt es dir bestimmt, die Inschrift auf dem Armreif zu entschlüsseln.",
		["9971358"] = "Aber was ist, wenn die Zeit nicht ausreicht? Ich will nicht mein ganzes Leben darauf verschwenden.",
		["9926044"] = "",
		["793381"] = "Vertrag",
		["791381"] = "Ich suche etwas Bestimmtes.",
		["793351"] = "Ich habe eine zerbrochene Tafel gefunden.",
		["793352"] = "Ich habe noch nichts gefunden.",
		["551367"] = "Ah ... gierige Obsession ... Sind wir nicht alle von ihr geblendet?",
		["221201"] = "Dieser strenge Geruch nach Sendril. Widerlich.",
		["791351"] = "Ich habe eine neue Tafel gefunden.",
		["4994993004"] = "Höhle des Dunklen Nebels",
		["9951034"] = "Ahh, Terrence, was kann ich für dich tun? Ich wollte gerade eine Pause einlegen, denn der Weg ist noch ziemlich weit.",
		["551290"] = "...",
		["665188"] = "Es tut so weh ... Es tut so weh ... Mein Körper ... Aufhören, bitte aufhören!",
		["791353"] = "Was bedeutet das?",
		["791354"] = "Es beschreibt den Tag meiner Geburt.",
		["1004100059"] = "Meine Brüder ... Demiurgen, die durch Edith wandeln ...",
		["791360"] = "Das kann vermutlich niemand.",
		["9962013"] = "",
		["114028"] = "Ich hätte nicht gedacht, dass Heggem so schwach ist.",
		["661104"] = "Liegt das am Fluch der Marcion-Hexe?",
		["551335"] = "Das Mittelmaß ist stets besessen davon, aufzusteigen.",
		["791382"] = "Leider nicht genug, um das gesamte Rätsel zu entschlüsseln.",
		["4994993032"] = "Grenze des Dunklen Nebels",
		["991147"] = "Ebbinghaus ... Ich hab vor langer Zeit mal in einem Buch etwas über ihn gelesen. Der Heilige, der sein Leben gab, um uns zu schützen, Ichthyosauria zu schützen ...",
		["553427"] = "Ja",
		["551347"] = "Die verborgene Wahrheit fördert unwissende böse Absichten.",
		["771085"] = "Wieso hast du uns nicht vorher von deinem Vater erzählt?",
		["791362"] = "\"Die Geburt der Feier der Flamme, die Wärme aus der Tiefe meines Herzens, das ewige Licht\"",
		["792379"] = "",
		["993002"] = "Nein",
		["791366"] = "Aber wenn die Welt zu Ende geht, ist das doch bedeutungslos.",
		["793367"] = "Vertrag",
		["9923036"] = "Gehen",
		["791368"] = "Vielleicht brauchen sie einfach eine neue Aufgabe? Zum Beispiel den Menschen des Seligenlandes helfen.",
		["662903"] = "Zurück zur Kutsche?",
		["661218"] = "Benita, begreifst du denn nicht, bei allem, was dem Delegaten zugestoßen ist, kann es keinen Gott geben.",
		["791369"] = "Ich werde dich niemals davon abhalten, etwas zu tun, aber um eines muss ich dich bitten: Geh keine sinnlosen Risiken ein.",
		["111130"] = "Mensch, willst du mit mir handeln? Ich besitze viele Schätze.",
		["113902"] = "Nein",
		["777023"] = "Es ist weitaus schlimmer hier als befürchtet. Hoffentlich haben wir nicht die falsche Entscheidung getroffen.",
		["9953031"] = "Handeln",
		["991162"] = "Hahaha, nimm das ... Ich hoffe, du kannst mir meinen unerfüllten Wunsch erfüllen.",
		["884000"] = "Woher rührt deine Entschlossenheit? Woran glaubst du? Spürst du dieselbe tödliche Anziehungskraft wie die Motte, die ins Feuer fliegt?",
		["884001"] = "Der Mann, der in seinen Obsessionen gefangen ist. Endlich begegnen wir einander. Von der dunklen Höhle in die Wüste der Wahrheit ...",
		["884002"] = "Der Mann, der in seinen Obsessionen gefangen ist. Endlich begegnen wir einander. Von der dunklen Höhle in die Wüste der Wahrheit ...",
		["9931009"] = "Ja, eine. Von einer alten Freundin von dir ...",
		["551070"] = "Ein verschmolzener Gigant? So etwas sehe ich zum ersten Mal.",
		["9973457"] = "An den geheimen Tunnel unter der Kammer des Delegaten",
		["773390"] = "<color=#DBBA7FFF>Ja </color>",
		["661263"] = "Armer Kurier, willst du mit mir handeln?",
		["884007"] = "Die Angst, sie zu verlieren? Oder etwas anderes? Etwas Dunkleres? Geheimeres? Ich muss dir die Wahrheit verraten ... eine schockierende Wahrheit ...",
		["9921002"] = "Bist du wach?",
		["9921010"] = "Terrence, ich habe dort nur dich gesehen ... Vielleicht warst du zu erschöpft und dein Verstand hast dir einen Streich gespielt.",
		["551318"] = "Begrabe sie anständig, sonst wird es schwer, sie zu kontrollieren.",
		["884008"] = "Die Angst, sie zu verlieren? Oder etwas anderes? Etwas Dunkleres? Geheimeres? Ich muss dir die Wahrheit verraten ... eine schockierende Wahrheit ...",
		["9961027"] = "Das geht dich nichts an. Sobald wir im Seligenland ankommen, will ich dich nie wiedersehen.",
		["227281"] = "Der Gestank aus diesem Topf raubt einem den Atem.",
		["884010"] = "Sie war immer hier ... hat immer hierher gehört. Umarme das Licht, Teresa! Du bist zu Hause.",
		["884037"] = "Das ist das Geschenk der Ahnen an die Marcion! Nur wir sind seiner würdig.",
		["991112"] = "Ehrlich gesagt bereue ich es jetzt, dass ich dich mit meiner letzten Falsche gerettet habe.",
		["332088"] = "",
		["884040"] = "Was für ein barbarisches Volk ... die eigenen Leute zu opfern!",
		["111037"] = "Wie läuft es bei euch?",
		["884044"] = "Ahh! Mein Hals ... er brennt! Wasser! Gib mir Wasser!",
		["551395"] = "Ich kann dir helfen, aber ich muss wissen, was ich tun soll.",
		["111083"] = "Ich habe keine Zeit dafür. Ich kann nicht lange hier verweilen.",
		["884046"] = "Hat der Delegat uns verlassen? Lass mich raus! Ich will nicht ewig hier drin gefangen sein!",
		["9971376"] = "Hast du wieder Ton gefunden?",
		["884052"] = "/t2",
		["551442"] = "Endlose Wut, gnadenlose Obsession ... Deine und meine Geschichte mögen verschieden sein, doch am Ende führen sie uns auf denselben Weg ...",
		["884068"] = "Trink, und Teresa wird von dem Fluch befreit werden ...",
		["9973387"] = "Reden",
		["884069"] = "Trink ... Trink, trink, trink ...",
		["333905"] = "Ja",
		["884073"] = "/t2",
		["884070"] = "Trink, wenn du Teresa wirklich sehen willst ...",
		["881072"] = "Oh ... Du hast noch nicht aufgegeben? Hast du gefunden, wonach du suchtest?",
		["661144"] = "Das Göttliche Schwert wird am Alter vor der Dreifaltigkeitshalle angebetet. Du wirst mir meinen letzten Wunsch doch erfüllen, oder?",
		["881074"] = "Wir haben ein unschuldiges Marcion-Mädchen geopfert und drangen in das Reich Gottes ein. Wir tranken vom verbotenen Wasser, das uns diese unmenschliche Gestalt gab. Wir erhielten die verdiente Strafe für unsere Sünden.",
		["994484"] = "/t0",
		["554166"] = "/t0",
		["881079"] = "Jemand hatte das Glück, von diesem Ort zu entkommen. Wenn auch du zu diesen Auserwählten gehörst, dann finde die Person, die diesen Armreif kennt, und überbringe ihr meine Nachricht: -\"Ich habe hier auf dich gewartet.\"",
		["991510"] = "Puh ... Erinnerst du dich an mich? Da oben ist offenbar viel passiert. Muss schlimmer gewesen sein als ich gedacht hatte ...",
		["9951042"] = "Jemand, der die Wahrheit sucht. Das ist die einzige Antwort, de ich dir geben kann.",
		["551285"] = "Leider kann ich dir keinen Rabatt gewähren, aber meine Sachen sind ihren Preis wert.",
		["551091"] = "Ach übrigens ... Es ist mir ein wenig peinlich, aber ... ich habe aus Versehen eine Falle der Marcion ausgelöst und vor Schreck meine Sanduhr verloren ... Hast du sie vielleicht irgendwo gesehen?",
		["667074"] = "Teresa sucht nach dem Delegaten? Womöglich ist doch etwas dran an dem Gerückt, dass der Delegat das Siegel von Yekub besitzt. Ich glaube, ich weiß, was Teresa vorhat.",
		["884100"] = "/t0",
		["9951050"] = "Ich habe nie verstanden, warum die Kirche nach Katib zurückgekehrt ist ...",
		["884102"] = "/t0",
		["882102"] = "Trink ...",
		["883103"] = "Ablehnen",
		["551258"] = "Wieso hast du nicht auf mich gehört?",
		["30274005"] = "/t0",
		["666264"] = "",
		["441240"] = "Kurier, hast du dein Versprechen erfüllt?",
		["4994993001"] = "Grenze des Dunklen Nebels",
		["993051"] = "Nehmen",
		["771139"] = "Sie wurde tief unter die Erde gebracht ... Das Monster darf sie nicht finden! Ich selbst vermag sie nicht zu retten, aber du kannst es, Terrence. Du bist jetzt ihre einzige Hoffnung ...",
		["991461"] = "Großzügiger Kurier, ich wünsche dir viel Erfolg.",
		["791336"] = "Nein, warum?",
		["441191"] = "Solange sie den Demiurgen Hoffnung bringt, spielt der Rest keine Rolle.",
		["9963023"] = "Handeln",
		["9941016"] = "Jetzt ist es das Auffangbecken der Demiurgen.",
		["4994992002"] = "Ha! Du brauchst also noch mehr Nervenkitzel?! Du bist unersättlich! Eigentlich würde ich davon abraten, aber bei einem erfahrenen Kurier wie dir mache ich eine Ausnahme ... wenn du mir Geisterpilze bringst.",
		["771210"] = "...",
		["557198"] = "Wenn wir der Wahrheit näher kommen wollen, müssen wir ein paar Wagnisse eingehen und uns zum Heiligen Turm vorwagen ...",
		["4994993008"] = "25 Geisterpilze anbieten",
		["4994993010"] = "75 Geisterpilze anbieten",
		["4994993012"] = "Wähle ein Gebiet, das du im Dunklen Nebel erkunden möchtest.",
		["441080"] = "Eine Ratte hat Edith infiltriert, also sei vorsichtig bei allem, was du machst ...",
		["4994993015"] = "Erhöhe die Leuchtkraft der Nebellampe.",
		["884049"] = "/t2",
		["441301"] = "Ha-ha, dummer Mensch.",
		["4994993018"] = "Materialien übergeben",
		["221260"] = "Das spielt jetzt keine Rolle mehr. Es ist vorbei.",
		["551336"] = "Nachsichtige Rache ... leidenschaftliche Konvergenz ... gefährlicher Versuch ...",
		["4994992005"] = "Erfordert verdicktes Walöl und 200 Geisterpilze.",
		["553357"] = "Die Melosflasche füllen.",
		["9931012"] = "Wen zur Hölle meinst du?",
		["994257"] = "/t0",
		["443903"] = "Ja",
		["4994992006"] = "Erfordert veredeltes Walöl und 300 Geisterpilze.",
