	StoryGuide = {
		["2001DescriptionJoystick"] = "Widme Sendril-Überreste, um die Altarstufe zu erhöhen.\nNach dem Stufenaufstieg erhalten alle Charaktere Punkte, um ihre Attribute zu erhöhen.",
		["2003DescriptionTouch"] = "Trophäen verleihen dir Macht.\nSie werden in die 3 Kategorien \"Narben\", \"Resistenz\" und \"Albtraum\" unterteilt und können nur am dafür vorgesehenen Platz ausgerüstet werden.",
		["2006DescriptionJoystick"] = "Benutzen: {94}\nWechseln: {94} halten",
		["2004Title"] = "Talente freischalten",
		["2007DescriptionJoystick"] = "Verbessern\nVerbessere Trophäen mit Items wie Schwarzwasser-Trümmern.\nNeu schmieden\nOpfere eine Trophäe, um der Haupttrophäe einen zufälligen Attributbonus zu verleihen. Je höher die Stufe des Opfers, desto stärker die Verbesserung.",
		["2005Title"] = "Schnellitems",
		["2004DescriptionJoystick"] = "Schalte Talente mit Immundus-Objekten an einem Altar frei. \nEs gibt vier Qualitätsstufen: Immundus-Splitter, Immundus, Immundus-Kristall und Immundus-Diamant. \nJe stärker das Talent, desto hochwertiger muss das Immundus-Objekt sein.",
		["2005DescriptionTouch"] = "Es gibt 6 Schnellitem-Slots zum Ausrüsten von Items.\nSchnellitem bearbeiten: auf Slot tippen",
		["2005DescriptionJoystick"] = "Es gibt 6 Schnellitem-Slots zum Ausrüsten von Items.\nSchnellitem bearbeiten: {80}",
		["2005DescriptionKeyboard"] = "Es gibt 6 Schnellitem-Slots zum Ausrüsten von Items.\nSchnellitem bearbeiten: {80}",
		["2006Title"] = "Schnellitems",
		["2006DescriptionTouch"] = "Benutzen: auf Item tippen\nWechseln: auf Item tippen & halten",
		["2002DescriptionTouch"] = "Mit alchemistischen Rezepten kannst du Tränke und Items herstellen.",
		["2006DescriptionKeyboard"] = "Benutzen: {94}\nWechseln: {94} halten",
		["2003Title"] = "Trophäen",
		["2004DescriptionTouch"] = "Schalte Talente mit Immundus-Objekten an einem Altar frei. \nEs gibt vier Qualitätsstufen: Immundus-Splitter, Immundus, Immundus-Kristall und Immundus-Diamant. \nJe stärker das Talent, desto hochwertiger muss das Immundus-Objekt sein.",
		["2007Title"] = "Verb. & neu schm.",
		["2004DescriptionKeyboard"] = "Schalte Talente mit Immundus-Objekten an einem Altar frei. \nEs gibt vier Qualitätsstufen: Immundus-Splitter, Immundus, Immundus-Kristall und Immundus-Diamant. \nJe stärker das Talent, desto hochwertiger muss das Immundus-Objekt sein.",
		["2002Title"] = "Alchemie",
		["2003DescriptionKeyboard"] = "Trophäen verleihen dir Macht.\nSie werden in die 3 Kategorien \"Narben\", \"Resistenz\" und \"Albtraum\" unterteilt und können nur am dafür vorgesehenen Platz ausgerüstet werden.",
		["2007DescriptionTouch"] = "Verbessern\nVerbessere Trophäen mit Items wie Schwarzwasser-Trümmern.\nNeu schmieden\nOpfere eine Trophäe, um der Haupttrophäe einen zufälligen Attributbonus zu verleihen. Je höher die Stufe des Opfers, desto stärker die Verbesserung.",
		["2002DescriptionKeyboard"] = "Mit alchemistischen Rezepten kannst du Tränke und Items herstellen.",
		["2001Title"] = "Widmen",
		["2001DescriptionKeyboard"] = "Widme Sendril-Überreste, um die Altarstufe zu erhöhen.\nNach dem Stufenaufstieg erhalten alle Charaktere Punkte, um ihre Attribute zu erhöhen.",
		["2007DescriptionKeyboard"] = "Verbessern\nVerbessere Trophäen mit Items wie Schwarzwasser-Trümmern.\nNeu schmieden\nOpfere eine Trophäe, um der Haupttrophäe einen zufälligen Attributbonus zu verleihen. Je höher die Stufe des Opfers, desto stärker die Verbesserung.",
		["2001DescriptionTouch"] = "Widme Sendril-Überreste, um die Altarstufe zu erhöhen.\nNach dem Stufenaufstieg erhalten alle Charaktere Punkte, um ihre Attribute zu erhöhen.",
		["2002DescriptionJoystick"] = "Mit alchemistischen Rezepten kannst du Tränke und Items herstellen.",
		["2003DescriptionJoystick"] = "Trophäen verleihen dir Macht.\nSie werden in die 3 Kategorien \"Narben\", \"Resistenz\" und \"Albtraum\" unterteilt und können nur am dafür vorgesehenen Platz ausgerüstet werden."
	},
