	StoryDialog = {
		["663703"] = "Sí.",
		["994281"] = "Ja, ja, ja, mis valerosos carroñeros no se detendrán hasta atrapar y eliminar a todos los caballeros de Handel de Ictiosauria...",
		["661152"] = "¡Vamos, antes de que sea tarde!",
		["111121"] = "¿Quién es el señor Ramones?",
		["771198"] = "¡¿Cómo que no había otra elección?! ¿Has entregado a Teresa para nada? ¡Tengo que entrar en ese maldito Sepulcro colosal y traerla de vuelta!",
		["111134"] = "Je, je, qué humano más astuto...",
		["661322"] = "Sí, el código... Si no sabes de qué hablo, te aconsejo que te vayas ahora mismo.",
		["100210022202"] = "Sí.",
		["997013"] = "Todavía siento el aliento de la vida en algún lugar de esta isla, aunque de forma muy tenue.",
		["551268"] = "Debes ir a Tierrabendita cuanto antes y cumplir tu cometido... Ahora debo descansar... No te preocupes por mí... Espero que todo acabe bien.",
		["771230"] = "El dubitativo Terrence que vagaba por la Niebla oscura ya es historia.",
		["333092"] = "¿Qué haces empalado en este lugar?",
		["552329"] = "El precio es 1 miel de obsesión y 30 huesos de antozoo. ¿Quieres continuar?",
		["771224"] = "Seguro que Teresa te impediría hacer tal cosa.",
		["663504"] = "No.",
		["1006100084"] = "Él tiene el Sello de Yekub...",
		["9971340"] = "Este brazalete lo significa todo para mí. Estoy buscando a su dueño...",
		["663706"] = "No.",
		["991431"] = "Piedra del Heggie",
		["331163"] = "¡Eso es imposible!",
		["884043"] = "¡Es mío!",
		["221293"] = "¡La bendición del Santo Padre es algo indudable! Si quieres irte de aquí de una pieza, te sugiero que no crees problemas.",
		["9971364"] = "Sin embargo, bajo la luz de Tierrabendita no disipará la neblina de mi corazón.",
		["663196"] = "No.",
		["553255"] = "Sí.",
		["771092"] = "Ve, yo me encargo de esto.",
		["331098"] = "...",
		["771077"] = "Norwood se la ha llevado a las ruinas que hay cerca de la cueva.",
		["100610064168"] = "No se puede abrir sin una llave.",
		["9931005"] = "Izquierda y derecha, luz y oscuridad, bien y mal... Todo está relacionado en este mundo, de un modo u otro. Todo acaba llevando a un inevitable final, a pesar de toda duda.",
		["773075"] = "Marcharse.",
		["552180"] = "",
		["221104"] = "Sé que la máscara puede ser algo inquietante, pero me llamo Norwood y, al igual que tú, soy un sirviente de la Iglesia.",
		["661310"] = "Parece que... todavía no estás contaminado por los Sendril, qué suerte...",
		["667124"] = "Otro fragmento.",
		["221262"] = "¿Qué hay en el lugar de la ejecución?",
		["9934003"] = "/t0",
		["991152"] = "Oí a mis padres hablar de él hace mucho tiempo... Una vez se produjo una rebelión ahí arriba en la que murió multitud de gente.",
		["551132"] = "Ah, eres tú, amigo mío. ¿Cómo va todo? ¿Has encontrado el reloj de arena?",
		["552341"] = "El precio es 1 miel de obsesión y 60 huesos de antozoo. ¿Quieres continuar?",
		["772003"] = "¿Quieres volver al carruaje?",
		["791319"] = "Parece que la maldición de los marcioneses acabará llegando a todo aquel conectado con ella. Puede que ese sea el pago por ofender a los marcioneses.",
		["441243"] = "El polvo blanco. Intenta destruir el presente entregado a Edith, la última esperanza de los demiurgos.",
		["332100"] = "Jenny... Ojalá pudiera volver a verte...",
		["661802"] = "Vete, aquí no encontrarás nada que te vaya a resultar de utilidad.",
		["661801"] = "¡No, por favor! ¡Busca la protección del apoderado en el Salón de la Trinidad, no tiene sentido quedarse aquí!",
		["9924033"] = "/t0",
		["111046"] = "¡Deberíamos ir hasta allí cuanto antes!",
		["661702"] = "Vete, aquí no encontrarás nada que te vaya a resultar de utilidad.",
		["111088"] = "Jerry se ha ido porque los hermanos John... ahora son monstruos.",
		["663602"] = "No.",
		["663505"] = "Sí.",
		["553154"] = "Marcharse.",
		["663503"] = "Sí.",
		["558302"] = "",
		["662503"] = "¿Quieres abrir la puerta?",
		["111069"] = "Jerry... Mi Jerry ha desaparecido... Monstruos, todos se han convertido en monstruos...",
		["663502"] = "No.",
		["9931004"] = "¡Terrence! ¿Hablas en serio?",
		["662501"] = "¿Quieres abrir la puerta?",
		["661405"] = "Dios mío, no hay nada que pueda hacer. Todo el mundo merece la oportunidad de vivir más que yo.",
		["551379"] = "La verdad oculta alimenta las intenciones malignas de los ignorantes.",
		["441186"] = "¿Es ese el precio que mencionaba aquella marcionesa?",
		["9932034"] = "",
		["663323"] = "Azath.",
		["558301"] = "",
		["663322"] = "¿Código?",
		["777046"] = "¡Vuelve ahora mismo! ¡La luz roja te está deteriorando la mente! ¡Rápido!",
		["441120"] = "Gracias, forastero, ya me siento mejor.",
		["441300"] = "Humano, ¿quieres hacer negocios conmigo? Tengo multitud de tesoros.",
		["441144"] = "¡Ja, ja! Qué gracioso, un mensajero que ignora la realidad. En lugar de perder tu tiempo aquí, deberías irte antes de que pase algo terrible.",
		["792382"] = "",
		["667126"] = "Una vez puestas todas las partes juntas, la forma resultante se parece al hueco que hay en la estatua de la Espada sagrada frente al santuario de la Trinidad... Tienen que tener algún tipo de relación, ¿no crees?",
		["661325"] = "Espera un momento, puede que haya más pistas.",
		["9961044"] = "Si me pasa algo, quiero que cojas a Viola y os marchéis.",
		["663241"] = "No.",
		["333906"] = "No.",
		["9963027"] = "Marcharse.",
		["661505"] = "Dios mío, no hay nada que pueda hacer. Todo el mundo merece la oportunidad de vivir más que yo.",
		["441125"] = "A nadie le importa una antigualla como yo... Ojalá pudiera volver a Tierrabendita...",
		["9961020"] = "¿Pero crees de veras que puedes hacerla cambiar de opinión?",
		["552165"] = "Tal vez hayan caído todos juntos en un sueño profundo…",
		["661223"] = "Ya hay suficientes idiotas ahí fuera, así que céntrate.",
		["661214"] = "¡Pues busca un modo de abrir esta maldita puerta!",
		["552116"] = "",
		["111105"] = "Ve a la estatua de la mina y encuentra la lágrima empedrada. Solo con su ayuda podrás disolver este cascarón tan duro.",
		["221227"] = "Sigo estando muy agradecida por todo lo que has hecho por mí, pero mírate... Tu codicia sin fin te ha convertido en un cadáver ambulante que cae en picado hacia el oscuro abismo. Dharam... ya no puede ser tuyo... Estás totalmente... corrompido.",
		["791279"] = "¿Has encontrado algo de arilo?",
		["665189"] = "Dios mío, ¿qué más puedo hacer por ti? Por favor, perdona nuestras transgresiones...",
		["9956021"] = "",
		["551266"] = "Su objetivo final no es entrar en el Sepulcro colosal... Quiere vengarse de la Iglesia y destruir Tierrabendita...",
		["111086"] = "Forastero, si encuentras a mi Jerry, te estaré muy agradecida.",
		["551410"] = "...",
		["551153"] = "Ja, ja... Tan solo sobrevivo en este mundo, pero de un modo diferente.",
		["663403"] = "Sí.",
		["667136"] = "Entregó su vida a la fe, pero se vio superada por ella.",
		["554180"] = "/t0",
		["117124"] = "Ajá, me retracto de lo dicho. Heggem ya está hecho un desastre.",
		["331192"] = "¿Con qué objetivo?",
		["221117"] = "No está lejos de aquí, pero su puerta está sellada por algún tipo de ritual, por lo que no podrás entrar.",
		["771107"] = "Agnes... Agnes... ¿Por qué resuena ese nombre en mi cabeza?",
		["661115"] = "Uf... El pútrido olor de los Sendril... me pone bastante nervioso.",
		["555251"] = "Misericordioso director, espero que puedas atender a mi plegaria...",
		["661106"] = "Quiero hallar la verdad, pero mírame... Buscar la verdad es buscar la muerte.",
		["661105"] = "Todo el mundo piensa eso en Tierrabendita, pero el poder de los Pilares sagrados empezó a decaer hace más de diez años... Yo, un pobre ciego, fui el único que se percató de la diferencia. Todos los demás se mostraron confundidos por la falsa ilusión difundida por la Iglesia.",
		["991201"] = "Ja, ja, ja... He destruido el barco de los dementes... Ahora no podrán abandonar este lugar... Ejecuta la voluntad de lord Ebbinghaus... Que paguen por su arrogancia...",
		["664160"] = "/t0",
		["661155"] = "No tiene nada que ver conmigo...",
		["663153"] = "<color=#DBBA7FFF>Usar la Basilisco bendito.</color>",
		["662152"] = "",
		["551179"] = "La oscuridad entre la luz sagrada, los Sendril de los que hablas... ¿son realmente los culpables?",
		["9933021"] = "Marcharse.",
		["30135237"] = "¡Por favor, detente! ¡Ya hemos hecho que desaparezcan los Sendril!",
		["331112"] = "Era mi compañera... Llegamos a este lugar desierto y nos encontramos a ese cobarde. No teníamos ni idea de las calamidades que nos esperaban.",
		["9936039"] = "",
		["662139"] = "",
		["331156"] = "¿Por qué?",
		["9941023"] = "Una pequeña chispa puede encender hasta el fuego más vivo.",
		["552281"] = "",
		["334186"] = "/t0",
		["551438"] = "¿Ictiosauria?",
		["442320"] = "¿Quieres ir a Tierrabendita?",
		["991066"] = "¿Qué es eso del barco de los dementes del que hablaste antes?",
		["9966053"] = "",
		["9951047"] = "Espero que no se nos aparezca el Fantasma de los marcioneses...",
		["111057"] = "Ten cuidado.",
		["9961045"] = "Oye, Terrence, no lo pintes todo tan oscuro y pesimista. Sabes que no se me dan bien esas situaciones por eso de que las emociones no se pueden medir en dinero.",
		["777031"] = "Silencio... Más te vale tener cuidado, no quieres despertar al lobo de su sueño.",
		["9961042"] = "Glenn, necesito tu ayuda.",
		["441293"] = "Nadie lo sabe... Los secretos esconden más secretos, y la mayoría de los infiltrados han acabado muertos.",
		["9966039"] = "",
		["334188"] = "/t0",
		["441148"] = "Las mentiras siempre están edulcoradas con las creencias, ja, ja...",
		["9961037"] = "Y siempre le pregunto a Dios que por qué hace que todo el mundo se enfrente a la adversidad.",
		["9961035"] = "Desde que te conozco, no consigo dejar de pensar en la época que pasé con Teresa.",
		["9963036"] = "Marcharse.",
		["772264"] = "",
		["551398"] = "Gracias igualmente, mensajero. Respeto tu decisión, pero debo decir que lamento que así sea... Parece que ante la aniquilación de este mundo no tengo otra opción que quedarme aquí e intentar buscar otra salida. No obstante, por favor, recuerda que el trozo que posees es la clave de todo.",
		["222294"] = "",
		["664230"] = "/t0",
		["996464"] = "",
		["114142"] = "/t1",
		["668302"] = "",
		["221130"] = "¿Te ha atrapado aquí el Santo Padre?",
		["111047"] = "Por cierto, encontré esto en el puente de allí.",
		["444149"] = "Esta puerta parece estar atascada.",
		["551387"] = "La verdad oculta alimenta las intenciones malignas de los ignorantes.",
		["554777"] = "No se puede abrir desde este lado.",
		["9943013"] = "Marcharse.",
		["991125"] = "¿Cómo sabes todo esto?",
		["331065"] = "No es momento de ponerse sentimentales... Vayamos primero a la capilla.",
		["441116"] = "La historia ha terminado. El destino está condenado. Esta falsa fe solo retrasa la destrucción que se avecina.",
		["553288"] = "Marcharse.",
		["992001"] = "¿Quieres irte de Ictiosauria?",
		["221249"] = "Mira a tu alrededor, ¡soy yo quien los ha salvado a todos! ¡Soy yo quien está prolongando el linaje de Adamina!",
		["221129"] = "El Santo Padre nos ordenó que atrapásemos a cualquiera que pasara por Adamina. Sabía que no estaba bien y, al principio, nadie quería hacerlo, pero nos acabamos acostumbrando y ahora nadie se opone.",
		["9922035"] = "",
		["663321"] = "...",
		["551282"] = "Lo siento, pero no puedo hacer ningún descuento. Eso sí, todos mis productos son dignos de su precio.",
		["461003"] = "Estamos todos en el mismo bando, solo te estoy entregando su mensaje...",
		["221108"] = "¿Insinúas que la Peste del coloso está relacionada con la bruja pelirroja?",
		["991017"] = "Cof... Cof... Rápido... Cof... Adéntrate en la cueva...",
		["554001"] = "No se puede entrar.",
		["221116"] = "¿El Espacio del oráculo?",
		["331078"] = "Vamos a la capilla.",
		["221254"] = "闭上你的嘴！",
		["225187"] = "El gran Santo Padre hará que perdure el linaje de Adamina...",
		["551310"] = "¿Quieres restaurar los atributos del personaje?",
		["555170"] = "/t0",
		["551433"] = "La verdad oculta alimenta las intenciones malignas de los ignorantes.",
		["773193"] = "Darle brote rosa brillante.",
		["9973451"] = "Hablar.",
		["991432"] = "Piedra del Heggie",
		["554165"] = "/t0",
		["552154"] = "",
		["551386"] = "La tolerancia a la venganza... la convergencia pasional... los intentos peligrosos...",
		["774033"] = "Como no me dejes marchar, ¡juro que acabaré contigo!",
		["551383"] = "Ah... La obsesión por la codicia... A todos nos ha cegado, ¿no es cierto?",
		["9931008"] = "Déjate de truquitos y dime, ¿hay alguna pista en Exilium?",
		["221056"] = "Si ya está muerta, no hay mucho que podamos hacer.",
		["551123"] = "¿Hablabas de esto?",
		["661224"] = "Teresa estaba justo delante de mí y he vuelto a perder mi oportunidad. Puede que no tenga otra...",
		["551374"] = "Los débiles y los pequeños ni siquiera dudan de las mentiras más obvias.",
		["111101"] = "¿Hay alguien ahí? ¡Ayuda! ¡Estoy atrapado en este huevo! ¡Ayuda! ¡Ayuda!",
		["441206"] = "Pareces alguien inteligente, confío en que tomarás la decisión más lógica.",
		["991451"] = "Piedra del coloso",
		["9911020"] = "无论做出什么样的选择，我们的灵魂还是会重归黑暗……",
		["551408"] = "...",
		["773244"] = "Hablar.",
		["771216"] = "Si llega ese día, seguro que te enterarás. Pero prométeme que volverás con Teresa.",
		["112907"] = "¿Quieres volver al carruaje?",
		["9921004"] = "A saber... He perdido la noción del tiempo en estas tinieblas eternas.",
		["551393"] = "Nunca pensé que te encontraría aquí, veo que todavía no has subsanado el deseo de tu corazón.",
		["773076"] = "¿Dónde está Teresa?",
		["773204"] = "Hablar.",
		["441203"] = "Mensajero, los demiurgos no tendrán otra oportunidad. ¡Lo hemos apostado todo por salvar a Vaillant de Tierrabendita!",
		["551281"] = "A un mensajero generoso siempre le deseo éxito.",
		["551355"] = "La verdad oculta alimenta las intenciones malignas de los ignorantes.",
		["444228"] = "Todavía no se puede abrir.",
		["551350"] = "Los débiles y los pequeños ni siquiera dudan de las mentiras más obvias.",
		["223113"] = "Marcharse.",
		["771199"] = "Morirás en el intento. Ahí dentro no puedes obtener nada que no sea tu propia muerte.",
		["221171"] = "¿Has encontrado a tu mamá?",
		["551396"] = "El trozo de meteoro rojizo que posees es la clave de todo. Lo necesitarás cuando llegue el momento. Gracias por ofrecerme tu promesa, pero tendrás que esperar. Pienso corresponder a tu confianza.",
		["114146"] = "/t1",
		["551234"] = "¿Y a ti qué te ha pasado?",
		["443188"] = "Marcharse.",
		["441225"] = "No es asunto tuyo...",
		["113077"] = "<color=#DBBA7FFF>¿Por qué te fuiste de la mina?</color>",
		["551188"] = "Escucha, haz lo que puedas hacer. Todo lo que te sobrepase resultará en un dolor agonizante que perdurará durante el eterno ciclo del pecado.",
		["4994993019"] = "Darle los materiales.",
		["333902"] = "No.",
		["1001100015"] = "¡Ahora despeja tu mente! ¡Es hora de danzar!",
		["331119"] = "Querida mía, siento haberte defraudado... No me queda mucho tiempo... Albertus... Albertus... Jamás serás perdonado. ¡Todos tus pecados perdurarán en esta tierra de exiliados!",
		["331074"] = "Tus ojos parecen estar repletos de dudas.",
		["9999291"] = "Pero dejarse absorber por las obsesiones no siempre lleva a buen puerto...",
		["441331"] = "¿Va todo bien?",
		["991117"] = "Eso es porque llevas demasiado tiempo sin beber agua del río del Alma. Has olvidado totalmente tu pasado.",
		["224170"] = "/t0",
		["222189"] = "",
		["551313"] = "¿Quién eres?",
		["775118"] = "No... Sálvame... Que alguien me... salve...",
		["771140"] = "¡Tú engañaste a Viola para que disparase a Teresa! ¡Tú estabas desesperado por abrir el Sepulcro colosal y crear todo este caos! ¡¿Y ahora quieres que salve a Teresa?!",
		["1004100076"] = "Pero los utilizaste...",
		["9923043"] = "Cambiar de atuendo",
		["551293"] = "Ja, ja... ¡Sabía que este sería mi día de suerte! ¡Por nuestra nueva amistad!",
		["551289"] = "¿Has oído eso?... Los fantasmas de los marcioneses... ¡No dejan de suspirar y vagar por aquí!",
		["551275"] = "Al fin puedo volver a Tierrabendita...",
		["9951031"] = "Deberías estar agradecido por haber podido abandonar la Iglesia y tener algo por lo que vivir.",
		["332905"] = "¿Quieres volver al carruaje?",
		["551273"] = "Ja, ja... ¡Sabía que este sería mi día de suerte! ¡Por nuestra nueva amistad!",
		["661240"] = "¿Qué pasa? No te hagas el sorprendido, tan solo odio la luz...",
		["331144"] = "Así es, pero la Iglesia no detuvo su investigación en Katib. Según ciertos rumores, la entrada al Sepulcro colosal fue hallada allí.",
		["551363"] = "La verdad oculta alimenta las intenciones malignas de los ignorantes.",
		["221303"] = "¿A quién se le ha ocurrido una idea tan macabra?",
		["228231"] = "",
		["9963039"] = "Bendecir.",
		["884045"] = "Aaah... Hay algo en mi interior...",
		["334071"] = "/t0",
		["4994993007"] = "No, gracias.",
		["791340"] = "Este brazalete lo significa todo para mí. Estoy buscando a su dueño...",
		["9961013"] = "Cuando lleguemos a Tierrabendita, quédate con Glenn en el carruaje. No tienes por qué involucrarte.",
		["551392"] = "Mensajero, ¿podrías escucharme un momento? Me gustaría pedirte un favor.",
		["447084"] = "En fin... Está claro que no podemos quedarnos de brazos cruzados y esperar a que llegue nuestro fin. Arriesgarnos a encontrar a ese tipo sería el modo más sencillo de llegar a Tierrabendita.",
		["9971308"] = "¿Cómo dices?",
		["661101"] = "¿Dónde podría estar si no? Mensajero, este lugar está repleto de Sendril y masacre, ¿hay algún lugar seguro?",
		["30134240"] = "¡Teresa! ¿Dónde estás?",
		["551262"] = "Si permitimos que vuelva a Tierrabendita así, todas las tragedias que han tenido lugar aquí volverán a repetirse, o podría ser incluso peor...",
		["554258"] = "/t0",
		["791303"] = "Me alegro por ti.",
		["663704"] = "No.",
		["991411"] = "No pasa nada.",
		["551244"] = "He visto algo... Era Teresa... con el Sello de Yekub... y unos caballeros arrodillándose ante ella...",
		["441228"] = "Voy a tocar la última campana. Después de eso, sabrás dónde encontrarme.",
		["991074"] = "Gracias, amable forastero. Me has ayudado a encontrarla antes de llegar a rendirme. Deben de haber sido los tipos del barco de los dementes... Le gastaron una broma muy cruel...",
		["662401"] = "¿Quieres abrir la puerta?",
		["551126"] = "Al fin puedo volver a Tierrabendita...",
		["112077"] = "",
		["331157"] = "Lo necesitarás si quieres estar lúcido ahí fuera.",
		["555253"] = "Detente, por favor... Déjame ir...",
		["447304"] = "Esta debe de ser la puerta trasera del pueblo que mencionó Frollo. Usa la llave para abrirla.",
		["551116"] = "Mensajero, volvemos a vernos. Lo siento, estaba pensando en mi esposa de Tierrabendita y, a pesar de que no puedo recordar su rostro, sigo deseando estar con ella... Ah, sí, ¿has encontrado el reloj de arena?",
		["9971341"] = "Tiene un texto extraño grabado...",
		["223106"] = "¿Por qué todos los discípulos de esta zona huelen como los Sendril?",
		["551276"] = "Los sifleros aprovechan cualquier oportunidad para ganar algo de dinero.",
		["1006100083"] = "Desde ese momento, perdiste algo...",
		["221138"] = "¿Señor mensajero? ¿Sigue vivo? No, no, no... ¡Tiene que estar muerto! ¡Solo podré ver a mi mamá cuando esté muerto! ¿Dónde está Elmar? ¡¿Dónde está?!",
		["551337"] = "La verdad oculta alimenta las intenciones malignas de los ignorantes.",
		["9971365"] = "Todos tus esfuerzos se verán recompensados, así como tu propio corazón.",
		["553366"] = "Sí.",
		["551270"] = "...",
		["995015"] = "Cof... Cof...",
		["9999295"] = "En Tierrabendita, los hijos de los infames son conocidos como «Criaturas de la noche»... Pero hace ya mucho de eso, prefiero no hablar de ello.",
		["661151"] = "Mensajero, ojalá mis actos pudieran aportarme alguna opción más para poder sobrevivir... Me he enfrentado toda la vida al destino... No pude decidir cómo nacer, pero al menos puedo decidir cómo morir.",
		["554178"] = "/t0",
		["551160"] = "Cuando el coloso empezó a fusionarse con la Torre sagrada, las brujas marcionesas durmieron a todo el mundo... y no fue fácil hacerlos despertar de ese sueño.",
		["441196"] = "Si todavía quieres ir a Tierrabendita, te ofrezco una última oportunidad. Ayudarme a mí es ayudarte a ti mismo, mensajero.",
		["557169"] = "Qué raro. Dice «La piedra del pasado».",
		["113207"] = "No.",
		["661311"] = "Toc toc.",
		["334224"] = "/t1",
		["551165"] = "Sin embargo, decidieron mantener el secreto bajo la Torre sagrada para siempre, por lo que nadie se fue…",
		["9932012"] = "",
		["111056"] = "Dividámonos. Tú ve a la cima de la montaña, yo buscaré por Heggem.",
		["991042"] = "Llegas demasiado tarde... Ya no queda nada... Igual que no quedan recuerdos ni esperanza... Ja, ja, ja... ¿Eh?",
		["441245"] = "Es parte del antiguo ritual de la bruja marcionesa. Instiga el inmenso poder oculto en nuestro interior y unirá a los demiurgos con Edith.",
		["554129"] = "/t0",
		["445090"] = "¡Luz! ¡Más luz! ¡No dejéis que se vaya!",
		["221101"] = "那个小女孩是个彻头彻尾的骗子！我早有预感！",
		["551154"] = "Antes era un sacerdote de la Iglesia, uno de carne y hueso, pero hace mucho de eso. Ahora, es muy poco habitual mantener la forma humana.",
		["662196"] = "¿Seguro que quieres continuar?",
		["771229"] = "¿Tú crees?",
		["553138"] = "Lo he encontrado.",
		["551345"] = "Los mediocres siempre se obsesionan con superar a los demás.",
		["551137"] = "Espero que olvides esa idea, de lo contrario... no te gustará lo que pase, amigo mío.",
		["9931006"] = "Lo siento, pero he ganado. Te dije que el resultado ya estaba predeterminado. Espero que estés listo para enfrentarte a la verdad.",
		["553135"] = "No.",
		["662320"] = "",
		["551131"] = "...",
		["111119"] = "¿Sigue por aquí?",
		["551127"] = "Sé paciente, mi astuto amigo, sé paciente...",
		["113071"] = "<color=#DBBA7FFF>¿Qué pasa en este lugar?</color>",
		["9933033"] = "¿Tienes artículos nuevos a la venta?",
		["331145"] = "¿Entonces también está buscando el Sepulcro colosal? ¿Por qué?",
		["441187"] = "¡¿Una marcionesa?! ¡¿Qué decía?!",
		["993257"] = "Marcharse.",
		["442063"] = "",
		["441260"] = "¿Los demiurgos saben algo de esto?",
		["331223"] = "No se cambia nada. No se devuelve nada. Que tengas buen viaje.",
		["9963032"] = "Comerciar.",
		["4994991010"] = "Camina indefenso por la noche oscura y acabarás hecho pedazos... ¿Por qué no buscas la manera de iluminar la oscuridad?",
		["9921039"] = "¿Quién es el hombre enmascarado?",
		["554086"] = "/t0",
		["221261"] = "Ve a Exilium, Terrence. Allí encontrarás la pista que estás buscando.",
		["771205"] = "Hiciste todo lo que estaba en tu mano, incluso más.",
		["551315"] = "¿Por qué estás en la Niebla oscura?",
		["441298"] = "¿Cuánto tardarás?",
		["991003"] = "Vaya... Pensaba que ya eras comida para peces.",
		["553903"] = "Sí.",
		["791316"] = "El mundo ha cambiado, Terrence. Para la Iglesia, la herejía ya no es la mayor de las amenazas. Al principio, el maestro no quería darme la mano, pero eso cambió cuando corté la mía propia...",
		["664083"] = "/t0",
		["225280"] = "Oh, no es suficiente ni por asomo... Deja de gritar. Pronto... el dolor desaparecerá.",
		["9951052"] = "Katib es, simple y llanamente, un tesoro inagotable para la Iglesia.",
		["225081"] = "He prolongado el linaje de nuestro Santo Padre.",
		["9924034"] = "/t0",
		["441250"] = "No, esto es solo el principio...",
		["9951038"] = "No soy un mero peón de la Iglesia, solo obedezco a mi maestro. Mis labios están sellados hasta que descubramos la verdad.",
		["337049"] = "Estaba claro que este lugar sería un paraíso de muerte y Sendril.",
		["9951030"] = "Así funciona la Iglesia. Manipula a la gente con fe y después los convierte en peones.",
		["441270"] = "Me has decepcionado, mensajero. Tuviste tu oportunidad, pero tomaste la decisión más estúpida de todas. El ritual ha sido completado, ¿estás listo para soportar la ira de los demiurgos?",
		["9951029"] = "...",
		["113903"] = "Sí.",
		["331160"] = "¿Y si sigue viva?",
		["9951018"] = "Si esperas que me confiese ante Dios, estás perdiendo el tiempo.",
		["441099"] = "...",
		["9951017"] = "Terrence, a pesar de que llevo mucho tiempo ciega, todavía puedo ver tu cara llena de tristeza. No ha cambiado nada.",
		["441278"] = "¡Maldición! ¡Esa mujer ha seguido adelante con el ritual, Edith va a renacer!",
		["111087"] = "Oh, es el abrigo de mi Jerry. Mi Jerry, has vuelto.",
		["115089"] = "Querido Jerry, mamá te traerá de vuelta a casa. Mamá estará siempre contigo.",
		["991456"] = "Piedra del Heggie",
		["338230"] = "",
		["444269"] = "/t0",
		["448312"] = "",
		["448311"] = "",
		["331063"] = "Imagino que sería bello...",
		["9911018"] = "咳……咳……它还在等待，它总是在等待……",
		["117127"] = "¡Mira! ¡Justo ahí! Hay sifleros por el pueblo. Parece que han reunido una pequeña fortuna.",
		["224047"] = "/t1",
		["551425"] = "...",
		["661219"] = "¡Ahí te has pasado!",
		["771108"] = "Ah... Has vuelto... ¿Has cambiado de opinión? Si quieres irte de aquí, libérame de mi prisión.",
		["331193"] = "Si lo que dices es cierto, la entrada al Sepulcro colosal debe de estar en Katib. Ese debe de ser el siguiente destino de Teresa.",
		["447158"] = "El tipo del campanario parece la clave para entrar en Tierrabendita.",
		["552287"] = "",
		["9931021"] = "No, claro que no.",
		["551150"] = "Vaya, qué extraño... ¿Todavía queda gente normal en Katib o solo sois gente que parece ser normal?",
		["118150"] = "",
		["221245"] = "¿Qué se ha llevado?",
		["665194"] = "Perdóname, Vaillant... Había que realizar este sacrificio...",
		["114023"] = "Glenn, solo puedes estar conmigo si mantienes el pico cerrado.",
		["221054"] = "Se acabó, no tengas miedo.",
		["665083"] = "La Iglesia... debe pagar... el precio...",
		["445089"] = "La tenue luz se volverá eterna y nos guiará hasta la tierra de la nada...",
		["441290"] = "¿Quieres decir que Vaillant no fue acusado injustamente?",
		["4994992004"] = "Requiere aceite de ballena rancio y 100 setas espectrales",
		["551099"] = "No tienen ninguno de repuesto... De hecho, no me resultó sencillo conseguir ese. Si pudieras encontrarlo por mí, ambos saldríamos beneficiados.",
		["221193"] = "Por favor, llévale esto, es su muñeco favorito... Una vez haya dado a luz al fruto del Santo Padre, mamá volverá...",
		["443080"] = "Marcharse.",
		["991515"] = "Lo único que podemos hacer es esperar con tranquilidad y acabar olvidándolo todo. Con suerte, podré conservar estos libros para tener algo de compañía.",
		["661116"] = "¿Por qué no aparece el apoderado? Nadie recuerda la última vez que salió de su residencia...",
		["221106"] = "¿Por qué todos los discípulos de esta zona huelen como los Sendril?",
		["337081"] = "¡Sigue con vida!",
		["663404"] = "No.",
		["1007100095"] = "¿A quién pertenecía esa voz?",
		["331224"] = "He conocido pocos mensajeros como tú. Siento decir que todos han muerto.",
		["994888"] = "Todavía no se puede abrir.",
		["991085"] = "Hace tanto que tuvo lugar esa historia que ya ha quedado en el olvido... Todo se ha venido abajo por aquí. Tal vez la verdadera respuesta esté solo en poder de esos tipos de ahí arriba…",
		["221306"] = "Este «milagro» tuvo un efecto secundario de lo más extraño, pues supuso un enorme aumento en la mortalidad masculina. Sin embargo, esto no es algo precisamente malo para el Santo Padre...",
		["224900"] = "/t0",
		["9933036"] = "Marcharse.",
		["221309"] = "Ya eres libre...",
		["441165"] = "Si hace sonar todas las campanas, el amanecer de los demiurgos volverá a sumirse en la oscuridad...",
		["541002"] = "¿Qué demonios quiere hacer? ¿Sigues escondiéndome cosas después de todo este tiempo?",
		["552132"] = "",
		["441109"] = "Otra vez a esperar, pues no sé cuándo volverán. Solo espero que no terminen como su padre, haciéndome esperar toda una vida.",
		["9941035"] = "Después de tantos años, esta es la primera vez que busco la ayuda de Dios.",
		["991104"] = "Me siento tan impotente... Puede que esta gente fuese parte de mi pueblo, pero soy incapaz de recordarlos.",
		["662701"] = "¿Quieres abrir la puerta?",
		["9972455"] = "",
		["225183"] = "Santo Padre, estoy dispuesta a ir al purgatorio por ti.",
		["441141"] = "¿Elena?",
		["111082"] = "Robé el tesoro... para encontrar a mi Jerry. Juré que en cuanto le encontrara, devolvería el tesoro, pero no pude encontrarlo por ninguna parte. Forastero, ¿podrías ayudarme?",
		["668303"] = "",
		["9971470"] = "De todos modos, la verdad oculta está bajo la Torre sagrada de Katib, tras la puerta repleta de poder de los Sendril.",
		["9953042"] = "Comerciar.",
		["551120"] = "No me sorprende, no es una tarea sencilla. A decir verdad, a veces hasta la gente con más suerte puede ser desafortunada. Al igual que yo no puedo saber cuándo encontraré esos malditos hechizos marcioneses.",
		["9971388"] = "Al fin y al cabo, Dios es una creación, es un concepto que no necesita ser visto para que los humanos le den significado a sus necedades. ¿Alguien se ha parado a pensar con seriedad por qué Dios creó un mundo maldito? Siempre hay una razón, pero hay cosas que ya no se pueden comprender.",
		["799315"] = "Tal vez lo haga, pero no tengo muchas esperanzas en lograrlo. Date prisa, mensajero. Sé implacable con la noche oscura y enfréntate a la muerte de la luz.",
		["9956058"] = "",
		["111098"] = "(El huevo gigante siente tus pasos y se estremece.)",
		["991421"] = "Piedra de la humanidad",
		["9961021"] = "No lo sé, pero tengo que intentarlo.",
		["791359"] = "Cuando me fui del orfanato de la Iglesia, pensé que era valiente, pero ahora me doy cuenta de que no podía dejar atrás mi pasado.",
		["664161"] = "/t0",
		["9933035"] = "¿Tienes artículos nuevos a la venta?",
		["461004"] = "Sé lo que hago.",
		["115066"] = "Jerry... ¿Dónde está Jerry? Mi Jerry...",
		["228233"] = "",
		["661180"] = "No, al menos no desde que Vaillant fue sentenciado. Desde entonces, desde las profundidades de Tierrabendita solo se puede oír un quejido de dolor que ha durado más de una década.",
		["333158"] = "¿Por qué sigues aquí?",
		["441071"] = "En fin... Pensábamos que el miedo haría que los demiurgos se marchasen, pero fue inútil.",
		["771147"] = "Terrence, sé que esto es demasiado que asimilar... Pero ahora mismo lo más importante es llegar a las profundidades de la cueva y salvar a Teresa. Te lo explicaré todo después.",
		["441204"] = "Tengo que volver a la sala, allí hay algo más importante esperándome.",
		["112111"] = "",
		["224888"] = "Todavía no se puede abrir.",
		["9941020"] = "¿Crees que nos podrían ayudar?",
		["554138"] = "/t0",
		["331099"] = "Mensajero, ¿podrías hacerme un favor? He perdido mi alianza en la capilla. Cuando vuelva mi prometida, me gustaría recitarle mis votos, intercambiar las alianzas y agradecerle su ayuda. ¿Podrías ayudarme a encontrar mi alianza?",
		["111053"] = "¿Es la persona que buscas?",
		["551136"] = "¿Me tomas el pelo? ¡No tengo tan mala suerte! ¿Acaso quieres quedarte el reloj de arena para ti?",
		["9961048"] = "¿Cómo crees que sigo vivo? Nadie entiende mejor Tierrabendita que yo, así que si quieres vivir, llévame contigo.",
		["221069"] = "Otro cobarde.",
		["221206"] = "¡Bendito sea el Santo Padre! Gracias por permitir que prolongue tu linaje...",
		["9921023"] = "¿Qué os pasó?",
		["111052"] = "...",
		["225232"] = "看看你们都干了什么好事！",
		["333221"] = "Marcharse.",
		["551164"] = "Todos volvieron a la Torre sagrada, donde se descubrió el espantoso secreto que se esconde debajo.",
		["443105"] = "Sí, lo he encontrado.",
		["991219"] = "No tengo tiempo para eso ahora mismo. Deberías encontrar un lugar seguro en el que quedarte.",
		["551397"] = "Gracias por tu ayuda, estoy muy agradecido. No obstante, lo siento, pero no estoy de humor para realizar buenas acciones.",
		["553105"] = "Lo he encontrado.",
		["551271"] = "Ah, eres tú, amigo mío. ¿Cómo va todo? ¿Has encontrado el reloj de arena?",
		["791325"] = "No te preocupes, se ha convertido en una herramienta muy útil para tener «a mano». El odio solo le aporta un flujo constante de poder.",
		["112067"] = "",
		["9943027"] = "Marcharse.",
		["441285"] = "Bueno, ¿puedes decirme la verdad ya?",
		["9971282"] = "He encontrado algo de arilo... Espero que te sirva de ayuda.",
		["551280"] = "Mi puesto lleva desierto mucho tiempo, igual puedo hacerte un buen descuento.",
		["111099"] = "(El huevo gigante siente tus pasos y se estremece.)",
		["1004100112"] = "Pero sabemos que todo lo que los demiurgos necesitan es una sola voz.",
		["112103"] = "",
		["994478"] = "/t0",
		["9932020"] = "",
		["551323"] = "Aquellos que persiguen una obsesión imposible pueden acabar con la conspiración de la luz y la oscuridad. ¿Cómo? Acabando bajo tierra.",
		["881080"] = "La ignorancia y la codicia convergen y colisionan... Acostumbrado a mantener la cabeza alta, nunca más volverá a estar bien inclinarse.",
		["441332"] = "Perdóname... Tenía la cabeza en esas olas tan serenas... No esperaba encontrar el meteoro rojizo de Ictiosauria en tan pocos meses... Todas nuestras dificultades han acabado dando frutos...",
		["227120"] = "He encontrado a Viola, pero me ha detenido un poder sobrenatural... No he podido acercarme a ella.",
		["551236"] = "Esa no es forma de responder a alguien que se preocupa por ti.",
		["557242"] = "Hay algo en este lugar que ha sido enclaustrado y venerado.",
		["551138"] = "¿Hablabas de esto?",
		["665131"] = "No puedes detenernos... Los demiurgos recuperaremos todo lo que hemos perdido...",
		["331148"] = "Eso me resulta familiar...",
		["441074"] = "Mensajero, la rata es mi presa, déjamelo a mí.",
		["446304"] = "",
		["223315"] = "Darle el Sendril activo",
		["551243"] = "¿Estás bien?",
		["9971354"] = "Habla del día en que nací.",
		["223191"] = "Marcharse.",
		["221190"] = "¿Un manto verde? He visto a tu hija.",
		["461005"] = "Me alegra oírlo. Creo que te has vuelto algo descuidada últimamente.",
		["111071"] = "¿Qué pasa en este lugar?",
		["1001100016"] = "Tiene un aspecto miserable...",
		["551263"] = "¿Por qué?",
		["115140"] = "La pena me mantiene sobrio...",
		["9999296"] = "Tal vez puedas encontrar un lugar en el que volver a empezar.",
		["661264"] = "Je, je, qué mensajero más simple...",
		["111074"] = "Un mensajero se llevó a los discípulos que sobrevivieron hasta la mina. Dijo que allí había un tesoro que podía salvar Heggem.",
		["773225"] = "Marcharse.",
		["224169"] = "/t0",
		["222249"] = "",
		["221126"] = "El Santo Padre me dijo que podría volver a ver a mi mamá si atraía a forasteros a Adamina para seguir con la caza.",
		["9923044"] = "Marcharse.",
		["113206"] = "Sí.",
		["551417"] = "Los débiles y los pequeños ni siquiera dudan de las mentiras más obvias.",
		["111080"] = "Mi querido Jerry... Salió fuera para jugar con los hermanos John, pero no ha vuelto en dos días. Es un bromista.",
		["553395"] = "Rechazar.",
		["111081"] = "¿Cómo has conseguido sobrevivir aquí fuera?",
		["551440"] = "...",
		["552238"] = "",
		["9953046"] = "Marcharse.",
		["553272"] = "Lo he encontrado.",
		["661170"] = "¿Qué demonios pasó hace quince años?",
		["551360"] = "El amor es... algo que te puede llevar a la locura.",
		["991038"] = "Da igual, solo espero que pueda vivir lo suficiente como para ver a ese tal lord Ebbinghaus.",
		["228232"] = "",
		["111076"] = "Sí, la luz que encontramos era lo bastante brillante como para dispersar los Sendril de la mina. Después, nos escondimos allí y esperamos a que la Iglesia nos rescatara.",
		["551274"] = "Salgamos de aquí, ahora que todo está listo. Con la ayuda de la suerte que he tenido hoy, seguro que encontramos el Sello de Yekub que está bajo la Torre sagrada.",
		["441205"] = "Los pioneros demiurgos ya han partido hacia Tierrabendita. Si no les ayudamos, su sacrificio habrá sido en vano.",
		["9951024"] = "Todo lo que pasó en Katib... La masacre de los marcioneses... ¿Es eso a lo que te aferras?",
		["9971309"] = "Cuando mi maestro me dio esta mano, me dijo que, además de este brazo, otras tres reliquias antiguas fueron introducidas en este féretro, pero, por algún motivo, fueron robadas y se perdieron. Solo hallará la calma tras encontrarlas, o bien devorará todo ser viviente si no se usa correctamente.",
		["551110"] = "Mírate, perderte entre pensamientos insignificantes solo te traerá mala suerte.",
		["994016"] = "/t0",
		["661261"] = "Je, je, qué mensajero más simple...",
		["113133"] = "Marcharse.",
		["661102"] = "...",
		["115142"] = "La pena me mantiene sobrio...",
		["555271"] = "Aaah... Qué estúpido soy... He empleado todo lo que tenía en... perseguirle... para nada... Ahora solo tengo mentiras y escarnio...",
		["111049"] = "Está escrita en el idioma de Tierrabendita, ¿qué hace esto en Heggem?",
		["9961014"] = "No pienso quedarme sentada esperando como si nada.",
		["775034"] = "/t0",
		["4994993006"] = "Darse por vencido.",
		["221111"] = "¿El Santo Padre? ¿También es un Discípulo de Adamina?",
		["994036"] = "/t0",
		["791338"] = "Tengo un objetivo.",
		["441061"] = "...",
		["114127"] = "/t0",
		["552284"] = "",
		["223294"] = "Todavía no las tengo.",
		["664888"] = "Todavía no se puede abrir.",
		["662901"] = "¿Quieres volver al carruaje?",
		["111126"] = "¿Eres tú, bondadoso señor? ¿Has vuelto para salvarme?",
		["113901"] = "Sí.",
		["441156"] = "No podemos permitir que toque todas las campanas o el plan de los demiurgos estará en serios problemas.",
		["991135"] = "Al igual que pensábamos que el río del Alma nunca se secaría, hasta que un día, de pronto, se hizo el silencio.",
		["9953021"] = "Bendecir.",
		["117095"] = "La luz de Heggem se desvanece y el tesoro no está. Seguro que esos pobres discípulos se han convertido en monstruos.",
		["771253"] = "Recordaré lo que acabas de decir.",
		["991512"] = "Cada vez es más difícil encontrar agua pura del río del Alma.",
		["661175"] = "Los muertos no mienten, pero los marcioneses están inextricablemente conectados con todo esto. Nadie es inocente. Cuando empiezas a explorar la verdad, te ves atrapado en ella.",
		["331058"] = "Esto es lo más parecido que hay a la muerte.",
		["9931007"] = "¡Debes de haber perdido la cabeza, Terrence!",
		["221200"] = "¡Voy a dar a luz! ¡Me duele!",
		["553359"] = "Sí.",
		["9922041"] = "",
		["9961049"] = "...",
		["221163"] = "Te alegrará verlo.",
		["771239"] = "Cuida de Viola.",
		["221172"] = "Se ha despedido de mí...",
		["221228"] = "¡No me dejas elección! ¡Devuélvemelo!",
		["111044"] = "¿Por qué estará tan débil?",
		["222268"] = "",
		["111073"] = "¿Ha sobrevivido algún discípulo?",
		["443901"] = "Sí.",
		["112205"] = "¿Quieres devolver el huevo del portador?",
		["553375"] = "Sí.",
		["9942032"] = "",
		["111048"] = "No es más que la nota de una bruja, solo tiene alquimia básica, nada del otro mundo.",
		["551375"] = "Ah... La obsesión por la codicia... A todos nos ha cegado, ¿no es cierto?",
		["9973381"] = "Contrato.",
		["9921036"] = "La respuesta está en Adamina.",
		["993218"] = "Rechazar.",
		["9933017"] = "Comerciar.",
		["221223"] = "Teresa...",
		["9921018"] = "¡Dame ese diario!",
		["9931031"] = "Muy bien, olvídalo, pues.",
		["334050"] = "Un guardia de la Iglesia. Su cadáver está marchito y tiene cortes letales en el cuello.",
		["553426"] = "Llenar botella de Melos",
		["226269"] = "",
		["991446"] = "Piedra del siflero",
		["221314"] = "Espero que no estés herido, oh, bondadoso mensajero. De lo contrario, me sentiría culpable.",
		["551343"] = "Ah... La obsesión por la codicia... A todos nos ha cegado, ¿no es cierto?",
		["333901"] = "Sí.",
		["225082"] = "¡Alejaos de mí, sucios forasteros!",
		["331179"] = "Si eso es cierto, quiero volver al pasado.",
		["441114"] = "Oh, cuanto ansío bañarme en la luz de Tierrabendita…",
		["552310"] = "",
		["778301"] = "",
		["551190"] = "No es nada, solo necesito descansar.",
		["9951040"] = "Podría decirse que sí, amigo mío. No te preocupes, sé qué puedo decir y qué no.",
		["771206"] = "¿Tú crees? Nunca me despedí como es debido. Ni siquiera sé si era una buena persona o no.",
		["551151"] = "Un momento... Uno de vosotros oculta algo, pero puedo oler el aroma a sangre... En algún momento fuiste un peón de la Iglesia... y tienes historia en este lugar...",
		["553351"] = "Sí.",
		["441246"] = "La campana de Frollo no solo elimina el polvo blanco, también hace enloquecer a aquellos que han realizado el ritual.",
		["774035"] = "¡Ni se te ocurra tocarle! ¡No dejaré que te vayas si lo haces!",
		["334058"] = "/t0",
		["224123"] = "/t0",
		["553285"] = "Marcharse.",
		["441303"] = "Humano, ¿quieres hacer negocios conmigo? Tengo multitud de tesoros.",
		["991150"] = "Obviamente, en Ictiosauria nadie puede confiar ciegamente en su memoria, así que no le des más vueltas.",
		["222288"] = "",
		["9933034"] = "¿Qué sabes sobre Exilium?",
		["9961022"] = "¿Y qué pasa si no lo consigues?",
		["774041"] = "/t0",
		["443104"] = "Lo siento, todavía no.",
		["331087"] = "Oh, lo siento mucho, mensajero. Espero que no te haya asustado mi horrible aspecto. No temas, yo también era un caballero de la Iglesia.",
		["9941013"] = "Como mensajero, no puedo ni siquiera acercarme a Tierrabendita...",
		["551312"] = "No tienes vino de miel rutilante.",
		["441271"] = "Como ya he dicho, todo aquel que se atreva a intentar impedir que los demiurgos recuperen la luz será destruido.",
		["221308"] = "Ahora mismo es nuestra única opción, así que supongo que no tiene nada de malo que trabajemos en equipo.",
		["443302"] = "Marcharse.",
		["665070"] = "Cuando la bruja marcionesa encuentre al apoderado, Vaillant será libre al fin.",
		["9963014"] = "Marcharse.",
		["661216"] = "Por lo que más quieras, no será que tú tampoco quieres que Teresa viva, ¿no?",
		["331154"] = "Benita, ¿podrías restaurar el meteoro rojizo si encontramos las partes que quedan?",
		["997208"] = "¿Ladrones? ¡Mira este lugar! No se me ocurre nada de valor que robar aquí... No perdamos el tiempo, nadie parece normal en este sitio.",
		["9921047"] = "Estoy un poco cansado, creo que necesito descansar.",
		["9963026"] = "Hablar.",
		["553311"] = "No.",
		["441070"] = "Parece que esta rata que comentas no es un tema sencillo.",
		["999282"] = "¡Eh! ¿Has encontrado ya la palanca y el engranaje para el mecanismo?",
		["9961039"] = "Pero el sufrimiento acabará. No importa en qué se convierta Teresa, estaré con ella en la dicha y en la adversidad.",
		["447137"] = "La campana suena en esa torre. ¡Tenemos que llegar hasta ahí antes de que se vaya!",
		["793312"] = "He encontrado una reliquia antigua.",
		["791282"] = "He encontrado algo de arilo... Espero que te sirva de ayuda.",
		["991092"] = "No te puedo ayudar... Toma, puede que a ti te sea útil.",
		["227119"] = "Menos mal que estás vivo, Terrence... Aquí apesta, por cierto.",
		["991044"] = "Márchate, no tenemos suficiente ni para nosotros mismos. No conseguirás nada hasta que alguien traiga algo de agua...",
		["447159"] = "El tipo del campanario parece la clave para entrar en Tierrabendita.",
		["331189"] = "Esto es... el Sello de Yekub. Dicen que es la llave del Sepulcro colosal.",
		["771187"] = "Ah... ¿Has encontrado alguno? Esa luz... Ese sabor...",
		["663332"] = "Marcharse.",
		["552373"] = "El precio es 1 miel de obsesión y 180 huesos de antozoo. ¿Quieres continuar?",
		["991190"] = "Lo siento, lord Handel, no puedo heredar la gloria de los caballeros.",
		["331168"] = "Aaah... No sé cómo podría mostrarte mi gratitud, mensajero.",
		["113104"] = "<color=#DBBA7FFF>¿Cómo puedo salvarte?</color>",
		["991086"] = "¿A qué esperas?",
		["221290"] = "Muchas gracias, mensajero.",
		["334051"] = "Un caballero de la Iglesia con una lanza. Su armadura oxidada chirría al ser empujada por el viento. No puede estar más muerto.",
		["100510052203"] = "Sí.",
		["333220"] = "Comerciar.",
		["331221"] = "He conocido pocos mensajeros como tú. Siento decir que todos han muerto.",
		["221107"] = "Estoy siguiendo el rastro de la Peste del coloso. El Santo Padre predijo que una feroz bruja pelirroja se dirigiría a Adamina para llevar a cabo su destrucción.",
		["443065"] = "La sublevación de los demiurgos...",
		["771196"] = "¡¿Sabías que esto iba a pasar?! ¿Y aun así la has puesto en peligro?",
		["331184"] = "Si al final Teresa se convierte en un monstruo, seguirás...",
		["993070"] = "Marcharse.",
		["773086"] = "Tu padre...",
		["551358"] = "Los débiles y los pequeños ni siquiera dudan de las mentiras más obvias.",
		["9952017"] = "",
		["9931022"] = "Parece que tienes algún problema con Norwood.",
		["9931025"] = "Es distinto.",
		["994415"] = "",
		["331142"] = "Entró en una zona prohibida de Katib.",
		["551403"] = "...",
		["551238"] = "Parece que ha sido contaminado por los Sendril.",
		["113113"] = "¿Una bruja extraña?",
		["9953018"] = "Marcharse.",
		["224049"] = "La entrada parece estar sellada.",
		["441295"] = "Es todo cuanto sé. Ve a Tierrabendita, pregúntale a la bruja marcionesa que buscas y ella contestará a tus preguntas.",
		["661179"] = "¿Nadie ha visto al apoderado?",
		["331177"] = "Al menos ha mantenido su promesa.",
		["113202"] = "Sí.",
		["9963053"] = "Comerciar.",
		["991467"] = "A un mensajero generoso siempre le deseo éxito.",
		["9943040"] = "La historia entre los demiurgos y la Iglesia.",
		["111041"] = "Según dicen las leyendas, es un augurio de que el coloso se está preparando para empezar su viaje hasta el Sepulcro colosal.",
		["661228"] = "Pero el apoderado ha muerto, no podemos entrar por ninguna de las dos entradas.",
		["441162"] = "¿Estás herida?",
		["661117"] = "Que Dios te bendiga, mensajero. Espero que nos volvamos a ver.",
		["441142"] = "Esa lunática que me persigue. Fue elegida por el vicepontífice. Los demiurgos están provocando su propia destrucción.",
		["441283"] = "No olvides nuestra arma secreta. Rápido, es lo último que te pido. Te lo contaré todo si me ayudas con esto.",
		["9971389"] = "Los humanos piensan que Dios les ha dado la capacidad de comprenderlo todo, ¿pero acaso todo significa «todo»? No les gusta la incertidumbre, por lo que buscan refugio en la fe, ese es su pecado. La luz del coloso no es la antorcha de la razón. Si los humanos no piensan, no son más que meros juncos.",
		["223902"] = "No.",
		["991105"] = "Encontremos primero a ese chico. Tal vez sepa algo.",
		["444320"] = "/t0",
		["117029"] = "Heggem no es el primer coloso que pierde su luz. Tal y como está, no puede detener la Niebla oscura que lo está invadiendo. ¿Dónde están los discípulos de Heggem?",
		["668301"] = "",
		["331181"] = "Estoy bien...",
		["551183"] = "Busco un modo de acabar con este sueño eterno, por eso acabé aquí...",
		["441077"] = "¿Qué tiene de especial la campana? Parece que te causa un gran dolor.",
		["663405"] = "Sí.",
		["225080"] = "¡Mi bebé! ¡Mi adorable bebé!",
		["337084"] = "Podría ser, pero no es eso...",
		["991094"] = "¡Ja, ja, al fin! ¡Es un milagro!",
		["447085"] = "¿Edith ha recuperado luz? ¿Cómo es posible?",
		["991240"] = "Ya basta... Lo entiendo... Podrías... decirme cómo es el mundo ahí fuera... Dime, ¿cómo es el cielo?",
		["884057"] = "No eres digno de la verdad.",
		["773393"] = "No.",
		["9971471"] = "Con tantas pistas, ¿cuál debemos seguir?",
		["551394"] = "¿Me echarías una mano si con ello lográsemos librarnos de esta pesadilla?",
		["772244"] = "",
		["9943012"] = "Hablar.",
		["447092"] = "Parece que la campana corrompe la mente de los lugareños.",
		["442188"] = "",
		["441100"] = "¿Acaso estás nervioso? Ja, ja... ¿Puedo pedirte un favor antes de que te marches?",
		["221148"] = "Pero, por favor, tenga cuidado. Últimamente está un poco... irritable. No haga que se enfade, no tengo ni idea de qué clase de cosas malas le podría hacer...",
		["793382"] = "Contrato.",
		["114038"] = "/t0",
		["9971455"] = "¿Sigues pensando en lo que ha sucedido?",
		["9931040"] = "Gracias por tu compra.",
		["9946046"] = "",
		["334227"] = "/t0",
		["441107"] = "He cumplido el objetivo que tenía en mi vida y he criado a tres soldados demiurgos. Ahora podré descansar un poco en paz.",
		["551241"] = "Hay algo en este lugar que ha sido enclaustrado y venerado.",
		["225233"] = "吼！",
		["991423"] = "Piedra del siflero",
		["221135"] = "Fue Elmar, la persona más amable del mundo, sin contar a mi mamá. Me encerró aquí para protegerme del peligro.",
		["9971307"] = "No te preocupes por ella, tan solo tiene un deseo frustrado, por lo que parece inquieta.",
		["443120"] = "Darle una vela grasienta.",
		["9961047"] = "Tengo que prepararme para lo peor.",
		["334180"] = "/t0",
		["661181"] = "¿Quieres decir que el quejido proviene del apoderado?",
		["9941041"] = "Solo se pueden culpar a sí mismos, pues en lugar de estudiar sobre la literatura clásica del Monasterio sagrado, quisieron estudiar sobre los colosos con el pontífice Vaillant.",
		["9973378"] = "Contrato.",
		["227222"] = "Aquí se esconde el Santo Padre. La entrada parece estar sellada con una especie de ritual antiguo.",
		["9962035"] = "",
		["9999304"] = "¿De verdad quieres jugarte el destino de todo Solas por eso?",
		["551163"] = "¿Qué pasa con los demás miembros de la Iglesia? ¿Dónde están?",
		["773008"] = "No.",
		["668304"] = "",
		["221059"] = "¡En Adamina!",
		["793290"] = "Lo siento.",
		["221057"] = "Está viva, pero no le queda mucho tiempo.",
		["551320"] = "Un momento, parece que tú también tienes una obsesión, pero todavía no es lo bastante intensa... Puede que algún día vuelvas aquí...",
		["441155"] = "Mensajero, me gustaría avisarte de nuevo. Todo este asunto es cosa de los demiurgos, no tuyo.",
		["114140"] = "/t1",
		["441259"] = "Sin embargo, esa gente se fusionó con la conciencia del coloso, para luego convertirse en sus marionetas.",
		["118148"] = "",
		["551325"] = "Aquellos que persiguen una obsesión imposible pueden acabar con la conspiración de la luz y la oscuridad. ¿Cómo? Acabando bajo tierra.",
		["551362"] = "La tolerancia a la venganza... la convergencia pasional... los intentos peligrosos...",
		["771089"] = "Teniendo en cuenta lo que ha hecho Norwood, a mi padre no le queda mucho tiempo.",
		["444179"] = "No se puede abrir sin una llave.",
		["9971283"] = "Gracias... Me alegro de te hayas acordado.",
		["441244"] = "¿Para qué sirve el polvo blanco?",
		["661803"] = "¡Detente! ¡No la abras!",
		["461006"] = "Deberías preocuparte de tus propios asuntos.",
		["553415"] = "Llenar botella de Melos",
		["667073"] = "Se ha visto superada por la voluntad vengativa de los demiurgos. La caída de Tierrabendita y una mujer demente, esta es la pesadilla de cualquier hombre.",
		["777040"] = "¡¿Qué demonios ha pasado aquí?!",
		["991470"] = "A un mensajero generoso siempre le deseo éxito.",
		["991216"] = "¡Gracias! El pueblo de Ictiosauria estará agradecido. Puesto que ahora somos amigos, quiero darte esto. Buena suerte.",
		["9971363"] = "Esas palabras son para ti, esa persona jamás te ha olvidado.",
		["9971331"] = "Pienso enterrar este brazo en su lugar de origen y dejar que descanse en paz. Tal vez los marcioneses no tarden mucho en seguir la senda de sus ancestros y se conviertan en meras leyendas. Tarde o temprano, seremos completamente olvidados, sin dejar rastro alguno.",
		["441286"] = "¿Crees en las coincidencias, mensajero?",
		["9999293"] = "Ya basta. Las criaturas de la noche no podemos volver a vivir como humanos. Hemos perdido el derecho al nacer.",
		["441337"] = "Sin embargo, Ictiosauria no se puede permitir ni un minuto más de espera.",
		["663705"] = "Sí.",
		["551338"] = "La búsqueda de lo sublime es la mayor amenaza para la humanidad.",
		["331107"] = "¿Mensajero? ¿Cómo has entrado aquí? ¿No te ha visto el tipo de la puerta? ¡No quiero meterme en problemas, así que no te me acerques, por favor!",
		["884006"] = "¿Temes perderla? O algo peor... más oscuro... más secreto... Debo contarte la verdad... Una verdad impactante...",
		["9973458"] = "El apoderado.",
		["9971462"] = "Te conoce demasiado bien.",
		["9971465"] = "Si fuese solo Norwood, no me sorprendería, pero Viola... No me creo que se haya involucrado.",
		["9971466"] = "Yo tampoco quiero creérmelo.",
		["9971467"] = "¿Era esa la verdad que Tierrabendita pretendía enterrar durante tanto tiempo? ¿Un apoderado totalmente contaminado por los Sendril?",
		["114001"] = "No se puede entrar.",
		["442119"] = "Forastero, ¿podrías echarme una mano? Tengo... mucho frío... ¿Tienes alguna vela?",
		["444092"] = "/t0",
		["9973388"] = "Marcharse.",
		["9951045"] = "¿Has oído hablar de la masacre de Katib?",
		["662240"] = "¿Estás listo para irte?",
		["9976379"] = "",
		["111075"] = "¿Lo encontrasteis?",
		["771260"] = "Mi puesto lleva desierto mucho tiempo, igual puedo hacerte un buen descuento.",
		["227053"] = "Terrence, parece que la mala suerte te sigue allá donde vayas, igual que los monstruos.",
		["773261"] = "Comerciar.",
		["661103"] = "Mensajero, ¿no te parece que todo esto es algo extraño? Este lugar está protegido por la luz eterna de los pilares y, aun así, es demasiado débil como para resistir la invasión de los Sendril.",
		["552104"] = "",
		["771262"] = "Lo siento, pero no puedo hacer ningún descuento. Eso sí, todos mis productos son dignos de su precio.",
		["551414"] = "...",
		["221170"] = "Señor mensajero, ha vuelto sano y salvo.",
		["791304"] = "Lo cierto es que también he hecho esto. Espero que te guste.",
		["551346"] = "La tolerancia a la venganza... la convergencia pasional... los intentos peligrosos...",
		["771233"] = "Espero que las cosas puedan volver a ser como antes...",
		["777022"] = "Al fin estamos aquí, ¿y qué nos encontramos? Escaleras, otra vez escaleras... A veces hasta agradezco lo metódica que es su búsqueda...",
		["116131"] = "",
		["776264"] = "",
		["771264"] = "A un mensajero generoso siempre le deseo éxito.",
		["991115"] = "Jerold, ¿por qué no nos has dicho nada de esto?",
		["775035"] = "/t1",
		["774777"] = "No se puede abrir desde este lado.",
		["9973275"] = "Contrato.",
		["9953024"] = "Hablar.",
		["221478"] = "Además, hay mucha gente que necesita ayuda, igual que mi mamá. Quiero hacer algo por ellos.",
		["663331"] = "<color=#DBBA7FFF>Poner los fragmentos en el hueco.</color>",
		["9971278"] = "Sigo queriendo terminar lo empezado. Si te sobra algo de arilo, dámelo, por favor... Sin prisa, claro, tienes asuntos más importantes a los que atender.",
		["771200"] = "No tengo nada que perder...",
		["771220"] = "Esperaré con ansias ese día...",
		["221225"] = "Solo estoy recuperando lo que me pertenece.",
		["9971279"] = "¿Has encontrado algo de arilo?",
		["9972279"] = "",
		["9973280"] = "He encontrado algo de arilo.",
		["9973281"] = "Lo siento.",
		["331203"] = "Eres muy valiente, mensajero. Te agradezco de corazón lo que has hecho. Por favor, permite que te desafíe como caballero. Te esperaré allí donde ella pereció. ¡En nombre del amor!",
		["771101"] = "Espera, no eres él... Hace tiempo que los vivos se han ido de aquí... ¡¿Has visto a ese monstruo?!",
		["9923035"] = "Hablar.",
		["333904"] = "No.",
		["9971284"] = "Tengo curiosidad, ¿a quién estás creando?",
		["994037"] = "/t0",
		["30135238"] = "¡Rápido, escondeos! ¡No dejéis que os encuentren!",
		["9971287"] = "Lo que me preocupa es que su cara se deshace con el tiempo... Espero que pueda terminarla antes de que se deshaga por completo... Es la única forma de expiación que conozco.",
		["557176"] = "Hay algo que conecta el pasado, el presente y el futuro.",
		["4994993029"] = "Interior de la Niebla oscura",
		["9971291"] = "Parece ser que puedo «ver» a la gente que he desterrado a la Niebla oscura y que intenta arrastrarme a su interior.",
		["9971377"] = "¿Has encontrado algo de arilo?",
		["221263"] = "Ignóralo, Terrence. Tiene malas intenciones.",
		["9973295"] = "He encontrado algo de arilo.",
		["9972311"] = "",
		["9971295"] = "Ya no te faltará mucho, ¿no?",
		["553358"] = "Sí.",
		["551388"] = "La búsqueda de lo sublime es la mayor amenaza para la humanidad.",
		["9971297"] = "Necesitas fuerza para ver la verdad a través de la «fe».",
		["771250"] = "Terrence, yo nunca te he mentido...",
		["9971298"] = "¿Habrá equilibrio algún día?",
		["9973301"] = "Contrato.",
		["9953045"] = "Hablar.",
		["554269"] = "/t0",
		["9971305"] = "Gracias... Es la primera vez que...",
		["9961026"] = "No pareces un hombre que vaya a encontrarse con su esposa. ¿Qué quieres decirle? ¿Has pensado en eso?",
		["111125"] = "¡Hueles igual que la bruja! ¡La estás ayudando!",
		["778302"] = "",
		["9971306"] = "Puede que no sea la última...",
		["9951041"] = "¿Quién es tu maestro?",
		["551272"] = "¿Hablabas de esto?",
		["331182"] = "¿Te recuerda a Teresa?",
		["221416"] = "Pronto se casarán en el Espacio del oráculo y, entonces, tu camarada dará a luz a una nueva vida para el Santo Padre.",
		["551267"] = "Debemos detenerla o Solas sufrirá el mismo destino que mi hogar...",
		["117143"] = "El asceta usa el dolor y la voluntad para disipar la Niebla oscura, pero es obvio que este asceta se ha convertido en un infame.",
		["552271"] = "",
		["667086"] = "Date prisa, antes de que Teresa encuentre al apoderado.",
		["331162"] = "Estuve con ella en Adamina...",
		["9973296"] = "Lo siento.",
		["9971313"] = "Habla claro, ¿por qué esta mano es una reliquia de los marcioneses?",
		["9971314"] = "Era la mano de un poderoso soldado marcionés. En sus últimos momentos, alguien llevó a cabo un ritual prohibido para fusionarla con los Sendril y la convirtió en un arma.",
		["9971316"] = "El mundo ha cambiado, Terrence. Para la Iglesia, la herejía ya no es la mayor de las amenazas. Al principio, el maestro no quería darme la mano, pero eso cambió cuando corté la mía propia...",
		["9961023"] = "...",
		["771213"] = "Perdóname, Viola... No puedo dejar a Teresa ahí.",
		["9971317"] = "Eres un lunático.",
		["221199"] = "¡Estás muy pálida!",
		["9971318"] = "Solo los lunáticos sobreviven en este mundo.",
		["662403"] = "¿Quieres abrir la puerta?",
		["441272"] = "Los demiurgos te han aceptado y he abierto la puerta a Tierrabendita para ti.",
		["222901"] = "¿Quieres volver al carruaje?",
		["774036"] = "Deberías preocuparte por ti misma. Norwood, te dejo esto a ti.",
		["221159"] = "Le cogí esto a Elmar. Con él, podrá entrar en el Espacio del oráculo. Seguro que el Santo Padre está allí.",
		["9971378"] = "¿Qué tal va todo? ¿Has enterado algo interesante?",
		["9971319"] = "Parece que la maldición de los marcioneses acabará llegando a todo aquel conectado con ella. Puede que ese sea el pago por ofender a los marcioneses.",
		["9971383"] = "...",
		["9971321"] = "Solo quiero deshacerme de este peligro oculto.",
		["9973379"] = "Contrato.",
		["9973324"] = "Lo siento.",
		["9971324"] = "Si pierdes el control, no pienso contenerme.",
		["9972382"] = "",
		["9971330"] = "¿Adónde piensas ir ahora?",
		["221168"] = "Mamá... Mamá... ¿Dónde estás? ¡Por favor, que alguien me lo diga!",
		["9971333"] = "Espero que este cobarde no les haya decepcionado.",
		["9973335"] = "Contrato.",
		["30135239"] = "¡Deja que se vaya! ¡No es más que un niño!",
		["771106"] = "Ejem... Tengo mucha sed, pero no puedo beber nada... Hasta una gota de agua me quemaría la garganta... Pero lo único que recuerdo con claridad es el continuo dolor causado por esta sed...",
		["9971387"] = "Poco a poco, Tierrabendita se acabará convirtiendo en historia del pasado, una historia que se repetirá constantemente en el futuro. La Iglesia no es más que un ingenuo seguidor, como demuestran esas cabezas brillantes que rodeaban al apoderado.",
		["665071"] = "¡No eres más que un idiota, no eres digno de mi tiempo!",
		["553110"] = "...",
		["9973380"] = "Contrato.",
		["111068"] = "¿Te has encontrado a algún otro forastero?",
		["9971380"] = "Tengo un objetivo.",
		["9973345"] = "He encontrado una losa rota.",
		["9971299"] = "Lo dudo.",
		["1002100024"] = "¿No quieres salvar a los demiurgos?",
		["664055"] = "/t0",
		["551370"] = "La tolerancia a la venganza... la convergencia pasional... los intentos peligrosos...",
		["992140"] = "",
		["991080"] = "¿Por qué?",
		["9931029"] = "Mi padre también se fiaba de su instinto... y acabó haciendo cosas de lo más desafortunadas, cosas que no se pueden deshacer.",
		["9971381"] = "Tengo un objetivo.",
		["551351"] = "Ah... La obsesión por la codicia... A todos nos ha cegado, ¿no es cierto?",
		["9931023"] = "En esta Niebla oscura no deberías confiar en nadie tan fácilmente.",
		["221060"] = "Algo me dice que esto no va a ser tan fácil.",
		["9972381"] = "",
		["9943036"] = "Bendecir.",
		["9971352"] = "Según el texto de la losa, el brazalete tiene grabadas las palabras «ardiente fuego» y «luz eterna».",
		["9946029"] = "",
		["555254"] = "Ajá... Es hora de despertar. ¡Al fin podré vengarme de toda la humillación que he tenido que soportar!",
		["9931027"] = "¿Es eso lo que te dice tu instinto?",
		["331155"] = "No sé si eso es posible, pero estoy dispuesta a intentarlo. Ah, sí, antes de que te vayas, no olvides llevarte esto contigo.",
		["9971355"] = "Parece que estamos progresando. ¿Puedes sacar algo más de provecho?",
		["332219"] = "",
		["9971356"] = "No, todavía no tengo información suficiente para descifrarlo todo.",
		["441297"] = "Me quedaré y haré todo lo que pueda por eliminar los efectos del polvo blanco. El fin de los demiurgos todavía no ha llegado.",
		["991045"] = "Ah, el goteo... El maravilloso sonido del agua relaja la mente. ¡Buena suerte! Espero que, cuando estés de vuelta, sigamos siendo capaces de reconocernos... ¡Ja, ja, ja!",
		["551265"] = "Ya has visto lo que sucede después de abrir uno de ellos...",
		["791350"] = "Buscar reliquias de una civilización que nadie ha conocido es algo bastante complicado en este mundo.",
		["661148"] = "...",
		["441292"] = "No lo entiendo... ¿Cómo puede un pontífice del Monasterio sagrado hacer algo así?",
		["991464"] = "A un mensajero generoso siempre le deseo éxito.",
		["9971360"] = "Tal vez nadie pueda.",
		["991021"] = "Esta maldición se convertirá en mi fortaleza. Mientras pueda salvar Ictiosauria, no tendré de qué arrepentirme... Ni siquiera si acabo convertido en un monstruo.",
		["441127"] = "Gracias, mensajero.",
		["558305"] = "",
		["993467"] = "Comerciar.",
		["551404"] = "...",
		["9971362"] = "«La celebración del nacimiento del ardiente fuego, la calidez de lo más profundo de mi corazón, la luz eterna».",
		["664163"] = "/t0",
		["331219"] = "Anda, ¿eres el nuevo mensajero? Entonces yo soy quien necesitas para sobrevivir en la Niebla oscura.",
		["664166"] = "/t0",
		["551187"] = "Mensajero, despréndete del pasado y aférrate a la mentira que tan firmemente crees. Inclínate y galopa hacia la oscuridad. Mirar hacia la luz solo te dejará ciego.",
		["9971366"] = "Eso no importa nada si el mundo se va a acabar.",
		["9973367"] = "Contrato.",
		["9971367"] = "Ahora que has encontrado el Sepulcro colosal, no hay razón para la existencia de los mensajeros.",
		["225083"] = "¡No nos abandones, Santo Padre!",
		["9971368"] = "Puede que reciban nuevos propósitos, como hacer algo por la gente de Tierrabendita.",
		["9971369"] = "Nunca impediré que cumplas tus propósitos, pero hay algo que quiero pedirte: no corras riesgos innecesarios.",
		["441143"] = "Pero la campana parece ser el origen de la locura de Edith.",
		["791275"] = "Sigues... ¿trabajando en eso?",
		["771385"] = "Terrence, lo siento, pero tengo que advertirte que nuestro contrato termina aquí. Has encontrado el Sepulcro colosal, mi labor ha terminado.",
		["551407"] = "...",
		["791276"] = "Esto...",
		["332222"] = "",
		["793279"] = "Contrato.",
		["664193"] = "/t2",
		["793280"] = "He encontrado algo de arilo.",
		["793281"] = "Lo siento.",
		["335225"] = "Acaba con mi vida sin sentido, mensajero. Te lo suplico.",
		["9973323"] = "He encontrado una reliquia antigua.",
		["664162"] = "/t0",
		["9971275"] = "Sigues... ¿trabajando en eso?",
		["772184"] = "",
		["221141"] = "Adamina cada vez da más miedo... Solo quiero irme de aquí con mi mamá cuanto antes...",
		["551122"] = "Sé paciente, mi astuto amigo, sé paciente...",
		["791287"] = "Lo que me preocupa es que su cara se deshace con el tiempo... Espero que pueda terminarla antes de que se deshaga por completo... Es la única forma de expiación que conozco.",
		["991404"] = "Palabra de Ebbinghaus: III",
		["661114"] = "Este no es un lugar muy seguro, pero... si todavía quieres saber qué ha pasado, podrás encontrarme en la cámara del Salón de la Trinidad.",
		["793288"] = "Contrato.",
		["792288"] = "",
		["331150"] = "¿Estás seguro?",
		["221295"] = "Mensajero, ¿podrías conseguirme algunas hierbas de Adamina? Lo siento, pero yo tengo que seguir revolviendo esto, no puedo detenerme.",
		["791290"] = "¿Qué te pasa?",
		["9941022"] = "No creo que se vayan a arriesgar tanto por unos cuantos don nadies.",
		["100310032203"] = "/t0",
		["791377"] = "¿Has encontrado algo de arilo?",
		["793296"] = "Lo siento.",
		["441179"] = "Toma esta llave y abre la puerta trasera del pueblo de ahí abajo. Entonces sabrás en quién confiar.",
		["9971276"] = "Esto...",
		["444777"] = "No se puede abrir desde este lado.",
		["791296"] = "Así es. Me pregunto qué pensaría ella, si aprobaría la «fe» según las circunstancias actuales.",
		["995278"] = "Fuimos demasiado descuidados... Esta vez, no dejaremos que molestes a lord Ebbinghaus...",
		["331199"] = "¿Por qué...? ¿Por qué no puedo sentir su presencia?",
		["993118"] = "Preguntar sobre las criaturas que acaba de espantar.",
		["551245"] = "Parece una señal... ¿Es posible que Teresa esté relacionada con la Iglesia de algún modo? Mira, ha aparecido una llave sobre el altar.",
		["773264"] = "Comerciar.",
		["791306"] = "Puede que no sea la última...",
		["791307"] = "No te preocupes por ella, tan solo tiene un deseo frustrado, por lo que parece inquieta.",
		["791311"] = "¿Qué tal va todo? ¿Has enterado algo interesante?",
		["333147"] = "<color=#DBBA7FFF>¿Por qué los Sendril son débiles en este lugar?</color>",
		["9971304"] = "Lo cierto es que también he hecho esto. Espero que te guste.",
		["792311"] = "",
		["9963042"] = "Hablar.",
		["9962042"] = "",
		["793313"] = "Lo siento.",
		["791313"] = "Habla claro, ¿por qué esta mano es una reliquia de los marcioneses?",
		["552901"] = "¿Quieres volver al carruaje?",
		["773184"] = "...",
		["793378"] = "Contrato.",
		["1004100077"] = "Eligieron esto por unanimidad.",
		["793320"] = "Lo siento.",
		["991016"] = "¡Jerold! ¡Jerold!",
		["441287"] = "...",
		["221128"] = "¿La caza?",
		["791320"] = "Ah... Lo siento, no quería ofenderte. Fue su codicia la que los llevó a ese final, tú solo te viste obligado...",
		["551237"] = "El altar de ahí arriba parece estar conectado con algo presente bajo la Torre sagrada... Está hambriento de oscuridad... Marchaos antes de que sea tarde...",
		["791321"] = "Solo quiero deshacerme de este peligro oculto.",
		["221258"] = "Despierta. Se acabó la hora de la siesta.",
		["991031"] = "Parece que tendremos que ver a Ebbinghaus cuanto antes para conseguir respuestas.",
		["791379"] = "¿Qué tal va todo? ¿Has enterado algo interesante?",
		["117136"] = "Un hombre tras una máscara supone engaños asegurados.",
		["791333"] = "Espero que este cobarde no les haya decepcionado.",
		["441330"] = "...",
		["9956042"] = "",
		["793335"] = "Contrato.",
		["791335"] = "¿Te has arrepentido?",
		["791337"] = "De ser una mensajera. La mayoría de la gente preferiría morir a seguir este camino.",
		["662703"] = "¿Quieres abrir la puerta?",
		["551322"] = "Ja, ja, ja, eres un tipo interesante. Toma, llena esto con la fuerza de la obsesión y puede que halles algo diferente.",
		["441302"] = "Je, je, qué humano más astuto...",
		["791343"] = "¿Los primigenios legendarios?",
		["791344"] = "No sé qué conexión hay entre ellos, pero si encuentras algo relacionado con Samarosa, aunque sea una nimiedad, tráemelo, por favor.",
		["668305"] = "",
		["774113"] = "Está atascado.",
		["793380"] = "Contrato.",
		["793345"] = "He encontrado una losa rota.",
		["793346"] = "Todavía no he encontrado nada.",
		["991418"] = "La puerta no se mueve. En ella hay cinco huecos.",
		["224048"] = "/t0",
		["791346"] = "Esta losa contiene las leyendas de los samarosanos... «El tótem sagrado de los samarosanos... nos guía hasta el hogar de Dios...».",
		["661182"] = "Dicen que, con el objetivo de mantener la paz en Tierrabendita, el apoderado protege el Sello de Yekub día y noche, evitando así que la maldición se cumpla.",
		["9999290"] = "Fue por Teresa, era mi única opción.",
		["221136"] = "Dime, ¿cómo puedo encontrar al Santo Padre?",
		["791349"] = "Cuantas más losas como esta encuentras, más te acercas a descifrar el texto grabado en el brazalete...",
		["9971358"] = "¿Y qué pasa si no lo conseguimos? No quiero dejar esto a medias durante el resto de mi vida.",
		["9926044"] = "",
		["793381"] = "Contrato.",
		["791381"] = "Tengo un objetivo.",
		["793351"] = "He encontrado una losa rota.",
		["793352"] = "Todavía no he encontrado nada.",
		["551367"] = "Ah... La obsesión por la codicia... A todos nos ha cegado, ¿no es cierto?",
		["221201"] = "Qué olor tan intenso e inmundo a Sendril.",
		["791351"] = "Te he traído otra losa.",
		["4994993004"] = "Cueva de la Niebla oscura",
		["9951034"] = "Aaah, Terrence. ¿Qué puedo hacer por ti? Iba a tomarme un descanso, todavía falta bastante hasta llegar a nuestro destino.",
		["551290"] = "...",
		["665188"] = "Me duele... Me duele... Mi cuerpo... ¡Ya basta, por favor!",
		["791353"] = "¿Qué quiere decir?",
		["791354"] = "Habla del día en que nací.",
		["1004100059"] = "Mis hermanos... demiurgos que vagan por Edith...",
		["791360"] = "Tal vez nadie pueda.",
		["9962013"] = "",
		["114028"] = "No esperaba que Heggem estuviera tan débil.",
		["661104"] = "¿Será por la maldición de la bruja marcionesa?",
		["551335"] = "Los mediocres siempre se obsesionan con superar a los demás.",
		["791382"] = "Todavía no tengo información suficiente para descifrarlo todo.",
		["4994993032"] = "Frontera de la Niebla oscura",
		["991147"] = "Ebbinghaus... Hace tiempo leí sobre él. El santo que entregó su vida para protegernos, para proteger Ictiosauria...",
		["553427"] = "Sí.",
		["551347"] = "La verdad oculta alimenta las intenciones malignas de los ignorantes.",
		["771085"] = "¿Cómo es que nunca nos hablaste de tu padre?",
		["791362"] = "«La celebración del nacimiento del ardiente fuego, la calidez de lo más profundo de mi corazón, la luz eterna».",
		["792379"] = "",
		["993002"] = "No.",
		["791366"] = "Eso no importa nada si el mundo se va a acabar.",
		["793367"] = "Contrato.",
		["9923036"] = "Marcharse.",
		["791368"] = "Puede que reciban nuevos propósitos, como hacer algo por la gente de Tierrabendita.",
		["662903"] = "¿Quieres volver al carruaje?",
		["661218"] = "Benita, tras ver lo que le ha pasado al apoderado, entiendes que no hay ningún Dios, ¿no?",
		["791369"] = "Nunca impediré que cumplas tus propósitos, pero hay algo que quiero pedirte: no corras riesgos innecesarios.",
		["111130"] = "Humano, ¿quieres hacer negocios conmigo? Tengo multitud de tesoros.",
		["113902"] = "No.",
		["777023"] = "Esto es peor de lo que me esperaba. Ojalá esta vez no hayamos tomado la peor decisión.",
		["9953031"] = "Comerciar.",
		["991162"] = "Ja, ja, ja, toma... Si puedes, espero que cumplas mi deseo inacabado.",
		["884000"] = "¿Por qué tanta determinación? ¿En qué crees? Es como una polilla yendo hacia una llama.. ¡Es una atracción letal!",
		["884001"] = "Un hombre atrapado por su obsesión... Al fin nos encontramos... Has abandonado la cueva oscura para adentrarte en el desierto de la verdad...",
		["884002"] = "Un hombre atrapado por su obsesión... Al fin nos encontramos... Has abandonado la cueva oscura para adentrarte en el desierto de la verdad...",
		["9931009"] = "Hay una pista de una de tus antiguas amistades...",
		["551070"] = "¿Un coloso fusionado con el entorno? Es la primera vez que veo algo así.",
		["9973457"] = "El túnel secreto bajo la cámara del apoderado.",
		["773390"] = "<color=#DBBA7FFF>Sí.</color>",
		["661263"] = "Mensajero infeliz, ¿quieres hacer negocios conmigo?",
		["884007"] = "¿Temes perderla? O algo peor... más oscuro... más secreto... Debo contarte la verdad... Una verdad impactante...",
		["9921002"] = "¿Estás despierto?",
		["9921010"] = "Terrence, allí no había nadie más que tú... Tal vez estabas demasiado cansado y creíste ver lo que no había.",
		["551318"] = "Darles una sepultura digna para que no se vuelvan difíciles de controlar.",
		["884008"] = "¿Temes perderla? O algo peor... más oscuro... más secreto... Debo contarte la verdad... Una verdad impactante...",
		["9961027"] = "No es de tu incumbencia. No quiero volver a verte tras llegar a Tierrabendita.",
		["227281"] = "El olor de esta olla es asfixiante.",
		["884010"] = "Siempre ha estado aquí, su sitio siempre ha sido este. ¡Abraza la luz, Teresa! Estas en casa.",
		["884037"] = "¡Es el regalo de los ancestros a los marcioneses! Somos los únicos seguidores dignos.",
		["991112"] = "Para serte sincero, me arrepiento de haber desperdiciado mi última botella salvándote.",
		["332088"] = "",
		["884040"] = "Sois una raza repleta de bárbaros, sacrificando a vuestros propios familiares...",
		["111037"] = "¿Cómo va todo por aquí?",
		["884044"] = "Aaah... Mi garganta... ¡Me quema! ¡Agua! ¡Dame agua!",
		["551395"] = "Puedo ayudarte, pero para ello debo saber qué tengo que hacer.",
		["111083"] = "No tengo tiempo, no puedo quedarme aquí por mucho tiempo.",
		["884046"] = "¿Nos ha abandonado el apoderado? Déjame ir... ¡No quiero estar atrapado aquí para siempre!",
		["9971376"] = "¿Has encontrado algo de arilo?",
		["884052"] = "/t2",
		["551442"] = "Una ira infinita, una obsesión intransigente... Tanto tú como yo estamos inmersos en nuestras propias historias. Con el tiempo, seguro que acabamos embarcándonos en el mismo viaje...",
		["884068"] = "Bebe, y Teresa será liberada de esta maldición...",
		["9973387"] = "Hablar.",
		["884069"] = "Bebe... Bebe, bebe, bebe...",
		["333905"] = "Sí.",
		["884073"] = "/t2",
		["884070"] = "Bebe, demostrarás cuánto quieres ver a Teresa...",
		["881072"] = "Ah... ¿Todavía no te has rendido? ¿Has encontrado lo que buscabas?",
		["661144"] = "La Espada sagrada está en el altar frente al Salón de la Trinidad. No le negarías la última voluntad a una persona moribunda, ¿verdad?",
		["881074"] = "Hemos sacrificado a la inocente marcionesa y nos hemos adentrado en el territorio de Dios. Entonces, nos hemos bebido el agua del tabú que nos convirtió en este esperpento... Es nuestro merecido por haber pecado.",
		["994484"] = "/t0",
		["554166"] = "/t0",
		["881079"] = "Alguien ha sido lo bastante afortunado para escapar de este lugar, puede que tú seas uno de esos elegidos. Busca a la persona que reconozca este brazalete por mí y entrégale mi mensaje: «Llevo mucho esperándote aquí».",
		["991510"] = "Uf... ¿Me recuerdas? Parece que han pasado muchas cosas por aquí. Debe de haber sido peor de lo que pensaba...",
		["9951042"] = "Alguien que busca la verdad, esa es la única respuesta que puedo darte.",
		["551285"] = "Lo siento, pero no puedo hacer ningún descuento. Eso sí, todos mis productos son dignos de su precio.",
		["551091"] = "Oh, perdona... Me avergüenza decir esto, pero... he activado una trampa de los marcioneses por accidente y he perdido un reloj de arena con la conmoción... ¿No lo habrás visto por casualidad?",
		["667074"] = "¿Teresa está buscando al apoderado? Tal vez los rumores sean ciertos y el apoderado tenga el Sello de Yekub. Creo que ya sé adónde va Teresa.",
		["884100"] = "/t0",
		["9951050"] = "Nunca he logrado comprender por qué la Iglesia volvió a Katib...",
		["884102"] = "/t0",
		["882102"] = "Bebe...",
		["883103"] = "Rechazar.",
		["551258"] = "¿Por qué no me escuchaste?",
		["30274005"] = "/t0",
		["666264"] = "",
		["441240"] = "Mensajero, ¿has cumplido la promesa que me hiciste?",
		["4994993001"] = "Frontera de la Niebla oscura",
		["993051"] = "Coger.",
		["771139"] = "Se la han llevado bajo tierra... ¡No dejes que ese monstruo la encuentre! No creo que yo pueda salvarla, pero tú, Terrence, sí puedes. Eres su única esperanza...",
		["991461"] = "A un mensajero generoso siempre le deseo éxito.",
		["791336"] = "¿De qué?",
		["441191"] = "Mientras traiga esperanza a los demiurgos, el resto poco importa.",
		["9963023"] = "Comerciar.",
		["9941016"] = "Ahora es el punto de reunión de los demiurgos.",
		["4994992002"] = "Ja... ¡Veo que quieres vivir más emociones! ¡Eres insaciable! No suelo recomendar esto, pero teniendo en cuenta que eres un mensajero experimentado, te ayudaré a cumplir tus deseos si consigues más setas espectrales.",
		["771210"] = "...",
		["557198"] = "Si queremos acercarnos a la verdad, tendremos que correr algunos riesgos y aventurarnos a la Torre sagrada...",
		["4994993008"] = "Ofrecer 25 setas espectrales.",
		["4994993010"] = "Ofrecer 75 setas espectrales.",
		["4994993012"] = "Elige la zona de Niebla oscura que quieres explorar.",
		["441080"] = "Hay una rata en Edith, ten cuidado en todo cuanto hagas...",
		["4994993015"] = "Aumenta el brillo de la lámpara de Niebla",
		["884049"] = "/t2",
		["441301"] = "Je, je, qué humano más simple...",
		["4994993018"] = "Darle los materiales.",
		["221260"] = "No te preocupes, todo ha terminado.",
		["551336"] = "La tolerancia a la venganza... la convergencia pasional... los intentos peligrosos...",
		["4994992005"] = "Requiere aceite de ballena espeso y 200 setas espectrales",
		["553357"] = "Llenar botella de Melos",
		["9931012"] = "¿Quién demonios es esa?",
		["994257"] = "/t0",
		["443903"] = "Sí.",
		["4994992006"] = "Requiere aceite de ballena refinado y 300 setas espectrales",
		["9973279"] = "Contrato.",
		["441294"] = "¿Qué más esconde esta tragedia?",
		["9971379"] = "¿Qué tal va todo? ¿Has enterado algo interesante?",
		["4994993026"] = "Ofrecer 75 setas espectrales.",
		["771099"] = "¿Quién anda ahí? ¿Higgins? ¿Eres tú? ¿Oyes los gritos de la chiquilla? Son gritos de dolor...",
		["774037"] = "Yo me encargo, señoría.",
		["9971468"] = "Oí algo al respecto en Tierrabendita, pero pensaba que solo eran rumores.",
		["4994993027"] = "Ofrecer 100 setas espectrales.",
		["4994991003"] = "Materiales insuficientes",
		["221270"] = "Je, je, qué mensajero más astuto...",
		["991156"] = "Lo siento...",
		["9971289"] = "Siento que me tengas que ver así.",
		["4994993030"] = "Abismo de la Niebla oscura ",
		["221109"] = "Todavía no tengo pruebas suficientes, pero la situación actual de Heggem es una buena prueba de ello.",
		["791315"] = "La Iglesia se ha rendido a una herejía que puede estallarles en la cara.",
		["664164"] = "/t0",
		["551356"] = "La búsqueda de lo sublime es la mayor amenaza para la humanidad.",
		["9974004"] = "/t0",
		["331180"] = "Pareces cansado, Terrence.",
		["4994991004"] = "Camina indefenso por la noche oscura y acabarás hecho pedazos... ¿Por qué no buscas la manera de iluminar la oscuridad?",
		["4994991008"] = "Oh... ¡Sabía que te morías de ganas de ponerte manos a la obra! ¡Disfrútalo, amigo mío!",
		["9931024"] = "¿Y qué pasa conmigo?",
		["992057"] = "",
		["4994992001"] = "Ah... ¿Qué puedo hacer por ti, mensajero perdido?",
		["4994992200"] = "Ja, ja, ja... ¡Es la hora de las recompensas!",
		["1005100055"] = "Este lugar está lleno de pecados bajo la apariencia de generosidad, pero aún podemos enmendar este pecado original. Ahora marchen al frente.",
		["441304"] = "Je, je, qué humano más simple...",
		["661165"] = "Mensajero... Volvemos a vernos. ¿Has encontrado la respuesta que buscabas?",
		["4994992203"] = "Ja, ja, ja... ¡Es la hora de las recompensas!",
		["551382"] = "Los débiles y los pequeños ni siquiera dudan de las mentiras más obvias.",
		["4994992204"] = "Ja, ja, ja... ¡Es la hora de las recompensas!",
		["4994993200"] = "Obtener pociones de salud.",
		["551178"] = "Un sueño del que es difícil despertar, las almas de aquellos que se han equivocado jamás recibirán justicia...",
		["4994993201"] = "Trae de vuelta a un personaje que no esté disponible para luchar.",
		["4994993202"] = "Obtener setas espectrales.",
		["551424"] = "...",
		["227118"] = "Menos mal que estás vivo, Terrence... Aquí apesta, por cierto.",
		["443906"] = "No.",
		["771245"] = "一直以来你都同我站在一边？",
		["221297"] = "Mensajero... ¿podrías conseguirme algo de Sendril fresco? Con una porción sería suficiente, pues reforzaría los efectos de las hierbas.",
		["221055"] = "¡Estimado mensajero! ¡Ayúdeme a salvar a mi mamá, por favor!",
		["4994993203"] = "Un regalo fantástico de las obsesiones de la Niebla oscura.",
		["333115"] = "<color=#DBBA7FFF>Sí, es seguro.</color>",
		["791339"] = "¿Y cuál es?",
		["4994991001"] = "TEST",
		["991107"] = "No te pongas nervioso... No encontrarán este lugar. Es tan extraño... Vosotros parecéis normales, ¿por qué arriesgaros así?",
		["9931030"] = "Sé lo que me hago...",
		["9933013"] = "Marcharse.",
		["553334"] = "Sí.",
		["1006100119"] = "Derrótala, derrota a esta Elena indeseada. Solo así podrás dejar atrás tu pasado, solo así guiarás a los demiurgos hacia el verdadero camino del renacimiento.",
		["441213"] = "¿Quieres decir que Elena ha mentido?",
		["9971472"] = "La que nuestro instinto nos señale.",
		["111117"] = "¿Cómo era?",
		["791363"] = "Esas palabras son para ti, esa persona jamás te ha olvidado.",
		["994002"] = "¡¿Glenn?! ¡¿Jerold?! Espero que hayan tenido más suerte que yo.",
		["991096"] = "La inscripción de la tabla de piedra se parece a la escritura de los samarosanos, pero el tiempo ha hecho tanta mella en ella que es difícil de leer.",
		["9971353"] = "¿Qué quiere decir?",
		["993072"] = "Darle la lanza rota del santo.",
		["113108"] = "<color=#DBBA7FFF>Toma, la lágrima empedrada que necesitabas.</color>",
		["661221"] = "Sé cuán importante es Teresa para ti, ¡pero eso no significa que puedas decir esas cosas!",
		["661701"] = "¡No, por favor! ¡Busca la protección del apoderado en el Salón de la Trinidad, no tiene sentido quedarse aquí!",
		["791297"] = "Necesitas fuerza para ver la verdad a través de la «fe».",
		["553901"] = "Sí.",
		["338233"] = "",
		["100110012201"] = "Investiga la Estatua del Portador.",
		["791285"] = "A una difunta amiga que, cuando no parecía haber luz alguna, consiguió hacérmela ver. No obstante, no consigo hacer bien su cara.",
		["991004"] = "Será que no soy muy sabroso. ¿Y bien? ¿Esto es Ictiosauria?",
		["991005"] = "Deberías preguntárselo al tipo que nos trajo hasta aquí.",
		["1004100114"] = "Se supone que estabas...",
		["991007"] = "Parece que esta tormenta ha venido para quedarse, así que será mejor que te pongas a buscar pistas. Sin embargo, este viejo saco de huesos no puede más, así que descansaré un rato por aquí...",
		["991008"] = "No te preocupes, mi búho te alcanzará enseguida.",
		["991420"] = "Piedra del coloso",
		["771190"] = "¿De veras? Puede que solo exista en mis sueños...",
		["994012"] = "Estas tablas rotas llevan aquí mucho tiempo...",
		["793295"] = "He encontrado algo de arilo.",
		["771386"] = "Entiendo, gracias por todo...",
		["115146"] = "¡Soy... el guardián... de Heggem!",
		["441115"] = "¡Largo! ¡Mira lo que has hecho! ¡Ahora, los demiurgos son historia! ¡La oscuridad nos devorará de nuevo!",
		["117020"] = "¡Terrence!",
		["441164"] = "Lo detendré... Por los demiurgos... Esto no es más que un rasguño.",
		["221292"] = "¡¿Cómo te atreves a faltarle al respeto al Santo Padre, mensajero ignorante?!",
		["331108"] = "¿Tú de nuevo? Ya te he dicho que no quiero que te me acerques.",
		["333140"] = "Marcharse.",
		["661183"] = "¿La maldición?",
		["9941043"] = "Puede que el hecho de aislarse solo fuera una treta, tal vez los demiurgos encontraron algo que enfureció a la Iglesia. Sin embargo, han pasado tantos años que es imposible descubrir la verdad...",
		["773002"] = "No.",
		["556281"] = "",
		["9971277"] = "Tantos años... Y ahora tus ojos...",
		["556287"] = "",
		["991019"] = "No te preocupes... Los mantendré bajo control antes de que le entreguemos el fragmento de meteoro rojizo a lord Ebbinghaus.",
		["884051"] = "Completa el mundo y hazlo uno solo...",
		["991020"] = "Yo no lo tendría tan claro...",
		["991088"] = "Pero parece que está totalmente seco.",
		["551071"] = "Si alguien me dijera hace unos días que la entrada al Sepulcro colosal estaba aquí, le habría llamado de loco para arriba.",
		["997022"] = "Hay un silencio de lo más siniestro...",
		["997025"] = "Me atrevería a decir que han comido muchos calamares y por eso ahora son así.",
		["334176"] = "/t0",
		["771235"] = "Puede que tengas razón...",
		["997026"] = "Viene una corriente desde esa dirección... Debemos estar cerca de una salida.",
		["991027"] = "¿Qué diablos ha pasado aquí?",
		["553118"] = "No.",
		["991029"] = "¿Cómo se habrá secado el río del Alma? Ah... Me vuelve a doler la cabeza... Es como si el pasado comenzase a desaparecer de mi memoria...",
		["793379"] = "Contrato.",
		["991032"] = "Está en el nacimiento del río del Alma. Camina contracorriente por el río para encontrarte con él.",
		["441098"] = "¿Hay algo más emocionante que derrotar a Tierrabendita? Piensas ponerte en la primera línea de ataque, ¿no es así, forastero?",
		["991033"] = "¿Te has fijado en lo que nos rodea? Parece que estemos en el interior de alguna criatura... Encima, el hedor a muerte del ambiente no ayuda...",
		["991034"] = "Tenías razón. Ictiosauria es una criatura ancestral extinta hace mucho. Hemos seguido a lord Ebbinghaus hasta aquí para huir de los Sendril.",
		["991036"] = "No hay de qué preocuparse, Ictiosauria no nos hará daño. Creamos lazos en vida.",
		["221068"] = "Él... nos abandonó y huyó.",
		["221155"] = "¡Gracias a Adamina! Todavía no ha encontrado a Elmar, ¿verdad?",
		["117032"] = "¡Terrence! ¿Has visto eso? Es un siflero.",
		["331117"] = "¿De veras? Entonces es hora de irse antes de que todo esté sumido por la oscuridad. Debo irme cuanto antes de este horrible lugar.",
		["9971285"] = "A una difunta amiga que, cuando no parecía haber luz alguna, consiguió hacérmela ver. No obstante, no consigo hacer bien su cara.",
		["9973336"] = "Contrato.",
		["995039"] = "No se debe desperdiciar ni una gota... Ni... una... gota...",
		["994040"] = "/t0",
		["991040"] = "Ah, amigo mío, qué alma más noble tienes... No querrías luchar contra alguien tan pusilánime como yo, ¿verdad?",
		["991041"] = "...",
		["333903"] = "Sí.",
		["991237"] = "¡No te rindas, Bravo! ¡Lo hemos conseguido! ¡El río del Alma ha vuelto a fluir!",
		["331109"] = "¡Gracias a Exilium! ¡Has matado a esa cosa! Mis plegarias al fin han sido contestadas. No tengo nada con lo que compensarte, pero te daré esto como muestra de agradecimiento.",
		["9971357"] = "Todavía tenemos tiempo a encontrar el resto...",
		["992051"] = "",
		["4994993003"] = "Abismo de la Niebla oscura",
		["991052"] = "¿Quieres más? No pasa nada, amigo. Tengo reservas.",
		["9956031"] = "",
		["333199"] = "Devolverle la cabeza.",
		["9973352"] = "Todavía no he encontrado nada.",
		["441257"] = "¿Qué es este polvo blanco?",
		["991053"] = "¿Quieres más? No pasa nada, amigo. Tengo reservas.",
		["992054"] = "",
		["771141"] = "Esta era la única opción posible si quería encontrar al asesino de los marcioneses y pagar mi deuda de sangre.",
		["553185"] = "¿Conoces al propietario de esta cabaña?",
		["551364"] = "La búsqueda de lo sublime es la mayor amenaza para la humanidad.",
		["551124"] = "Ja, ja... ¡Sabía que este sería mi día de suerte! ¡Por nuestra nueva amistad!",
		["993054"] = "Coger.",
		["991055"] = "Mmm... ¿Qué intentas recordar, exactamente? ¿Eso no es suficiente? Amigo mío, puede que esos recuerdos olvidados sean peligrosos...",
		["991056"] = "Mmm... ¿Qué intentas recordar, exactamente? ¿Eso no es suficiente? Amigo mío, puede que esos recuerdos olvidados sean peligrosos...",
		["993057"] = "Marcharse.",
		["554081"] = "/t0",
		["9921008"] = "¿Quién demonios es Teresa? No dejabas de decir su nombre cuando estabas inconsciente.",
		["991058"] = "¿Oyes eso? Es el latido de Ictiosauria... Qué sonido más agradable...",
		["991060"] = "Ah... Forastero, ¿has visto ese rostro? ¿Ese rostro tan horrible y perverso?",
		["991062"] = "Lleva escondiéndose desde que escuchó ese sonido tan fuerte, ocultándose en esa palangana. Ese bastardo del barco de los dementes... Tras tanto tiempo, todavía se niega a hablar...",
		["791298"] = "¿Habrá equilibrio algún día?",
		["9921006"] = "Al pueblo más cercano, a por suministros. Aunque tú no necesites comer, yo sí necesito descansar.",
		["9921014"] = "Pero... ¿Cómo es posible?",
		["991063"] = "¡Seguro que sabe dónde está Rochette! ¡Tiene un montón de secretos ocultos!",
		["992064"] = "",
		["771086"] = "Si lo hiciera, el maestro de Norwood le habría matado. No tenía elección.",
		["441315"] = "No atiende a razones tras echar a perder los esfuerzos de los demiurgos. A pesar de todo eso, ¡esos imbéciles siguen pensando que es un héroe!",
		["993064"] = "Preguntar por el sonido fuerte.",
		["551340"] = "¿Podrán estos seres tan insignificantes derrocar a los colosos? Es una posibilidad... creada por los humanos... en la cual no se ha pensado desde hace mucho tiempo...",
		["221196"] = "Mi pequeña, espero que estés orgullosa... Esta es la gracia del Santo Padre.",
		["993066"] = "Preguntar sobre el barco de los dementes.",
		["9953034"] = "Hablar.",
		["9921005"] = "¿Adónde vamos?",
		["991434"] = "En ella hay cinco huecos. Inserta las gemas en los huecos en el orden correspondiente.",
		["221158"] = "Oh, no... Ella también...",
		["772103"] = "",
		["991069"] = "¿Por qué no logro recordar nada de eso?",
		["221248"] = "Es por su propio bien. No hay un lugar más seguro para la Caja sagrada que este.",
		["441224"] = "Quiero saber la verdad.",
		["444212"] = "/t0",
		["791312"] = "Ah... Semeja que los rumores eran ciertos, esta reliquia parece funcionar.",
		["991071"] = "Lo siento... Estaba a punto de decir algo... Aaah... ¿Hueles el aroma que ha quedado en la olla? Ja, ja, qué tentador...",
		["661401"] = "¡No, por favor! ¡Busca la protección del apoderado en el Salón de la Trinidad, no tiene sentido quedarse aquí!",
		["441238"] = "Conrad, Albert... Andrew... Son los nombres grabados en la estela, hablando silenciosamente sobre la determinación de los demiurgos.",
		["332199"] = "",
		["992071"] = "",
		["777024"] = "Otra vez las cabezas brillantes, empieza a mosquearme...",
		["994003"] = "/t0",
		["661260"] = "Mensajero infeliz, ¿quieres hacer negocios conmigo?",
		["441065"] = "Eso no funcionará contra el poder de la Iglesia.",
		["991072"] = "¿Qué es esto?... Lo reconozco, ja, ja... ¡Reconozco esta cosa! ¡Pertenece a Rochette!",
		["991073"] = "Está en la cueva presente tras la cascada, esperando a que el agua vuelva a fluir...",
		["443223"] = "<color=#DBBA7FFF>Ayudar a Frollo.</color>",
		["553239"] = "Marcharse.",
		["991076"] = "El río vuelve a fluir... ¿Por qué no ha vuelto Rochette todavía?",
		["995078"] = "Oh... Rochette... No puedes quedarte dormida... No te duermas...",
		["991502"] = "Ja, ja, ja, ya he dicho demasiado. Si quieres coger un poco, adelante, pero no seas avaricioso.",
		["9971349"] = "Cuantas más losas como esta encuentras, más te acercas a descifrar el texto grabado en el brazalete...",
		["991081"] = "Hay un demonio comepersonas ahí dentro. Susurra de una forma tan espeluznante que se te pondrán los pelos de punta... Aaah... Una voz tan indescriptible que nadie podría entenderla...",
		["991505"] = "Obviamente, si tu objetivo es terminar con tu insignificante vida, no tienes por qué escuchar mis palabras, je, je...",
		["884054"] = "Libera tu conocimiento... Permite que tus pensamientos sean libres y abraza la luz...",
		["991083"] = "Nadie sabe cómo es su apariencia. Desde que se secó el río del Alma, nadie ha conseguido salir de esa cueva tan oscura.",
		["9921003"] = "¿Cuánto tiempo llevo durmiendo?",
		["9921011"] = "Eso no es posible. Ese pelo rojizo... Tenía que ser ella.",
		["111084"] = "Veré lo que puedo hacer.",
		["991084"] = "¿Por qué motivo se secó el río del Alma?",
		["9961028"] = "Terrence, después de todo lo que hemos pasado, ¿sigues sin confiar en mí?",
		["9961036"] = "Ya...",
		["771252"] = "También está la otra parte de la historia. La reencarnación no está hecha de remordimientos, pues solo aquellos con un objetivo claro tienen un pasado, un presente y un futuro completo.",
		["113231"] = "Comerciar.",
		["221202"] = "Aaah, el linaje del Santo Padre... El linaje del Santo Padre... Aaah...",
		["111085"] = "Jerry, oh, mi Jerry, ¿dónde estás?",
		["662405"] = "¿Quieres abrir la puerta?",
		["111106"] = "¿Te lo puedes creer? ¡El ser más agradable de todo Heggem es un huevo! Bueno, sin contarte a ti, claro...",
		["993086"] = "¿Qué estás haciendo?",
		["9951051"] = "Hay muchos misterios sin resolver en Katib, como los antiguos rituales que solo conocían los marcioneses, así como el linaje único de los discípulos presentes en ese lugar.",
		["771225"] = "Puede, o puede que no. Ya sabes cuál es mi decisión.",
		["223112"] = "<color=#DBBA7FFF>Han capturado a mi camarada, ¿la has visto?</color>",
		["441063"] = "Yo misma he sellado la puerta que lleva a Tierrabendita. Nadie se irá antes de que esto se solucione.",
		["661138"] = "¿Un mensajero? Nunca he visto a uno de verdad. He oído muchas cosas sobre vosotros, sobre todo malas... Teniendo en cuenta que estoy a punto de morir, ¿podrías concederme un último deseo?",
		["991087"] = "Agua... Estoy esperando a que vuelva a fluir el agua...",
		["995196"] = "Ya los he tirado para abajo.",
		["991091"] = "No quiero morir en silencio... Mi corazón me sigue recordando que solía tener algún tipo de misión...",
		["331094"] = "...",
		["991507"] = "No quiero morir así, en la oscuridad... Debo recuperar mi antiguo yo...",
		["331146"] = "Nadie lo sabe, nunca lo dijo.",
		["221426"] = "No la he visto desde que se la llevó el Santo Padre...",
		["551411"] = "...",
		["993092"] = "Marcharse.",
		["443902"] = "No.",
		["221213"] = "Ha muerto... Siento que hayas tenido que ver esto.",
		["100610064169"] = "/t0",
		["443904"] = "No.",
		["331178"] = "Las cosas ya no son como antes, Terrence. Hoy en día, una promesa no vale nada.",
		["445056"] = "Los demiurgos no olvidarán lo que has hecho... Que Dios esté siempre contigo.",
		["443119"] = "...",
		["995095"] = "Te encontraré... Rochette... Ja, ja... Mientras siga bebiendo agua, te recordaré...",
		["221065"] = "Mi mamá me estaba protegiendo de los monstruos que nos estaban atacando, pero la capturaron y se la llevaron a una mazmorra.",
		["9931013"] = "Benita, la monja devota. Es tu única amiga en Tierrabendita.",
		["667123"] = "Parece que los Pilares sagrados están tan débiles por culpa de este fragmento. ¿Quién lo habrá puesto aquí?",
		["994005"] = "/t1",
		["551180"] = "En este mundo no existe ni el bien ni el mal, solo existe el oscuro abismo en lo más profundo del corazón de ese hombre que determina qué está bien y qué está mal.",
		["997097"] = "Parece que este demonio tiene una afición de lo más peculiar...",
		["221264"] = "Perdone usted, bella durmiente. Me temo que no tienes derecho a decir tal cosa.",
		["9953055"] = "Comerciar.",
		["663152"] = "<color=#DBBA7FFF>Usar la Piedad de Tristram.</color>",
		["995099"] = "Libérame...",
		["993075"] = "Marcharse.",
		["995100"] = "No nos abandones...",
		["661177"] = "¿Cómo sabes todo esto?",
		["9941015"] = "¿La ciudad sin luz rechazada por Tierrabendita?",
		["991103"] = "Esos movimientos... Debían de estar bien entrenados.",
		["771078"] = "No había oído hablar de un lugar como ese debajo de Tierrabendita.",
		["774385"] = "/t0",
		["111077"] = "¿Por qué te fuiste de la mina?",
		["551286"] = "Mi puesto lleva desierto mucho tiempo, igual puedo hacerte un buen descuento.",
		["661331"] = "Otra Espada sagrada, una espada que puede absorber los Sendril y contener la luz... ¿Es este el secreto que encontraron en el Sepulcro colosal? Soy un pecador, cómo puedo haber pensado cosas tan horribles...",
		["9971361"] = "Esta debería ser la última. ¿Qué tiene grabado el brazalete?",
		["221301"] = "La constante migración y los entornos complicados hacen que los discípulos de Adamina sean estériles, algo que los ha llevado al borde de la extinción. Pretenden prolongar su estirpe con el poder de los Sendril.",
		["991082"] = "¿Un demonio comepersonas? ¿A qué te refieres?",
		["4994994002"] = "La Obsesión terrorífica de la Niebla oscura vuelve a las andadas. Debes derrotarla para poder marcharte.",
		["441105"] = "Ha seguido los pasos de los demiurgos y va en dirección a Tierrabendita.",
		["774028"] = "/t0",
		["997106"] = "Démonos prisa y subamos ahí cuanto antes. Con suerte, podrá decirnos algo sobre lo que está pasando aquí.",
		["994107"] = "/t0",
		["551376"] = "El amor es... algo que te puede llevar a la locura.",
		["444285"] = "/t0",
		["992107"] = "",
		["993108"] = "Preguntar sobre el agua del río del Alma.",
		["991109"] = "¿Por qué está todo el mundo tan ansioso por la dichosa agua del río?",
		["991522"] = "Solo logran recordar algunos fragmentos del pasado bebiendo agua del río del Alma.",
		["774030"] = "Nunca quisiste cumplir tu promesa, Eckhart.",
		["553284"] = "Comerciar.",
		["4994993031"] = "Cueva de la Niebla oscura",
		["9931026"] = "Tendré cuidado con él.",
		["884039"] = "¡Extranjero! ¡Si no hubiésemos sacrificado a tu bruja, jamás habrías podido abrirte camino hasta el Sepulcro colosal!",
		["221246"] = "La Caja sagrada... La Iglesia está persiguiendo a Teresa en su busca. Siempre he mantenido mi palabra y me he encargado de ella.",
		["991113"] = "¿El Muro del Silencio?",
		["991114"] = "Lo construyeron esos tipos de arriba tan egoístas. Si hubiera algún modo de abrirlo, no nos tendríamos que matar entre nosotros de esta manera.",
		["771265"] = "Lo siento, pero no puedo hacer ningún descuento. Eso sí, todos mis productos son dignos de su precio.",
		["221103"] = "¿Quién eres y qué quieres de mí?",
		["441081"] = "Tiene que haber alguna pista por aquí...",
		["551369"] = "Los mediocres siempre se obsesionan con superar a los demás.",
		["9941027"] = "Lo hecho, hecho está. Ahora debemos centrarnos en cómo detener a tu esposa.",
		["991424"] = "Piedra del Heggie",
		["227322"] = "¡Mira! ¡Debe de ser Elmar!",
		["793301"] = "Contrato.",
		["771209"] = "Ahora es libre de su tormento eterno... Él querría lo mismo para ti.",
		["9971469"] = "Vaillant debió de descubrir esa información sobre el apoderado, por eso silenció a los demiurgos.",
		["991119"] = "¿Qué son esas criaturas que acabas de espantar?",
		["991120"] = "Carroñeros... Todo el mundo los llama así... Son especialistas en cazar y matar dementes que quieren subir más arriba, como tú.",
		["9941012"] = "Mírate, yendo de un lado a otro como un pollo sin cabeza...",
		["991122"] = "Tal vez para evitar que la gente encuentre más agua del río del Alma. Tal vez solo por mero odio. En fin, tú procura no provocarlos.",
		["9971302"] = "...",
		["551100"] = "El director prometió que, si encontrábamos el Sello de Yekub, el apoderado nos daría amnistía y nos permitiría volver a Tierrabendita.",
		["661142"] = "...",
		["991513"] = "Aunque tengas la suerte de escapar de ellos, no tendrás ninguna oportunidad de derrotar al guarda que hay más allá.",
		["118149"] = "",
		["551437"] = "Me congratula comprobar que has vuelto sano y salvo. Ya es hora de volver a Ictiosauria. Coge el fragmento de meteoro rojizo y ve al puerto abandonado de Edith. Allí encontrarás mi embarcación atracada.",
		["991126"] = "No está en mi mano elegir qué recuerdo y qué olvido.",
		["771246"] = "我不会原谅你对我同胞所做的一切，但我必须感谢你收留了特蕾莎。",
		["991134"] = "Ríndete. No hay por qué seguir pensando en llegar ahí arriba.",
		["200120019999"] = "/t0",
		["663601"] = "Sí.",
		["793311"] = "Contrato.",
		["441220"] = "¡Hoy haré sonar tu propio toque de difuntos!",
		["991516"] = "La destrucción siempre llega sin previo aviso y en un abrir y cerrar de ojos.",
		["1006100118"] = "Tu débil, incierta y despistada naturaleza... Está en tu camino hacia adelante.",
		["553342"] = "Sí.",
		["552903"] = "¿Quieres volver al carruaje?",
		["331176"] = "Ese caballero tan patético no se creerá que su prometida ya ha...",
		["791286"] = "Tal vez es tan importante para ti que una cara de arilo nunca puede ser suficiente.",
		["994140"] = "/t0",
		["991140"] = "Ah... ¿Eso es todo? ¿Cómo?... No esperaba que nos volviéramos a ver.",
		["223901"] = "Sí.",
		["9971348"] = "Desde que me fui de Tierrabendita, no he dejado de buscar pistas e investigar, pero, al final, soy incapaz de saber qué es realmente importante.",
		["991141"] = "¿Qué estás haciendo aquí?",
		["999285"] = "No te preocupes, tenemos tiempo. Te esperaré aquí.",
		["991145"] = "No pareces muy interesado en conseguir agua del río del Alma. Qué raro... ¿Qué narices estás buscando entonces?",
		["221134"] = "No puedo dejar que se enfrente a todo sola, tengo que ir con ella...",
		["793362"] = "Todavía no he encontrado nada.",
		["991148"] = "Qué descubrimiento más sorprendente.",
		["9941034"] = "Deja de culparte. Tráela de vuelta y terminará todo este dolor y sufrimiento.",
		["993151"] = "¿Cómo encontraste este lugar?",
		["991151"] = "¿Cómo encontraste este lugar?",
		["773007"] = "Sí.",
		["771191"] = "Ah... Más comida deliciosa... Ñam... Ah... Puedo sentir la luz... está bañando mi cuerpo... Ah... A pesar de que los monos se han llevado mis ojos, todavía puedo ver... Casi... Casi... El carruaje llegará al final...",
		["443907"] = "Sí.",
		["991519"] = "Dejaron todos los cadáveres aquí amontonados.",
		["664165"] = "/t0",
		["447180"] = "¿Cuál de los dos estará mintiendo? No tenemos tiempo de averiguarlo, tienes que decidir ya.",
		["991153"] = "Fue por aquel entonces cuando se cerró el Muro del Silencio y, poco tiempo después, se secó el río del Alma.",
		["991155"] = "No... no lo recuerdo, puede que los carroñeros...",
		["9931041"] = "¡Aaah, cuanto más dinero tengas, más artículos podrás comprar!",
		["441269"] = "Mensajero, ¿has cumplido la promesa que me hiciste?",
		["4994993028"] = "Frontera de la Niebla oscura",
		["441068"] = "Pronto lo sabrás, mensajero, pero todavía no es el momento.",
		["551429"] = "Ah... La obsesión por la codicia... A todos nos ha cegado, ¿no es cierto?",
		["554114"] = "/t0",
		["991157"] = "No pasa nada. Ya he asumido que no van a volver.",
		["333141"] = "¿Por qué la Iglesia sentenció a muerte a Teresa?",
		["993158"] = "Marcharse.",
		["997159"] = "Parece que los carroñeros se crean aquí. Eso quiere decir que no son demonios, son gente de verdad...",
		["991160"] = "No sé cuánto tiempo habrá pasado. Una y otra vez, cada vez que recuerdo mi misión, intento despertar su alma encarcelada... Sin embargo, nunca podría romper una coraza tan dura...",
		["662705"] = "¿Quieres abrir la puerta?",
		["774999"] = "Necesitas una llave.",
		["9971286"] = "Tal vez es tan importante para ti que una cara de arilo nunca puede ser suficiente.",
		["665129"] = "Somos demiurgos... Estamos con Dios...",
		["991163"] = "Ja, ja, ja... Solo la oscuridad prevalece ante mis ojos. Al fin olvidaré mis antiguas creencias... Al final, se ha demostrado que todo es en vano...",
		["991188"] = "En nombre de Handel, cumple el juramento de lealtad.",
		["9973329"] = "Contrato.",
		["9941017"] = "¿Y por qué están aquí los monjes del Monasterio sagrado?",
		["991018"] = "El efecto de los Sendril sobre ti se está haciendo más fuerte.",
		["661226"] = "¿Por qué dices eso?",
		["553119"] = "No.",
		["991191"] = "Ah... La brillante insignia de los caballeros de Handel. Por favor, deja que la vea una vez más...",
		["995195"] = "¿Has tirado la «basura»?",
		["111051"] = "¿Teresa? ¿Quién es esa?",
		["9971328"] = "El odio es la luz eterna en esta Niebla oscura.",
		["791294"] = "Si ella logró sobrevivir, puede que no tenga que portar más esta carga.",
		["553902"] = "No.",
		["995199"] = "Oh... No te preocupes, ya los he tirado para abajo.",
		["9961050"] = "No olvides que todavía me tienes que pagar lo que me debes. No te creas que te vas a escabullir así como así. No pienso permitirlo.",
		["991202"] = "Lord Ebbinghaus nos dio un significado, nos dio el agua del río del Alma... Debemos proteger su favor...",
		["9962026"] = "",
		["334083"] = "¿Serán los Sendril?",
		["991204"] = "Un sueño dulce, una luz cálida y resplandeciente... Ah... En la parte más profunda de los recuerdos... Un abismo remoto e intocable...",
		["113079"] = "¿Jerry? ¿Es tu hijo?",
		["661400"] = "La mayoría se han ido al Salón de la Trinidad en busca de la protección del apoderado.",
		["993133"] = "Marcharse.",
		["9973452"] = "Bendecir.",
		["441249"] = "¿De verdad crees que puedes acabar con Tierrabendita solo con esto?",
		["991205"] = "Por favor, no me molestes... Déjame permanecer en este recuerdo tan maravilloso, solo un poco más... Ah... Ah... Vuelvo a oírlo... Ese sonido tan fuerte y espantoso...",
		["991206"] = "(Inhala) Tu cuerpo... Ese olor tan familiar, el mismo hedor de los ladrones que surgieron de aquel barco de los dementes... Seres inmundos, tipos arrogantes... La gran Ictiosauria os volverá a maldecir.",
		["773221"] = "Comerciar.",
		["441221"] = "Mensajero... No entiendes nada.",
		["991207"] = "Ja, ja, ja... Nadie se marcha de Ictiosauria, nadie...",
		["111050"] = "Teresa...",
		["441254"] = "¿Fuiste tú quien diseminó el polvo blanco en el cielo?",
		["771236"] = "Cuando veas a Teresa, por favor, dile que la echo mucho de menos.",
		["995209"] = "Eh... ¡Aquí! ¡Tipo extraño! ¡Aquí!",
		["661184"] = "El Sello de Yekub puede cumplir los deseos de los humanos que buscan la vida eterna en su sed de poder. No obstante, eso conlleva una horrible maldición...",
		["991401"] = "Nos otorgamos el privilegio de la elección, y nuestras elecciones nos conforman.",
		["111128"] = "Ja, ja, ja, un humano vivo...",
		["991212"] = "No te preocupes... Se cuidar de mí mismo. Por cierto, deja que te cuente una buena noticia. ¡Al fin he encontrado el mecanismo que abre el Muro del Silencio!",
		["991213"] = "Sin embargo, esos tipos tan egoístas ocultaron la palanca que controla el mecanismo y el engranaje en sitios diferentes.",
		["991511"] = "¿Me ayudarías?",
		["991149"] = "No me lo creo...",
		["221294"] = "Por la bendición del Santo Padre, has vuelto sano y salvo, mensajero. ¿Ha ido todo bien?",
		["221165"] = "Está en el lugar del nacimiento, esperando a dar a luz a un bebé de Adamina... Me pidió que te dijera que volverá contigo pronto.",
		["993214"] = "Aceptar.",
		["551348"] = "La búsqueda de lo sublime es la mayor amenaza para la humanidad.",
		["551385"] = "Los mediocres siempre se obsesionan con superar a los demás.",
		["441154"] = "De no ser por la bondad de Vaillant, ese desleal alarmista no seguiría vivo...",
		["661800"] = "La mayoría se han ido al Salón de la Trinidad en busca de la protección del apoderado.",
		["991121"] = "¿Por qué hacen eso?",
		["771217"] = "Te lo prometo...",
		["771142"] = "¿Marcioneses?",
		["9941040"] = "¿Por qué la Iglesia quiso deshacerse de los demiurgos?",
		["773241"] = "Bendecir.",
		["551069"] = "Las criaturas se han adaptado a su entorno... Es raro, pero bello al mismo tiempo.",
		["991220"] = "¿En serio?... Podrías habernos salvado a todos. No importa, me quedaré aquí y no iré a ningún sitio.",
		["991050"] = "No hay de qué. Pero no olvides que hay unos límites.",
		["991222"] = "Si ya he llegado hasta aquí, ¡seguro que encuentro alguna manera de abrir el Muro del Silencio!",
		["331097"] = "Se llevó mi cabeza para buscar una cura... Era la única que estaba dispuesta a hacer algo así por mí...",
		["331151"] = "¡Totalmente!",
		["999283"] = "Solo he encontrado una de las dos cosas.",
		["999284"] = "¡Oh, genial! ¡Ya falta poco! ¡No podemos rendirnos, ahora que hemos llegado tan lejos!",
		["111115"] = "¿Quién es la bruja extraña? ¿Por qué está aquí?",
		["777113"] = "¡Lo hemos conseguido! A pesar de que no me gusta un pelo el tipo ese, es alguien honesto. Al fin podremos ir a las ruinas.",
		["991229"] = "Lo siento, todavía no la he encontrado.",
		["9971326"] = "Bah...",
		["991144"] = "Sé que suena asqueroso, pero es mejor que arriesgar mi vida contra esos monstruos. Una vez te acostumbras, no es para tanto.",
		["999286"] = "¿Dónde podrá estar? Debería estar cerca, seguro que anda por aquí.",
		["443190"] = "Noticias sobre Teresa.",
		["884009"] = "La puerta está cerrada... Ha vuelto...",
		["991239"] = "...",
		["9951025"] = "¿Quién te ha dicho eso?",
		["445088"] = "Va a empezar... Va a empezar... La rebelión final... Seguid a los guerreros... Liberad vuestros sentimientos reprimidos...",
		["9921007"] = "Pero Teresa todavía está en Heggem, ¡tenemos que volver!",
		["9921015"] = "Hablando de pelo rojizo... Mira lo que he sacado del asceta.",
		["4994993021"] = "Darle los materiales.",
		["771079"] = "Solía ser una mazmorra de la Iglesia. En ella encerraban a los prisioneros de los que se querían deshacer en secreto, pero seguro que prefieres saber la otra parte de la historia.",
		["113904"] = "No.",
		["221160"] = "He encontrado esto.",
		["442114"] = "Sin embargo, tras ocultarme en la oscuridad durante tanto tiempo, tal vez me haya debilitado tanto como para no poder soportar la luz.",
		["993258"] = "Mostrar la invitación delicada.",
		["884071"] = "/t2",
		["661402"] = "Vete, aquí no encontrarás nada que te vaya a resultar de utilidad.",
		["114034"] = "/t0",
		["991154"] = "¿Qué pasa con tus padres? ¿Dónde están?",
		["991259"] = "Pero no olvides que solo se permite coger un tesoro, así que no te vengas muy arriba. El amo te estará observando, y yo también.",
		["995264"] = "¡Hay que acabar con él, y rápido! ¡Antes de que vuelva el amo!",
		["551162"] = "Ah, eso son solo unos pequeños trucos de los marcioneses. Te daré una pista. Los marcioneses creen en el flujo eterno de la vida: el pasado, el presente y la vida tras la muerte. Solo se puede hallar la verdad experimentando las tres vidas.",
		["995266"] = "¡No dejaré que eches a perder mi fortuna! ¡Usaré tu cráneo para guardar más tesoros!",
		["441062"] = "¿No te apetece contestar? No te preocupes, acabarás decepcionado de todos modos. Hay una rata complicando el trabajo que están realizando los demiurgos contra la Iglesia. No podemos dejar que filtre información...",
		["991268"] = "Oh... ¿Has terminado? ¿Por qué has tardado tanto? Ahora, ¡largo! ¡Y que no te vuelva a ver por aquí!",
		["9921012"] = "Si no me crees, pregúntale a la cotorra esa de ahí.",
		["553238"] = "Poner un trozo de meteoro rojizo en el altar.",
		["221197"] = "¡Mami, despierta! ¡Morirás si sigues con esto!",
		["9961029"] = "Debo decirte que nadie puede predecir el futuro. Sin embargo, mientras te aferres a la fe, todavía pueden suceder milagros.",
		["551141"] = "Al fin puedo volver a Tierrabendita...",
		["992270"] = "¿Quieres combinar los fragmentos de meteoro rojizo en uno solo?",
		["993271"] = "No.",
		["992470"] = "",
		["9971382"] = "Todavía no tengo información suficiente para descifrarlo todo.",
		["9971350"] = "Buscar reliquias de una civilización que nadie ha conocido es algo bastante complicado en este mundo.",
		["991273"] = "Ja, ja, ja... Es increíble, hacía mucho tiempo que no veía a nadie llegar hasta aquí arriba.",
		["777025"] = "Jamás había visto una luz tan brillante, ¡y menos una bajo tierra!",
		["221067"] = "¿Dónde está tu padre?",
		["773185"] = "Marcharse.",
		["663140"] = "Aceptar.",
		["553367"] = "Sí.",
		["995316"] = "Siempre hay algún pez que se escapa de la red, pero, a partir de ahora, todos los pasos que des te acercarán cada vez más a la muerte.",
		["221224"] = "¡Teresa, devuélveme eso!",
		["331110"] = "¿Quién iba a pensar que ese estúpido caballero traicionaría a nuestra señora por esa lunática? Sin embargo... ¿quién iba a pensar que esta historia tendría un final? Puede que este anillo lleve años maldito.",
		["991274"] = "¡Qué despreciable! Menudo intento de aprovecharte de la benevolencia de lord Ebbinghaus para satisfacer tus deseos egoístas.",
		["791300"] = "Dios mío, ¿por qué te llevaste un alma tan inocente? ¿Por qué no te llevaste también el caos y la locura? ¿Me torturas por haber mentido?",
		["991412"] = "La conciencia que se desvanece encuentra un nuevo hogar.",
		["9999316"] = "¿Puedes oír cómo aúlla el viento? Se está formando una tormenta...",
		["9999288"] = "Desde luego.",
		["9999289"] = "Pero, aun así, te convertiste en un mensajero...",
		["777026"] = "Con una luz tan intensa, ¿cómo es posible que sobrevivieran tras ser contaminados por los Sendril?",
		["791347"] = "He oído hablar de su historia, una civilización que poseía un conocimiento superior a cualquier otra, pero que desapareció al mismo tiempo del sol.",
		["551185"] = "¿Conoces al propietario de esta cabaña?",
		["994414"] = "",
		["221209"] = "Se ha vuelto completamente loca.",
		["9999294"] = "¿Criaturas de la noche?",
		["663165"] = "Mostrarle la espada.",
		["9971329"] = "Ya no necesito este brazo... Sin odio alguno, mi venganza ha sido cumplida.",
		["551182"] = "Me llamo Jerold. Soy un forastero del este y no debería estar aquí, pues mi hogar lleva mucho tiempo sumido en un profundo sueño. A diferencia de Solas, aquí hace poco que todo está durmiendo.",
		["551072"] = "El problema es... ¿cómo encuentras un lugar que nunca has visto o en el que nunca has estado?",
		["441227"] = "Ja, ja... Si quieres satisfacer tu curiosidad, ve a buscar la llave oculta en la sala de Elena.",
		["441239"] = "Son los demiurgos que se unieron a la rebelión de forma voluntaria. Es una lápida colectiva, estaban listos para entregar sus vidas.",
		["663330"] = "<color=#DBBA7FFF>Coger la espada.</color>",
		["9931015"] = "Desterró a Teresa hace muchos años... Nadie sabe qué ocurrió, pero desde entonces no ha abandonado ese lugar.",
		["9999299"] = "Qué irónico. ¿Es este el mundo que tanto quiere proteger Ictiosauria?",
		["9999300"] = "...",
		["9999301"] = "Existe una leyenda en Ictiosauria, la cual dice así: «Reúne los tres Sellos de Yekub y todos los colosos del mundo caerán, suponiendo el fin de todas las cosas».",
		["9999302"] = "Cuando el mundo vuelva a estar en silencio, la verdadera luz aparecerá.",
		["9999303"] = "No me creo nada de esas ideas tan fantasiosas.",
		["331225"] = "Mors certa, hora incerta.",
		["9999305"] = "Lo siento, pero no soy ningún héroe destinado a salvar el mundo.",
		["224308"] = "/t0",
		["799307"] = "Nunca he creído en la humanidad. Los humanos son exiguos, pero se ven mucho más poderosos de lo que realmente son, creando mentiras sin cesar e involucrándose en batallas constantes.",
		["799308"] = "Sin embargo, al ver tu persistencia con Teresa, de pronto sentí que todavía quedaba algo de esperanza en la humanidad.",
		["799309"] = "Toma el último Sello de Yekub, tal vez te ayude a traer a Teresa de vuelta.",
		["799311"] = "Sé que esta vez volverás a seguir tu corazón.",
		["799312"] = "Las leyendas siempre deben de ser verificadas por alguien. Si proteger los Sellos de Yekub solo sirve para prolongar este mundo sin esperanza... La verdad, no le veo mucho sentido a eso.",
		["1002100021"] = "¿Cómo regresé? Algo no está bien...",
		["441288"] = "Para mí, las coincidencias no existen, igual que tampoco existen los planes de Dios... Nuestros destinos se forjan a partir de nuestras propias decisiones.",
		["113067"] = "Marcharse.",
		["991210"] = "Ja... No te habrán seguido esos monstruos, ¿verdad?",
		["791334"] = "Tu mano izquierda es de lo más perturbadora.",
		["335226"] = "Todo sea por volver a ver a mi amada.",
		["9924031"] = "/t2",
		["441060"] = "Ja, ja... ¿Tan cansado estás de la vida?",
		["116231"] = "",
		["991402"] = "Palabra de Ebbinghaus: II",
		["791288"] = "Si encuentras más arilo, tráemelo, por favor.",
		["223249"] = "Hay que tomar una decisión.",
		["991405"] = "Ictiosauria es el único lugar de felicidad permanente.",
		["991406"] = "Palabra de Ebbinghaus: IV",
		["991407"] = "El autocontrol es el único principio moral. La supuesta libertad no consiste en permitirse a uno mismo actuar como le plazca, sino en controlarse a sí mismo.",
		["9931014"] = "¿Por qué está en Exilium?",
		["991408"] = "Palabra de Ebbinghaus: V",
		["991409"] = "Mientras cumplas con tu deber, la gente te respetará.",
		["992410"] = "",
		["9921040"] = "No tengo ni idea, pero su máscara me hace sentir incómoda. Se marchó poco después de que perdieras el conocimiento.",
		["331201"] = "Eres muy valiente, mensajero. Te agradezco de corazón lo que has hecho. Por favor, permite que te desafíe como caballero. Te esperaré allí donde ella pereció. ¡En nombre del amor!",
		["9921020"] = "¡Dámelo ahora mismo!",
		["441106"] = "Qué alivio, eso significa que sus hermanos cuidarán bien de él. A ese chico siempre le gustó guardarse las cosas para sus adentros.",
		["993412"] = "Insertar el corazón celeste.",
		["441212"] = "Eran mis compañeros de armas, pero sus vidas fueron arruinadas por completo por culpa de una mentira.",
		["221115"] = "Sí, pero... por desgracia, ha sido elegida para ser una esposa del Santo Padre, al igual que todas las mujeres de este lugar.",
		["998301"] = "",
		["994476"] = "/t0",
		["998304"] = "",
		["334048"] = "No se puede abrir desde este lado.",
		["998305"] = "",
		["9999292"] = "Todos tenemos algo a lo que aferrarnos. Igual que Ebbinghaus...",
		["661107"] = "Mensajero, puedo sentir que tienes numerosas dudas acerca de los acontecimientos que han tenido lugar aquí, pero si investigas los cuatro pilares por mí, tal vez ambos podamos encontrar las respuestas que buscamos.",
		["991429"] = "Piedra del tesoro",
		["9942012"] = "",
		["791345"] = "He encontrado esto.",
		["991009"] = "Este sitio parece seguro... Venga, date prisa y encuentra al paliducho.",
		["557082"] = "Los orgullosos marcioneses nunca pensaron que la Iglesia podría conquistarlos…",
		["772007"] = "¿Quieres salir de las reliquias de los ancestros?",
		["991116"] = "No... no lo entiendo. No recuerdo nada de esto.",
		["991425"] = "Marcharse.",
		["778305"] = "",
		["991427"] = "Piedra del coloso",
		["991428"] = "Piedra de la humanidad",
		["994416"] = "",
		["771095"] = "Debo encontrar a mi padre antes de que sea tarde... No importa si está vivo o muerto...",
		["227182"] = "Vete de aquí, no puedo soportarlo...",
		["772204"] = "",
		["661143"] = "Por favor, mátame con la Espada sagrada del apoderado... Dicha espada puede disipar la maldición de los Sendril que me ha contaminado. No quiero convertirme en un monstruo...",
		["443905"] = "Sí.",
		["9971303"] = "Me alegro por ti.",
		["772224"] = "",
		["441198"] = "¿Queréis salvar la vida de Vaillant en Tierrabendita?",
		["4994994001"] = "No puedes volver. Solo puedes seguir explorando.",
		["991068"] = "Lord Ebbinghaus logró derrotarlos, pero los supervivientes siguen vagando por Ictiosauria a día de hoy.",
		["991435"] = "Piedra del coloso",
		["443908"] = "Sí.",
		["991436"] = "Piedra de la humanidad",
		["551181"] = "¿Quién eres?",
		["9971254"] = "Esta moneda es de mi difunto hermano. Toma, te dará suerte.",
		["991437"] = "Piedra de la humanidad",
		["991438"] = "Piedra del tesoro",
		["441177"] = "Toma esta llave y abre la puerta trasera del pueblo de ahí abajo. Entonces sabrás en quién confiar.",
		["991439"] = "Piedra del siflero",
		["9933031"] = "Comerciar.",
		["771247"] = "Pero sigue pensando que soy un mentiroso... Mantendrá sus sentimientos en secreto hasta el final.",
		["4994992003"] = "Ah... La lámpara iluminará tu camino. Ahora puedes entrar en la Niebla oscura, pero cuidado... Cuanto más ilumines la oscuridad, más fácil será que te vean las criaturas que ahí acechan...",
		["551354"] = "La tolerancia a la venganza... la convergencia pasional... los intentos peligrosos...",
		["1006100117"] = "¿Ves? Esa es la Elena de la que has querido deshacerte.",
		["553343"] = "Sí.",
		["9941033"] = "Lo que ha pasado hoy es culpa de mi flaqueza y mi incapacidad.",
		["792380"] = "",
		["663240"] = "Sí.",
		["991442"] = "En ella hay cinco huecos. Inserta las gemas en los huecos en el orden correspondiente.",
		["771137"] = "Terrence... Ja, ja, ja...",
		["551430"] = "El amor es... algo que te puede llevar a la locura.",
		["221131"] = "Pero, por favor, tenga cuidado. Últimamente está un poco... irritable. No haga que se enfade, no tengo ni idea de qué clase de cosas malas le podría hacer...",
		["441215"] = "El destino de los demiurgos es todo cosa de Vaillant. No es ningún héroe. A la gente le cuesta comprender esa realidad. Prefieren la comodidad de las mentiras mientras se refugian en las historias que cuentan.",
		["991443"] = "Piedra del coloso",
		["991445"] = "Piedra del tesoro",
		["221269"] = "Je, je, qué mensajero más simple...",
		["441145"] = "¿A qué te refieres?",
		["991447"] = "Piedra del siflero",
		["227203"] = "Madre del amor hermoso, ¿por qué tengo que ver esto? ¡Mátalo de una vez!",
		["991449"] = "Marcharse.",
		["991450"] = "En ella hay cinco huecos. Inserta las gemas en los huecos en el orden correspondiente.",
		["9946036"] = "",
		["661703"] = "¡Detente! ¡No la abras!",
		["991454"] = "Piedra del tesoro",
		["991455"] = "Piedra del siflero",
		["444322"] = "/t0",
		["991459"] = "La puerta sigue sin moverse.",
		["773001"] = "Sí.",
		["991460"] = "Mi puesto lleva desierto mucho tiempo, igual puedo hacerte un buen descuento.",
		["992461"] = "",
		["993461"] = "Comerciar.",
		["996461"] = "",
		["771144"] = "¡Mentira! ¡No te creo, te lo estás inventando!",
		["551264"] = "Hay otro Sello de Yekub en Tierrabendita, el último que estuvo en posesión de la Iglesia. Es lo que mantiene a los Sendril a raya.",
		["791352"] = "Según el texto de la losa, el brazalete tiene grabadas las palabras «ardiente fuego» y «luz eterna».",
		["991462"] = "Lo siento, pero no puedo hacer ningún descuento. Eso sí, todos mis productos son dignos de su precio.",
		["331062"] = "Hace mucho tiempo, cuando el sol nacía en el mar, ¿qué aspecto tenía el continente?",
		["991463"] = "Mi puesto lleva desierto mucho tiempo, igual puedo hacerte un buen descuento.",
		["992464"] = "",
		["993464"] = "Comerciar.",
		["9961019"] = "Debo estar con ella.",
		["9971359"] = "Cuando me fui del orfanato de la Iglesia, pensé que era valiente, pero ahora me doy cuenta de que no podía dejar atrás mi pasado.",
		["447181"] = "¿Qué acaba de pasar? ¡El coloso del cielo se está volviendo a mover! Esto no pinta bien...",
		["993465"] = "Marcharse.",
		["111067"] = "Tú... Has conseguido atravesar la Niebla oscura... Debes de ser un mensajero. ¡Sabía que la Iglesia no nos abandonaría!",
		["9931042"] = "Estoy un poco cansado, creo que necesito descansar.",
		["551101"] = "Teniendo en cuenta que el Sello de Yekub se mostró aquí una vez, puede que los rumores de que el Sepulcro colosal está en Katib sean ciertos.",
		["992467"] = "",
		["447168"] = "¡Es nuestra oportunidad! ¡Ve a detenerlo!",
		["9973361"] = "He encontrado una losa rota.",
		["100110012203"] = "No.",
		["9943032"] = "Hablar.",
		["993468"] = "Marcharse.",
		["991469"] = "Mi puesto lleva desierto mucho tiempo, igual puedo hacerte un buen descuento.",
		["663406"] = "No.",
		["991272"] = "El silencio entre la serenidad, una duermevela tranquila en tu hogar... En esta ola de olvido podrás retirarte e irte lejos con ellos...",
		["772390"] = "¿Quieres entrar en el Sepulcro colosal?",
		["993470"] = "Comerciar.",
		["445057"] = "No vayas más allá... o te arrepentirás.",
		["996470"] = "",
		["771138"] = "Dime, ¿dónde está Teresa?",
		["993471"] = "Marcharse.",
		["994555"] = "Una especie de sello aparentemente frágil.",
		["443218"] = "<color=#DBBA7FFF>Luchar con Frollo.</color>",
		["1005100052"] = "Nos utilizó, utilizó nuestra obsesión.",
		["331059"] = "Míralos, ni siquiera recuerdan sus nombres ni su pasado... Se pasan la eternidad deambulando entre la vida y la muerte.",
		["772261"] = "",
		["663801"] = "Sí.",
		["994777"] = "No se puede abrir desde este lado.",
		["113087"] = "<color=#DBBA7FFF>Darle el abrigo con manchas de sangre.</color>",
		["441149"] = "Esta campana no tiene brujería alguna, solo impide que los demiurgos vuelvan a perder la cabeza.",
		["994472"] = "/t0",
		["994473"] = "/t0",
		["994474"] = "/t0",
		["553332"] = "Sí.",
		["998303"] = "",
		["994477"] = "/t0",
		["441176"] = "Ja, ja, ja... Parece que me tienes en muy alta estima. El camino hacia el poder está plagado de sangre y cicatrices. Solo quiero devolverle a los demiurgos su tranquila vida de antaño.",
		["994481"] = "/t0",
		["994483"] = "/t0",
		["441299"] = "Quién sabe... Probablemente me lleve un tiempo.",
		["111089"] = "Gracias, mensajero. Jerry me pidió que te diera una muestra de agradecimiento de su parte. Ya no necesitamos esto, por favor, quédatelo.",
		["30274001"] = "/t0",
		["881078"] = "¿Qué estoy viendo? ¿A qué estoy esperando? Es probable que ya no importe, por favor, llévate esto.",
		["994485"] = "/t0",
		["991143"] = "...",
		["444888"] = "Todavía no se puede abrir.",
		["100310034999"] = "No se puede abrir sin una llave.",
		["100410044999"] = "No se puede abrir sin una llave.",
		["4994993013"] = "Aumenta el brillo de la lámpara de Niebla",
		["441251"] = "La puerta que lleva a Tierrabendita está abierta... ¡La Iglesia te pondrá a prueba!",
		["100510054999"] = "No se puede abrir sin una llave.",
		["100710074999"] = "No se puede abrir sin una llave.",
		["100510055999"] = "No se puede abrir desde este lado.",
		["100610065999"] = "No se puede abrir desde este lado.",
		["100210026001"] = "Realiza Transformación de la Niebla oscura",
