	StoryGuide = {
		["2001DescriptionJoystick"] = "Ofrece restos de Sendril para subir el nivel de los altares.\nTras subirlos de nivel, todos los personajes recibirán puntos para mejorar sus atributos.",
		["2003DescriptionTouch"] = "Equipa trofeos para obtener un gran poder.\nLos trofeos se dividen en 3 categorías: cicatrices, resistencia y pesadilla. Solo puedes equipar los trofeos en su posición correspondiente.",
		["2006DescriptionJoystick"] = "Para usarlo: {94}\nPara cambiarlo: mantén pulsado {94}",
		["2004Title"] = "Desbloquear dones",
		["2007DescriptionJoystick"] = "Mejorar\nConsume objetos, como fragmentos de aguas negras, para mejorar trofeos.\nReforjar\nSacrifica un trofeo para darle un atributo de bonificación a otro trofeo de tu elección. Cuanto mayor sea el nivel del sacrificio, mayor será la mejora.",
		["2005Title"] = "Uso rápido",
		["2004DescriptionJoystick"] = "Puedes desbloquear dones en los altares usando materiales de immundus.\nHay cuatro tipos de immundus: immundus astillado, immundus, cristal de immundus y diamante de immundus.\nSe necesitan materiales de mayor nivel para desbloquear dones más poderosos.",
		["2005DescriptionTouch"] = "Hay 6 ranuras de Objetos ágiles disponibles para equipar objetos.\nPara editar un Objeto ágil toca en su ranura.",
		["2005DescriptionJoystick"] = "Hay 6 ranuras de Objetos ágiles disponibles para equipar objetos.\nPara editar un Objeto ágil toca {80}.",
		["2005DescriptionKeyboard"] = "Hay 6 ranuras de Objetos ágiles disponibles para equipar objetos.\nPara editar un Objeto ágil toca {80}.",
		["2006Title"] = "Uso rápido",
		["2006DescriptionTouch"] = "Para usarlo: toca un objeto\nPara cambiarlo: toca un objeto y mantenlo pulsado",
		["2002DescriptionTouch"] = "Tras recibir una receta alquímica, podrás crear las pociones y objetos correspondientes.",
		["2006DescriptionKeyboard"] = "Para usarlo: {94}\nPara cambiarlo: mantén pulsado {94}",
		["2003Title"] = "Trofeos",
		["2004DescriptionTouch"] = "Puedes desbloquear dones en los altares usando materiales de immundus.\nHay cuatro tipos de immundus: immundus astillado, immundus, cristal de immundus y diamante de immundus.\nSe necesitan materiales de mayor nivel para desbloquear dones más poderosos.",
		["2007Title"] = "Mejorar y reforjar",
		["2004DescriptionKeyboard"] = "Puedes desbloquear dones en los altares usando materiales de immundus.\nHay cuatro tipos de immundus: immundus astillado, immundus, cristal de immundus y diamante de immundus.\nSe necesitan materiales de mayor nivel para desbloquear dones más poderosos.",
		["2002Title"] = "Alquimia",
		["2003DescriptionKeyboard"] = "Equipa trofeos para obtener un gran poder.\nLos trofeos se dividen en 3 categorías: cicatrices, resistencia y pesadilla. Solo puedes equipar los trofeos en su posición correspondiente.",
		["2007DescriptionTouch"] = "Mejorar\nConsume objetos, como fragmentos de aguas negras, para mejorar trofeos.\nReforjar\nSacrifica un trofeo para darle un atributo de bonificación a otro trofeo de tu elección. Cuanto mayor sea el nivel del sacrificio, mayor será la mejora.",
		["2002DescriptionKeyboard"] = "Tras recibir una receta alquímica, podrás crear las pociones y objetos correspondientes.",
		["2001Title"] = "Ofrecer",
		["2001DescriptionKeyboard"] = "Ofrece restos de Sendril para subir el nivel de los altares.\nTras subirlos de nivel, todos los personajes recibirán puntos para mejorar sus atributos.",
		["2007DescriptionKeyboard"] = "Mejorar\nConsume objetos, como fragmentos de aguas negras, para mejorar trofeos.\nReforjar\nSacrifica un trofeo para darle un atributo de bonificación a otro trofeo de tu elección. Cuanto mayor sea el nivel del sacrificio, mayor será la mejora.",
		["2001DescriptionTouch"] = "Ofrece restos de Sendril para subir el nivel de los altares.\nTras subirlos de nivel, todos los personajes recibirán puntos para mejorar sus atributos.",
		["2002DescriptionJoystick"] = "Tras recibir una receta alquímica, podrás crear las pociones y objetos correspondientes.",
		["2003DescriptionJoystick"] = "Equipa trofeos para obtener un gran poder.\nLos trofeos se dividen en 3 categorías: cicatrices, resistencia y pesadilla. Solo puedes equipar los trofeos en su posición correspondiente."
	},
