	StoryDialog = {
		["663703"] = "Oui",
		["994281"] = "Hahaha, mes charognards continueront à chasser jusqu'à ce que tous les Chevaliers d'Handel soient éliminés dans Ichthyosauria...",
		["661152"] = "Dépêchez-vous, je me sens partir !",
		["111121"] = "Qui est Ramones ?",
		["771198"] = "Comment ça, pas d'autre choix ? Tu lui as donné Teresa pour rien ? Je dois entrer dans ce maudit Tombeau des Colosses maintenant pour aller la chercher !",
		["111134"] = "Ha ha, humain malin.",
		["661322"] = "Oui... Le code... Si vous ne le connaissez pas, je vous conseille de déguerpir immédiatement !",
		["100210022202"] = "Oui",
		["997013"] = "Je sens encore le souffle de la vie sur cette île, mais c'est extrêmement léger.",
		["551268"] = "Dépêchez-vous, allez à Terrebénie, faites ce que vous avez à faire... Je dois me reposer maintenant... Ne vous inquiétez pas... J'espère que tout ira bien.",
		["771230"] = "Le Terrence empli de doutes qui errait dans la Brume sombre a disparu.",
		["333092"] = "Pourquoi êtes-vous cloué ainsi ?",
		["552329"] = "Consomme 1 miel de condensation et 30 os d'Anthozoan. Voulez-vous continuer ?",
		["771224"] = "Teresa t'empêcherait sûrement de faire une telle chose.",
		["663504"] = "Non",
		["1006100084"] = "Il détient le Sceau de Yekub...",
		["9971340"] = "Ce bracelet a beaucoup de valeur pour moi. Je cherche son propriétaire...",
		["663706"] = "Non",
		["991431"] = "Pierre d'Heggie",
		["331163"] = "C'est impossible !",
		["884043"] = "Elle est à moi !",
		["221293"] = "La bénédiction du Saint-Père est indubitable ! Si vous voulez partir sain et sauf, je vous conseille de ne pas causer de problèmes.",
		["9971364"] = "Mais sous la lumière de Terrebénie, cela ne dissipera pas la brume de mon cœur.",
		["663196"] = "Non",
		["553255"] = "Oui",
		["771092"] = "Va, je m'en occupe.",
		["331098"] = "...",
		["771077"] = "Norwood l'a emmenée dans les ruines près de la grotte.",
		["100610064168"] = "Impossible de l'ouvrir sans clé.",
		["9931005"] = "Gauche ou droite, obscurité ou lumière, bien ou mal... Tout est connecté d'une façon ou d'une autre dans ce monde. Tout a une fin inexorable, quelles que soient les incertitudes.",
		["773075"] = "Quitter",
		["552180"] = "",
		["221104"] = "Je sais que le masque te met mal à l'aise, mais je suis Norwood. Et tout comme toi, je suis un serviteur de l'Église.",
		["661310"] = "On dirait que... vous n'êtes pas encore contaminé par le Sendril, vous avez de la chance.",
		["667124"] = "Un autre fragment.",
		["221262"] = "Qu'y a-t-il sur ce terrain d'exécution ?",
		["9934003"] = "/t0",
		["991152"] = "J'ai entendu mes parents en parler il y a longtemps... Il y a eu une révolte un jour et beaucoup y ont perdu la vie.",
		["551132"] = "Oh, c'est vous, mon ami. Comment allez-vous ? Avez-vous trouvé le sablier ?",
		["552341"] = "Consomme 1 miel de condensation et 60 os d'Anthozoan. Voulez-vous continuer ?",
		["772003"] = "Retourner à la voiture ?",
		["791319"] = "Il semble que la malédiction des Marcions finira par trouver quiconque y est connecté. C'est peut-être ainsi que tu mourras après avoir offensé les Marcions.",
		["441243"] = "La poussière blanche. Il essaie de détruire le cadeau offert par Edith, le dernier espoir des Demiurges.",
		["332100"] = "Jenny, j'espère seulement pouvoir te revoir.",
		["661802"] = "Partez, vous ne trouverez rien qui puisse vous être utile.",
		["661801"] = "S'il vous plaît, non ! Allez trouver refuge auprès du pontife dans la cour de la Trinité, il n'y a aucun intérêt à rester ici !",
		["9924033"] = "/t0",
		["111046"] = "Nous devrions y aller le plus vite possible !",
		["661702"] = "Partez, vous ne trouverez rien qui puisse vous être utile.",
		["111088"] = "Jerry a disparu parce que les frères John... sont des monstres maintenant.",
		["663602"] = "Non",
		["663505"] = "Oui",
		["553154"] = "Quitter",
		["663503"] = "Oui",
		["558302"] = "",
		["662503"] = "Ouvrir la porte ?",
		["111069"] = "Jerry... Mon Jerry a disparu... Des monstres, tout le monde s'est transformé en monstre...",
		["663502"] = "Non",
		["9931004"] = "Terrence ! Tu n'es pas sérieux ?",
		["662501"] = "Ouvrir la porte ?",
		["661405"] = "Mon Dieu, je ne peux rien faire. Tout le monde mérite une chance de vivre une meilleure vie que la mienne.",
		["551379"] = "La vérité cachée cultive les intentions maléfiques ignorantes.",
		["441186"] = "Est-ce le prix que la Marcionne a mentionné ?",
		["9932034"] = "",
		["663323"] = "Azath",
		["558301"] = "",
		["663322"] = "Le code ?",
		["777046"] = "Reviens maintenant ! Cette lumière rouge te ronge l'esprit ! Vite !",
		["441120"] = "Merci, étranger, ça va beaucoup mieux.",
		["441300"] = "Humain, veux-tu faire affaire avec moi ? J'ai beaucoup de trésors.",
		["441144"] = "Ah, ah ! Un Héraut qui ignore les faits, peu importe. Au lieu de perdre votre temps ici, partez avant que quelque chose de terrible ne se produise.",
		["792382"] = "",
		["667126"] = "Une fois ces éléments assemblés, ça ressemble à la rainure sur la statue de l'épée divine dans la cour de la Trinité... Il pourrait y avoir une connexion, non ?",
		["661325"] = "Attends, il pourrait y avoir plus d'indices.",
		["9961044"] = "S'il m'arrive quoi que ce soit, j'aimerais que tu partes et que tu emmènes Viola.",
		["663241"] = "Non",
		["333906"] = "Non",
		["9963027"] = "Quitter",
		["661505"] = "Mon Dieu, je ne peux rien faire. Tout le monde mérite une chance de vivre une meilleure vie que la mienne.",
		["441125"] = "Personne ne se soucie d'un vieux bonhomme comme moi... J'aimerais tellement retourner à Terrebénie...",
		["9961020"] = "Mais penses-tu vraiment pouvoir la faire changer d'avis ?",
		["552165"] = "Peut-être sont-ils tous tombés dans un profond sommeil…",
		["661223"] = "Il y a assez de crétins dans le monde, alors reprends-toi.",
		["661214"] = "Alors, trouve un moyen d'ouvrir ce fichu portail !",
		["552116"] = "",
		["111105"] = "Allez chercher la pierre de larme à la statue de la mine. Seule cette pierre peut vous permettre de faire fondre cette coquille dure.",
		["221227"] = "Je suis reconnaissante pour tout ce que tu as fait pour moi. Mais regarde-toi, maintenant. Ton avidité infinie t'a transformé en cadavre ambulant, en pleine descente dans l'obscurité de l'abysse. Dharam, tu dois t'en défaire... Tu es... complètement corrompu.",
		["791279"] = "As-tu trouvé plus d'argile ?",
		["665189"] = "Mon Dieu, que puis-je faire d'autre pour vous ? Pardonnez nos offenses.",
		["9956021"] = "",
		["551266"] = "Son but ultime est d'entrer dans le Tombeau des Colosses... Elle veut se venger de l'Église et détruire Terrebénie...",
		["111086"] = "Étranger, si vous trouvez mon Jerry, je vous en serais très reconnaissante.",
		["551410"] = "...",
		["551153"] = "Ah, ah... Je survis dans ce monde d'une autre façon.",
		["663403"] = "Oui",
		["667136"] = "Elle a consacré sa vie à sa foi, mais celle-ci l'a assaillie.",
		["554180"] = "/t0",
		["117124"] = "Oh oh, je retire ce que j'ai dit. Heggem est déjà en ruine.",
		["331192"] = "Mais pour quelle raison ?",
		["221117"] = "Ce n'est pas loin d'ici, mais le portail est scellé par une sorte de magie. C'est impossible d'entrer.",
		["771107"] = "Agnes... Agnes... Pourquoi ce nom me reste-t-il dans la tête ?",
		["661115"] = "Erm, l'horreur putride du Sendril... Ça me rend nerveux.",
		["555251"] = "Supérieur miséricordieux, j'espère que vous entendez ma prière...",
		["661106"] = "Je veux trouver la vérité, mais regardez-moi... La chercher, c'est demander à mourir.",
		["661105"] = "Tout le monde à Terrebénie semble le croire, mais le pouvoir des Piliers saints a commencé à s'éteindre il y a plus de dix ans... Moi, un aveugle, j'ai été le seul à le remarquer. Tout le monde était confus par cette illusion que véhiculait l'Église.",
		["991201"] = "Ha, ha, ha... J'ai détruit le navire des fous... Maintenant ils ne pourront plus partir... Impose la volonté du seigneur Ebbinghaus... Fais-leur payer le prix de leur arrogance...",
		["664160"] = "/t0",
		["661155"] = "Ça n'a rien à voir avec moi...",
		["663153"] = "<color=#DBBA7FFF>Utiliser Basilic béni</color>",
		["662152"] = "",
		["551179"] = "L'obscurité sous la lumière sainte, le Sendril dont vous parlez, sont-ils vraiment responsables ?",
		["9933021"] = "Quitter",
		["30135237"] = "S'il vous plaît ! Arrêtez, le Sendril a été emporté.",
		["331112"] = "C'était ma partenaire... Nous sommes venus dans cet endroit désolé pour trouver ce lâche. Mais nous ne savions pas ce qui nous attendait.",
		["9936039"] = "",
		["662139"] = "",
		["331156"] = "Qu'est-ce que c'est ?",
		["9941023"] = "Une étincelle suffit pour allumer un feu.",
		["552281"] = "",
		["334186"] = "/t0",
		["551438"] = "Ichthyosauria ?",
		["442320"] = "Aller à Terrebénie ?",
		["991066"] = "Qu'est-ce que c'était ce navire des fous ?",
		["9966053"] = "",
		["9951047"] = "J'espère que le fantôme des Marcions ne nous hante pas...",
		["111057"] = "Fais attention.",
		["9961045"] = "Terrence, ne sois pas si glauque. Tu sais que je ne suis pas doué pour ces situations où les émotions ne peuvent pas se mesurer en unités monétaires.",
		["777031"] = "Ne fais pas de bruit... Et fais attention à ne pas réveiller le loup.",
		["9961042"] = "Glenn, je risque d'avoir besoin de ton aide.",
		["441293"] = "Personne ne le sait... Les secrets dissimulent d'autres mystères, et la plupart des personnes concernées ne sont plus de ce monde.",
		["9966039"] = "",
		["334188"] = "/t0",
		["441148"] = "Les mensonges sont toujours exagérés, ah, ah...",
		["9961037"] = "Et je remets Dieu en question. Pourquoi mène-t-il tout le monde à l'adversité ?",
		["9961035"] = "Depuis que je t'ai rencontré, je repense sans cesse à l'époque où j'étais avec Teresa.",
		["9963036"] = "Quitter",
		["772264"] = "",
		["551398"] = "Merci quand même, Héraut. Je respecte votre choix, mais j'en suis navré... Il semble qu'avant la destruction de ce monde, je n'aie pas d'autres choix que de rester ici pour trouver un autre moyen. N'oubliez pas que le fragment reste la clé pour tout.",
		["222294"] = "",
		["664230"] = "/t0",
		["996464"] = "",
		["114142"] = "/t1",
		["668302"] = "",
		["221130"] = "Est-ce le Saint-Père qui t'a enfermé ici ?",
		["111047"] = "Au fait, j'ai trouvé ça sur le pont là-bas.",
		["444149"] = "Cette porte semble être coincée.",
		["551387"] = "La vérité cachée cultive les intentions maléfiques ignorantes.",
		["554777"] = "Impossible de l'ouvrir de ce côté.",
		["9943013"] = "Quitter",
		["991125"] = "Comment sais-tu tout cela ?",
		["331065"] = "Ce n'est pas le moment d'être sentimental... Allons examiner la chapelle.",
		["441116"] = "L'histoire est révolue. Le destin est condamné. Cette fausse foi ne fait que retarder la destruction inéluctable.",
		["553288"] = "Quitter",
		["992001"] = "Quitter Ichthyosauria?",
		["221249"] = "Regardez autour de vous, c'est moi qui les ai toutes sauvées ! C'est moi qui perpétue la lignée d'Adamina !",
		["221129"] = "Le Saint-Père nous a ordonné d'attraper tous les visiteurs d'Adamina. Je savais que ce n'était pas bien, et au début, personne ne voulait le faire, mais nous nous sommes habitués et plus personne ne s'y oppose.",
		["9922035"] = "",
		["663321"] = "...",
		["551282"] = "Désolé, je ne fais pas de remises, mais mes produits valent leur prix.",
		["461003"] = "Nous sommes tous dans le même bateau, je veux juste te donner son message...",
		["221108"] = "Tu veux dire que l'épidémie des Colosses est liée à cette sorcière rousse ?",
		["991017"] = "Tousse... Tousse... Vite... Tousse... Entrez dans la grotte.",
		["554001"] = "Impossible d'entrer.",
		["221116"] = "Le site de l'oracle ?",
		["331078"] = "Allons... à la chapelle.",
		["221254"] = "闭上你的嘴！",
		["225187"] = "C'est le formidable Saint-Père qui continue la lignée d'Adamina...",
		["551310"] = "Réinitialiser les attributs du personnage ?",
		["555170"] = "/t0",
		["551433"] = "La vérité cachée cultive les intentions maléfiques ignorantes.",
		["773193"] = "Lui donner un bourgeon rose luisant.",
		["9973451"] = "Parler",
		["991432"] = "Pierre d'Heggie",
		["554165"] = "/t0",
		["552154"] = "",
		["551386"] = "Vengeance indulgente... convergence passionnée... tentative dangereuse...",
		["774033"] = "Si tu ne me laisses pas sortir, je te promets de te poursuivre !",
		["551383"] = "Ah... l'obsession ardente... Elle nous a tous aveuglés, n'est-ce pas ?",
		["9931008"] = "Arrête ces petits tours inutiles... Dis-moi s'il y a des indices à Exilium.",
		["221056"] = "Si elle est déjà morte, nous ne pouvons rien faire.",
		["551123"] = "Est-ce l'objet dont vous parliez ?",
		["661224"] = "Teresa était juste devant moi et j'ai encore échoué. Je n'aurais peut-être pas d'autre...",
		["551374"] = "Les faibles ne remettent jamais les mensonges en question.",
		["111101"] = "Il y a quelqu'un ? À l'aide ! Je suis prisonnier dans cet œuf ! Au secours !",
		["441206"] = "Vous me semblez intelligent, j'espère que votre jugement sera le plus raisonnable.",
		["991451"] = "Pierre de Colosse",
		["9911020"] = "无论做出什么样的选择，我们的灵魂还是会重归黑暗……",
		["551408"] = "...",
		["773244"] = "Parler",
		["771216"] = "Si ce jour devait venir, tu recevras de mes nouvelles. Mais promets-moi de revenir avec Teresa.",
		["112907"] = "Retourner à la voiture ?",
		["9921004"] = "Qui sait... Je perds déjà la notion du temps dans cette obscurité infinie.",
		["551393"] = "Je ne pensais pas vous rencontrer ici. Vous êtes sans doute comme moi, vous n'avez pas trouvé ce que vous désiriez.",
		["773076"] = "Où est Teresa ?",
		["773204"] = "Parler",
		["441203"] = "Héraut, il n'y aura pas de seconde chance pour les Demiurges cette fois-ci. Pour sauver Vaillant de Terrebénie, nous avons déjà tout donné !",
		["551281"] = "Généreux Héraut, je te souhaite de réussir.",
		["551355"] = "La vérité cachée cultive les intentions maléfiques ignorantes.",
		["444228"] = "Impossible de l'ouvrir pour l'instant.",
		["551350"] = "Les faibles ne remettent jamais les mensonges en question.",
		["223113"] = "Quitter",
		["771199"] = "Tu vas te faire tuer. Il n'y a rien à y gagner.",
		["221171"] = "As-tu trouvé ta maman ?",
		["551396"] = "Le fragment de la météorite écarlate que vous avez... c'est la clé. Le moment venu, vous en aurez besoin. Merci de votre promesse, mais vous devez patienter. Je serai à la hauteur de votre confiance.",
		["114146"] = "/t1",
		["551234"] = "Qu'est-ce qu'il vous est arrivé exactement ?",
		["443188"] = "Quitter",
		["441225"] = "Cela ne vous regarde pas...",
		["113077"] = "<color=#DBBA7FFF>Pourquoi avez-vous quitté la mine ?</color>",
		["551188"] = "Écoutez-moi, faites ce que vous pouvez. Au-delà, vous ne trouverez qu'une douleur atroce que vous endurerez dans un cycle éternel de péchés.",
		["4994993019"] = "Donner vos matériaux",
		["333902"] = "Non",
		["1001100015"] = "Vide ton esprit désormais ! Il est temps de danser !",
		["331119"] = "Madame, je suis navrée de vous décevoir... Je n'ai pas beaucoup de temps... Albertus... Albertus... Votre nom ne peut être pardonné. Tous vos péchés seront ensevelis dans cette terre d'exilés !",
		["331074"] = "Tu sembles être emplie de doutes.",
		["9999291"] = "Mais se livrer à l'obsession ne mène pas toujours à une fin heureuse...",
		["441331"] = "Tout va bien ?",
		["991117"] = "C'est parce que trop de temps s'est écoulé sans que vous ne buviez de l'eau de la rivière des Âmes, vous avez donc complètement oublié le passé.",
		["224170"] = "/t0",
		["222189"] = "",
		["551313"] = "Qui êtes-vous ?",
		["775118"] = "Non... aidez-moi... Qui va... me sauver...",
		["771140"] = "C'est toi qui as forcé Viola à tirer sur Teresa avec cette balle ! C'est toi qui étais désespéré pour ouvrir le Tombeau des Colosses et provoquer ce chaos ! Alors, dis-moi pourquoi tu veux sauver Teresa maintenant !",
		["1004100076"] = "Mais vous les avez utilisés...",
		["9923043"] = "Changer de tenue",
		["551293"] = "Ah, ah... Je savais que ce serait mon jour de chance aujourd'hui ! Voici pour cette nouvelle amitié !",
		["551289"] = "Avez-vous entendu ça... les fantômes des Marcions... Ils rôdent aux alentours et murmurent dans le vent !",
		["551275"] = "Je peux enfin retourner à Terrebénie...",
		["9951031"] = "Tu devrais être reconnaissant d'avoir quitté l'Église et d'avoir maintenant un but dans la vie.",
		["332905"] = "Retourner à la voiture ?",
		["551273"] = "Ah, ah... Je savais que ce serait mon jour de chance aujourd'hui ! Voici pour cette nouvelle amitié !",
		["661240"] = "Quoi ? Ne sois pas si surpris, je n'aime simplement pas la lumière...",
		["331144"] = "En effet, mais l'Église n'a jamais cessé de fouiller Katib. D'après les rumeurs, c'est là-bas que se trouverait l'entrée du Tombeau des Colosses.",
		["551363"] = "La vérité cachée cultive les intentions maléfiques ignorantes.",
		["221303"] = "Qui a eu cette idée morbide ?",
		["228231"] = "",
		["9963039"] = "Bénir",
		["884045"] = "Ah... Quelque chose est en moi...",
		["334071"] = "/t0",
		["4994993007"] = "Non, merci.",
		["791340"] = "Ce bracelet a beaucoup de valeur pour moi. Je cherche son propriétaire...",
		["9961013"] = "Lorsque nous arriverons à Terrebénie, reste dans la voiture avec Glenn. Je n'ai pas besoin de toi.",
		["551392"] = "Héraut, arrêtez-vous un instant, j'aimerais vous demander quelque chose.",
		["447084"] = "Eh bien... Nous ne pouvons pas attendre de mourir sans rien faire. Prendre le risque de trouver cet homme pourrait être le moyen le plus facile d'accéder à Terrebénie.",
		["9971308"] = "Quel souhait ?",
		["661101"] = "Où puis-je aller ? Héraut, cet endroit n'est que Sendril et massacre, où est-on en sécurité ?",
		["30134240"] = "Teresa, où étais-tu ?",
		["551262"] = "Si nous la laissons retourner à Terrebénie ainsi, toutes les tragédies qui se sont produites ici seront répétées, en pire...",
		["554258"] = "/t0",
		["791303"] = "Je suis content pour toi.",
		["663704"] = "Non",
		["991411"] = "Il n'y a aucune réaction",
		["551244"] = "J'ai vu quelque chose... concernant Teresa... elle tenait le Sceau de Yekub... et des Chevaliers étaient agenouillés devant elle.",
		["441228"] = "Je vais sonner la cloche de la dernière tour. Vous saurez où me trouver ensuite.",
		["991074"] = "Merci, bon étranger. Vous m'avez aidé à la trouver avant que je n'abandonne. Ce devait être ces hommes sur le navire des fous... ils lui ont joué un tour si cruel...",
		["662401"] = "Ouvrir la porte ?",
		["551126"] = "Je peux enfin retourner à Terrebénie...",
		["112077"] = "",
		["331157"] = "Tu en auras besoin pour rester lucide dans la brume.",
		["555253"] = "Arrêtez, je vous en supplie... Laissez-moi partir...",
		["447304"] = "Ce doit être le portail derrière le village dont Frollo a parlé. Ouvre-le avec la clé.",
		["551116"] = "Héraut, nous nous retrouvons. Désolé, je pensais à mon épouse à Terrebénie. Elle me manque, même si j'ai oublié son visage... Ah oui, avez-vous trouvé le sablier ?",
		["9971341"] = "Il y une inscription étrange dessus...",
		["223106"] = "Pourquoi tous les Disciples ici portent-ils l'odeur du Sendril ?",
		["551276"] = "Ces Malfes saisissent toute occasion de faire affaire.",
		["1006100083"] = "Depuis ce moment, vous avez perdu quelque chose...",
		["221138"] = "Monsieur le Héraut ? Vous êtes vivant ? Non, non, non... Vous deviez mourir ! Je ne verrai maman que si vous êtes mort ! Où est Elmar ? Où est-elle ?",
		["551337"] = "La vérité cachée cultive les intentions maléfiques ignorantes.",
		["9971365"] = "Tous les efforts finiront par être récompensés, même dans ton cœur.",
		["553366"] = "Oui",
		["551270"] = "...",
		["995015"] = "Tousse... Tousse...",
		["9999295"] = "Dans Terrebénie, les enfants nés des Défigurés sont appelés les « rôdeurs nocturnes »... Mais c'était il y a longtemps. Je préfère ne pas en parler.",
		["661151"] = "Héraut, j'espère que ce que j'ai fait pourra sauver les survivants... J'ai résisté à mon destin toute ma vie... Je n'ai pas pu choisir comment naître, mais je peux au moins choisir comment mourir.",
		["554178"] = "/t0",
		["551160"] = "Lorsque le Colosse a commencé à fusionner avec la Tour sacrée, les sorcières marcionnes ont endormi tout le monde... et ça n'a pas été facile de les réveiller de ce rêve.",
		["441196"] = "C'est votre dernière chance d'aller à Terrebénie. M'aider est votre sésame, Héraut.",
		["557169"] = "C'est bizarre. Ça dit : « La pierre du passé ».",
		["113207"] = "Non",
		["661311"] = "Toc, toc (on frappe à la porte)",
		["334224"] = "/t1",
		["551165"] = "Mais ils ont décidé de conserver ce secret dans la Tour sacrée pour toujours, donc personne n'en est jamais sorti…",
		["9932012"] = "",
		["111056"] = "Séparons-nous, tu vas au sommet de la montagne et je vais examiner Heggem.",
		["991042"] = "Vous êtes arrivés trop tard. Il ne reste plus rien... Ni même souvenirs ou espoir... Ha, ha, ha... Hein ?",
		["441245"] = "C'est un ancien rituel de la sorcière marcionne qui stimule l'immense pouvoir que nous portons tous en nous et permet aux Demiurges de rejoindre Edith.",
		["554129"] = "/t0",
		["445090"] = "De la lumière ! Plus de lumière ! Laissez-la briller !",
		["221101"] = "那个小女孩是个彻头彻尾的骗子！我早有预感！",
		["551154"] = "J'étais prêtre à l'Église, en chair et en os, mais c'était il y a bien longtemps. C'est assez étrange de rester sous forme humaine ces temps-ci.",
		["662196"] = "Souhaitez-vous continuer ?",
		["771229"] = "Ah bon ?",
		["553138"] = "Je l'ai trouvé.",
		["551345"] = "Les personnes médiocres sont toujours obsédées par la grandeur.",
		["551137"] = "J'espère que vous oublierez cette idée, autrement... la suite ne sera pas aussi plaisante, mon ami.",
		["9931006"] = "Je suis navré, mais j'ai gagné. Je t'ai dit que le résultat était déjà déterminé. J'espère que tu es prêt à affronter la vérité.",
		["553135"] = "Non.",
		["662320"] = "",
		["551131"] = "...",
		["111119"] = "Est-elle toujours ici ?",
		["551127"] = "Patience, mon bon ami, patience...",
		["113071"] = "<color=#DBBA7FFF>Que se passe-t-il ici ?</color>",
		["9933033"] = "Est-ce que tu as de nouveaux articles ?",
		["331145"] = "Elle chercherait donc aussi le Tombeau des Colosses ? Pourquoi ?",
		["441187"] = "La Marcionne ?! Que vous a-t-elle dit ?!",
		["993257"] = "Quitter",
		["442063"] = "",
		["441260"] = "Les Demiurges connaissent-ils ces conséquences ?",
		["331223"] = "Ni échange ni remboursement possible. Bon voyage.",
		["9963032"] = "Commercer",
		["4994991010"] = "Avance sans défense dans la nuit noire et tu te feras mettre en pièces... Peut-être devrais-tu trouver quelque chose qui éclaire dans l'obscurité ?",
		["9921039"] = "Qui est l'homme qui porte un masque ?",
		["554086"] = "/t0",
		["221261"] = "Va à Exilium, Terrence. Tu y trouveras ce que tu cherches.",
		["771205"] = "Tu as fait tout ce que tu pouvais.",
		["551315"] = "Pourquoi êtes-vous dans la Brume sombre ?",
		["441298"] = "Combien de temps cela prendra-t-il ?",
		["991003"] = "Oh... Je croyais que tu allais nourrir les poissons.",
		["553903"] = "Oui",
		["791316"] = "Les temps ont changé, Terrence. Pour l'Église, l'hérésie n'est peut-être plus une si grande menace. Au départ, mon maître ne voulait pas me donner cette main, jusqu'à ce que je coupe la mienne...",
		["664083"] = "/t0",
		["225280"] = "Oh, ça ne suffit pas, non, ça ne suffit pas... Arrête de crier. Bientôt... la douleur cessera.",
		["9951052"] = "Katib est simplement un trésor inépuisable pour l'Église.",
		["225081"] = "J'ai perpétué la lignée du Saint-Père.",
		["9924034"] = "/t0",
		["441250"] = "Non, ce n'est que le début...",
		["9951038"] = "Je ne suis pas qu'un soldat de l'Église, j'obéis à mon maître. Je ne dirai rien jusqu'à ce que nous découvrions la vérité.",
		["337049"] = "Comme je le pensais, cet endroit n'est plus rien qu'un paradis pour la mort et le Sendril.",
		["9951030"] = "C'est ainsi que fonctionne l'Église. Elle manipule ses fidèles pour les utiliser.",
		["441270"] = "Vous me décevez. Je vous ai donné une chance, mais vous avez pris la mauvaise décision. Le rituel a été réalisé. Êtes-vous prêt à endurer le courroux des Demiurges ?",
		["9951029"] = "...",
		["113903"] = "Oui",
		["331160"] = "Et si elle était toujours en vie ?",
		["9951018"] = "Si tu me demandes de me confesser auprès de Dieu, tu perds ton temps.",
		["441099"] = "...",
		["9951017"] = "Terrence, je suis aveugle depuis longtemps, mais je vois encore ton visage chagriné. Rien n'a changé.",
		["441278"] = "Sapristi ! Cette femme a commencé le rituel en avance, Edith va renaître !",
		["111087"] = "Oh, c'est le manteau de Jerry. Mon Jerry, tu es revenu.",
		["115089"] = "Mon cher Jerry, maman va te ramener à la maison. Maman va rester avec toi pour toujours.",
		["991456"] = "Pierre d'Heggie",
		["338230"] = "",
		["444269"] = "/t0",
		["448312"] = "",
		["448311"] = "",
		["331063"] = "J'imagine que c'était magnifique...",
		["9911018"] = "咳……咳……它还在等待，它总是在等待……",
		["117127"] = "Regarde ! Le voilà. Il y a des Malfes qui fouillent le village. Ils semblent avoir accumulé une grosse fortune.",
		["224047"] = "/t1",
		["551425"] = "...",
		["661219"] = "Tu vas trop loin !",
		["771108"] = "Ah... Vous êtes de retour, avez-vous changé d'avis ? Si vous voulez sortir d'ici, libérez-moi de mes chaînes.",
		["331193"] = "Si ce que tu dis est vrai, alors l'entrée du Tombeau des Colosses est à Katib. C'est la prochaine destination de Teresa.",
		["447158"] = "L'homme du clocher semble être notre clé pour entrer dans Terrebénie.",
		["552287"] = "",
		["9931021"] = "Non, pas du tout.",
		["551150"] = "Oh, c'est étrange... Y a-t-il encore des gens normaux à Katib ou est-ce juste des gens qui semblent normaux ?",
		["118150"] = "",
		["221245"] = "Qu'a-t-elle pris exactement ?",
		["665194"] = "Pardonne-moi, Vaillant... C'est le sacrifice qui doit être fait.",
		["114023"] = "Glenn, tu peux rester avec moi, mais tu dois te taire.",
		["221054"] = "C'est fini maintenant, n'aie pas peur.",
		["665083"] = "L'Église... doit payer... pour ce qu'elle a fait...",
		["445089"] = "Cette faible lumière deviendra éternelle et nous guidera vers la terre du néant...",
		["441290"] = "Voulez-vous dire que Vaillant n'a pas été accusé à tort ?",
		["4994992004"] = "Requiert de l'huile de baleine rance et 100 champignons spectraux",
		["551099"] = "Elle n'en pas d'autres... Ça a déjà été assez difficile d'obtenir celui-ci. Si vous pouvez m'aider à le retrouver, ce serait bien pour nous deux.",
		["221193"] = "Apportez-lui ceci, c'est son jouet préféré. Je ne veux pas qu'elle soit triste... Après avoir donné naissance à l'enfant du Saint-Père, maman reviendra.",
		["443080"] = "Quitter",
		["991515"] = "La seule chose que nous pouvons faire, c'est d'attendre calmement et de finir par tout oublier. Heureusement, il me reste ces livres pour m'occuper.",
		["661116"] = "Pourquoi le pontife ne fait-il pas une apparence ? Personne ne se souvient de la dernière fois qu'il est sorti de sa résidence...",
		["221106"] = "Pourquoi tous les Disciples ici portent-ils l'odeur du Sendril ?",
		["337081"] = "Il est encore en vie !",
		["663404"] = "Non",
		["1007100095"] = "À qui appartenait cette voix ?",
		["331224"] = "J'ai rencontré d'autres Hérauts pingres comme toi, et je suis navré de dire qu'ils sont tous morts.",
		["994888"] = "Impossible de l'ouvrir pour l'instant.",
		["991085"] = "C'est déjà de l'histoire ancienne... Depuis longtemps oubliée... Tout ici est tombé en ruines. Peut-être que ces hommes en haut sont les seuls à connaître la réponse...",
		["221306"] = "Ce « miracle » semble avoir eu un effet secondaire étrange. Il a entraîné une augmentation énorme de la mortalité des hommes. Mais ce n'est pas forcément négatif pour le Saint-Père...",
		["224900"] = "/t0",
		["9933036"] = "Quitter",
		["221309"] = "Tu es libre...",
		["441165"] = "S'il fait sonner toutes les cloches, au lieu de s'éveiller, les Demiurges retourneront dans l'abysse des ténèbres...",
		["541002"] = "Qu'est-ce qu'elle cherche ? Est-ce que tu me caches encore des choses après tout ce temps ?",
		["552132"] = "",
		["441109"] = "Encore un long moment à attendre, je ne sais pas quand ils reviendront. J'espère juste qu'ils ne me feront pas attendre toute ma vie comme leur père.",
		["9941035"] = "Après tant d'années, c'est la première fois que je demande l'aide de Dieu.",
		["991104"] = "Je me sens si désespéré maintenant. C'était peut-être mon peuple jadis, mais je n'ai aucun souvenir d'eux.",
		["662701"] = "Ouvrir la porte ?",
		["9972455"] = "",
		["225183"] = "Saint-Père, je suis prête à aller au Purgatoire pour toi.",
		["441141"] = "Elena ?",
		["111082"] = "J'ai volé le trésor... pour trouver mon Jerry. J'ai juré de le rapporter dès que je l'aurais retrouvé, mais je ne l'ai trouvé nulle part. Étranger, pouvez-vous m'aider ?",
		["668303"] = "",
		["9971470"] = "Enfin, sous la Tour sacrée de Katib, derrière le portail imprégné du pouvoir du Sendril se trouve cette vérité cachée.",
		["9953042"] = "Commercer",
		["551120"] = "En effet, c'est une trivialité. Parfois même les personnes les plus chanceuses manquent de chance. Tout comme je ne sais jamais si je vais rencontrer cette maudite sorcellerie de Marcions ici.",
		["9971388"] = "Après tout, Dieu n'est qu'un concept qui a été créé sans avoir besoin d'être visible, c'est ainsi que les humains ajoutent du sens à leurs inepties. A-t-on déjà réfléchi aux raisons qui auraient poussé Dieu à créer un monde maudit ? Il y a toujours une raison, mais certaines choses ne sont plus comprises.",
		["799315"] = "Peut-être, mais je n'entretiens pas d'espoir. Fais vite, Héraut. N'entre pas doucement dans cette nuit sombre, rugis contre l'évanouissement de la lumière.",
		["9956058"] = "",
		["111098"] = "(L'œuf gigantesque sent vos pas et tremble.)",
		["991421"] = "Pierre de nature humaine",
		["9961021"] = "Je ne sais pas, mais je dois essayer.",
		["791359"] = "Lorsque j'ai quitté l'orphelinat de l'Église, j'ai cru faire preuve de courage, mais je me rends compte que je n'ai jamais pu laisser le passé derrière moi.",
		["664161"] = "/t0",
		["9933035"] = "Est-ce que tu as de nouveaux articles ?",
		["461004"] = "Je sais ce que je fais...",
		["115066"] = "Jerry... Où est mon Jerry... Mon Jerry...",
		["228233"] = "",
		["661180"] = "Personne ne l'a vu depuis la condamnation de Vaillant. Seul un gémissement douloureux se fait entendre depuis les profondeurs de Terrebénie. Cela fait plus de dix ans maintenant.",
		["333158"] = "Pourquoi es-tu toujours ici ?",
		["441071"] = "Arf... Il pensait que la peur ferait reculer les Demiurges, il avait tort !",
		["771147"] = "Terrence, je sais que tout cela est difficile à accepter... Mais le plus important maintenant, c'est d'atteindre le fond de la grotte et de sauver Teresa. Je t'expliquerai tout après.",
		["441204"] = "Je dois aller au prétoire où quelque chose de plus important m'attend.",
		["112111"] = "",
		["224888"] = "Impossible de l'ouvrir pour l'instant.",
		["9941020"] = "Penses-tu qu'ils puissent nous aider ?",
		["554138"] = "/t0",
		["331099"] = "Héraut, pouvez-vous me rendre un service ? J'ai perdu mon alliance dans la chapelle. Lorsque ma fiancée reviendra, je veux réciter mes vœux, échanger les anneaux et la remercier pour son aide. Pouvez-vous m'aider à trouver cet anneau ?",
		["111053"] = "La personne que tu cherches ?",
		["551136"] = "Vous plaisantez ? Je ne manque pas tant de chance ! Voulez-vous garder ce sablier pour vous, c'est ça ?",
		["9961048"] = "Comment penses-tu que j'ai survécu jusqu'à présent ? Personne ne comprend mieux Terrebénie que moi. Si tu veux survivre, emmène-moi.",
		["221069"] = "Encore un lâche.",
		["221206"] = "Béni soit le Saint-Père ! Merci de me laisser porter ta progéniture..",
		["9921023"] = "Qu'est-il arrivé entre vous ?",
		["111052"] = "...",
		["225232"] = "看看你们都干了什么好事！",
		["333221"] = "Quitter",
		["551164"] = "Ils sont tous retournés à la Tour sacrée et le terrible secret sous la Tour a été découvert.",
		["443105"] = "Oui.",
		["991219"] = "Je n'ai pas le temps à présent. Tu devrais trouver un endroit sûr où te mettre à l'abri.",
		["551397"] = "Merci de votre aide, je vous en suis reconnaissant, mais je ne suis pas d'humeur à faire une bonne action.",
		["553105"] = "Je l'ai trouvé.",
		["551271"] = "Oh, c'est vous, mon ami. Comment allez-vous ? Avez-vous trouvé le sablier ?",
		["791325"] = "Ne t'inquiète pas, c'est maintenant une arme « authentique ». Sa haine lui donne un flot régulier de puissance.",
		["112067"] = "",
		["9943027"] = "Quitter",
		["441285"] = "Pourquoi ne pas me dire tout maintenant ?",
		["9971282"] = "J'ai trouvé de l'argile... j'espère que ça t'aidera.",
		["551280"] = "Ça fait un certain temps que je n'ai vu personne. Peut-être que je peux offrir une remise.",
		["111099"] = "(L'œuf gigantesque sent vos pas et tremble.)",
		["1004100112"] = "Nous savons que les Demiurges n'ont besoin que d'une voix unique.",
		["112103"] = "",
		["994478"] = "/t0",
		["9932020"] = "",
		["551323"] = "Quiconque poursuit une obsession peut mettre fin au complot de la lumière et de l'obscurité une fois enterré.",
		["881080"] = "L'ignorance et la convoitise convergent et se heurtent... Habitué à avoir la tête haute, l'incliner ne semble plus acceptable.",
		["441332"] = "Pardonne-moi... J'étais perdu dans mes pensées de ces vagues tranquilles... Je ne m'attendais pas à trouver la météorite écarlate d'Ichthyosauria en quelques mois. Toutes les difficultés que nous avons surmontées ont enfin porté leurs fruits.",
		["227120"] = "J'ai trouvé Viola, mais un pouvoir étrange m'a empêché d'avancer... Je n'ai pas pu m'approcher d'elle.",
		["551236"] = "Ce n'est pas une façon de répondre à quelqu'un qui se fait du souci.",
		["557242"] = "Quelque chose a été conservé pour être vénéré ici.",
		["551138"] = "Est-ce l'objet dont vous parliez ?",
		["665131"] = "Vous ne pouvez pas nous arrêter... Les Demiurges reprendront tout ce qu'ils ont perdu...",
		["331148"] = "Ça me dit quelque chose...",
		["441074"] = "Héraut, cet homme est ma proie, laissez-moi m'en occuper.",
		["446304"] = "",
		["223315"] = "Lui donner du Sendril actif.",
		["551243"] = "Ça va ?",
		["9971354"] = "C'est le jour de ma naissance.",
		["223191"] = "Quitter",
		["221190"] = "Un manteau vert ? J'ai vu votre fille.",
		["461005"] = "C'est mieux ainsi. Je suis un peu plus distrait ces derniers temps.",
		["111071"] = "Que se passe-t-il ici ?",
		["1001100016"] = "Il a l'air misérable...",
		["551263"] = "Pourquoi ?",
		["115140"] = "La douleur me permet de rester lucide...",
		["9999296"] = "Peut-être trouveras-tu un endroit où refaire ta vie ?",
		["661264"] = "Ha ha, Héraut innocent.",
		["111074"] = "Un Héraut a emmené les Disciples survivants à la mine. Il a dit qu'un trésor pouvait sauver Heggem.",
		["773225"] = "Quitter",
		["224169"] = "/t0",
		["222249"] = "",
		["221126"] = "Le Saint-Père m'a dit que tant que j'amenais des étrangers à Adamina pour finir la chasse, je pourrais revoir ma maman.",
		["9923044"] = "Quitter",
		["113206"] = "Oui",
		["551417"] = "Les faibles ne remettent jamais les mensonges en question.",
		["111080"] = "Mon cher Jerry, il est sorti pour jouer avec les frères John, mais ça fait deux jours qu'il n'est pas rentré. Il aime trop jouer.",
		["553395"] = "Refuser",
		["111081"] = "Comment vous êtes-vous protégée seule ici ?",
		["551440"] = "...",
		["552238"] = "",
		["9953046"] = "Quitter",
		["553272"] = "Je l'ai trouvé.",
		["661170"] = "Qu'est-ce qu'il s'est passé il y a quinze ans ?",
		["551360"] = "L'amour... C'est ce qui rend fou.",
		["991038"] = "Quoi qu'il arrive, j'espère pouvoir rencontrer ce fameux seigneur Ebbinghaus.",
		["228232"] = "",
		["111076"] = "Oui, la lumière que nous avons trouvée était assez brillante pour disperser le Sendril dans la mine. Nous nous sommes ensuite cachés en attendant que l'Église vienne nous secourir.",
		["551274"] = "Partons maintenant que tout est prêt. Avec la chance que nous avons eue aujourd'hui, nous devrions pouvoir trouver le Sceau de Yekub sous cette Tour sacrée.",
		["441205"] = "Les éclaireurs Demiurges sont déjà partis pour Terrebénie. Si nous ne parvenons pas à soutenir leurs efforts, leur sacrifice aura été en vain.",
		["9951024"] = "Ce qu'il s'est passé à Katib... Le massacre des Marcions... C'est ce qui t'empêche encore d'avancer ?",
		["9971309"] = "Quand j'ai reçu cette main de mon maître, il m'a dit que trois autres reliques avaient aussi été placées dans ce cercueil, mais qu'elles avaient été volées et avaient disparu. Je ne peux calmer la main qu'en trouvant ces reliques, autrement elle dévorera tout ce qui vit.",
		["551110"] = "Regarde-toi à te perdre dans tes pensées insignifiantes, ça ne t'apportera que mauvaise fortune.",
		["994016"] = "/t0",
		["661261"] = "Ha ha, Héraut innocent.",
		["113133"] = "Quitter",
		["661102"] = "…",
		["115142"] = "La douleur me permet de rester lucide...",
		["555271"] = "Ah... Je suis un imbécile... J'ai dépensé tout ce que j'avais... pour le poursuivre... en vain... Il ne me reste que mensonges et ridicule...",
		["111049"] = "Ce sont les mots de Terrebénie, mais on les trouve maintenant à Heggem.",
		["9961014"] = "Je ne vais pas rester ici à attendre de voir ce qu'il se passe.",
		["775034"] = "/t0",
		["4994993006"] = "Abandonner",
		["221111"] = "Le Saint-Père ? C'est aussi un Disciple d'Adamina ?",
		["994036"] = "/t0",
		["791338"] = "J'ai un objectif.",
		["441061"] = "...",
		["114127"] = "/t0",
		["552284"] = "",
		["223294"] = "Pas encore.",
		["664888"] = "Impossible de l'ouvrir pour l'instant.",
		["662901"] = "Retourner à la voiture ?",
		["111126"] = "Est-ce vous, mon bon monsieur ? Êtes-vous revenu pour me sauver ?",
		["113901"] = "Oui",
		["441156"] = "Nous ne pouvons pas le laisser sonner toutes les cloches, ça compliquerait le plan des Demiurges.",
		["991135"] = "Tout comme nous pensions tous que la rivière des Âmes ne tarirait jamais, jusqu'au jour où tout est soudainement devenu silencieux.",
		["9953021"] = "Bénir",
		["117095"] = "La lumière d'Heggem s'éteint doucement et le trésor est perdu. Ces pauvres Disciples se sont sûrement transformés en monstres.",
		["771253"] = "Je me souviendrais de ce que tu viens de dire.",
		["991512"] = "C'est de plus en plus difficile de trouver de l'eau pure de la rivière.",
		["661175"] = "Les morts ne peuvent pas parler, mais les Marcions sont inextricablement liés à tout ça. Personne n'est innocent. Lorsque vous commencerez à explorer la vérité, vous serez pris au piège.",
		["331058"] = "Il n'y a pas endroit plus proche de la mort sur terre.",
		["9931007"] = "Terrence, tu as perdu la tête !",
		["221200"] = "Je suis sur le point d'accoucher ! Ça fait mal !",
		["553359"] = "Oui",
		["9922041"] = "",
		["9961049"] = "...",
		["221163"] = "Ça devrait te faire plaisir.",
		["771239"] = "Prends soin de Viola.",
		["221172"] = "Elle m'a fait ses adieux...",
		["221228"] = "Tu ne me laisses pas le choix, dans ce cas ! Redonne-le-moi !",
		["111044"] = "Mais qu'est-ce qui le rend si faible ?",
		["222268"] = "",
		["111073"] = "Est-ce que certains des Disciples ont survécu ?",
		["443901"] = "Oui",
		["112205"] = "Replacer l'œuf du porteur ?",
		["553375"] = "Oui",
		["9942032"] = "",
		["111048"] = "Ce n'est qu'un message ordinaire de sorcière avec de l'alchimie de base, rien de spécial.",
		["551375"] = "Ah... l'obsession ardente... Elle nous a tous aveuglés, n'est-ce pas ?",
		["9973381"] = "Contrat",
		["9921036"] = "Il semble que la réponse se trouve à Adamina.",
		["993218"] = "Refuser",
		["9933017"] = "Commercer",
		["221223"] = "Teresa...",
		["9921018"] = "Donne-moi ce journal !",
		["9931031"] = "D'accord, je ne dirai plus rien.",
		["334050"] = "Des gardes de l'Église. Leurs corps sont desséchés et ils ont des coupures fatales dans le cou.",
		["553426"] = "Remplissez le flacon de Melos.",
		["226269"] = "",
		["991446"] = "Pierre de Malfe",
		["221314"] = "J'espère que vous ne serez pas blessé, mon bon Héraut, je me sentirais coupable.",
		["551343"] = "Ah... l'obsession ardente... Elle nous a tous aveuglés, n'est-ce pas ?",
		["333901"] = "Oui",
		["225082"] = "Ne vous approchez pas, sales étrangers !",
		["331179"] = "Dans ce cas, je préférerais retourner dans le passé.",
		["441114"] = "Ah, comme j'ai rêvé d'être baigné de la lumière radieuse de Terrebénie…",
		["552310"] = "",
		["778301"] = "",
		["551190"] = "Ce n'est rien de grave, je dois juste me reposer.",
		["9951040"] = "En quelque sorte, mon ami. Ne t'inquiète pas, je sais ce qui peut se dire ou non.",
		["771206"] = "C'est vrai ? Je n'ai même pas pu lui dire au revoir. Je ne sais même pas si c'était une bonne personne ou pas.",
		["551151"] = "Attendez... l'un de vous... essaie de cacher quelque chose, mais je sens le sang... vous étiez autrefois un soldat de l'Église... et vous avez un long passé à cet endroit...",
		["553351"] = "Oui",
		["441246"] = "La cloche de Frollo n'élimine pas seulement la poussière blanche, mais rend également quiconque a pris part au rituel fou.",
		["774035"] = "Ne le touche pas ! Je ne te laisserai jamais partir si tu lui fais du mal !",
		["334058"] = "/t0",
		["224123"] = "/t0",
		["553285"] = "Quitter",
		["441303"] = "Humain, veux-tu faire affaire avec moi ? J'ai beaucoup de trésors.",
		["991150"] = "Bien entendu, à Ichthyosauria, personne ne peut jamais être sûr de sa mémoire, alors ne vous en faites pas.",
		["222288"] = "",
		["9933034"] = "Que sais-tu sur Exilium ?",
		["9961022"] = "Et si tu échoues ?",
		["774041"] = "/t0",
		["443104"] = "Désolé, pas encore.",
		["331087"] = "Oh, je suis navrée, j'espère que mon apparence hideuse ne vous effraie pas. N'ayez crainte, j'étais moi aussi un Chevalier de l'Église.",
		["9941013"] = "En tant que Héraut, je ne peux même pas m'approcher de Terrebénie...",
		["551312"] = "Vous n'avez pas de vin étoilé",
		["441271"] = "Comme je l'avais promis, tout obstacle empêchant les Demiurges de récupérer la lumière sera anéanti.",
		["221308"] = "Nous n'avons pas d'autres options pour le moment, un peu d'aide ne nous fera pas de mal.",
		["443302"] = "Quitter",
		["665070"] = "Lorsque la sorcière marcionne trouvera le pontife, Vaillant sera enfin libre.",
		["9963014"] = "Quitter",
		["661216"] = "Pour l'amour du ciel, tu veux voir Teresa en vie ou non ?",
		["331154"] = "Benita, si nous trouvons le reste des fragments de la météorite écarlate, peux-tu la restaurer ?",
		["997208"] = "Des voleurs ? Regardez cet endroit ! Je n'imagine rien qui puisse valoir la peine d'être volé... Ne perdons pas notre temps, aucune de ces personnes ne semble normale.",
		["9921047"] = "Je suis un peu trop fatigué, je dois me reposer.",
		["9963026"] = "Parler",
		["553311"] = "Non",
		["441070"] = "On dirait que ce traître n'est pas si simple.",
		["999282"] = "Hé ! Avez-vous trouvé le levier de contrôle et la roue dentée ?",
		["9961039"] = "Mais la souffrance prendra fin. Peu importe ce qu'est devenue Teresa, je serai toujours là pour elle.",
		["447137"] = "La cloche sonne depuis cette tour. Nous devons y aller au plus vite tant qu'il est encore là !",
		["793312"] = "J'ai trouvé d'anciennes reliques.",
		["791282"] = "J'ai trouvé de l'argile... j'espère que ça t'aidera.",
		["991092"] = "Je ne peux pas vous aider... Prenez ceci, ça pourrait vous être utile.",
		["227119"] = "Dieu merci, Terrence, tu es en vie... C'est vraiment nauséabond ici...",
		["991044"] = "Allez-vous-en, il n'y en a pas assez pour partager. Vous n'obtiendrez rien tant qu'on n'apportera pas d'eau...",
		["447159"] = "L'homme du clocher semble être notre clé pour entrer dans Terrebénie.",
		["331189"] = "C'est... le Sceau de Yekub. On dit que c'est la clé du Tombeau des Colosses.",
		["771187"] = "Ah... L'avez-vous trouvé ? Cette lumière, ce goût...",
		["663332"] = "Quitter",
		["552373"] = "Consomme 1 miel de condensation et 180 os d'Anthozoan. Voulez-vous continuer ?",
		["991190"] = "Seigneur Handel, je suis désolé, je ne peux pas hériter de la gloire des Chevaliers.",
		["331168"] = "Ah... Je ne sais comment vous remercier, Héraut.",
		["113104"] = "<color=#DBBA7FFF>Comment puis-je vous aider ?</color>",
		["991086"] = "Qu'attendez-vous ?",
		["221290"] = "Merci beaucoup, Héraut.",
		["334051"] = "Des Chevaliers de l'Église avec des lances. Leurs armures rouillées grincent dans le vent, ils sont bien morts.",
		["100510052203"] = "Oui",
		["333220"] = "Commercer",
		["331221"] = "J'ai rencontré d'autres Hérauts pingres comme toi, et je suis navré de dire qu'ils sont tous morts.",
		["221107"] = "Suite aux indices de l'épidémie des Colosses, le Saint-Père a prédit qu'une cruelle sorcière rousse mènerait sûrement Adamina à sa perte.",
		["443065"] = "La révolte des Demiurges...",
		["771196"] = "Tu savais ce qui arriverait ? Mais tu l'as quand même mise en danger ?",
		["331184"] = "Si Teresa était devenue un monstre au final, est-ce que tu continuerais...",
		["993070"] = "Quitter",
		["773086"] = "Ton père...",
		["551358"] = "Les faibles ne remettent jamais les mensonges en question.",
		["9952017"] = "",
		["9931022"] = "Tu sembles avoir quelque chose contre Norwood.",
		["9931025"] = "C'est différent.",
		["994415"] = "",
		["331142"] = "Elle est entrée dans une zone interdite de Katib.",
		["551403"] = "...",
		["551238"] = "Il semble avoir été contaminé par le Sendril.",
		["113113"] = "Une sorcière étrange ?",
		["9953018"] = "Quitter",
		["224049"] = "Cette entrée semble verrouillée.",
		["441295"] = "C'est tout ce que je sais. Rendez-vous à Terrebénie, demandez à cette sorcière marcionne que vous cherchez depuis si longtemps. Je suis sûr qu'elle aura des réponses.",
		["661179"] = "Le pontife n'est pas venu ?",
		["331177"] = "Mais au moins, il a tenu sa promesse.",
		["113202"] = "Oui",
		["9963053"] = "Commercer",
		["991467"] = "Généreux Héraut, je te souhaite de réussir.",
		["9943040"] = "Le passé entre les Demiurges et l'Église ?",
		["111041"] = "Selon les légendes, c'est un signe qui indique que le Colosse se prépare pour son voyage jusqu'au Tombeau.",
		["661228"] = "Mais le pontife est mort, nous ne pouvons donc emprunter ni l'un ni l'autre.",
		["441162"] = "Vous êtes blessée ?",
		["661117"] = "Que Dieu vous bénisse, Héraut. J'espère que nous vous reverrons.",
		["441142"] = "La folle qui me poursuit. Elle a été nommée vice-grande prêtresse. Les Demiurges sont en train de causer leur propre destruction.",
		["441283"] = "N'oubliez pas notre arme secrète. Vite, c'est ma dernière requête. Je vous dirai tout si vous m'aidez dans cette tâche.",
		["9971389"] = "Les humains pensent que Dieu leur a donné la capacité de tout comprendre, mais sont-ils conscients de ce que représente « tout » ? Pour éviter l'incertitude, ils cherchent refuge dans la foi, c'est leur péché. La lumière du Colosse n'est pas la torche de la raison. Sans réflexion, l'homme n'est rien.",
		["223902"] = "Non",
		["991105"] = "Allons d'abord trouver ce garçon. Il pourrait savoir quelque chose.",
		["444320"] = "/t0",
		["117029"] = "Heggem est le premier Colosse à perdre sa lumière. Il ne peut plus arrêter la Brume sombre qui l'envahit. Où sont ses Disciples ?",
		["668301"] = "",
		["331181"] = "Je vais bien...",
		["551183"] = "Je veux trouver un moyen de mettre fin à ce rêve éternel, c'est la raison de ma venue...",
		["441077"] = "Quel est le problème avec cette cloche ? Elle semble vous faire du mal.",
		["663405"] = "Oui",
		["225080"] = "Bébé ! Mon adorable bébé !",
		["337084"] = "Presque, mais pas tout à fait.",
		["991094"] = "Ha, ha, enfin ! C'est un miracle !",
		["447085"] = "Edith a récupéré sa lumière ? Comment est-ce possible ?",
		["991240"] = "Ça suffit... Je comprends... Pouvez-vous... Dites-moi à quoi ça ressemble dehors... À quoi ressemble le ciel ?",
		["884057"] = "Tu n'es pas prêt pour la vérité.",
		["773393"] = "Non",
		["9971471"] = "Il y a tant d'indices, lequel suivre ?",
		["551394"] = "Si nous pouvons être tous deux libérés de ce cauchemar, m'aiderez-vous ?",
		["772244"] = "",
		["9943012"] = "Parler",
		["447092"] = "On dirait que la cloche perturbe l'esprit de tout le monde ici.",
		["442188"] = "",
		["441100"] = "Vous semblez encore nerveux, non ? Ah, ah... Puis-je vous demander de me rendre un service avant que vous ne partiez ?",
		["221148"] = "Faites attention, elle est un peu... grincheuse ces derniers temps. Ne l'agacez pas, je ne sais pas de quoi elle est capable.",
		["793382"] = "Contrat",
		["114038"] = "/t0",
		["9971455"] = "Tu penses à ce qu'il vient de se passer ?",
		["9931040"] = "Merci pour tes achats.",
		["9946046"] = "",
		["334227"] = "/t0",
		["441107"] = "J'ai terminé la grande mission de ma vie en élevant trois soldats Demiurges. Je peux enfin me reposer pendant un instant.",
		["551241"] = "Quelque chose a été conservé pour être vénéré ici.",
		["225233"] = "吼！",
		["991423"] = "Pierre de Malfe",
		["221135"] = "Non, c'est Elmar à Adamina. C'était la personne la plus gentille avec moi, à part ma maman. Elle m'a enfermée ici pour me protéger.",
		["9971307"] = "Ne t'inquiète pas, c'est à cause d'un souhait non satisfait qu'elle semble agitée.",
		["443120"] = "Lui donner une bougie graisseuse.",
		["9961047"] = "Je dois me préparer au pire.",
		["334180"] = "/t0",
		["661181"] = "Vous voulez dire que... ce gémissement, c'est le pontife ?",
		["9941041"] = "Ils ne peuvent s'en prendre qu'à eux-mêmes. Au lieu d'étudier les classiques à l'Abbaye divine, ils insistèrent pour étudier les Colosses avec le grand prêtre Vaillant.",
		["9973378"] = "Contrat",
		["227222"] = "C'est la cachette du Saint-Père. Le portail semble être scellé par une sorte d'ancien rituel.",
		["9962035"] = "",
		["9999304"] = "Veux-tu vraiment parier le sort de tout Solas ?",
		["551163"] = "Et les autres membres de l'Église ? Où sont-ils maintenant ?",
		["773008"] = "Non",
		["668304"] = "",
		["221059"] = "Adamina !",
		["793290"] = "Désolé.",
		["221057"] = "Elle est en vie, mais il reste peu de temps.",
		["551320"] = "Oh, mais il semble que vous ayez aussi une obsession. Elle est encore faible... Peut-être reviendrez-vous un jour...",
		["441155"] = "Héraut, je vous avertis à nouveau. C'est un problème pour les Demiurges. Restez en dehors de ça.",
		["114140"] = "/t1",
		["441259"] = "Mais ces personnes devinrent une partie de la conscience du Colosse, puis ses marionnettes.",
		["118148"] = "",
		["551325"] = "Quiconque poursuit une obsession peut mettre fin au complot de la lumière et de l'obscurité une fois enterré.",
		["551362"] = "Vengeance indulgente... convergence passionnée... tentative dangereuse...",
		["771089"] = "Étant donné ce que Norwood a fait, il reste peu de temps pour mon père.",
		["444179"] = "Impossible de l'ouvrir sans clé.",
		["9971283"] = "Merci... Je suis contente que tu n'aies pas oublié.",
		["441244"] = "Que fait cette poussière blanche ?",
		["661803"] = "Arrêtez ! Ne l'ouvrez pas !",
		["461006"] = "Tu devrais t'occuper de tes affaires.",
		["553415"] = "Remplissez le flacon de Melos.",
		["667073"] = "Elle est submergée par la volonté vengeresse des Demiurges. Terrebénie vaincue, la folle, c'est le cauchemar de tout homme.",
		["777040"] = "Que diable s'est-il passé ?",
		["991470"] = "Généreux Héraut, je te souhaite de réussir.",
		["991216"] = "Merci ! Les habitants d'Ichthyosauria vous en seront reconnaissants. Et puisque nous sommes amis maintenant, prenez ceci. Bonne chance.",
		["9971363"] = "Ces mots sont pour toi, cette personne ne t'a jamais oubliée.",
		["9971331"] = "Je vais enterrer cette main dans sa ville natale et la laisser reposer en paix. Peut-être que les Marcions suivront bientôt la voie des ancêtres et ne vivront plus que dans les légendes. Finalement nous disparaîtrons de l'histoire, sans laisser de trace.",
		["441286"] = "Croyez-vous aux coïncidences, Héraut ?",
		["9999293"] = "Assez. Les rôdeurs nocturnes ne peuvent plus vivre comme des êtres humains. Nous avons perdu le droit de choisir à la naissance.",
		["441337"] = "Mais Ichthyosauria ne peut pas attendre plus longtemps.",
		["663705"] = "Oui",
		["551338"] = "Poursuivre le sublime est la menace ultime de l'humain.",
		["331107"] = "Héraut ? Comment êtes-vous entré ici ? Le garde à la porte ne vous a-t-il pas vu ? Je ne veux pas de problèmes, laissez-moi !",
		["884006"] = "As-tu peur de la perdre ? Ou d'autres... secrets... plus sombres ? Je dois te dire la vérité... Et elle est terrifiante...",
		["9973458"] = "Je pense au pontife.",
		["9971462"] = "Elle te connaît juste trop bien.",
		["9971465"] = "Venant de Norwood, je n'aurais pas été surpris, mais Viola... Je ne comprends pas pourquoi elle était impliquée.",
		["9971466"] = "Je ne veux pas le croire non plus.",
		["9971467"] = "C'était donc la vérité que Terrebénie voulait enterrer depuis tout ce temps ? Un pontife contaminé par le Sendril ?",
		["114001"] = "Impossible d'entrer.",
		["442119"] = "Étranger, pouvez-vous m'aider ? J'ai... si froid, avez-vous des bougies ?",
		["444092"] = "/t0",
		["9973388"] = "Quitter",
		["9951045"] = "As-tu entendu parler du massacre à Katib ?",
		["662240"] = "Prêt à partir ?",
		["9976379"] = "",
		["111075"] = "L'avez-vous trouvé ?",
		["771260"] = "Ça fait un certain temps que je n'ai vu personne. Peut-être que je peux offrir une remise.",
		["227053"] = "Terrence, il semble que ta malchance te suive partout, tout comme les monstres.",
		["773261"] = "Commercer",
		["661103"] = "Héraut, ne pensez-vous pas que c'est assez étrange ? Cet endroit est protégé par la lumière éternelle des piliers et pourtant, il est trop faible pour résister à l'invasion du Sendril.",
		["552104"] = "",
		["771262"] = "Désolé, je ne fais pas de remises, mais mes produits valent leur prix.",
		["551414"] = "...",
		["221170"] = "Monsieur le Héraut, vous êtes sain et sauf.",
		["791304"] = "J'ai aussi fabriqué ceci. J'espère que ça te plaît.",
		["551346"] = "Vengeance indulgente... convergence passionnée... tentative dangereuse...",
		["771233"] = "J'espère que les choses pourront revenir à ce qu'elles étaient...",
		["777022"] = "Enfin, nous y sommes, et maintenant quoi ? Un autre fichu escalier... Parfois j'admire la rigueur et le formalisme des membres de l'Église...",
		["116131"] = "",
		["776264"] = "",
		["771264"] = "Généreux Héraut, je te souhaite de réussir.",
		["991115"] = "Jerold, pourquoi ne nous as-tu rien dit à ce sujet ?",
		["775035"] = "/t1",
		["774777"] = "Impossible de l'ouvrir de ce côté.",
		["9973275"] = "Contrat",
		["9953024"] = "Parler",
		["221478"] = "Et puis, il y a plein d'autres femmes comme ma maman qui ont besoin d'aide. Je veux faire quelque chose pour elles.",
		["663331"] = "<color=#DBBA7FFF>Placer le fragment.</color>",
		["9971278"] = "Mais je veux quand même la terminer. Si tu as de l'argile, donne-le-moi... Bien entendu, ce n'est pas urgent, il y a plus important à présent.",
		["771200"] = "Je n'ai rien à perdre...",
		["771220"] = "J'attendrai ce jour...",
		["221225"] = "Je ne fais que reprendre ce qui m'appartient.",
		["9971279"] = "As-tu trouvé plus d'argile ?",
		["9972279"] = "",
		["9973280"] = "J'ai trouvé de l'argile.",
		["9973281"] = "Désolé.",
		["331203"] = "Brave Héraut, je vous remercie pour votre geste. Permettez-moi de vous provoquer en duel de Chevaliers. Je vous attendrai à l'endroit où elle a péri. Au nom de ma bien-aimée !",
		["771101"] = "Attendez, vous n'êtes pas lui... Les vivants sont partis depuis... Avez-vous vu ce monstre ?",
		["9923035"] = "Parler",
		["333904"] = "Non",
		["9971284"] = "J'ai toujours été curieux : qui est cette figurine que tu crées ?",
		["994037"] = "/t0",
		["30135238"] = "Cachez-vous, vite ! Ne les laissez pas vous trouver !",
		["9971287"] = "Ce qui me perturbe, c'est que j'oublie son visage avec le temps... J'espère que je pourrais terminer avant de ne plus m'en souvenir du tout... C'est mon seul moyen d'expiation.",
		["557176"] = "Il y a des connexions entre le passé, le présent et le futur.",
		["4994993029"] = "Intérieur de la Brume sombre",
		["9971291"] = "Il semble que je puisse « voir » les gens que j'ai bannis dans la Brume sombre et ils essaient de m'entraîner.",
		["9971377"] = "As-tu trouvé plus d'argile ?",
		["221263"] = "Ignore-le, Terrence. Ses intentions ne sont pas bonnes.",
		["9973295"] = "J'ai trouvé de l'argile.",
		["9972311"] = "",
		["9971295"] = "Tu as presque fini ?",
		["553358"] = "Oui",
		["551388"] = "Poursuivre le sublime est la menace ultime de l'humain.",
		["9971297"] = "Il faut du courage pour voir la vérité dans la « foi ».",
		["771250"] = "Terrence, je ne t'ai jamais menti...",
		["9971298"] = "N'y a-t-il pas un équilibre ?",
		["9973301"] = "Contrat",
		["9953045"] = "Parler",
		["554269"] = "/t0",
		["9971305"] = "Merci... C'est la première fois...",
		["9961026"] = "Tu n'as pas l'air d'un homme sur le point de voir sa femme. Que veux-tu lui dire ? Y as-tu même songé ?",
		["111125"] = "Vous avez la même odeur que la sorcière ! Vous êtes avec elle !",
		["778302"] = "",
		["9971306"] = "Ce ne sera peut-être pas la dernière...",
		["9951041"] = "Qui est ton maître ?",
		["551272"] = "Est-ce l'objet dont vous parliez ?",
		["331182"] = "Elle te rappelle Teresa ?",
		["221416"] = "Elles seront bientôt mariées au site de l'oracle et donneront ensuite naissance aux bébés du Saint-Père.",
		["551267"] = "Nous devons l'arrêter ou Solas subira le même sort que ma ville...",
		["117143"] = "Les ascètes utilisent la douleur et la volonté pour repousser la Brume sombre, mais de toute évidence, cet ascète a été transformé en Défiguré.",
		["552271"] = "",
		["667086"] = "Va, avant que Teresa ne trouve le pontife.",
		["331162"] = "Je l'ai vue à Adamina...",
		["9973296"] = "Désolé.",
		["9971313"] = "Sois plus clair, pourquoi cette main est-elle une relique des Marcions ?",
		["9971314"] = "C'était la main d'un soldat marcion puissant. Dans ses derniers instants, un rituel interdit a été réalisé pour la fusionner avec le Sendril et en faire une arme.",
		["9971316"] = "Les temps ont changé, Terrence. Pour l'Église, l'hérésie n'est peut-être plus une si grande menace. Au départ, mon maître ne voulait pas me donner cette main, jusqu'à ce que je coupe la mienne...",
		["9961023"] = "...",
		["771213"] = "Pardonne-moi, Viola... Je ne peux pas laisser Teresa là-bas.",
		["9971317"] = "Tu es fou.",
		["221199"] = "Vous êtes très pâle !",
		["9971318"] = "Seuls les fous peuvent survivre dans ce monde.",
		["662403"] = "Ouvrir la porte ?",
		["441272"] = "Vous avez été reconnu par les Demiurges. En vertu de notre accord, je vais vous ouvrir le portail vers Terrebénie.",
		["222901"] = "Retourner à la voiture ?",
		["774036"] = "Tu devrais te faire du souci pour toi-même. Norwood, je te laisse t'en charger.",
