	StoryDialog = {
		["663703"] = "Sì",
		["994281"] = "Ah ah ah, i miei saccheggiatori non si fermeranno finché i cavalieri di Handel non scompariranno da Ichthyosauria.",
		["661152"] = "Forza, non mi riconosco più!",
		["111121"] = "Chi è Lord Ramones?",
		["771198"] = "Cosa intendi dire che non avevate altra scelta? Hai consegnato Teresa per niente? Ora devo andare dentro questa dannata Tomba colossale per riprenderla!",
		["111134"] = "Ah ah ah, umano furbetto.",
		["661322"] = "Sì... Il codice... Se non ne sai niente, ti consiglio di andartene in fretta da qui!",
		["100210022202"] = "Sì",
		["997013"] = "Riesco ancora a percepire un alito di vita da qualche parte su quest'isola, anche se molto debole.",
		["551268"] = "Sbrigati, vai alla Terra benedetta e fai ciò che devi... Io ho bisogno di riposare... Non preoccuparti... Spero che andrà tutto bene.",
		["771230"] = "Il Terrence che vagava nella Foschia oscura dubitando di se stesso, ormai non esiste più.",
		["333092"] = "Perché sei inchiodato qui?",
		["552329"] = "Costo: 1 Miele di condensazione e 30 ossa di Anthozoan. Vuoi davvero procedere?",
		["771224"] = "Teresa ti impedirebbe di fare una cosa del genere.",
		["663504"] = "No",
		["1006100084"] = "Ha il Sigillo di Yekub...",
		["9971340"] = "Questo braccialetto è molto importante per me. Sto cercando il suo proprietario originale.",
		["663706"] = "No",
		["991431"] = "Pietra dell'Heggie",
		["331163"] = "Questo è impossibile!",
		["884043"] = "È mia!",
		["221293"] = "Non si possono mettere in discussione le benedizioni del Santo Padre! Se vuoi andartene di qui senza problemi, ti conviene non causare guai.",
		["9971364"] = "Ma nonostante la luce della Terra benedetta, non dissiperanno la nebbia che alberga nel mio cuore.",
		["663196"] = "No",
		["553255"] = "Sì",
		["771092"] = "Ora vai, ci penso io.",
		["331098"] = "...",
		["771077"] = "Norwood l'ha portata alle rovine vicino alla grotta.",
		["100610064168"] = "È impossibile da aprire senza una chiave.",
		["9931005"] = "Sinistra o destra, luce e oscurità, giusto o sbagliato... Tutto in questo mondo è collegato. Tutte le cose sono condannate a finire, al di là di ogni incertezza.",
		["773075"] = "Vattene",
		["552180"] = "",
		["221104"] = "So che la mia maschera potrebbe metterti un po' a disagio. Sono Norwood e sono un servitore della Chiesa, proprio come te.",
		["661310"] = "Sembra che... tu non soffra ancora della contaminazione dei Sendril, che fortuna.",
		["667124"] = "Un altro frammento.",
		["221262"] = "Cosa si nasconde presso quel patibolo?",
		["9934003"] = "/t0",
		["991152"] = "Ho sentito i miei genitori nominarlo molto tempo fa... Un tempo lassù c'è stata una rivolta, e molte persone sono morte.",
		["551132"] = "Oh, sei tu, amico mio. Come va? Hai trovato la clessidra?",
		["552341"] = "Costo: 1 Miele di condensazione e 60 ossa di Anthozoan. Vuoi davvero procedere?",
		["772003"] = "Vuoi tornare alla carrozza?",
		["791319"] = "Sembra che la maledizione dei Marcioni sia capace di ritrovare chiunque vi sia associato. Forse segnerà anche la tua fine, dal momento che hai offeso i Marcioni.",
		["441243"] = "La polvere bianca. Vuole distruggere il dono di Edith, l'ultima speranza dei Demiurghi.",
		["332100"] = "Oh Jenny... se solo potessi rivederti!",
		["661802"] = "Vattene, non troverai nulla che ti serva.",
		["661801"] = "No, ti prego! Cerca rifugio dal Procuratore nella Sala della Trinità, non ha senso stare qui!",
		["9924033"] = "/t0",
		["111046"] = "Dovremmo andarci il prima possibile!",
		["661702"] = "Vattene, non troverai nulla che ti serva.",
		["111088"] = "Jerry non c'è più perché i John... si sono tramutati in mostri.",
		["663602"] = "No",
		["663505"] = "Sì",
		["553154"] = "Vattene",
		["663503"] = "Sì",
		["558302"] = "",
		["662503"] = "Aprire la porta?",
		["111069"] = "Jerry... il mio Jerry è sparito... Si sono tramutati in mostri, tutti quanti...",
		["663502"] = "No",
		["9931004"] = "Terrence! Non dirai sul serio?",
		["662501"] = "Aprire la porta?",
		["661405"] = "Dio, non c'è nulla che possa fare. Chiunque merita la possibilità di vivere meglio di me.",
		["551379"] = "Occultare la verità è coltivare ignoranza e intenzioni malvagie.",
		["441186"] = "È questo il prezzo di cui parlava quella Marciona?",
		["9932034"] = "",
		["663323"] = "Azath",
		["558301"] = "",
		["663322"] = "Codice?",
		["777046"] = "Indietro, adesso! La luce rossa ti sta consumando la mente! Veloce!",
		["441120"] = "Grazie straniero, ora va molto meglio.",
		["441300"] = "Umano, ho un affare da proporti, che ne dici? Possiedo molti oggetti preziosi.",
		["441144"] = "Ah ah! Una Guida che non conosce i fatti, senza dubbio. Invece di sprecare il tuo tempo qui, vattene prima che accada qualcosa di terribile.",
		["792382"] = "",
		["667126"] = "Se uniti, la loro forma somiglia alla scanalatura sulla statua della Spada sacra nel Giardino della Trinità... Potrebbe esserci un collegamento tra loro, giusto?",
		["661325"] = "Aspetta, potrebbero esserci più indizi.",
		["9961044"] = "Se dovesse accadermi qualcosa, dovresti prendere Viola e andartene.",
		["663241"] = "No",
		["333906"] = "No",
		["9963027"] = "Vattene",
		["661505"] = "Dio, non c'è nulla che possa fare. Chiunque merita la possibilità di vivere meglio di me.",
		["441125"] = "A nessuno interessa un vecchio relitto come me... Come vorrei poter tornare nella Terra benedetta...",
		["9961020"] = "Pensi davvero di poterle far cambiare idea?",
		["552165"] = "Forse sono tutti quanti caduti in un sonno profondo…",
		["661223"] = "Ci sono abbastanza idioti là fuori, quindi datti una regolata.",
		["661214"] = "Allora trova un modo per aprire questo dannatissimo cancello!",
		["552116"] = "",
		["111105"] = "Dirigiti verso la statua nella miniera e trova la Pietra di lacrima. È l'unica cosa che potrà aiutarti a sciogliere questo guscio così duro.",
		["221227"] = "Ti sono ancora grata per tutto ciò che hai fatto per me. Ma ora, guardati. La tua infinita avidità ti ha trasformato in un cadavere ambulante, che sprofonda sempre di più nell'abisso oscuro. Dharam, non puoi più tenerla tu... Ti ha... completamente corrotto.",
		["791279"] = "Hai trovato dell'altra argilla?",
		["665189"] = "Dio, cos'altro posso fare per te? Ti prego, perdona la nostra tracotanza.",
		["9956021"] = "",
		["551266"] = "Il suo obiettivo non è quello di entrare nella Tomba colossale... Vuole vendicarsi della Chiesa e distruggere la Terra benedetta...",
		["111086"] = "Buon uomo, ti sarò eternamente grata se riuscirai a ritrovare il mio Jerry.",
		["551410"] = "...",
		["551153"] = "Ah ah... Ho solo trovato il modo di sopravvivere sotto altre forme.",
		["663403"] = "Sì",
		["667136"] = "Ha dedicato la sua vita alla fede, ma, ahimè, ne è stata vinta.",
		["554180"] = "/t0",
		["117124"] = "Hmmm, come non detto. Heggem è già in rovina.",
		["331192"] = "Ma perché?",
		["221117"] = "Non dista molto da qui, ma il cancello è sigillato grazie ad un qualche rituale. È impossibile entrare.",
		["771107"] = "Agnes... Agnes... Perché ho questo nome stampato in testa?",
		["661115"] = "Ehm, il putrido fetore dei Sendril... Mi rende nervoso.",
		["555251"] = "Oh Supervisore, nella tua misericordia, spero che tu senta le mie preghiere...",
		["661106"] = "Voglio sapere la verità, ma guardami... Andarne in cerca equivale a morire.",
		["661105"] = "Nella Terra benedetta tutti sembrano pensarla così, ma il potere dei Pilastri sacri ha iniziato a svanire più di dieci anni fa... Io, un uomo cieco, sono stato l'unico a notare la differenza. Tutti gli altri erano confusi da questa falsa illusione diffusa dalla Chiesa.",
		["991201"] = "Ah ah ah... Ho distrutto la nave dei pazzi... Adesso non potranno lasciare questo posto... Rafforza il volere di Lord Ebbinghaus... Lascia che paghino il prezzo della loro arroganza...",
		["664160"] = "/t0",
		["661155"] = "Non è affar mio...",
		["663153"] = "<color=#DBBA7FFF>Usa Basilisco benedetto</color>",
		["662152"] = "",
		["551179"] = "L'oscurità all'ombra della sacra luce, questi Sendril di cui parli, pensi davvero che siano i colpevoli?",
		["9933021"] = "Vattene",
		["30135237"] = "Ti prego, basta! I Sendril sono stati scacciati.",
		["331112"] = "Lei era la mia compagna... Venimmo in questo luogo deserto per trovare quel codardo. Non immaginavamo minimamente i disastri che ci attendevano.",
		["9936039"] = "",
		["662139"] = "",
		["331156"] = "Questi?",
		["9941023"] = "Una piccola scintilla può scatenare un incendio.",
		["552281"] = "",
		["334186"] = "/t0",
		["551438"] = "Ichthyosauria?",
		["442320"] = "Vuoi andare alla Terra benedetta?",
		["991066"] = "Che cos'è questa nave dei pazzi di cui parli?",
		["9966053"] = "",
		["9951047"] = "Spero che il fantasma dei Marcioni non ci perseguiti...",
		["111057"] = "Fai attenzione.",
		["9961045"] = "Ehi, Terrence, non rendere tutto così oscuro e tenebroso. Lo sai che non sono capace di gestire situazioni come queste perché le emozioni non si possono misurare col denaro.",
		["777031"] = "Calma... e fai molta attenzione, non vogliamo svegliare il can che dorme.",
		["9961042"] = "Glenn, potrei avere bisogno del tuo aiuto.",
		["441293"] = "Impossibile dirlo... I segreti celano altri segreti, e non sono più in molti a conoscere la verità.",
		["9966039"] = "",
		["334188"] = "/t0",
		["441148"] = "Le menzogne sono sempre coperte dalla fede, ah ah...",
		["9961037"] = "E metto sempre in discussione Dio: perché conduce tutti allo scontro?",
		["9961035"] = "Sin da quando ti ho incontrato, continuo a pensare al tempo passato con Teresa.",
		["9963036"] = "Vattene",
		["772264"] = "",
		["551398"] = "Grazie comunque, Guida. Rispetto la tua scelta, ma non riesco a nascondere il mio rammarico... Sembra che non abbia altra scelta che rimanere qui e cercare un'altra soluzione, prima che questo mondo venga annientato. In ogni caso, ricorda che il frammento è la chiave di tutto.",
		["222294"] = "",
		["664230"] = "/t0",
		["996464"] = "",
		["114142"] = "/t1",
		["668302"] = "",
		["221130"] = "È stato il Santo Padre a rinchiuderti qui?",
		["111047"] = "Tra l'altro, ho trovato questi su quel ponte laggiù.",
		["444149"] = "La porta sembra essere bloccata.",
		["551387"] = "Occultare la verità è coltivare ignoranza e intenzioni malvagie.",
		["554777"] = "Impossibile aprire da questo lato.",
		["9943013"] = "Vattene",
		["991125"] = "Come fai a sapere queste cose?",
		["331065"] = "Non è il momento di fare i sentimentali... Controlliamo prima la cappella.",
		["441116"] = "La storia è finita. Il destino è nero. Questa fede fasulla sta solo ritardando la distruzione imminente.",
		["553288"] = "Vattene",
		["992001"] = "Lasciare Ichthyosauria?",
		["221249"] = "Guardati attorno, sono io che ho salvato tutti! Sono io a portare avanti la stirpe di Adamina!",
		["221129"] = "Il Santo Padre ci ha ordinato di catturare chiunque attraversi Adamina. Sapevo che fosse una cosa sbagliata da fare, all'inizio nessuno era d'accordo, ma poi ci siamo abituati all'idea e adesso nessuno oppone resistenza.",
		["9922035"] = "",
		["663321"] = "...",
		["551282"] = "Mi dispiace, niente sconti, ma tutti i miei prodotti valgono il loro prezzo pieno.",
		["461003"] = "Siamo tutti sulla stessa barca, voglio solo trasmetterti un messaggio...",
		["221108"] = "Stai dicendo che il morbo dei Colossi è legato a questa Strega dai capelli rossi?",
		["991017"] = "Coff... Coff... Presto... Coff... Nella grotta!",
		["554001"] = "Impossibile entrare.",
		["221116"] = "L'oracolo?",
		["331078"] = "Andiamo... alla cappella.",
		["221254"] = "闭上你的嘴！",
		["225187"] = "La stirpe di Adamina è mantenuta in vita dal Santo Padre...",
		["551310"] = "Vuoi ripristinare gli attributi del personaggio?",
		["555170"] = "/t0",
		["551433"] = "Occultare la verità è coltivare ignoranza e intenzioni malvagie.",
		["773193"] = "Dagli il Bocciolo rosa lucente",
		["9973451"] = "Parla",
		["991432"] = "Pietra dell'Heggie",
		["554165"] = "/t0",
		["552154"] = "",
		["551386"] = "Vendetta indulgente... convergenza ardente... tentativo rischioso...",
		["774033"] = "Se non mi fai uscire, giuro che ti verrò a cercare!",
		["551383"] = "Ah... una vorace ossessione... Ci ha accecati tutti, proprio così.",
		["9931008"] = "Smettila con questi inutili trucchetti... Dimmi, ci sono indizi a Exilium?",
		["221056"] = "Se è già morta, non possiamo far niente per lei.",
		["551123"] = "Era questa che stavi cercando?",
		["661224"] = "Teresa era proprio di fronte a me e ho perso un'altra occasione. Potrebbe essere stata l'ultima...",
		["551374"] = "I piccoli e deboli non dubitano neanche delle bugie più ovvie.",
		["111101"] = "C'è qualcuno? Aiuto! Sono in trappola, qui dentro l'uovo! Aiuto! Aiutatemi!",
		["441206"] = "Mi sembri una persona intelligente, quindi confido che farai la scelta giusta.",
		["991451"] = "Pietra del Colosso",
		["9911020"] = "无论做出什么样的选择，我们的灵魂还是会重归黑暗……",
		["551408"] = "...",
		["773244"] = "Parla",
		["771216"] = "Se mai verrà quel giorno, te lo farò sapere. Ma promettimi che tornerai con Teresa.",
		["112907"] = "Vuoi tornare alla carrozza?",
		["9921004"] = "Chi può dirlo... Ho perso la cognizione del tempo in questa oscurità infinita.",
		["551393"] = "Non mi aspettavo di incontrarti qui, immagino che neanche tu abbia trovato ciò che cercavi.",
		["773076"] = "Dov'è Teresa?",
		["773204"] = "Parla",
		["441203"] = "Guida, non ci sarà una seconda possibilità per i Demiurghi questa volta. Abbiamo già scommesso tutto sul salvataggio di Vaillant dalla Terra benedetta!",
		["551281"] = "Oh, munifica Guida, ti auguro ogni successo.",
		["551355"] = "Occultare la verità è coltivare ignoranza e intenzioni malvagie.",
		["444228"] = "Al momento non si può aprire.",
		["551350"] = "I piccoli e deboli non dubitano neanche delle bugie più ovvie.",
		["223113"] = "Vattene",
		["771199"] = "Ti farai uccidere. Non otterrai che la tua morte.",
		["221171"] = "Hai trovato la tua mamma?",
		["551396"] = "Il frammento della meteora rossa in tuo possesso è la chiave di tutto. Ne avrai bisogno quando arriverà il momento giusto. Ti ringrazio per aver accettato, ma ti chiedo di aspettare. Ripagherò la tua fiducia.",
		["114146"] = "/t1",
		["551234"] = "Ma esattamente, cosa ti è successo?",
		["443188"] = "Vattene",
		["441225"] = "Non è affar tuo...",
		["113077"] = "<color=#DBBA7FFF>Perché hai lasciato la miniera?</color>",
		["551188"] = "Ascoltami, fai quello che puoi. Se ti spingi oltre, non farai che cadere in una spirale di peccato senza fine e patirai le pene dell'inferno.",
		["4994993019"] = "Consegna materiali",
		["333902"] = "No",
		["1001100015"] = "Schiarisciti le idee! Che le danze abbiano inizio!",
		["331119"] = "Mia signora, mi dispiace deluderti... Non ho molto tempo... Albertus… Albertus… Il tuo nome non può essere perdonato. Tutti i tuoi peccati verranno sepolti in questa terra d'esilio!",
		["331074"] = "I tuoi occhi sembrano essere pieni di dubbi.",
		["9999291"] = "Tuttavia, l'ossessione non ci porta sempre dove vorremmo...",
		["441331"] = "Va tutto bene?",
		["991117"] = "Perché è troppo tempo che non bevi l'acqua del Fiume delle anime: hai completamente dimenticato il passato.",
		["224170"] = "/t0",
		["222189"] = "",
		["551313"] = "Chi sei?",
		["775118"] = "No... Salvatemi... Chi mi... salverà...",
		["771140"] = "Sei stato tu a costringere Viola a sparare quel proiettile a Teresa! Eri tu così disperato da aprire la Tomba colossale e creare questo caos! E ora mi dici di voler salvare Teresa?!",
		["1004100076"] = "Ma tu li hai usati...",
		["9923043"] = "Cambia costume",
		["551293"] = "Ah ah... Sapevo che era il mio giorno fortunato! E mi sono fatto un amico!",
		["551289"] = "Hai sentito? I fantasmi dei Marcioni... Vagano qui attorno, sento i loro sussurri!",
		["551275"] = "Finalmente potrò tornare alla Terra benedetta...",
		["9951031"] = "È un bene che tu abbia abbandonato la Chiesa, adesso hai qualcosa per cui vivere.",
		["332905"] = "Vuoi tornare alla carrozza?",
		["551273"] = "Ah ah... Sapevo che era il mio giorno fortunato! E mi sono fatto un amico!",
		["661240"] = "Che? Non essere così sorpreso, è che odio la luce...",
		["331144"] = "Infatti, ma la Chiesa non ha mai smesso di investigare a Katib. Si vocifera che l'entrata alla Tomba colossale fu trovata lì.",
		["551363"] = "Occultare la verità è coltivare ignoranza e intenzioni malvagie.",
		["221303"] = "Chi ha pensato di inventare questa macabra procedura?",
		["228231"] = "",
		["9963039"] = "Benedici",
		["884045"] = "Ahh... C'è qualcosa dentro di me...",
		["334071"] = "/t0",
		["4994993007"] = "No, grazie.",
		["791340"] = "Questo braccialetto è molto importante per me. Sto cercando il suo proprietario originale.",
		["9961013"] = "Quando arriveremo alla Terra benedetta, rimani nella carrozza con Glenn. Non c'è bisogno che tu ti faccia coinvolgere.",
		["551392"] = "Guida, solo un momento, vorrei chiederti un favore.",
		["447084"] = "Beh... Certo, non possiamo starcene seduti qui ad aspettare la nostra fine. Forse correre il rischio di trovare quel tizio può essere il modo più semplice per giungere alla Terra benedetta.",
		["9971308"] = "Cosa sarebbe?",
		["661101"] = "Dove altro potrei andare? Guida, questo luogo pullula di Sendril e distruzione, dov'è che posso essere al sicuro?",
		["30134240"] = "Teresa, dove sei?",
		["551262"] = "Se la Strega tornerà nella Terra benedetta, tutte le tragedie avvenute qui si ripeteranno, forse saranno ancora peggiori...",
		["554258"] = "/t0",
		["791303"] = "Sono felice per te.",
		["663704"] = "No",
		["991411"] = "Non vi è alcuna reazione",
		["551244"] = "Ho avuto una visione... di Teresa. Teneva in mano il Sigillo di Yekub... e c'erano dei cavalieri inginocchiati di fronte a lei...",
		["441228"] = "Vado a suonare la campana dell'ultima torre. Se tornerai, saprai dove trovarmi.",
		["991074"] = "Grazie, viandante gentile. Mi hai aiutato a trovarla prima che mi arrendessi. Devono essere stati quei tizi sulla nave dei pazzi... Le hanno fatto uno scherzo molto crudele...",
		["662401"] = "Aprire la porta?",
		["551126"] = "Finalmente potrò tornare alla Terra benedetta...",
		["112077"] = "",
		["331157"] = "Ne avrete bisogno se volete mantenere una mente lucida là fuori.",
		["555253"] = "Basta... Lasciatemi andare...",
		["447304"] = "Questa dev'essere la porta posteriore del villaggio di cui parlava Frollo, usa la chiave per aprirla.",
		["551116"] = "Ci incontriamo di nuovo, Guida. Scusami, stavo pensando a mia moglie nella Terra benedetta. Non mi ricordo più che aspetto abbia, ma mi manca molto... Ah già, hai trovato la clessidra?",
		["9971341"] = "È inciso in uno strano alfabeto...",
		["223106"] = "Perché tutti i discepoli, qui, hanno lo stesso odore dei Sendril?",
		["551276"] = "Quei Sifler non si fanno mai scappare un'opportunità per fare soldi.",
		["1006100083"] = "Da quel momento hai perso qualcosa...",
		["221138"] = "Signora Guida? Sei vivo? No, no, no... Dovresti essere morto! Non posso vedere la mia mamma se non sei morto! Dov'è Elmar? Dov'è?",
		["551337"] = "Occultare la verità è coltivare ignoranza e intenzioni malvagie.",
		["9971365"] = "I nostri sforzi verranno ripagati, e anche tu ritroverai i tuoi ricordi.",
		["553366"] = "Sì",
		["551270"] = "...",
		["995015"] = "Coff... Coff...",
		["9999295"] = "Nella Terra benedetta, i figli dei Deturpati vengono chiamati \"Notturni\"... Ma è stato tanto tempo fa. Preferisco non parlarne.",
		["661151"] = "Guida, spero che quello che ho fatto possa essere fonte di sopravvivenza... Ho lottato contro il destino tutta la mia vita... Non ho potuto scegliere come nascere, ma almeno posso scegliere come morire.",
		["554178"] = "/t0",
		["551160"] = "Quando il Colosso ha cominciato a fondersi con la Torre sacra, le Streghe Marcione hanno fatto piombare tutti in un sonno profondo... uno dal quale sarebbe stato molto difficile svegliarsi.",
		["441196"] = "Se vuoi davvero andare nella Terra benedetta, questa è la tua ultima possibilità. Aiutami e aiuterai te stesso, Guida.",
		["557169"] = "Strano. Dice \"Pietra del passato\".",
		["113207"] = "No",
		["661311"] = "Toc toc (bussano alla porta)",
		["334224"] = "/t1",
		["551165"] = "Tuttavia, hanno scelto di continuare a custodire il segreto al suo interno, quindi sono ancora là…",
		["9932012"] = "",
		["111056"] = "Dividiamoci: dirigiti verso la vetta, io esplorerò Heggem.",
		["991042"] = "È troppo tardi. Non è rimasto niente... Insieme al ricordo e alla speranza... Ah ah ah... Eh?",
		["441245"] = "È parte di un antico rituale della Strega Marciona. Serve a far affiorare il potere nascosto dentro ognuno di noi e permetterà ai Demiurghi di unirsi a Edith.",
		["554129"] = "/t0",
		["445090"] = "Luce! Più luce! Non portatela via!",
		["221101"] = "那个小女孩是个彻头彻尾的骗子！我早有预感！",
		["551154"] = "Una volta ero un sacerdote della Chiesa in carne e ossa, tanto tempo fa. Oramai ho abbandonato la mia forma umana.",
		["662196"] = "Vuoi continuare?",
		["771229"] = "Davvero?",
		["553138"] = "Trovata.",
		["551345"] = "I mediocri sono sempre ossessionati dall'essere al di sopra degli altri.",
		["551137"] = "Spero proprio che non sia così, altrimenti... non ti piacerà quello che succederà, amico mio.",
		["9931006"] = "Mi spiace ma ho vinto io. Ti avevo detto che il risultato era già scritto. Ora è il momento di scoprire la verità.",
		["553135"] = "No.",
		["662320"] = "",
		["551131"] = "...",
		["111119"] = "È ancora qui?",
		["551127"] = "Pazienza, ci vuole pazienza...",
		["113071"] = "<color=#DBBA7FFF>Cosa sta succedendo?</color>",
		["9933033"] = "Hai oggetti nuovi?",
		["331145"] = "Quindi anche lei sta cercando la Tomba colossale? Perché?",
		["441187"] = "Una donna Marciona?! Che cosa ti ha detto?!",
		["993257"] = "Vattene",
		["442063"] = "",
		["441260"] = "I Demiurghi sanno cosa li aspetta?",
		["331223"] = "Nessun reso, nessun rimborso. Ti auguro un buon viaggio.",
		["9963032"] = "Commercia",
		["4994991010"] = "Vaga nella notte oscura senza alcuna protezione, e ti faranno a pezzi... Perché non cercare qualcosa che illumini l'oscurità?",
		["9921039"] = "Chi è l'uomo mascherato, di preciso?",
		["554086"] = "/t0",
		["221261"] = "Vai a Exilium, Terrence. Lì troverai l'indizio che stai cercando.",
		["771205"] = "Hai fatto tutto quello che potevi e anche di più.",
		["551315"] = "Perché ti trovi qui nella Foschia oscura?",
		["441298"] = "Quanto tempo ci vorrà?",
		["991003"] = "Umpf... Credevo che fossi cibo per pesci.",
		["553903"] = "Sì",
		["791316"] = "I tempi stanno cambiando, Terrence. L'eresia potrebbe non essere più il pericolo peggiore che corre la Chiesa. All'inizio, il mio Maestro non voleva darmi questa mano, finché non ho tagliato via quella che avevo già...",
		["664083"] = "/t0",
		["225280"] = "Oh, non non è sufficiente, neanche lontanamente sufficiente... Smetti di urlare. Il dolore andrà via... presto.",
		["9951052"] = "Katib è come una miniera d'oro per la Chiesa.",
		["225081"] = "Ho portato avanti la stirpe del Santo Padre.",
		["9924034"] = "/t0",
		["441250"] = "No, è solo il primo passo...",
		["9951038"] = "Non sono una semplice pedina della Chiesa, io rispondo solo al mio maestro. Le mie labbra rimarranno cucite finché non scopriremo la verità.",
		["337049"] = "Come previsto questo luogo è diventato solo un paradiso di morte e Sendril.",
		["9951030"] = "È così che funziona la Chiesa. Manipola le persone con le fede e le usa come pedine.",
		["441270"] = "Mi hai delusa profondamente, Guida. hai avuto la tua occasione e l'hai sprecata facendo la scelta più stupida. Il rituale è completo, preparati dunque ad affrontare la collera dei Demiurghi.",
		["9951029"] = "...",
		["113903"] = "Sì",
		["331160"] = "E se lei fosse ancora viva?",
		["9951018"] = "Se ti aspetti che confessi a Dio i miei peccati, stai perdendo tempo.",
		["441099"] = "...",
		["9951017"] = "Terrence, ho perso la vista tanto tempo fa, ma riesco ancora a percepire il dolore sul tuo volto. Non è cambiato nulla.",
		["441278"] = "Maledizione! Quella dannata donna ha proseguito il rituale, Edith rinascerà molto presto!",
		["111087"] = "Oh, è il cappotto di Jerry. Mio piccolo Jerry, sei a casa.",
		["115089"] = "Mio carissimo Jerry, mamma ti riporterà a casa. La tua mamma non ti abbandonerà mai.",
		["991456"] = "Pietra dell'Heggie",
		["338230"] = "",
		["444269"] = "/t0",
		["448312"] = "",
		["448311"] = "",
		["331063"] = "Immagino fosse molto bello...",
		["9911018"] = "咳……咳……它还在等待，它总是在等待……",
		["117127"] = "Guarda laggiù! Ci sono dei Sifler che stanno saccheggiando il villaggio. Sembra che abbiano già ammassato un bel gruzzolo.",
		["224047"] = "/t1",
		["551425"] = "...",
		["661219"] = "Ora hai esagerato!",
		["771108"] = "Ah... Sei tornato, hai cambiato idea? Se vuoi andartene da qui, liberami dalle catene.",
		["331193"] = "Se quello che dici è vero, allora l'ingresso alla Tomba colossale è a Katib. È dove Teresa voleva andare.",
		["447158"] = "Quel tizio sul campanile sembra essere la nostra chiave per raggiungere la Terra benedetta.",
		["552287"] = "",
		["9931021"] = "No, per niente.",
		["551150"] = "Oh, che strano... Ci sono ancora persone normali a Katib? O sono solo persone che appaiono normali?",
		["118150"] = "",
		["221245"] = "Che cosa ha preso esattamente?",
		["665194"] = "Perdonami, Vaillant... È un sacrificio necessario.",
		["114023"] = "Glenn, hai il permesso di rimanere, ma non ti azzardare ad aprir bocca.",
		["221054"] = "È tutto finito, non avere paura.",
		["665083"] = "La Chiesa... deve pagare... il prezzo...",
		["445089"] = "La luce soffusa diverrà eterna e ci guiderà alla terra del nulla...",
		["441290"] = "Vuoi dire che Vaillant non è stato ingiustamente accusato?",
		["4994992004"] = "Richiede olio di balena rancido e 100 funghi spettrali",
		["551099"] = "Non ce ne sono molte... Ho fatto fatica anche a procurarmi la mia. Se riesci a ritrovarla, potrà servire a entrambi.",
		["221193"] = "Riportale anche questo, il suo giocattolo preferito. Non voglio che sia triste. Mamma tornerà a casa dopo aver dato alla luce il bambino del Santo Padre.",
		["443080"] = "Vattene",
		["991515"] = "Non dobbiamo fare altro che aspettare tranquilli e alla fine dimenticare tutto. Per fortuna ho ancora questi libri a farmi compagnia.",
		["661116"] = "Perché non viene il Procuratore? Nessuno ricorda l'ultima volta che se n'è andato dalla sua dimora...",
		["221106"] = "Perché tutti i discepoli, qui, hanno lo stesso odore dei Sendril?",
		["337081"] = "È ancora vivo!",
		["663404"] = "No",
		["1007100095"] = "A chi apparteneva quella voce?",
		["331224"] = "Ho incontrato altre misere Guide proprio come te, ma mi spiace dire che ora sono tutte morte.",
		["994888"] = "Al momento non si può aprire.",
		["991085"] = "È successo molto tempo fa... È stato dimenticato da tempo... Tutto qui è andato in pezzi. Forse solo quei tizi lassù conoscono la vera risposta...",
		["221306"] = "Questo \"miracolo\" ha avuto un piccolo effetto collaterale: uno spaventoso aumento della mortalità maschile. Non che sia necessariamente un problema per il Santo Padre...",
		["224900"] = "/t0",
		["9933036"] = "Vattene",
		["221309"] = "Sei libera....",
		["441165"] = "Se suona tutte le campane, quest'alba dei Demiurghi tornerà a ripiombare nell'oscurità...",
		["541002"] = "Cosa diavolo pensa di ottenere? C'è ancora qualcosa che mi stai nascondendo, dopo tutto questo tempo?",
		["552132"] = "",
		["441109"] = "Mi aspetta un'altra lunga attesa, chissà quando torneranno. Spero solo che non mi facciano aspettare tutt'una vita come loro padre.",
		["9941035"] = "È la prima volta che cerco di farmi aiutare da Dio dopo tanto tempo.",
		["991104"] = "Mi sento così senza speranza, ora. Forse un tempo erano la mia gente, ma non ne ho alcun ricordo.",
		["662701"] = "Aprire la porta?",
		["9972455"] = "",
		["225183"] = "Santo Padre, andrei in Purgatorio in tuo nome.",
		["441141"] = "Elena?",
		["111082"] = "Ho rubato il tesoro... per cercare il mio piccolo Jerry. Ho giurato che lo avrei restituito non appena avessi trovato Jerry, ma lui è sparito. Puoi aiutarmi, buon uomo?",
		["668303"] = "",
		["9971470"] = "Comunque stiano le cose, la verità si cela sotto la Torre sacra a Katib, oltre il cancello ricolmo del potere dei Sendril.",
		["9953042"] = "Commercia",
		["551120"] = "Questa clessidra non è una questione da poco, ma a volte anche il più fortunato degli uomini può avere un po' di sfortuna. E io spero di non avere la sfortuna di imbattermi nella stregoneria Marciona.",
		["9971388"] = "Dopotutto, Dio è solo un concetto che viene creato e che non ha bisogno di essere visibile, serve solo agli esseri umani per dare un senso alle proprie sciocchezze. Avete mai pensato al perché Dio abbia creato un mondo maledetto? Ci dev'essere una ragione, ma è stata persa nel tempo.",
		["799315"] = "Forse, ma non ci conterei troppo. Sbrigati, Guida. Non andarci piano con quell'oscurità. Dimostrale che la luce non si spegnerà così facilmente.",
		["9956058"] = "",
		["111098"] = "(L'Uovo gigantesco ti sente avvicinare e comincia a tremare.)",
		["991421"] = "Pietra della Natura umana",
		["9961021"] = "Non lo so, ma ci devo provare.",
		["791359"] = "Quando me ne sono andato dall'orfanotrofio della Chiesa, pensavo di potermi lasciare il passato alle spalle, ma mi sbagliavo.",
		["664161"] = "/t0",
		["9933035"] = "Hai oggetti nuovi?",
		["461004"] = "So cosa sto facendo.",
		["115066"] = "Jerry... Dov'è Jerry? Il mio povero Jerry...",
		["228233"] = "",
		["661180"] = "Nessuno l'ha più visto da quando Vaillant è stato condannato. Da allora, si ode solo un lamento doloroso dalle profondità della Terra benedetta. E dura da un decennio.",
		["333158"] = "Perché sei ancora qui?",
		["441071"] = "Umpf... Pensava che la paura avrebbe fatto indietreggiare i Demiurghi, che idiozia!",
		["771147"] = "Terrence, so che è troppo per te... Ma in questo momento la cosa più importante è andare in fondo a quella grotta e salvare Teresa. Ti spiegherò tutto dopo.",
		["441204"] = "Devo tornare all'aula di tribunale dove mi aspetta qualcosa di più importante.",
		["112111"] = "",
		["224888"] = "Al momento non si può aprire.",
		["9941020"] = "Pensi che possano aiutarci?",
		["554138"] = "/t0",
		["331099"] = "Guida, mi faresti un favore? Sembra che io abbia perso il mio anello nuziale nella cappella. Quando la mia fidanzata ritornerà, voglio esprimerle i miei voti nuziali, scambiare gli anelli e ringraziarla per avermi aiutato. Mi aiuteresti a trovare l'anello?",
		["111053"] = "La persona che stai cercando?",
		["551136"] = "Mi prendi in giro? Non sono così sfortunato! Vuoi tenerti la clessidra per te?",
		["9961048"] = "Come pensi che abbia fatto a sopravvivere? Nessuno conosce la Terra benedetta meglio di me, quindi, se vuoi vivere, portami con te.",
		["221069"] = "L'ennesimo codardo.",
		["221206"] = "Il Santo Padre, il benedetto! Grazie per avermi permesso di portare avanti la tua stirpe...",
		["9921023"] = "Che cosa vi è successo?",
		["111052"] = "...",
		["225232"] = "看看你们都干了什么好事！",
		["333221"] = "Vattene",
		["551164"] = "Sono tornati tutti dentro la Torre sacra per scoprire il terribile segreto celato sotto di essa.",
		["443105"] = "Sì, l'ho trovato.",
		["991219"] = "Non ho tempo per farlo, adesso. Dovresti trovare un posto sicuro in cui stare.",
		["551397"] = "Ti ringrazio davvero dell'aiuto, ma non sono dell'umore giusto per un atto di altruismo.",
		["553105"] = "Trovata.",
		["551271"] = "Oh, sei tu, amico mio. Come va? Hai trovato la clessidra?",
		["791325"] = "Non ti preoccupare, il mio braccio è ormai un'ottima arma da avere a portata di \"mano\". L'odio serve solo ad alimentare il suo potere.",
		["112067"] = "",
		["9943027"] = "Vattene",
		["441285"] = "Perché non dirmi subito la verità?",
		["9971282"] = "Ho trovato dell'argilla... Spero che ti sia d'aiuto.",
		["551280"] = "La mia bancarella non riceve visite da tempo, forse posso farti un buon prezzo.",
		["111099"] = "(L'Uovo gigantesco ti sente avvicinare e comincia a tremare.)",
		["1004100112"] = "Eppure sappiamo che tutti i Demiurghi hanno bisogno di una sola voce.",
		["112103"] = "",
		["994478"] = "/t0",
		["9932020"] = "",
		["551323"] = "Chi è malato di ossessione può spezzare il ciclo di luce e ombra solo quando è morto e sepolto.",
		["881080"] = "Ignoranza e cupidigia confluiscono e si fondono... Dopo una vita vissuta a testa alta, inginocchiarsi sarà ripugnante.",
		["441332"] = "Perdonami... Ero sovrappensiero, queste onde sono così rilassanti. Non mi aspettavo di incontrare la meteora rossa di Ichthyosauria dopo solo qualche mese. I nostri sacrifici hanno finalmente dato i loro frutti.",
		["227120"] = "Ho trovato Viola, ma sono stato bloccato da una qualche forza soprannaturale... Non sono neanche riuscito ad avvicinarmi a lei.",
		["551236"] = "Non è questo il modo di rispondere, ci tengo.",
		["557242"] = "Qualcosa era custodito e venerato qui.",
		["551138"] = "Era questa che stavi cercando?",
		["665131"] = "Non puoi fermarci... I Demiurghi si riprenderanno tutto quello che hanno perso...",
		["331148"] = "Tutto questo sembra molto familiare...",
		["441074"] = "Guida, quell'uomo è la mia preda, lascialo a me.",
		["446304"] = "",
		["223315"] = "Prendi il suo Sendril attivo.",
		["551243"] = "Tutto a posto?",
		["9971354"] = "È il giorno della mia nascita.",
		["223191"] = "Vattene",
		["221190"] = "Mantello verde? Ho visto tua figlia.",
		["461005"] = "È per il tuo bene. Ho l'impressione che ultimamente tu sia un po' più smemorata del solito.",
		["111071"] = "Cosa sta succedendo?",
		["1001100016"] = "Ha un aspetto orribile...",
		["551263"] = "Perché?",
		["115140"] = "È il cordoglio che mi mantiene lucido...",
		["9999296"] = "Forse potresti rifarti una vita da qualche parte...",
		["661264"] = "Ah ah ah, Guida sempliciotta.",
		["111074"] = "Una Guida ha condotto tutti i sopravvissuti alla miniera, dicendo che quest'ultima celava un tesoro che poteva salvare Heggem.",
		["773225"] = "Vattene",
		["224169"] = "/t0",
		["222249"] = "",
		["221126"] = "Il Santo Padre mi ha detto che se avessi continuato ad attirare stranieri ad Adamina e concludere la caccia, avrei potuto rivedere la mia mamma.",
		["9923044"] = "Vattene",
		["113206"] = "Sì",
		["551417"] = "I piccoli e deboli non dubitano neanche delle bugie più ovvie.",
		["111080"] = "Il mio piccolo Jerry! È uscito a giocare con i fratelli John, ma sono due giorni che non ho sue notizie. È così vivace.",
		["553395"] = "Rifiuta",
		["111081"] = "Come hai fatto a proteggerti qui fuori?",
		["551440"] = "...",
		["552238"] = "",
		["9953046"] = "Vattene",
		["553272"] = "Trovata.",
		["661170"] = "Che diamine è successo quindici anni fa?",
		["551360"] = "L'amore... conduce solo alla follia.",
		["991038"] = "In ogni caso, spero di sopravvivere abbastanza a lungo da poter incontrare questo Lord Ebbinghaus.",
		["228232"] = "",
		["111076"] = "Sì, abbiamo trovato una luce abbastanza intensa da disperdere i Sendril nella miniera. Poi ci siamo nascosti e abbiamo aspettato che la Chiesa venisse a soccorrerci.",
		["551274"] = "È tutto pronto, possiamo partire. La fortuna ci sorride, troveremo sicuramente il Sigillo di Yekub sotto la Torre sacra.",
		["441205"] = "I pionieri Demiurghi si sono già messi in viaggio verso la Terra benedetta. Se non li aiutiamo, il loro sacrificio sarà vano.",
		["9951024"] = "Gli avvenimenti di Katib... Il massacro dei Marcioni... È questo che non riesci a dimenticare?",
		["9971309"] = "Quando il mio Maestro mi ha dato questa mano, mi ha detto che la bara originale conteneva altre tre reliquie antiche, che però sono andate perdute per qualche ragione. Il mio braccio si calmerà solo quando avrò trovato le altre reliquie, oppure entrerà in frenesia, se non sto attento.",
		["551110"] = "Guardati, non farai che attirare la sfortuna se continuerai a perderti in pensieri futili.",
		["994016"] = "/t0",
		["661261"] = "Ah ah ah, Guida sempliciotta.",
		["113133"] = "Vattene",
		["661102"] = "...",
		["115142"] = "È il cordoglio che mi mantiene lucido...",
		["555271"] = "Ahh... Sono un tale stupido... Così tanto tempo... alla sua ricerca... Tutto invano... Ora sono solo deriso e armato solo di menzogne...",
		["111049"] = "Provengono dalla Terra benedetta, ma adesso si trovano a Heggem.",
		["9961014"] = "Mi rifiuto di rimanere qui seduta con le mani in mano.",
		["775034"] = "/t0",
		["4994993006"] = "Rinuncia",
		["221111"] = "Il Santo Padre? Anche lui è un discepolo di Adamina?",
		["994036"] = "/t0",
		["791338"] = "Ho un obiettivo.",
		["441061"] = "...",
		["114127"] = "/t0",
		["552284"] = "",
		["223294"] = "Non ancora.",
		["664888"] = "Al momento non si può aprire.",
		["662901"] = "Vuoi tornare alla carrozza?",
		["111126"] = "È lei, generoso Signore? È tornato a salvarmi?",
		["113901"] = "Sì",
		["441156"] = "Non possiamo lasciargli suonare tutte le campane o i piani dei Demiurghi saranno in serio pericolo.",
		["991135"] = "Tutti pensavamo che il Fiume delle anime non si sarebbe mai prosciugato, finché un giorno non diventò silenzioso all'improvviso.",
		["9953021"] = "Benedici",
		["117095"] = "La luce di Heggem si sta affievolendo e il tesoro non è più qui. Quei poveri discepoli si sono sicuramente trasformati in mostri.",
		["771253"] = "Mi ricorderò delle tue parole.",
		["991512"] = "Trovare acqua pura del Fiume delle anime sta diventando sempre più difficile.",
		["661175"] = "I morti non parlano, ma i Marcioni sono intrinsecamente legati alla questione. Nessuno è innocente. Quando si inizia ad esplorare la verità, ci si rimane intrappolati.",
		["331058"] = "Questo è il luogo più vicino alla morte.",
		["9931007"] = "Terrence, devi essere impazzito!",
		["221200"] = "Sto per partorire! Fa male!",
		["553359"] = "Sì",
		["9922041"] = "",
		["9961049"] = "...",
		["221163"] = "Ti piacerà.",
		["771239"] = "Prenditi cura di Viola.",
		["221172"] = "Le ho detto addio per sempre...",
		["221228"] = "Allora non mi lasci altra scelta! Ridammela subito!",
		["111044"] = "Cosa diavolo lo sta indebolendo così?",
		["222268"] = "",
		["111073"] = "Ci sono sopravvissuti tra i discepoli?",
		["443901"] = "Sì",
		["112205"] = "Vuoi restituire l'Uovo del messaggero?",
		["553375"] = "Sì",
		["9942032"] = "",
		["111048"] = "Sono solo dei comuni appunti della strega che parlano di alchimia, niente di speciale.",
		["551375"] = "Ah... una vorace ossessione... Ci ha accecati tutti, proprio così.",
		["9973381"] = "Contratto",
		["9921036"] = "La risposta non può che essere ad Adamina.",
		["993218"] = "Rifiuta",
		["9933017"] = "Commercia",
		["221223"] = "Teresa...",
		["9921018"] = "Dammi quel diario!",
		["9931031"] = "Bene, allora non ne parliamo più.",
		["334050"] = "Guardie della Chiesa. I cadaveri sono raggrinziti, con ferite letali sul collo.",
		["553426"] = "Riempi flacone di Melos.",
		["226269"] = "",
		["991446"] = "Pietra del Sifler",
		["221314"] = "Spero che non si sia ferito, Guida, o mi sentirò in colpa.",
		["551343"] = "Ah... una vorace ossessione... Ci ha accecati tutti, proprio così.",
		["333901"] = "Sì",
		["225082"] = "State lontani da me, sporchi esseri!",
		["331179"] = "Se è così, riportami a quei tempi.",
		["441114"] = "Oh, quanto ho bramato di immergermi nella luce radiosa della Terra benedetta…",
		["552310"] = "",
		["778301"] = "",
		["551190"] = "Non è niente di grave, ho solo bisogno di riposare.",
		["9951040"] = "Sì, in un certo senso. Niente paura, sono bravo a mantenere segreti.",
		["771206"] = "Davvero? Non l'ho neanche salutato a dovere. Non so neanche se fosse una brava persona o no.",
		["551151"] = "Un momento, uno di voi... nasconde qualcosa, lo sento dall'odore. Tu eri una pedina della Chiesa... e hai già avuto a che fare con questo luogo.",
		["553351"] = "Sì",
		["441246"] = "La campana di Frollo dissipa la polvere bianca, ma porta alla pazzia chiunque si sia sottoposto al rituale.",
		["774035"] = "Non osare toccarlo! Non ti lascerò andare se lo farai!",
		["334058"] = "/t0",
		["224123"] = "/t0",
		["553285"] = "Vattene",
		["441303"] = "Umano, ho un affare da proporti, che ne dici? Possiedo molti oggetti preziosi.",
		["991150"] = "Ma certo, a Ichthyosauria nessuno si fida troppo della propria memoria, perciò non preoccuparti.",
		["222288"] = "",
		["9933034"] = "Cosa sai riguardo a Exilium?",
		["9961022"] = "E se fallissi?",
		["774041"] = "/t0",
		["443104"] = "Mi spiace, no.",
		["331087"] = "Oh, mi dispiace molto, Guida. Spero che la mia orribile apparizione non ti abbia spaventato. Non avere paura, anch'io ero un cavaliere della Chiesa.",
		["9941013"] = "Come Guida, non posso neanche avvicinarmi alla Terra benedetta...",
		["551312"] = "Niente Vino di miele splendente",
		["441271"] = "Te l'ho già detto, chiunque tenti di impedire ai Demiurghi di riconquistare la luce verrà annientato.",
		["221308"] = "Non abbiamo altra scelta, una mano in più non guasta mai.",
		["443302"] = "Vattene",
		["665070"] = "Quando la Strega Marciona troverà il Procuratore, Vaillant sarà finalmente libero.",
		["9963014"] = "Vattene",
		["661216"] = "Per la Chiesa! Non vuoi rivedere Teresa viva, vero?",
		["331154"] = "Benita, se troviamo gli altri frammenti della meteora rossa, riuscirai a rimettere tutto a posto?",
		["997208"] = "Ladri? Guarda questo posto! Non c'è niente che valga la pena rubare qui... Non perdiamo tempo... nessuna di queste persone sembra normale.",
		["9921047"] = "Comincio a essere stanco, ho bisogno di riposare.",
		["9963026"] = "Parla",
		["553311"] = "No",
		["441070"] = "Sembra che questa talpa sia un osso duro.",
		["999282"] = "Ehi! Hai già trovato la leva di controllo e la ruota dentata?",
		["9961039"] = "Ma la sofferenza finirà. Non importa cosa sia diventata Teresa, starò con lei nel bene e nel male.",
		["447137"] = "La campana sta suonando da quella torre. Dobbiamo raggiungerla il prima possibile mentre lui è ancora là!",
		["793312"] = "Ho trovato delle reliquie antiche.",
		["791282"] = "Ho trovato dell'argilla... Spero che ti sia d'aiuto.",
		["991092"] = "Non posso aiutarti... Prendi, potrebbe servirti.",
		["227119"] = "Grazie al cielo sei vivo, Terrence... Tra parentesi, c'è un odore terribile...",
		["991044"] = "Vai via, non è rimasto abbastanza da poter condividere. Non otterrai niente finché qualcuno non porterà un po' d'acqua...",
		["447159"] = "Quel tizio sul campanile sembra essere la nostra chiave per raggiungere la Terra benedetta.",
		["331189"] = "Questo è... il Sigillo di Yekub. Si dice che sia la chiave della Tomba colossale.",
		["771187"] = "Ah... ne hai trovato? Questa luce, questo sapore...",
		["663332"] = "Vattene",
		["552373"] = "Costo: 1 Miele di condensazione e 180 ossa di Anthozoan. Vuoi davvero procedere?",
		["991190"] = "Lord Handel, mi dispiace, non posso ereditare la gloria dei cavalieri.",
		["331168"] = "Oh, Guida... Non so come dimostrare la mia gratitudine.",
		["113104"] = "<color=#DBBA7FFF>Come posso aiutarti?</color>",
		["991086"] = "Che cosa aspetti?",
		["221290"] = "Grazie infinite, signora Guida.",
		["334051"] = "Cavalieri della Chiesa con lance. Le loro armature arrugginite cigolano nel vento, loro morti stecchiti.",
		["100510052203"] = "Sì",
		["333220"] = "Commercia",
		["331221"] = "Ho incontrato altre misere Guide proprio come te, ma mi spiace dire che ora sono tutte morte.",
		["221107"] = "Sulla base degli indizi legati al morbo dei Colossi, il Santo Padre ha pronosticato che una malvagia Strega dai capelli rossi sarebbe stata la causa della distruzione di Adamina.",
		["443065"] = "La rivolta dei Demiurghi...",
		["771196"] = "Sapevi che sarebbe successo?! E l'hai messa lo stesso in pericolo?",
		["331184"] = "Se Teresa, alla fine, diventasse un mostro, continueresti a...?",
		["993070"] = "Vattene",
		["773086"] = "Tuo padre...",
		["551358"] = "I piccoli e deboli non dubitano neanche delle bugie più ovvie.",
		["9952017"] = "",
		["9931022"] = "Sembra che tu abbia qualcosa contro Norwood.",
		["9931025"] = "È diverso.",
		["994415"] = "",
		["331142"] = "È entrata in un'area proibita di Katib.",
		["551403"] = "...",
		["551238"] = "Sembra che sia stato contaminato dai Sendril.",
		["113113"] = "Quale strana Strega?",
		["9953018"] = "Vattene",
		["224049"] = "L'entrata sembra essere sigillata.",
		["441295"] = "Non so altro. Vai alla Terra benedetta e cerca la Strega Marciona che insegui da così tanto tempo, lei saprà rispondere alle tue domande.",
		["661179"] = "Il Procuratore non si è fatto vedere?",
		["331177"] = "Ma almeno lui ha mantenuto la sua promessa.",
		["113202"] = "Sì",
		["9963053"] = "Commercia",
		["991467"] = "Oh, munifica Guida, ti auguro ogni successo.",
		["9943040"] = "Il passato dei Demiurghi e della Chiesa?",
		["111041"] = "Secondo le antiche leggende, è un segno che il Colosso si sta preparando a dirigersi verso la Tomba.",
		["661228"] = "Ma il Procuratore è morto: non possiamo attraversare né l'uno né l'altro adesso.",
		["441162"] = "Stai bene?",
		["661117"] = "Che tu sia benedetto, Guida. Spero che ti rivedremo ancora.",
		["441142"] = "Quella pazza mi vuole braccare. È stata eletta vice-pontefice. I Demiurghi si stanno scavando la fossa da soli.",
		["441283"] = "Non dimenticare che abbiamo un asso nella manica. Presto, è la mia ultima richiesta. Se mi aiuterai, ti dirò tutto.",
		["9971389"] = "Gli esseri umani pensano che Dio abbia dato loro la capacità di capire tutto, ma pensano davvero di vedere \"tutto\"? Non amano le incertezze, quindi cercano rifugio nella fede: ecco il loro peccato. La luce del Colosso non è il lume della ragione. Senza la ragione, l'uomo smette di essere uomo.",
		["223902"] = "No",
		["991105"] = "Per prima cosa troviamo quel ragazzo. Forse sa qualcosa.",
		["444320"] = "/t0",
		["117029"] = "Heggem non è il primo Colosso a spegnersi. Adesso non può più arrestare la diffusione della Foschia oscura. Dove sono i discepoli di Heggem?",
		["668301"] = "",
		["331181"] = "Sto bene...",
		["551183"] = "Sto cercando un modo per mettere fine a questo sonno eterno, per questo sono giunto qui...",
		["441077"] = "Perché ti infastidisce la campana? Sembra ti provochi un gran dolore.",
		["663405"] = "Sì",
		["225080"] = "Il mio bambino! Il mio bel bambino!",
		["337084"] = "Quasi, ma forse è qualcos'altro.",
		["991094"] = "Ah ah, finalmente！È un miracolo！",
		["447085"] = "Edith ha riconquistato la luce? Com'è possibile?",
		["991240"] = "Basta... Capisco... Potresti per favore... Dirmi com'è là fuori... Dimmi, com'è il cielo?",
		["884057"] = "Non sei pronto per la verità.",
		["773393"] = "No",
		["9971471"] = "Ci sono così tanti possibili indizi, quale devo seguire?",
		["551394"] = "Se entrambi riusciremo a svegliarci da questo incubo, mi aiuterai?",
		["772244"] = "",
		["9943012"] = "Parla",
		["447092"] = "Sembra che la campana faccia impazzire tutti, qui.",
		["442188"] = "",
		["441100"] = "Sembra che tu sia ancora nervoso... Ah ah... Posso chiederti un favore prima che te ne vada?",
		["221148"] = "Ma fai attenzione. Ultimamente è sempre un po'... scontrosa, ecco. Non fare niente che possa irritarla, non so come reagirebbe.",
		["793382"] = "Contratto",
		["114038"] = "/t0",
		["9971455"] = "Stai ancora ripensando a quello che è appena successo?",
		["9931040"] = "Grazie degli acquisti.",
		["9946046"] = "",
		["334227"] = "/t0",
		["441107"] = "Ho completato la mia unica missione nella vita e cresciuto tre soldati Demiurghi. Ora posso finalmente riposarmi per un po'.",
		["551241"] = "Qualcosa era custodito e venerato qui.",
		["225233"] = "吼！",
		["991423"] = "Pietra del Sifler",
		["221135"] = "È stata Elmar. Era sempre gentile con me, come la mia mamma. Mi ha chiusa qui dentro per proteggermi.",
		["9971307"] = "Niente paura, è solo perché ha un desiderio inesaudito che sembra irrequieta.",
		["443120"] = "Dagli una candela di grasso.",
		["9961047"] = "Ma devo prepararmi al peggio.",
		["334180"] = "/t0",
		["661181"] = "Intendi dire che... quello è il lamento del Procuratore?",
		["9941041"] = "La colpa è solo loro: invece di studiare i classici nella Sacra abbazia, insistettero per studiare il Colosso col pontefice Vaillant.",
		["9973378"] = "Contratto",
		["227222"] = "Questo è il nascondiglio del Santo Padre. Il cancello sembra essere sigillato da un qualche antico rituale.",
		["9962035"] = "",
		["9999304"] = "Vuoi davvero rischiare il destino di tutta Solas?",
		["551163"] = "E gli altri seguaci della Chiesa? Dove sono adesso?",
		["773008"] = "No",
		["668304"] = "",
		["221059"] = "Ad Adamina!",
		["793290"] = "Mi dispiace.",
		["221057"] = "È viva, ma non ha molto tempo.",
		["551320"] = "Aspetta, anche tu hai un'ossessione, ma non abbastanza potente... Forse un giorno ci rivedremo...",
		["441155"] = "Guida, ti avverto di nuovo: questi sono affari dei Demiurghi, non tuoi. ",
		["114140"] = "/t1",
		["441259"] = "Ciononostante, tutti quanti sono diventati parte della coscienza del Colosso, per poi trasformarsi nei suoi burattini.",
		["118148"] = "",
		["551325"] = "Chi è malato di ossessione può spezzare il ciclo di luce e ombra solo quando è morto e sepolto.",
		["551362"] = "Vendetta indulgente... convergenza ardente... tentativo rischioso...",
		["771089"] = "Visto il comportamento di Norwood, non c'è molto tempo rimasto per mio padre.",
		["444179"] = "È impossibile da aprire senza una chiave.",
		["9971283"] = "Grazie... Sono contenta che te ne sia ricordato.",
		["441244"] = "A cosa serve questa polvere bianca?",
		["661803"] = "Fermo! Non aprirla!",
		["461006"] = "Bada agli affari tuoi.",
		["553415"] = "Riempi flacone di Melos.",
		["667073"] = "È stata travolta dalla volontà vendicativa dei Demiurghi. La Terra benedetta caduta, la donna pazza, è l'incubo di tutti.",
		["777040"] = "Che accidenti è successo qui!",
		["991470"] = "Oh, munifica Guida, ti auguro ogni successo.",
		["991216"] = "Grazie! La gente di Ichthyosauria ti sarà grata. E siccome abbiamo fatto amicizia, prendi. Buona fortuna.",
		["9971363"] = "Queste parole sono rivolte a te, chi le ha incise non voleva dimenticarti.",
		["9971331"] = "Seppellirò questa mano nella mia città natale affinché riposi in pace. Forse non manca molto al momento in cui i Marcioni non saranno altro che una leggenda, proprio come i loro antenati. Anche noi finiremo con l'essere dimenticati, prima o poi, e svaniremo senza lasciare traccia.",
		["441286"] = "Credi nelle coincidenze, Guida?",
		["9999293"] = "Basta. I Notturni non possono vivere come esseri umani. Abbiamo perso il diritto di scegliere quando siamo nati.",
		["441337"] = "Ma Ichthyosauria non può aspettare oltre.",
		["663705"] = "Sì",
		["551338"] = "La ricerca dell'eccelso è la minaccia definitiva dell'umanità.",
		["331107"] = "Guida? Come hai fatto a entrare qui? La guardia alla porta non ti ha visto? Non voglio guai, quindi ti prego di lasciarmi in pace!",
		["884006"] = "Paura di perderla? O di qualcosa... di peggiore... un oscuro segreto? Devo confessarti la verità... la scioccante verità...",
		["9973458"] = "Riguardo il Procuratore",
		["9971462"] = "E lei ti conosce troppo bene.",
		["9971465"] = "Se si trattasse solo di Norwood non mi sorprenderei, ma Viola... Non riesco a capire perché si sia fatta coinvolgere.",
		["9971466"] = "Non voglio crederci neanche io.",
		["9971467"] = "Quindi è questa la verità che la Terra benedetta ha cercato di insabbiare per tutto questo tempo? Un Procuratore completamente contaminato dai Sendril?",
		["114001"] = "Impossibile entrare.",
		["442119"] = "Straniero, puoi aiutarmi? Ho tanto... freddo, hai qualche candela?",
		["444092"] = "/t0",
		["9973388"] = "Vattene",
		["9951045"] = "Hai sentito del massacro di Katib?",
		["662240"] = "Si va?",
		["9976379"] = "",
		["111075"] = "Avete trovato questo tesoro?",
		["771260"] = "La mia bancarella non riceve visite da tempo, forse posso farti un buon prezzo.",
		["227053"] = "Terrence, sembri essere costantemente inseguito dalla sfortuna, nonché da mostri.",
		["773261"] = "Commercia",
		["661103"] = "Guida, non credi che sia curioso? Questo luogo è protetto dalla luce eterna dei pilastri e, ciononostante, è troppo debole per resistere all'invasione dei Sendril.",
		["552104"] = "",
		["771262"] = "Mi dispiace, niente sconti, ma tutti i miei prodotti valgono il loro prezzo pieno.",
		["551414"] = "...",
		["221170"] = "Signora Guida, sei tornato sano e salvo.",
		["791304"] = "In realtà, ho fatto anche qualcos'altro, spero che ti piaccia.",
		["551346"] = "Vendetta indulgente... convergenza ardente... tentativo rischioso...",
		["771233"] = "Chissà se torneremo mai ai bei vecchi tempi...",
		["777022"] = "Finalmente arriviamo qui, e poi... Un'altra rampa di maledettissime scale... A volte apprezzo proprio la loro ricerca metodica...",
		["116131"] = "",
		["776264"] = "",
		["771264"] = "Oh, munifica Guida, ti auguro ogni successo.",
		["991115"] = "Jerold, perché non ce ne hai parlato?",
		["775035"] = "/t1",
		["774777"] = "Impossibile aprire da questo lato.",
		["9973275"] = "Contratto",
		["9953024"] = "Parla",
		["221478"] = "Oltretutto, qui ci sono così tante persone come la mia mamma che hanno bisogno di aiuto. Voglio aiutarle, per quanto possibile. ",
		["663331"] = "<color=#DBBA7FFF>Inserisci il frammento</color>",
		["9971278"] = "È qualcosa che voglio finire, quindi se hai dell'argilla in più, portamela... Senza fretta naturalmente, ci sono cose più importanti.",
		["771200"] = "Non ho nulla da perdere...",
		["771220"] = "Aspetterò quel giorno con ansia...",
		["221225"] = "Mi riprendo solo ciò che mi appartiene.",
		["9971279"] = "Hai trovato dell'altra argilla?",
		["9972279"] = "",
		["9973280"] = "Ho trovato dell'argilla.",
		["9973281"] = "Mi dispiace.",
		["331203"] = "Oh, prode Guida, ti sono davvero grato per ciò che hai fatto. Permettimi di sfidarti come cavaliere. Ti aspetterò nel luogo in cui è morta. Nel nome del mio amore!",
		["771101"] = "Aspetta un attimo, tu non sei lui... I viventi se ne sono andati da... Hai visto quel mostro!?",
		["9923035"] = "Parla",
		["333904"] = "No",
		["9971284"] = "Sono curioso, chi stai cercando di ricreare con l'argilla?",
		["994037"] = "/t0",
		["30135238"] = "Presto, nascondetevi, non lasciate che vi trovino!",
		["9971287"] = "Quello che mi preoccupa è che il suo viso sta lentamente svanendo dai miei ricordi... Spero di riuscire a terminare prima che accada... È l'unico modo per espiare i miei peccati.",
		["557176"] = "Il passato, il presente e il futuro sono in qualche modo collegati.",
		["4994993029"] = "Interno della Foschia oscura",
		["9971291"] = "Sembra che possa \"vedere\" coloro che ho esiliato nella Foschia oscura, che cercano di trascinarmi con loro.",
		["9971377"] = "Hai trovato dell'altra argilla?",
		["221263"] = "Ignoralo, Terrence. Ha cattive intenzioni.",
		["9973295"] = "Ho trovato dell'argilla.",
		["9972311"] = "",
		["9971295"] = "Quasi finito?",
		["553358"] = "Sì",
		["551388"] = "La ricerca dell'eccelso è la minaccia definitiva dell'umanità.",
		["9971297"] = "Ci vuole molto coraggio per vedere la verità dietro la \"fede\".",
		["771250"] = "Terrence, non ti ho mai mentito...",
		["9971298"] = "Raggiungeremo mai un equilibrio?",
		["9973301"] = "Contratto",
		["9953045"] = "Parla",
		["554269"] = "/t0",
		["9971305"] = "Grazie... È la prima volta che ricevo qualcosa...",
		["9961026"] = "Non hai l'aspetto di un uomo in procinto di ricongiungersi con sua moglie. Cosa le dirai? Ci hai pensato, almeno?",
		["111125"] = "Hai lo stesso odore della Strega! Sei dalla sua parte!",
		["778302"] = "",
		["9971306"] = "Forse non sarà l'ultima volta...",
		["9951041"] = "Chi è il tuo maestro?",
		["551272"] = "Era questa che stavi cercando?",
		["331182"] = "Ti ricorda Teresa?",
		["221416"] = "Verranno presto uniti in matrimonio presso l'oracolo, e poi sarà suo compito dare luce a una nuova vita per il Santo Padre.",
		["551267"] = "Dobbiamo fermarla, o Solas subirà la stessa sorte della mia città natale...",
		["117143"] = "L'Anacoreta sta usando il dolore per scacciare la Foschia oscura grazie alla sua forza di volontà, ma è chiaro che si sia trasformato in un Deturpato.",
		["552271"] = "",
		["667086"] = "Vai, prima che Teresa trovi il Procuratore.",
		["331162"] = "L'ho incontrata ad Adamina...",
		["9973296"] = "Mi dispiace.",
		["9971313"] = "Niente indovinelli, stai dicendo che questa reliquia apparteneva ai Marcioni?",
		["9971314"] = "Era la mano di un potente soldato Marcione. Poco prima che spirasse, qualcuno ha usato un rituale proibito per fonderla con i Sendril e trasformarla in un'arma.",
		["9971316"] = "I tempi stanno cambiando, Terrence. L'eresia potrebbe non essere più il pericolo peggiore che corre la Chiesa. All'inizio, il mio Maestro non voleva darmi questa mano, finché non ho tagliato via quella che avevo già...",
		["9961023"] = "...",
		["771213"] = "Perdonami Viola... Non posso lasciare Teresa laggiù.",
		["9971317"] = "Sei un pazzo.",
		["221199"] = "Sei così pallida!",
		["9971318"] = "Solo i pazzi riescono a sopravvivere in questo mondo.",
		["662403"] = "Aprire la porta?",
		["441272"] = "I Demiurghi riconoscono il tuo valore, rispetterò il nostro accordo e aprirò il cancello per la Terra benedetta.",
		["222901"] = "Vuoi tornare alla carrozza?",
		["774036"] = "Dovresti preoccuparti di te stessa. Norwood, occupatene tu.",
		["221159"] = "Questo apparteneva a Elmar. Puoi usarlo per raggiungere l'oracolo. Il Santo Padre è sicuramente là.",
		["9971378"] = "Come va? Hai trovato qualcosa di interessante?",
		["9971319"] = "Sembra che la maledizione dei Marcioni sia capace di ritrovare chiunque vi sia associato. Forse segnerà anche la tua fine, dal momento che hai offeso i Marcioni.",
		["9971383"] = "...",
		["9971321"] = "Voglio solo liberarmi di questa bomba a orologeria.",
		["9973379"] = "Contratto",
		["9973324"] = "Mi dispiace.",
		["9971324"] = "Se perdi il controllo, non ci andrò piano con te.",
		["9972382"] = "",
		["9971330"] = "Dove andrai adesso?",
		["221168"] = "Mamma... Mamma... Dove sei? Ti prego, rispondimi!",
		["9971333"] = "Spero solo che le mie azioni passate non abbiano causato un danno irreparabile.",
		["9973335"] = "Contratto",
		["30135239"] = "Lasciatelo andare, è solo un bambino!",
		["771106"] = "Ehm... Ho molta sete, ma non posso bere nulla... Persino una goccia d'acqua potrebbe bruciarmi la gola... Ma l'unica cosa che riesco a ricordare chiaramente è questo dolore continuo causato dalla sete.",
		["9971387"] = "Pian piano anche la Terra benedetta diventerà parte del passato, una storia che si ripeterà nuovamente in futuro. La Chiesa è accecata dal proprio credo, come dimostrano quelle teste luminose attorno al Procuratore...",
		["665071"] = "Sei solo uno sciocco che non merita di occupare il mio tempo!",
		["553110"] = "...",
		["9973380"] = "Contratto",
		["111068"] = "Qualcun altro è passato di qui?",
		["9971380"] = "Ho un obiettivo.",
		["9973345"] = "Ho trovato una porzione di Lastra rotta.",
		["9971299"] = "Temo di no.",
		["1002100024"] = "Non vuoi salvare i Demiurghi?",
		["664055"] = "/t0",
		["551370"] = "Vendetta indulgente... convergenza ardente... tentativo rischioso...",
		["992140"] = "",
		["991080"] = "Perché?",
		["9931029"] = "Anche mio padre si fidava del suo istinto, ma finì per compiere gesti così gravi che non vi si può più rimediare.",
		["9971381"] = "Ho un obiettivo.",
		["551351"] = "Ah... una vorace ossessione... Ci ha accecati tutti, proprio così.",
		["9931023"] = "In questa Foschia oscura non dovresti mai fidarti così facilmente di qualcuno.",
		["221060"] = "Il mio istinto mi dice che non sarà così semplice.",
		["9972381"] = "",
		["9943036"] = "Benedici",
		["9971352"] = "Secondo il testo sulla lastra, sul braccialetto sono incise le parole \"... festa del fuoco... luce eterna\"",
		["9946029"] = "",
		["555254"] = "Ah! È ora di svegliarsi. Finalmente potrò vendicarmi di tutte le umiliazioni che ho subito!",
		["9931027"] = "Lo dice di nuovo il tuo istinto?",
		["331155"] = "Non so se sia possibile, ma almeno sono disposta a provarci. Ah, sì, prima che ve ne andiate, prendete questi con voi.",
		["9971355"] = "Abbiamo fatto qualche passo avanti. Riesci a decifrare qualcos'altro?",
		["332219"] = "",
		["9971356"] = "No, non è ancora abbastanza per decifrare il codice.",
		["441297"] = "Resterò qui e mi batterò per eliminare gli effetti della polvere bianca. C'è ancora speranza per i Demiurghi.",
		["991045"] = "Plin, plin... Il meraviglioso suono dell'acqua mi dà una sensazione di pace. Buona fortuna! Spero che quando tornerai riusciremo ancora a riconoscerci... Ah ah ah!",
		["551265"] = "Hai già visto cos'è successo quando ne ha aperto uno...",
		["791350"] = "Sembra pura follia chiederti di cercare le reliquie di una civiltà completamente sconosciuta.",
		["661148"] = "...",
		["441292"] = "Non capisco… Come è possibile che un pontefice della Sacra abbazia abbia permesso una cosa del genere?",
		["991464"] = "Oh, munifica Guida, ti auguro ogni successo.",
		["9971360"] = "Credo che nessuno sia in grado di farlo.",
		["991021"] = "Questa maledizione diventerà la mia forza. Non rimpiangerò di essere diventato un mostro, se questo permetterà di salvare Ichthyosauria.",
		["441127"] = "Grazie, Guida.",
		["558305"] = "",
		["993467"] = "Commercia",
		["551404"] = "...",
		["9971362"] = "\"La nascita della festa del fuoco ardente porta calore e luce eterna nel mio cuore\"",
		["664163"] = "/t0",
		["331219"] = "Fiuu, sei tu la nuova Guida?! Sono proprio il tipo che ti serve per sopravvivere un po' più a lungo nella Foschia oscura.",
		["664166"] = "/t0",
		["551187"] = "Guida, abbandona il passato e aggrappati a quella menzogna in cui credi così ardentemente. Inchinati e gettati nell'oscurità. La luce è troppo intensa per poterla fissare direttamente.",
		["9971366"] = "Ma il mondo sta per finire, è tutto inutile.",
		["9973367"] = "Contratto",
		["9971367"] = "Adesso che abbiamo trovato la Tomba colossale, le Guide non hanno più motivo di esistere.",
		["225083"] = "Santo Padre, non abbandonarci!",
		["9971368"] = "Forse si può dare loro un nuovo scopo, come aiutare le persone nella Terra benedetta.",
		["9971369"] = "Non ti impedirò mai di seguire il tuo cuore, ti chiedo solo di fare attenzione e non correre rischi inutili.",
		["441143"] = "Ma sembra che sia la campana la fonte della pazzia a Edith...",
		["791275"] = "Ci stai ancora lavorando?",
		["771385"] = "Mi dispiace Terrence. Volevo solo avvertiti che il mio contratto si esaurisce qui. Hai trovato la Tomba colossale, il mio lavoro è finito.",
		["551407"] = "...",
		["791276"] = "Ehm...",
		["332222"] = "",
		["793279"] = "Contratto",
		["664193"] = "/t2",
		["793280"] = "Ho trovato dell'argilla.",
		["793281"] = "Mi dispiace.",
		["335225"] = "Metti fine alla mia insensata vita, Guida, ti supplico.",
		["9973323"] = "Ho trovato delle reliquie antiche.",
		["664162"] = "/t0",
		["9971275"] = "Ci stai ancora lavorando?",
		["772184"] = "",
		["221141"] = "Adamina mi fa sempre più paura. Voglio solo scappare il prima possibile con la mia mamma...",
		["551122"] = "Pazienza, ci vuole pazienza...",
		["791287"] = "Quello che mi preoccupa è che il suo viso sta lentamente svanendo dai miei ricordi... Spero di riuscire a terminare prima che accada... È l'unico modo per espiare i miei peccati.",
		["991404"] = "Proverbi di Ebbinghaus: III",
		["661114"] = "Qui non è molto sicuro... Ma se vuoi ancora sapere cos'è successo, mi potrai trovare alla Camera della Sala della Trinità.",
		["793288"] = "Contratto",
		["792288"] = "",
		["331150"] = "Lo sai con certezza?",
		["221295"] = "Signora Guida, puoi raccogliere un po' di erbe di Adamina per me? Mi scuso, ma devo continuare a mescolare senza mai fermarmi.",
		["791290"] = "Cosa ti succede?",
		["9941022"] = "Non penso che si assumerebbero un tale rischio per qualche sconosciuto.",
		["100310032203"] = "/t0",
		["791377"] = "Hai trovato dell'altra argilla?",
		["793296"] = "Mi dispiace.",
		["441179"] = "Prendi questa chiave e apri la porta posteriore del villaggio laggiù, poi saprai di chi fidarti.",
		["9971276"] = "Ehm...",
		["444777"] = "Impossibile aprire da questo lato.",
		["791296"] = "Sì, ma mi chiedo sempre... Se fossi in lei, approverei ancora la \"fede\"?",
		["995278"] = "Siamo stati troppo superficiali. Questa volta non lasceremo che Lord Ebbinghaus venga disturbato...",
		["331199"] = "Come mai... Come mai non riesco a sentirla?",
		["993118"] = "Chiedi delle creature che sono state appena allontanate.",
		["551245"] = "È un segno... Teresa è collegata alla Chiesa, in qualche modo? Guarda, è comparsa una chiave sull'altare.",
		["773264"] = "Commercia",
		["791306"] = "Forse non sarà l'ultima volta...",
		["791307"] = "Niente paura, è solo perché ha un desiderio inesaudito che sembra irrequieta.",
		["791311"] = "Come va? Hai trovato qualcosa di interessante?",
		["333147"] = "<color=#DBBA7FFF>Perché qui i Sendril sono deboli?</color>",
		["9971304"] = "In realtà, ho fatto anche qualcos'altro, spero che ti piaccia.",
		["792311"] = "",
		["9963042"] = "Parla",
		["9962042"] = "",
		["793313"] = "Mi dispiace.",
		["791313"] = "Niente indovinelli, stai dicendo che questa reliquia apparteneva ai Marcioni?",
		["552901"] = "Vuoi tornare alla carrozza?",
		["773184"] = "...",
		["793378"] = "Contratto",
		["1004100077"] = "Hanno deciso all'unanimità.",
		["793320"] = "Mi dispiace.",
		["991016"] = "Jerold！Jerold！",
		["441287"] = "...",
		["221128"] = "La caccia?",
		["791320"] = "Ah... Mi dispiace, non era mia intenzione mancarti di rispetto. Loro hanno fatto la loro scelta per cupidigia, tu sei stato costretto...",
		["551237"] = "L'altare, lassù, sembra essere connesso a qualcosa che si trova sotto la Torre sacra... Stuzzica l'oscurità... Vattene, prima che sia troppo tardi.",
		["791321"] = "Voglio solo liberarmi di questa bomba a orologeria.",
		["221258"] = "Sveglia. È finita l'ora del pisolino.",
		["991031"] = "Dobbiamo incontrare Ebbinghaus il prima possibile per avere delle risposte.",
		["791379"] = "Come va? Hai trovato qualcosa di interessante?",
		["117136"] = "Un uomo che si nasconde dietro una maschera, è tutto una farsa.",
		["791333"] = "Spero solo che le mie azioni passate non abbiano causato un danno irreparabile.",
		["441330"] = "...",
		["9956042"] = "",
		["793335"] = "Contratto",
		["791335"] = "Ti sei pentita?",
		["791337"] = "La maggior parte delle persone preferirebbe la morte all'essere una Guida.",
		["662703"] = "Aprire la porta?",
		["551322"] = "Ah ah ah, sei un tipo interessante. prendi questo contenitore e riempilo con una forte ossessione, forse troverai qualcosa di interessante.",
		["441302"] = "Ah ah ah, umano furbetto.",
		["791343"] = "Il leggendario primo popolo?",
		["791344"] = "Neanche io so di preciso cosa significhi, ma se trovi qualcosa legato a Samarosa, o sei riesci a venire a capo di una parte di quest'enigma, vieni da me.",
		["668305"] = "",
		["774113"] = "È bloccata.",
		["793380"] = "Contratto",
		["793345"] = "Ho trovato una porzione di Lastra rotta.",
		["793346"] = "Non ho ancora trovato niente.",
		["991418"] = "La porta non si smuove. Ci sono cinque scanalature sulla porta.",
		["224048"] = "/t0",
		["791346"] = "Questa lastra narra le leggende dei Samarosiani... \"Il totem sacro dei Samarosa... ci guida verso la dimora di Dio...\"",
		["661182"] = "Si dice che per mantenere la pace nella Terra benedetta, il Procuratore protegga il Sigillo di Yekub giorno e notte per evitare che la maledizione sia completa.",
		["9999290"] = "Era la mia unica possibilità di ritrovare Teresa.",
		["221136"] = "Dimmi, come posso trovare il Santo Padre?",
		["791349"] = "Se trovi altre lastre come questa, riusciremo sicuramente a decifrare le parole incise sul braccialetto...",
		["9971358"] = "E se non ci riuscissimo? Non voglio rimanere a rimuginare su questo enigma per il resto della vita.",
		["9926044"] = "",
		["793381"] = "Contratto",
		["791381"] = "Ho un obiettivo.",
		["793351"] = "Ho trovato una porzione di Lastra rotta.",
		["793352"] = "Non ho ancora trovato niente.",
		["551367"] = "Ah... una vorace ossessione... Ci ha accecati tutti, proprio così.",
		["221201"] = "Quell'odore disgustoso di Sendril...",
		["791351"] = "Ho portato una nuova lastra.",
		["4994993004"] = "Grotta della Foschia oscura",
		["9951034"] = "Ahh, Terrence, cosa posso fare per te? Stavo per fare una pausa, manca ancora molto alla nostra destinazione.",
		["551290"] = "...",
		["665188"] = "Fa male... Mi fa male... Il mio corpo... Basta, vi prego, basta!",
		["791353"] = "Che cosa intendi?",
		["791354"] = "È il giorno della mia nascita.",
		["1004100059"] = "Fratelli miei... Demiurghi che vagano per Edith...",
		["791360"] = "Credo che nessuno sia in grado di farlo.",
		["9962013"] = "",
		["114028"] = "Non immaginavo che Heggem fosse così debole.",
		["661104"] = "È a causa della maledizione della Strega Marciona?",
		["551335"] = "I mediocri sono sempre ossessionati dall'essere al di sopra degli altri.",
		["791382"] = "No, non è ancora abbastanza per decifrare il codice.",
		["4994993032"] = "Limitare della Foschia oscura",
		["991147"] = "Ebbinghaus... Ho letto di lui in un libro molto tempo fa. Il santo che ha dato la sua vita per proteggere noi, per proteggere Ichthyosauria...",
