		["441238"] = "Conrad, Albert... Andrew... Questi sono i nomi incisi sulla stele, un monito silenzioso della determinazione dei Demiurghi.",
		["332199"] = "",
		["992071"] = "",
		["777024"] = "Sono di nuovo le teste luminose, iniziano a dare fastidio...",
		["994003"] = "/t0",
		["661260"] = "Povera Guida, ho un affare da proporti, che ne dici?",
		["441065"] = "Contro il potere della Chiesa, probabilmente non finirà bene...",
		["991072"] = "Che cos'è...? Lo riconosco, ah ah... Riconosco quest'affare! Appartiene a Rochette!",
		["991073"] = "È nella grotta dietro alla cascata, in attesa che l'acqua scrosci dall'alto...",
		["443223"] = "<color=#DBBA7FFF>Aiuta Frollo.</color>",
		["553239"] = "Vattene",
		["991076"] = "Il fiume scorre di nuovo... Perché Rochette non è ancora tornata?",
		["995078"] = "Oh... Rochette... Non puoi addormentarti... Non addormentarti...",
		["991502"] = "Ah ah ah, ho detto fin troppo. Se vuoi posso condividere un po', ma non approfittarne.",
		["9971349"] = "Se trovi altre lastre come questa, riusciremo sicuramente a decifrare le parole incise sul braccialetto...",
		["991081"] = "Dentro c'è un demone mangia-uomini. Sussurra in un modo così inquietante da farti rizzare i capelli... Aah... Una voce indescrivibile che nessuno capisce...",
		["991505"] = "Certo, se vuoi solo porre fine alla tua inutile vita, non dare ascolto alle mie parole, ah ah...",
		["884054"] = "Abbandona ogni certezza. In un fiume fai fluire i tuoi pensieri e accogli la luce...",
		["991083"] = "Nessuno conosce il suo aspetto. Da quando il Fiume delle anime si è prosciugato, nessuno è mai uscito da quella grotta oscura.",
		["9921003"] = "Per quanto tempo ho dormito?",
		["9921011"] = "Non è possibile, quei capelli rossi... Non poteva che essere lei.",
		["111084"] = "Vedrò cosa posso fare.",
		["991084"] = "Perché il Fiume delle anime si è prosciugato?",
		["9961028"] = "Terrence, dopo tutto quello che abbiamo passato insieme, ancora non ti fidi di me?",
		["9961036"] = "Uhm...",
		["771252"] = "Dimentichi l'altra metà del nostro credo: reincarnazione non significa necessariamente stagnazione e coloro che possiedono un obiettivo chiaro possono completare il ciclo di passato, presente e futuro.",
		["113231"] = "Commercia",
		["221202"] = "Ahh, la stirpe del Santo Padre... la stirpe del Santo Padre... Ahhhh...",
		["111085"] = "Jerry, oh Jerry, dove sei?",
		["662405"] = "Aprire la porta?",
		["111106"] = "Ti rendi conto? La cosa più interessante a Heggem è questo egregio uovo dal grezzo guscio! Se escludiamo la testa d'uovo qui di fianco a me.",
		["993086"] = "Che cosa fai?",
		["9951051"] = "Ci sono molti misteri irrisolti a Katib, come gli antichi rituali dei Marcioni e la singolare stirpe di discepoli che vi puoi trovare.",
		["771225"] = "Forse, o forse no. Ho fatto la mia scelta.",
		["223112"] = "<color=#DBBA7FFF>La mia compagna è stata catturata, l'hai vista?</color>",
		["441063"] = "Ho chiuso io stessa il cancello per la Terra benedetta. Nessuno se ne andrà prima che saremo riusciti a sistemare le cose.",
		["661138"] = "Una Guida? Non ho mai visto una vera Guida. Ho sentito dire molte cose su di voi, la maggior parte poco raccomandabili... Sai che sto morendo, quindi potrei chiederti di aiutarmi un'ultima volta?",
		["991087"] = "Acqua... Aspetto che l'acqua ricominci a scorrere...",
		["995196"] = "Li ho già buttati di sotto.",
		["991091"] = "Non voglio morire in silenzio... Il mio cuore continua a ricordarmi che avevo un qualche tipo di missione...",
		["331094"] = "...",
		["991507"] = "Non voglio morire così, nell'oscurità. Devo riscoprire chi ero...",
		["331146"] = "Nessuno lo sa, non l'ha detto.",
		["221426"] = "Non l'ho più vista dal momento in cui il Santo Padre l'ha portata via...",
		["551411"] = "...",
		["993092"] = "Vattene",
		["443902"] = "No",
		["221213"] = "È morta... mi dispiace che tu abbia dovuto assistere a questo.",
		["100610064169"] = "/t0",
		["443904"] = "No",
		["331178"] = "I tempi sono cambiati, Terrence. Oggigiorno una promessa non vale più nulla.",
		["445056"] = "I Demiurghi non dimenticheranno questo gesto... Possa Dio accompagnarti sempre...",
		["443119"] = "...",
		["995095"] = "Ti troverò... Rochette... Ah ah... Se continuo a bere l'acqua, ricorderò...",
		["221065"] = "Mia mamma mi ha protetto dai mostri che ci hanno attaccati. L'hanno catturata e l'hanno portata nel sotterraneo.",
		["9931013"] = "Benita, la suora devota. È l'unica amica che ti è rimasta nella Terra benedetta.",
		["667123"] = "Sembra che sia stato questo frammento a indebolire così tanto i Pilastri sacri. Chi sarà stato ad averlo messo qui?",
		["994005"] = "/t1",
		["551180"] = "Non c'è giusto o sbagliato in questo mondo, c'è soltanto l'abisso nel cuore degli uomini che distingue il bene dal male.",
		["997097"] = "A quanto pare questo demone ha un hobby particolare...",
		["221264"] = "Scusami tanto, bella addormentata, ma non hai il diritto di affermare una cosa del genere.",
		["9953055"] = "Commercia",
		["663152"] = "<color=#DBBA7FFF>Usa Misericordia di Tristan</color>",
		["995099"] = "Liberami...",
		["993075"] = "Vattene",
		["995100"] = "Non abbandonarci...",
		["661177"] = "Come fai a sapere tutto ciò?",
		["9941015"] = "La città senza luci esclusa dalla Terra benedetta?",
		["991103"] = "Quei movimenti... Di sicuro sono stati addestrati bene.",
		["771078"] = "Non ho mai sentito di un luogo simile nella Terra benedetta.",
		["774385"] = "/t0",
		["111077"] = "Perché hai lasciato la miniera?",
		["551286"] = "La mia bancarella non riceve visite da tempo, forse posso farti un buon prezzo.",
		["661331"] = "Un'altra Spada sacra, una spada che può assorbire i Sendril e ripristinare la luce... È questo il segreto celato nella Tomba colossale? Sono un peccatore, come posso avere certi pensieri...",
		["9971361"] = "Dovrebbe essere l'ultima. Cosa dice il braccialetto?",
		["221301"] = "Le costanti migrazioni e gli ambienti ostili hanno reso i discepoli di Adamina sterili e li hanno portati a un passo dall'estinzione. Sperano di prolungare la propria stirpe grazie al potere dei Sendril.",
		["991082"] = "Demone spaventoso? Che cosa intendi?",
		["4994994002"] = "L'ossessione spaventosa nella Foschia oscura colpisce ancora. Devi sconfiggerla per riuscire a scappare.",
		["441105"] = "Ha seguito le tracce dei Demiurghi e si sta dirigendo verso la Terra benedetta.",
		["774028"] = "/t0",
		["997106"] = "Muoviamoci. Speriamo possa dirci qualcosa su quello che sta succedendo qui.",
		["994107"] = "/t0",
		["551376"] = "L'amore... conduce solo alla follia.",
		["444285"] = "/t0",
		["992107"] = "",
		["993108"] = "Chiedi dell'acqua del Fiume delle anime.",
		["991109"] = "Perché tutti vogliono l'acqua maledetta di questo fiume?",
		["991522"] = "Solo bevendo l'acqua del Fiume delle anime riescono a ricordare alcuni frammenti del passato ma,",
		["774030"] = "Non hai mai voluto mantenere la tua promessa, sin dall'inizio, Eckhart.",
		["553284"] = "Commercia",
		["4994993031"] = "Grotta della Foschia oscura",
		["9931026"] = "Lo terrò d'occhio.",
		["884039"] = "Straniero! Se non avessimo sacrificato la tua Strega, non avremmo potuto aprire la Tomba colossale!",
		["221246"] = "La Scatola sacra... è per quello che la Chiesa sta dando la caccia a Teresa. Io ho sempre mantenuto la parola e custodito la scatola per lei.",
		["991113"] = "Muro del silenzio?",
		["991114"] = "Fu costruito da quegli egoisti lassù. Se ci fosse un modo per aprirlo, non dovremmo ucciderci a vicenda in questo modo.",
		["771265"] = "Mi dispiace, niente sconti, ma tutti i miei prodotti valgono il loro prezzo pieno.",
		["221103"] = "Chi sei? Cosa vuoi da me?",
		["441081"] = "Deve pur aver lasciato qualche indizio...",
		["551369"] = "I mediocri sono sempre ossessionati dall'essere al di sopra degli altri.",
		["9941027"] = "Il passato è passato, ora dobbiamo concentrarci solo su come fermare tua moglie.",
		["991424"] = "Pietra dell'Heggie",
		["227322"] = "Guarda! Deve essere Elmar!",
		["793301"] = "Contratto",
		["771209"] = "Ora è libero da questo tormento incessante... Avrebbe voluto lo stesso per te.",
		["9971469"] = "Vaillant deve avere scoperto la verità sul Procuratore, quindi i Demiurghi sono stati messi a tacere.",
		["991119"] = "Quelle creature che hai allontanato, che cosa sono esattamente?",
		["991120"] = "Saccheggiatori... Così li chiamano tutti... Sono specializzati nella caccia e nell'uccisione dei pazzi come te che vogliono salire.",
		["9941012"] = "Ma guardati... Te ne stai lì a razzolare come una gallina decapitata.",
		["991122"] = "Forse per impedire alla gente di trovare altra acqua del Fiume delle anime. O forse è solo per odio. In ogni caso, non provocarli.",
		["9971302"] = "...",
		["551100"] = "Il Supervisore ha promesso che, se riusciremo a trovare il Sigillo di Yekub, il Procuratore ci concederà l'amnistia e ci permetterà di tornare alla Terra benedetta.",
		["661142"] = "...",
		["991513"] = "Ma anche se dovessi avere abbastanza fortuna da sfuggirgli, non hai alcuna possibilità di sconfiggere il guardiano della soglia.",
		["118149"] = "",
		["551437"] = "Mi fa piacere vedere che tu sia tornato sano e salvo. È ora di tornare a Ichthyosauria. Prendi il frammento di meteora rossa e vai al porto abbandonato a Edith, dov'è attraccata la mia nave.",
		["991126"] = "Non posso scegliere cosa ricordare e cosa dimenticare.",
		["771246"] = "我不会原谅你对我同胞所做的一切，但我必须感谢你收留了特蕾莎。",
		["991134"] = "Lascia perdere. Non c'è bisogno di continuare a pensare di tornare lassù.",
		["200120019999"] = "/t0",
		["663601"] = "Sì",
		["793311"] = "Contratto",
		["441220"] = "Il rintocco delle campane annuncerà la tua morte, oggi!",
		["991516"] = "La distruzione arriva sempre in maniera silenziosa, in una frazione di secondo.",
		["1006100118"] = "Quella parte di te debole, incerta e incapace... Ti aspetta.",
		["553342"] = "Sì",
		["552903"] = "Vuoi tornare alla carrozza?",
		["331176"] = "Patetico cavaliere, non ci crederà, sua moglie ha già...",
		["791286"] = "Forse è stata talmente importante per te che un volto di argilla non sarà mai abbastanza per immortalarla.",
		["994140"] = "/t0",
		["991140"] = "Ah... È tutto qui? ...Oh? Non credevo che ci saremmo incontrati di nuovo...",
		["223901"] = "Sì",
		["9971348"] = "Sto cercando di raccogliere indizi da quando ho lasciato la Terra benedetta, ma non riesco a venire a capo della questione.",
		["991141"] = "Che cosa ci fai qui?",
		["999285"] = "Non preoccuparti, abbiamo tempo. Ti aspetterò qui.",
		["991145"] = "Non sembra che l'acqua del Fiume delle anime ti interessi molto. Che strano. Allora cosa diavolo stai cercando?",
		["221134"] = "Non posso abbandonarla in questa situazione, devo rimanere al suo fianco...",
		["793362"] = "Non ho ancora trovato niente.",
		["991148"] = "Che scoperta sorprendente.",
		["9941034"] = "Smettila di accusarti. Riportala indietro e dolore e sofferenza finiranno.",
		["993151"] = "Come hai trovato questo posto?",
		["991151"] = "Come hai trovato questo posto?",
		["773007"] = "Sì",
		["771191"] = "Oh... Altro buon cibo... (Inghiotte) Ah... Percepisco la luce... Riempie il mio corpo... Ah... Sebbene le scimmie mi abbiano rubato gli occhi, posso ancora vedere... quasi... quasi... la carrozza arriverà fino in fondo...",
		["443907"] = "Sì",
		["991519"] = "Si sono sbarazzati dei cadaveri ammucchiandoli qui.",
		["664165"] = "/t0",
		["447180"] = "Chi di loro sta mentendo? Non abbiamo molto tempo per capirlo, devi prendere una decisione adesso.",
		["991153"] = "Il Muro del silenzio venne chiuso in quel periodo e non molto tempo dopo, il Fiume delle anime si prosciugò completamente.",
		["991155"] = "Io... Non me lo ricordo, forse i saccheggiatori...",
		["9931041"] = "Di risparmiar smetti se vuoi più oggetti!",
		["441269"] = "Guida, hai rispettato la promessa fatta?",
		["4994993028"] = "Limitare della Foschia oscura",
		["441068"] = "Lo saprai presto, Guida, ma a tempo debito.",
		["551429"] = "Ah... una vorace ossessione... Ci ha accecati tutti, proprio così.",
		["554114"] = "/t0",
		["991157"] = "Va tutto bene. Ho già accettato il fatto che non torneranno.",
		["333141"] = "Perché la Chiesa ha condannato a morte Teresa?",
		["993158"] = "Vattene",
		["997159"] = "È qui che vengono creati i saccheggiatori. Allora non sono demoni. Sono persone vere.",
		["991160"] = "Non so quanto tempo è passato. Ogni volta che ricordo la mia missione, cerco di risvegliare la sua anima imprigionata, ancora e ancora.. Ma non potrei mai rompere quel guscio duro...",
		["662705"] = "Aprire la porta?",
		["774999"] = "Hai bisogno di una chiave.",
		["9971286"] = "Forse è stata talmente importante per te che un volto di argilla non sarà mai abbastanza per immortalarla.",
		["665129"] = "Siamo i Demiurghi... Siamo con Dio...",
		["991163"] = "Ah ah ah... Davanti ai miei occhi resta solo l'oscurità. Finalmente, dimenticherò tutte le mie credenze passate... Dopotutto, si è dimostrato tutto futile...",
		["991188"] = "Nel nome di Handel, onora il giuramento di lealtà.",
		["9973329"] = "Contratto",
		["9941017"] = "Cosa ci fanno qui i monaci della Sacra abbazia?",
		["991018"] = "L'effetto che il Sendril ha su di te si sta rafforzando.",
		["661226"] = "Che stai dicendo?",
		["553119"] = "No.",
		["991191"] = "Ah... La scintillante medaglia dei cavalieri di Handel. Fammela vedere ancora una volta...",
		["995195"] = "Hai buttato la \"spazzatura\"?",
		["111051"] = "Teresa? Chi è?",
		["9971328"] = "L'odio è l'unica luce ad illuminare questa Foschia oscura...",
		["791294"] = "Se fosse stata lei a sopravvivere, forse ora non dovrei fare i conti con tutto questo.",
		["553902"] = "No",
		["995199"] = "Oh... Non c'è problema, li ho già buttati di sotto.",
		["9961050"] = "Non dimenticare che devi pagarmi ciò che mi spetta. Non osare neanche pensare di andartene così. Non approvo.",
		["991202"] = "Lord Ebbinghaus ci ha dato uno scopo, ci ha dato l'acqua del Fiume delle anime... Dobbiamo proteggere il suo favore...",
		["9962026"] = "",
		["334083"] = "I Sendril?",
		["991204"] = "Un dolce sogno, una calda luce abbagliante... Ah... Nella parte più profonda di quel ricordo... Un abisso remoto e intoccabile...",
		["113079"] = "Jerry? È tuo figlio?",
		["661400"] = "La maggior parte di loro si sono diretti alla Sala della Trinità per cercare la protezione del Procuratore.",
		["993133"] = "Vattene",
		["9973452"] = "Benedici",
		["441249"] = "Credi davvero che basti questo a sconfiggere la Terra benedetta?",
		["991205"] = "Non disturbarmi... Lasciami dentro a questo splendido ricordo, almeno per un po'... Ah... Ah... Lo sento di nuovo... Quel terribile rumore forte...",
		["991206"] = "(Inspira) Il tuo corpo... Quell'odore familiare, la stessa puzza dei ladri che arrivavano dalla nave dei pazzi... Tizi luridi, fin troppo sicuri di sé... La grande Ichthyosauria vi maledirà tutti di nuovo.",
		["773221"] = "Commercia",
		["441221"] = "Guida... Sei solo un povero stolto.",
		["991207"] = "Ah ah ah... Nessuno può lasciare Ichthyosauria, nessuno...",
		["111050"] = "Teresa...",
		["441254"] = "Sei stato tu a disperdere la polvere bianca in cielo?",
		["771236"] = "Quando vedi Teresa, dille che mi manca tantissimo.",
		["995209"] = "Ehi... Qui! Dico proprio a te! Qui!",
		["661184"] = "Potere, vita eterna, il Sigillo di Yekub può soddisfare tutti i desideri umani, a patto di accettare una terribile maledizione...",
		["991401"] = "Abbiamo il privilegio di poter compiere delle scelte, e queste scelte determinano chi siamo.",
		["111128"] = "Ah ah ah, un umano ancora vivo.",
		["991212"] = "Non preoccuparti... So prendermi cura di me. Comunque, lascia che ti dia qualche buona notizia. Finalmente ho scoperto il meccanismo che apre il Muro del silenzio!",
		["991213"] = "Quei tizi egoisti hanno nascosto la leva di controllo del meccanismo e la ruota dentata in due posti separati.",
		["991511"] = "Vuoi aiutarmi?",
		["991149"] = "Non ci credo...",
		["221294"] = "Sia benedetto il Santo Padre, sei tornato sano e salvo, signora Guida. È andato tutto bene?",
		["221165"] = "È nella Culla dei neonati, in attesa della nascita del bambino di Adamina... Mi ha dato il permesso di dirti che tornerà molto presto.",
		["993214"] = "Acconsenti",
		["551348"] = "La ricerca dell'eccelso è la minaccia definitiva dell'umanità.",
		["551385"] = "I mediocri sono sempre ossessionati dall'essere al di sopra degli altri.",
		["441154"] = "Se non fosse stato per Vaillant e il suo buon cuore, quel pericoloso allarmista non sarebbe vivo...",
		["661800"] = "La maggior parte di loro si sono diretti alla Sala della Trinità per cercare la protezione del Procuratore.",
		["991121"] = "Perché lo fanno?",
		["771217"] = "Te lo prometto...",
		["771142"] = "Marcioni?",
		["9941040"] = "Perché la Chiesa ordinò l'allontanamento dei Demiurghi?",
		["773241"] = "Benedici",
		["551069"] = "Le creature che vivono qui si sono fuse con l'ambiente circostante... è strano, ma paradossalmente bellissimo.",
		["991220"] = "Davvero...? Avresti potuto salvarci tutti. Non preoccuparti, starò qui, non andrò da nessuna parte.",
		["991050"] = "Prego. Sii consapevole dei limiti.",
		["991222"] = "Dopo aver fatto tutta questa strada, troverò di sicuro un modo per aprire il Muro del silenzio!",
		["331097"] = "Mi ha staccato la testa per andare a cercare una cura... Era la sola che volesse fare questo per me...",
		["331151"] = "Assolutamente sì!",
		["999283"] = "Ho trovato solo una delle due.",
		["999284"] = "Ah, accidenti! Abbiamo quasi finito! Non possiamo arrenderci ora!",
		["111115"] = "Chi è questa strana Strega? E perché è qui?",
		["777113"] = "Ce l'abbiamo fatta! Sebbene non mi vada a genio quell'uomo con la zeppola, è un tipo onesto. Finalmente potremo andare alle rovine.",
		["991229"] = "Spiacente, non ho trovato alcun indizio.",
		["9971326"] = "Umpf...",
		["991144"] = "So che sembra una cosa schifosa, ma è meglio che rischiare la vita combattendo quei mostri. Una volta che ti abitui non è niente di che.",
		["999286"] = "Dove potrebbe essere? Sarà nei paraggi, la troverò di sicuro.",
		["443190"] = "Novità su Teresa...",
		["884009"] = "La porta è chiusa, è tornato...",
		["991239"] = "...",
		["9951025"] = "Chi te l'ha detto?",
		["445088"] = "Sta cominciando.... Sta cominciando... La rivolta finale... Segui i guerrieri... Lascia liberi i sentimenti soppressi da tempo...",
		["9921007"] = "Ma Teresa è ancora a Heggem, dobbiamo tornare indietro!",
		["9921015"] = "Beh, a proposito di capelli rossi... guarda cosa ho trovato sull'Anacoreta.",
		["4994993021"] = "Consegna materiali",
		["771079"] = "Era un sotterraneo della Chiesa in cui rinchiudevano segretamente i prigionieri di cui volevano disfarsi, ma sono certa che tu voglia sapere l'altra parte della storia.",
		["113904"] = "No",
		["221160"] = "Guarda cosa ho trovato.",
		["442114"] = "Ma dopo la lunga permanenza nell'oscurità, forse si è troppo deboli per sopportare la luce.",
		["993258"] = "Presenta l'invito delicato",
		["884071"] = "/t2",
		["661402"] = "Vattene, non troverai nulla che ti serva.",
		["114034"] = "/t0",
		["991154"] = "E i tuoi genitori? Dove sono adesso?",
		["991259"] = "ma ricorda che ti è permesso prendere un solo tesoro, perciò non farti strane idee. Il maestro ti osserverà, e io farò lo stesso.",
		["995264"] = "Sbarazzatene in fretta! Prima che torni il maestro!",
		["551162"] = "Oh, si tratta solo di trucchetti dei Marcioni. Ecco alcuni suggerimenti. I Marcioni credono nell'eterno fluire della vita: il passato, il presente e il futuro. Solo sperimentando questi tre stadi della vita puoi trovare la verità.",
		["995266"] = "Non ti permetterò di rovinare la mia fortuna! Userò il tuo cranio per trasportare ancora più tesori!",
		["441062"] = "Non vuoi rispondere? Non importa, rimarrai deluso in ogni modo. Una talpa sta ostacolando i piani dei Demiurghi contro la Chiesa e non possiamo fargli spifferare le informazioni...",
		["991268"] = "Oh... Finito? Perché ci hai messo tanto? E adesso sparisci! Non voglio vederti mai più!",
		["9921012"] = "Se non mi credi, prova a chiedere al chiacchierone qui di fianco.",
		["553238"] = "Appoggia un frammento della meteora rossa",
		["221197"] = "Mamma, svegliati! Morirai se continui!",
		["9961029"] = "(Sospiro) Voglio solo dirti che nessuno può prevedere il futuro. Ma finché c'è fede, ci possono essere miracoli.",
		["551141"] = "Finalmente potrò tornare alla Terra benedetta...",
		["992270"] = "Unire i Frammenti della meteora rossa?",
		["993271"] = "No",
		["992470"] = "",
		["9971382"] = "No, non è ancora abbastanza per decifrare il codice.",
		["9971350"] = "Sembra pura follia chiederti di cercare le reliquie di una civiltà completamente sconosciuta.",
		["991273"] = "Ah ah ah... Incredibile. Era da tanto che nessuno si arrampicava fin quassù.",
		["777025"] = "È la luce più intesa che abbia mai visto e, in realtà, siamo sottoterra!",
		["221067"] = "Dov'è tuo padre?",
		["773185"] = "Vattene",
		["663140"] = "Accetta",
		["553367"] = "Sì",
		["995316"] = "C'è sempre un'eccezione alla regola, ma da questo momento in poi, ogni passo che farai ti porterà sempre più vicino alla morte.",
		["221224"] = "Teresa, ridammela!",
		["331110"] = "Chi avrebbe potuto immaginare che quello stupido cavaliere avrebbe tradito la nostra signora per quella pazza, ma... chi sa come andrà a finire... Questo anello potrebbe essere stato maledetto molti anni fa.",
		["991274"] = "Vergognoso! Approfittare della benevolenza di Lord Ebbinghaus per soddisfare brame egoistiche.",
		["791300"] = "Dio, perché hai chiamato a te quell'anima innocente, perché non hai portato via anche il caos e la follia... È per colpa delle mie bugie? È il mio contrappasso?",
		["991412"] = "La coscienza evanescente trova una nuova casa",
		["9999316"] = "Lo senti il vento che ulula? Sta per arrivare una tempesta...",
		["9999288"] = "Certamente.",
		["9999289"] = "Eppure, hai scelto di diventare ugualmente una Guida...",
		["777026"] = "A contatto con questa luce abbacinante come hanno fatto a sopravvivere dopo essere stati contaminati dai Sendril?",
		["791347"] = "Conosco questa leggenda, si trattava di un popolo che è tramontato con il sole, e di cui non si conosce più nulla.",
		["551185"] = "Hai incontrato il proprietario di questa capanna?",
		["994414"] = "",
		["221209"] = "È completamente impazzita.",
		["9999294"] = "I Notturni?",
		["663165"] = "Mostragli la spada.",
		["9971329"] = "Non ho più bisogno di questo braccio... Senza odio, non ci può più essere vendetta.",
		["551182"] = "Mi chiamo Jerold. Vengo dalle terre a est, il mio paese è già caduto in un sonno profondo da tempo, quindi non dovrei potermi trovare qui. Diversamente da Solas, il sonno è arrivato qui da poco.",
		["551072"] = "Il problema è... come si fa a trovare un posto che non è mai stato visto o visitato prima?",
		["441227"] = "Ah ah... Se ci tieni tanto a soddisfare la tua curiosità, trova la chiave che Elena ha nascosto nell'aula di tribunale.",
		["441239"] = "Questa singola lapide ricorda i nomi dei Demiurghi che si sono uniti alla rivolta di propria volontà e che hanno scelto di sacrificare la propria vita.",
		["663330"] = "<color=#DBBA7FFF>Prendi la spada</color>",
		["9931015"] = "Bandì Teresa molti anni fa... Nessuno sa cosa accadde, ma è rimasta là da allora.",
		["9999299"] = "Ironico. È questo il mondo che Ichthyosauria vuole proteggere così disperatamente?",
		["9999300"] = "...",
		["9999301"] = "Esiste una leggenda a Ichthyosauria: riunendo i tre Sigilli di Yekub tutti i Colossi cadranno, mettendo fine a ogni cosa.",
		["9999302"] = "Quando sul mondo tornerà il silenzio, splenderà la vera luce.",
		["9999303"] = "Non credo a queste assurde dicerie.",
		["331225"] = "Mors certa, hora incerta.",
		["9999305"] = "Mi dispiace, ma non sono un eroe venuto a salvare il mondo.",
		["224308"] = "/t0",
		["799307"] = "Non mi sono mai piaciuti gli umani. Sono deboli, eppure sono così pieni di sé, intessono intricate bugie e indulgono in incessanti guerre.",
		["799308"] = "Tuttavia, vedendo la tua perseveranza con Teresa, sento che potrebbe esserci ancora speranza per il genere umano.",
		["799309"] = "Prendi l'ultimo Sigillo di Yekub. Chissà, magari ti aiuterà a riportare indietro Teresa.",
		["799311"] = "So che anche questa volta seguirai il tuo cuore.",
		["799312"] = "Bisogna verificare le leggende. Se proteggere i Sigilli di Yekub ha come risultato solo quello di prolungare le sofferenze di questo mondo, non vedo perché continuare a farlo.",
		["1002100021"] = "Come ho fatto a tornare? C'è qualcosa di strano...",
		["441288"] = "Per come la vedo io, non esistono coincidenze né piani divini... Siamo noi gli artefici del nostro destino, con le nostre scelte.",
		["113067"] = "Vattene",
		["991210"] = "Ah... Quei mostri, non ti hanno seguito, vero?",
		["791334"] = "La tua mano sinistra è abbastanza inquietante.",
		["335226"] = "Potrò finalmente vedere il mio amore.",
		["9924031"] = "/t2",
		["441060"] = "Ah ah... Perché così stanco di essere vivo?",
		["116231"] = "",
		["991402"] = "Proverbi di Ebbinghaus: II",
		["791288"] = "Se trovi dell'altra argilla, portamela.",
		["223249"] = "Decidi!",
		["991405"] = "Ichthyosauria è l'unica città della felicità eterna.",
		["991406"] = "Proverbi di Ebbinghaus: IV",
		["991407"] = "L'autodisciplina è l'unico principio morale. La cosiddetta libertà non significa fare ciò che vuoi ma avere il controllo su di te.",
		["9931014"] = "Perché è a Exilium?",
		["991408"] = "Proverbi di Ebbinghaus: V",
		["991409"] = "Adempi al tuo dovere e le persone ti rispetteranno.",
		["992410"] = "",
		["9921040"] = "Non ne ho idea, ma quella sua maschera mi mette a disagio. Hai perso i sensi e dopo poco se n'è andato.",
		["331201"] = "Oh, prode Guida, ti sono davvero grato per ciò che hai fatto. Permettimi di sfidarti come cavaliere. Ti aspetterò nel luogo in cui è morta. Nel nome del mio amore!",
		["9921020"] = "Dammelo, adesso!",
		["441106"] = "Allora sono tranquilla, i suoi fratelli si prenderanno cura di lui. Quel ragazzo è sempre stato introverso.",
		["993412"] = "Inserisci il cuore azzurro",
		["441212"] = "Erano tutti miei compagni, ma la loro vita è stata distrutta da una menzogna.",
		["221115"] = "Sì... Ma purtroppo, è stata scelta per diventare la sposa del Santo Padre, come tutte le donne qui.",
		["998301"] = "",
		["994476"] = "/t0",
		["998304"] = "",
		["334048"] = "Impossibile aprire da questo lato.",
		["998305"] = "",
		["9999292"] = "Tutti ci aggrappiamo a qualcosa per sopravvivere. Come Ebbinghaus...",
		["661107"] = "Guida, sento che anche tu hai molti dubbi riguardo ciò che è accaduto qui, ma se indagherai sui Quattro pilastri per me, forse potremo trovare entrambi le risposte che cerchiamo.",
		["991429"] = "Pietra della Camera del tesoro",
		["9942012"] = "",
		["791345"] = "Guarda cosa ho trovato.",
		["991009"] = "Questo sembra un posto sicuro... Tu fa' in fretta e trova il tizio pallido.",
