		["9943032"] = "Parla",
		["993468"] = "Vattene",
		["991469"] = "La mia bancarella non riceve visite da tempo, forse posso farti un buon prezzo.",
		["663406"] = "No",
		["991272"] = "Silenzio e serenità, sonni tranquilli nella tua casa... Che tu possa andare lontano tra le onde di questo oblio...",
		["772390"] = "Vuoi entrare nella Tomba colossale?",
		["993470"] = "Commercia",
		["445057"] = "Non un passo di più, o ci sarà da pentirsene.",
		["996470"] = "",
		["771138"] = "Dimmi, dov'è Teresa?",
		["993471"] = "Vattene",
		["994555"] = "Un sigillo che può essere rotto.",
		["443218"] = "<color=#DBBA7FFF>Attacca Frollo.</color>",
		["1005100052"] = "Ci ha usati, ha usato la nostra ossessione.",
		["331059"] = "Basta guardarli, non riescono a ricordare il loro nome, né il loro passato... Vagano continuamente tra la vita e la morte.",
		["772261"] = "",
		["663801"] = "Sì",
		["994777"] = "Impossibile aprire da questo lato.",
		["113087"] = "<color=#DBBA7FFF>(Consegna il cappotto insanguinato)</color>",
		["441149"] = "La campana non è un artefatto di stregoneria: serve solo a impedire che i Demiurghi finiscano di nuovo alla deriva.",
