		["998303"] = "",
		["994477"] = "/t0",
		["441176"] = "Ah ah ah... Hai un'alta considerazione di me. La via per il potere è ricoperta di sangue e cicatrici. Voglio solo riportare i Demiurghi ai giorni antichi, a una vita tranquilla.",
		["994481"] = "/t0",
		["994483"] = "/t0",
		["441299"] = "Impossibile dirlo... Potrebbe volerci un po'.",
		["111089"] = "Grazie, Guida. Jerry mi ha chiesto di offrirti questo come segno della sua gratitudine. Noi non ne abbiamo più bisogno, consideralo un regalo.",
		["30274001"] = "/t0",
		["881078"] = "Cosa sto cercando? Cosa sto aspettando? Probabilmente non è nulla di importante. Tieni, prendi questo.",
		["994485"] = "/t0",
		["991143"] = "...",
		["444888"] = "Al momento non si può aprire.",
		["100310034999"] = "È impossibile da aprire senza una chiave.",
		["100410044999"] = "È impossibile da aprire senza una chiave.",
		["4994993013"] = "Aumenta la luminosità della lanterna della Foschia",
		["441251"] = "Il cancello per la Terra benedetta è aperto... È tempo che la Chiesa risponda dei propri crimini!",
		["100510054999"] = "È impossibile da aprire senza una chiave.",
		["100710074999"] = "È impossibile da aprire senza una chiave.",
		["100510055999"] = "Impossibile aprire da questo lato.",
		["100610065999"] = "Impossibile aprire da questo lato.",
