		["791301"] = "Sembra che tu abbia finito.",
		["553331"] = "Vattene",
		["884056"] = "Nulla conosci... La tua anima diverrà parte del nulla.",
		["331111"] = "Come mai la nobiltà della Terra benedetta appare a Exilium?",
		["114029"] = "/t0",
		["9921024"] = "Mi ha lasciato per colpa di una bugia e io la sto cercando da allora.",
		["1001100017"] = "Un'altra vittima, immagino... senza scopo... né speranza...",
		["551418"] = "Ah... una vorace ossessione... Ci ha accecati tutti, proprio così.",
		["771204"] = "Mio padre... Ucciso con le mie stesse mani.",
		["884067"] = "Bevi, se vuoi rivedere Teresa con cotanto fervore.",
		["1001100019"] = "La Terra benedetta... Dobbiamo raggiungere la Terra benedetta...",
		["1002100020"] = "Forse non sarei mai dovuta tornare in questo mondo...",
		["799314"] = "Spero che alla fine tu possa trovare la pace.",
		["791380"] = "Ho un obiettivo.",
		["1002100022"] = "Abbandona... Abbandona tutte queste domande senza senso...",
		["1002100023"] = "Vaillant, dove ti nascondi?",
		["9973346"] = "Non ho ancora trovato niente.",
		["1004100071"] = "Rimarremo con te... Quindi, prendi la nostra forza...",
		["1002100026"] = "Quando ti è mai importato della verità?",
		["9931016"] = "Erano come sorelle...",
		["332114"] = "Guida, puoi dirmi se fuori è sicuro?",
		["1002100027"] = "Concentrati... Uccidi... Non fermarti mai... Lascia che tutta Solas ammiri la tua danza...",
		["1002100028"] = "La percepisco! Una scatola di Yekub...",
		["557172"] = "Non ho molta voglia di rivedere quell'inquietante girasole, ma potrebbe essere l'unico a sapere a cosa servono queste pietre.",
		["663506"] = "No",
		["331159"] = "Quando Teresa scomparve, feci il voto di rimanere qui e pentirmi per il resto della mia vita.",
		["331222"] = "Fiuu, sei tu la nuova Guida?! Sono proprio il tipo che ti serve per sopravvivere un po' più a lungo nella Foschia oscura.",
		["1002100029"] = "È qui, nelle mani di qualcuno...",
		["9973382"] = "Contratto",
		["4994992100"] = "Vuoi tornare alla carrozza?",
		["791318"] = "Solo i pazzi riescono a sopravvivere in questo mondo.",
		["30135235"] = "Non mi toccare! Stai indietro!",
		["113906"] = "No",
		["9971459"] = "Sembrava sapere qualcosa... Ma si rifiuta sempre di dirmi la verità.",
		["557199"] = "Naturalmente sta a te la scelta. Ma ricorda, non è una questione di orgoglio o di onore, la cosa più importante è sopravvivere.",
		["1002100030"] = "Ottieni il Sigillo di Yekub...",
		["1002100031"] = "Ce l'ha lui...",
		["663901"] = "Sì",
		["441289"] = "Alcune scelte sono irrilevanti, altre hanno un enorme potenziale distruttivo. La scelta di Vaillant apparteneva alla seconda categoria.",
		["994001"] = "Maledizione! Ecco perché odio andare in barca.",
		["221288"] = "Signora Guida, puoi raccogliere un po' di erbe di Adamina per me? Mi scuso, ma devo continuare a mescolare senza mai fermarmi.",
		["111129"] = "Sorprendente... Ci sono ancora degli umani a Heggem! Ho fatto bene a rimanere qui.",
		["113081"] = "<color=#DBBA7FFF>Come hai fatto a proteggerti qui fuori?</color>",
		["1002100032"] = "Chi è?",
		["9931028"] = "...",
		["1002100033"] = "Uccidilo! Abbiamo dedicato tutto...",
		["9921021"] = "Voglio solo aiutarti!",
		["1002100034"] = "Ma non posso saperlo con certezza...",
		["1002100036"] = "Se lo merita! È un uomo malvagio! Un uomo che si nasconde dietro una donna!",
		["551334"] = "L'amore... conduce solo alla follia.",
		["221105"] = "Perché ti trovi qui?",
		["9924032"] = "Mostrami dove vuoi andare.",
		["9964012"] = "/t0",
		["9941029"] = "Nulla di speciale, avevo solo bisogno di un po' di riposo. Sarà dura nei prossimi giorni.",
		["221112"] = "La mia compagna è stata catturata, l'hai vista?",
		["1006100082"] = "Ha portato la gelida morte... Ha tagliato la tua carne calda...",
		["667055"] = "Come mai la luce dei Pilastri sacri si è indebolita così tanto? Se continua così, questo luogo sarà divorato dai Sendril. Non ci rimane molto tempo.",
		["791331"] = "Seppellirò questa mano nella mia città natale affinché riposi in pace. Forse non manca molto al momento in cui i Marcioni non saranno altro che una leggenda, proprio come i loro antenati. Anche noi finiremo con l'essere dimenticati, prima o poi, e svaniremo senza lasciare traccia.",
		["334049"] = "/t0",
		["1002100038"] = "Chi sei?! Esci dalla mia testa!",
		["1002100039"] = "Non deluderci... Non deludere i Demiurghi...",
		["9941014"] = "Hai sentito di Edith?",
		["994486"] = "/t0",
		["1003100040"] = "Ti ricordi?",
		["661700"] = "La maggior parte di loro si sono diretti alla Sala della Trinità per cercare la protezione del Procuratore.",
		["1003100041"] = "Come hai potuto dimenticare?",
		["1003100042"] = "Schiava di Exilium...",
		["1003100043"] = "La fonte dell'odio... ",
		["9961051"] = "È dura immaginare che un giorno il destino della Terra benedetta sarà nelle mani di una Guida... quel luogo imponente, circonfuso di luce, l'ultimo rifugio dell'umanità.",
		["1003100046"] = "Stai ancora rincorrendo il passato?",
		["1003100047"] = "Exilium è morto... Il tuo passato è morto...",
		["991517"] = "Prendi, ne avrai bisogno.",
		["1003100049"] = "Finché rimane, tragedie del genere non termineranno...",
		["1005100050"] = "È qui che l'ho incontrata...",
		["551111"] = "Devo assolutamente trovarla!",
		["331158"] = "Perché sei ancora qui?",
		["1005100051"] = "È lei ad averci condotto sulla via della distruzione.",
		["994666"] = "È necessario trovare la ruota dentata e la leva per attivare il meccanismo che apre il Muro del silenzio!",
		["771240"] = "Non preoccuparti. La tratterò come se fosse di famiglia.",
		["1005100053"] = "Eppure, i Demiurghi non avrebbero mai avuto una seconda possibilità senza di lei.",
		["4994992202"] = "Ah ah... È l'ora della ricompensa!",
		["663603"] = "Sì",
		["775098"] = "Ehm... ehm... La mia gola... brucia...",
		["221070"] = "Signora Guida, la mia mamma indossava un abito nero. È un abito molto bello. Se lo vedi, la riconoscerai subito.",
		["771261"] = "Oh, munifica Guida, ti auguro ogni successo.",
		["1001100007"] = "Sono sicura che fosse la voce di Vaillant... Devo trovarlo... Non posso lasciarmi sfuggire questa opportunità...",
		["117141"] = "L'Anacoreta sta usando il dolore per scacciare la Foschia oscura grazie alla sua forza di volontà, ma è chiaro che si sia trasformato in un Deturpato.",
		["1004100057"] = "La rabbia dei Demiurghi... Sta ancora bruciando...",
		["1004100058"] = "Ho fatto del mio meglio, ma la situazione sta peggiorando...",
		["557085"] = "Strano... Se non fosse per queste catapecchie, parrebbe che nessuno sia mai vissuto qui.",
		["551317"] = "Anime con un'ossessione?",
		["1004100060"] = "Vi ricordate ancora di me?",
		["221300"] = "Perché?",
		["1004100061"] = "Sono ancora vivi?",
		["1004100062"] = "No! No! Non sono altro che gusci vuoti...",
		["771080"] = "Perché?",
		["225234"] = "我的小心肝，杀了他，所有的肉都是你的！",
		["331202"] = "Farò in modo che finisca questa maledizione, amico mio.",
		["441146"] = "Pensi che sia la mia campana a causare il disagio di Edith? Se fosse così, perché tu non ne risenti?",
		["221296"] = "Ah... Altre erbe... Finalmente... Questo ridurrà molto i tempi... Ma non è ancora abbastanza.",
		["221145"] = "È stata Elmar. Era sempre gentile con me, come la mia mamma. Mi ha chiusa qui dentro per proteggermi.",
		["1004100064"] = "Ma devi portare a termine la tua missione...",
		["1004100065"] = "Ascolta attentamente...",
		["9931043"] = "Oh, tutto esaurito? Bisogna fare scorta di nuovo.",
		["1004100066"] = "Qualcuno ti sta chiamando...",
		["997098"] = "L'hai sentito? Qualcosa viene verso di noi...",
		["1004100069"] = "Porta con te i resti delle nostre anime...",
		["9973311"] = "Contratto",
		["551431"] = "I mediocri sono sempre ossessionati dall'essere al di sopra degli altri.",
		["1002100025"] = "Sono disposta a rischiare tutto... Ma ne vale la pena? Davvero non ti importa della verità?",
		["331183"] = "No... Sono alquanto diverse!",
		["1004100072"] = "Saremo con te...",
		["9933020"] = "Parla",
		["553161"] = "<color=#DBBA7FFF>Qual è la trappola?</color>",
		["9951019"] = "Le cose sarebbero andate diversamente se mi avessi ascoltata...",
		["1004100074"] = "È l'unico modo...",
		["1004100075"] = "Un tempo erano vivi e vegeti... Si fidavano di te...",
		["557241"] = "Qualcosa era custodito e venerato qui.",
		["793319"] = "Ho trovato delle reliquie antiche.",
		["552255"] = "Vuoi entrare?",
		["771098"] = "Quindi ti stava solo usando...",
		["1004100078"] = "Non ne vale la pena! Tutte le loro vite sacrificate per una persona morta? A cosa potrebbe mai servire?",
		["1004100079"] = "Fai la volontà di tutti i Demiurghi e cerca la risposta che brami.",
		["331093"] = "Durante la notte di nozze la mia fidanzata mi decapitò, infilzò il mio cuore e mi inchiodò a questa parete... Questa maledizione infinita non mi permette di riposare.",
		["2004100125"] = "È la prima Guida, ed è anche la prima pedina abbandonata dalla Terra benedetta.",
		["1002100037"] = "I Demiurghi non hanno mai temuto di versare sangue per ottenere ciò che vogliono!",
		["9963013"] = "Parla",
		["9941045"] = "Una volta ritornato nella Terra benedetta non avrai più la possibilità di comprare questi tesori, quindi è meglio acquistarli ora, finché puoi.",
		["551102"] = "Devo assolutamente trovarla!",
		["991035"] = "Oh, magnifico! Ora siamo tutti cibo per pesci.",
		["553904"] = "No",
		["551121"] = "La clessidra deve trovarsi dietro questa trappola, solleviamola.",
		["441188"] = "Ha detto che Vaillant è ancora vivo!",
		["1006100085"] = "Prendilo...",
		["224050"] = "È impossibile da aprire senza una chiave.",
		["1006100092"] = "Sta ancora nascondendo qualcosa...",
		["221215"] = "...",
		["1006100093"] = "La verità... La verità è che...",
		["1007100094"] = "Vaillant è morto ormai da tempo...",
		["333071"] = "Non farti ingannare da quelle distrazioni. Andiamo prima alla cappella.",
		["1007100096"] = "Rispondi!",
		["1007100097"] = "Elena, dimmi una cosa. Ha davvero importanza rispetto a tutto ciò che vuoi ottenere?",
		["1007100098"] = "Allora aiutami a realizzare il mio ultimo desiderio.",
		["1007100099"] = "Vai alla Tomba del Colosso. Metti fine a questo gioco senza senso e ti darò le risposte che cerchi.",
		["331064"] = "O forse no: il sole potrebbe essere solo un residuo della nostra immaginazione.",
		["8100100"] = "Finalmente ci incontriamo, vecchio amico e ombra...",
		["8100101"] = "Un codardo che desidera la supremazia... Un infido malvagio...",
		["112230"] = "",
		["223903"] = "Sì",
		["442127"] = "",
		["8100103"] = "Un burattino ignorante manipolato nella mani di un altro... Ingenuo, triste e patetico...",
		["1004100110"] = "Non è soddisfatto di essere un inutile campanaro... Non è soddisfatto di vivere sotto il rintocco della campana...",
		["100310032202"] = "Mors certa, hora incerta.",
		["791291"] = "Sembra che possa \"vedere\" coloro che ho esiliato nella Foschia oscura, che cercano di trascinarmi con loro.",
		["443304"] = "Commercia",
		["661220"] = "......",
		["441256"] = "Sì, per liberare la popolazione dalle catene che la vincolavano.",
		["1003100105"] = "Un altro cavaliere della Chiesa. Sai cosa fare.",
		["1003100106"] = "Per un idiota che risponde alla Chiesa la testa è solo una decorazione.",
		["1004100107"] = "La senti? È di nuovo quella campana fastidiosa...",
		["1004100108"] = "Un traditore con la fede scossa. Ha rovinato i nostri piani.",
		["1004100109"] = "Il desiderio di farsi notare...",
		["8100104"] = "Assaggerai nuovamente la sconfitta...",
		["1004100111"] = "Vuole fare un passo avanti e diventare speciale...",
		["993228"] = "No.",
		["441124"] = "È una vergogna... Mia figlia non ha abbandonato solo suo padre ma anche la sua identità per unirsi ai Demiurghi!",
		["1004100113"] = "Elena? Com'è possibile?",
		["334001"] = "Impossibile entrare.",
		["9941042"] = "Ne ho sentito parlare. Studiare il Colosso è considerato un tabù nella Terra benedetta. Tuttavia, visto che i Demiurghi non fecero scoperte sensazionali, la Chiesa fece finta di niente.",
		["221132"] = "Mamma... Mamma... Dove sei? Ti prego, rispondimi!",
		["771145"] = "Potrai anche non credermi ma dovresti riconoscere questo.",
		["551321"] = "So dove andrò a finire, e non sarà qui.",
		["991006"] = "Se è ancora vivo.",
		["1004100115"] = "No... Non sei lei. Sei un fantasma infuriato.",
		["991440"] = "Pietra dell'Heggie",
		["773254"] = "Commercia",
		["551095"] = "Ma come? Allora non hai mai incontrato nessun membro della Chiesa?",
		["441223"] = "Cosa intendi con \"menzogna\"?",
		["997136"] = "A quanto sembra non possiamo passare da qui. Dobbiamo trovare un'altra strada...",
		["441296"] = "E tu cosa farai?",
		["991217"] = "L'ho trovata da un'altra parte... Uh, considerala un riciclo.",
		["771238"] = "Certo, purché rientri nelle mie capacità. Dimmi di cosa si tratta.",
		["661111"] = "Mi piaci proprio, Guida. Ti aspetterò alla Camera della Sala della Trinità. quel posto è abbastanza sicuro, quindi porta quel che trovi e lo scambieremo con alcune vecchie storie.",
		["221142"] = "La caccia?",
		["9951035"] = "Parli troppo per i miei gusti.",
		["772187"] = "",
		["993001"] = "Sì",
		["9951027"] = "Non è questione di cambiamenti, è questione di cominciare a vedere il mondo per quello che è.",
		["221299"] = "Abbi fiducia, signora Guida. Sono la mia famiglia, non farei mai loro del male.",
		["9921009"] = "Era in cima alla vetta! L'ho vista con i miei stessi occhi!",
		["9921017"] = "Il diario di una Guida, pieno zeppo di simboli, enigmi e vaneggiamenti...",
		["9971300"] = "Dio, perché hai chiamato a te quell'anima innocente, perché non hai portato via anche il caos e la follia... È per colpa delle mie bugie? È il mio contrappasso?",
		["441195"] = "Mi stai minacciando? Non dimenticare che sono la sola che possa aprire il cancello per la Terra benedetta. Improvvisamente è tutto più semplice: uccidi quella talpa, poi vieni da me nell'aula di tribunale.",
		["2001100121"] = "Elena, anche questo uomo ha cercato di confrontare la luce magnifica, proprio come te.",
		["2001100122"] = "L'Ossessione l'ha rovinato, e ora è un fanatico senza scopo.",
		["1006100081"] = "Quella spada...",
		["2004100126"] = "Alla Terra benedetta serve un eroe, ma non ciò che è diventato.",
		["2004100127"] = "La gente deve sapere la verità...",
		["2004100128"] = "La gente vuole solo la speranza.",
		["791358"] = "E se non ci riuscissimo? Non voglio rimanere a rimuginare su questo enigma per il resto della vita.",
		["9943023"] = "Commercia",
		["771087"] = "Eckhart? L'ho incontrato nella Terra benedetta.",
		["331071"] = "Ti riporta alla mente brutti ricordi?",
		["776261"] = "",
		["791332"] = "Ma qualcuno terrà viva la tua memoria, forse qualcuno della tua terra natia.",
		["994999"] = "È impossibile da aprire senza una chiave.",
		["663802"] = "No",
		["665133"] = "Vaillant... tornerà da noi... Grazie per tutti i dispiaceri che hai dovuto sopportare per colpa nostra...",
		["221226"] = "Ti prego, è la mia vita. Ti ho salvata a Katib, non puoi farmi questo!",
		["663167"] = "Dimmi tutto quello che sai...",
		["1004100073"] = "Vaillant... Perché? Perché non puoi risparmiarli?",
		["9961015"] = "Qualsiasi cosa accada, voglio che tu sopravviva.",
		["991514"] = "A Ichthyosauria, se vuoi avere un passato, devi accettarlo senza discutere, sia esso buono o cattivo.",
		["9999297"] = "Non voglio più avere a che fare con nessuno. E non voglio nemmeno essere in debito con nessuno. Dopo averti aiutato a ritrovare Teresa, me ne andrò.",
		["100610062205"] = "Materiale insufficiente.",
		["997233"] = "Ce l'hai fatta!",
		["111109"] = "Finalmente sei di ritorno! Sbrigati a sfregare la pietra contro il guscio!",
		["331120"] = "È ora di andare, prima che scenda il buio. Dovrei andarmene in fretta questo posto.",
		["223314"] = "Non ancora.",
		["221180"] = "Non posso abbandonarla in questa situazione, devo rimanere al suo fianco...",
		["223190"] = "<color=#DBBA7FFF>Mantello verde? Ho visto tua figlia.</color>",
		["791302"] = "...",
		["661099"] = "Sono solo un uomo cieco senza importanza, cara Guida.",
		["9999287"] = "Mentre attraversavi la Foschia oscura, hai mai temuto di morire?",
		["771221"] = "Ora vai! Trova Teresa, io resterò qui.",
		["444184"] = "/t0",
		["664999"] = "È impossibile da aprire senza una chiave.",
		["9951054"] = "Non puoi mai sapere cosa nasconde la Chiesa...",
		["554068"] = "/t0",
		["9931017"] = "Che sia stato il destino oppure no, per lei fu una dura scelta... Da allora ne ha pagato il prezzo, un prezzo molto alto, in effetti.",
		["998302"] = "",
		["111055"] = "Forza, la mia carrozza non aspetterà in eterno.",
		["222314"] = "",
		["661185"] = "Dimmi tutto quello che sai...",
		["667084"] = "Accidenti, è proprio andata.",
		["441175"] = "Quindi vorresti diventare il loro nuovo leader, eh?",
		["553281"] = "Commercia",
		["791355"] = "Abbiamo fatto qualche passo avanti. Riesci a decifrare qualcos'altro?",
		["9921025"] = "Ed è per questo che sei diventato una miserabile Guida? Ne è davvero valsa la pena?",
		["775113"] = "Fermati, non avvicinarti! Tra me e questa prigione è finita...",
		["337082"] = "Dovrebbe essere morto, cos'è che lo tiene in vita?",
		["115147"] = "E tu sarai il prossimo sacrificio!",
		["551097"] = "Bisogna uscire da lì prima che la sabbia nella clessidra si esaurisca, o ti trasformerai in un Deturpato.",
		["221212"] = "Mamma! Mamma!",
		["221289"] = "Ci posso provare.",
		["553329"] = "Riempi flacone di Melos.",
		["9921022"] = "Teresa... è mia moglie, in realtà. Non la vedo da molto tempo.",
		["441108"] = "Oh, ma prima, straniero, tieni: come ricompensa.",
		["773104"] = "Accetta",
		["773187"] = "Dagli il Bocciolo rosa lucente",
		["994479"] = "/t0",
		["113907"] = "Sì",
		["991444"] = "Pietra della Natura umana",
		["221064"] = "Cosa diavolo è successo qui?",
		["114020"] = "/t2",
		["4994994003"] = "/t0",
		["551294"] = "È tutto pronto, possiamo partire. La fortuna ci sorride, troveremo sicuramente il Sigillo di Yekub sotto la Torre sacra.",
		["777039"] = "Sembra essere il maestro di Norwood, la sua voce mi suona stranamente familiare.",
		["791370"] = "Non ti preoccupare, terrò a mente tutti i tuoi insegnamenti. Non sono più la Viola che hai conosciuto.",
		["441258"] = "Fu Edith a crearla, tramite un antico rituale dei Marcioni. La polvere è stata poi trasformata in cristalli e fusa con gli esseri umani, per concedere loro maggiore potere.",
		["9934226"] = "/t0",
		["441097"] = "Abbiamo già fatto così tanto oggi... Sono orgogliosa dei miei due figli. Se fossi solo un po' più giovane, li seguirei anche io.",
		["331171"] = "Sacrificherei di nuovo la mia testa per poterla salvare.",
		["111039"] = "Heggem sembra essere... malato.",
		["9943029"] = "Commercia",
		["553189"] = "Come va la ferita?",
		["552291"] = "",
		["553163"] = "Dove sono gli altri membri della Chiesa?",
		["663902"] = "No",
		["4994992201"] = "Ah ah... È l'ora della ricompensa!",
		["336223"] = "",
		["9971288"] = "Se trovi dell'altra argilla, portamela.",
		["9961052"] = "Terrence, ancora una volta hai confermato il limite della cattiva sorte. Ma a volte il caos non viene per nuocere e può far vedere la verità alle persone.",
		["663265"] = "Vattene",
		["551371"] = "Occultare la verità è coltivare ignoranza e intenzioni malvagie.",
		["551368"] = "L'amore... conduce solo alla follia.",
		["221137"] = "Non preoccuparti signora Guida, te lo dirò. Ma prima trova Elmar, ti prego. Voglio che mi faccia uscire, sono molto preoccupata per la mia mamma.",
		["441147"] = "Pensaci bene prima di prendere una decisione...",
		["9973459"] = "Vuoi tornare alla carrozza?",
		["554271"] = "/t1",
		["227079"] = "Riconosco quest'odore terribile, hanno cessato di essere umani!",
		["221071"] = "Ti prego, salvala...",
		["9941026"] = "Mi dispiace di non aver detto tutto.",
		["551314"] = "Un becchino.",
		["991465"] = "Mi dispiace, niente sconti, ma tutti i miei prodotti valgono il loro prezzo pieno.",
		["994286"] = "/t0",
		["442102"] = "",
		["551352"] = "L'amore... conduce solo alla follia.",
		["663604"] = "No",
		["663605"] = "Sì",
		["444083"] = "/t0",
		["112201"] = "Vuoi mangiare l'Uovo del messaggero?",
		["551277"] = "Gli ingressi al sottosuolo sono stati sigillati, dobbiamo trovare un'altra strada...",
		["441128"] = "Albertus, Kassandra, quand'è che torneranno quei due idioti?",
		["551432"] = "Vendetta indulgente... convergenza ardente... tentativo rischioso...",
		["9952034"] = "",
		["100664001"] = "Impossibile entrare.",
		["791328"] = "L'odio è l'unica luce ad illuminare questa Foschia oscura...",
		["30274006"] = "/t0",
		["772193"] = "",
		["551401"] = "...",
		["552381"] = "Costo: 1 Miele di condensazione e 210 ossa di Anthozoan. Vuoi davvero procedere?",
		["221316"] = "A esclusione del Santo Padre, sei la persona più generosa che io abbia mai incontrato.",
		["9963043"] = "Vattene",
		["221167"] = "Mamma... Mamma... Dove sei? Ti prego, rispondimi!",
		["332140"] = "",
		["551428"] = "I piccoli e deboli non dubitano neanche delle bugie più ovvie.",
		["661230"] = "Questa chiave...ha lo stesso motivo di quella del cancello a Katib.",
		["223904"] = "No",
		["993141"] = "Che cosa ci fai qui?",
		["444220"] = "/t0",
		["225052"] = "Oh, gentili signori! Aiutatemi, vi prego!",
		["551260"] = "Gli esseri umani... non sono in grado di liberarsi dai vincoli delle emozioni. Ma non ha importanza... Se le cose stanno così, ti conviene andare nella Terra benedetta, prima che sia troppo tardi.",
		["771081"] = "Molti anni fa la Tomba colossale venne aperta accidentalmente dalla Chiesa. In seguito, fu richiusa dal Procuratore. Ovviamente, come hai potuto vedere con i tuoi stessi occhi, si continuavano a portare avanti esperimenti.",
		["9972380"] = "",
		["9923041"] = "Vediamo cosa offri.",
		["791348"] = "Sto cercando di raccogliere indizi da quando ho lasciato la Terra benedetta, ma non riesco a venire a capo della questione.",
		["221146"] = "Dimmi, come posso trovare il Santo Padre?",
		["773003"] = "Sì",
		["100310035998"] = "Impossibile aprire da questo lato.",
		["991158"] = "Non ho visto niente di nuovo per tantissimo tempo. Ho sempre l'impressione che debba succedere qualcosa di brutto.",
		["771146"] = "Questo è... Dove l'hai preso?",
		["551326"] = "La tua ossessione sembra essere più forte... Non dimenticare, sarò sempre qui ad aspettare il giorno in cui sarai pronto a varcare la soglia del cimitero.",
		["884003"] = "Oh, uomo accecato dall'ossessione, ci incontriamo infine... Hai abbandonato l'oscura caverna in cambio del deserto di verità...",
		["661211"] = "Terrence, non sei in grado di aiutare, quindi torna alla carrozza.",
		["221143"] = "Il Santo Padre ci ha ordinato di catturare chiunque attraversi Adamina. Sapevo che fosse una cosa sbagliata da fare, all'inizio nessuno era d'accordo, ma poi ci siamo abituati all'idea e adesso nessuno oppone resistenza.",
		["9971325"] = "Non ti preoccupare, il mio braccio è ormai un'ottima arma da avere a portata di \"mano\". L'odio serve solo ad alimentare il suo potere.",
		["9952045"] = "",
		["554169"] = "/t0",
		["9971301"] = "Sembra che tu abbia finito.",
		["221179"] = "Spero che ti aiuterà ad attraversare la Foschia oscura.",
		["331096"] = "Non riesco proprio a capire... Perché mai avrebbe dovuto farlo?",
		["792294"] = "",
		["9951036"] = "Preferiresti che me ne andassi?",
		["791289"] = "Mi dispiace che tu mi debba vedere così.",
		["9951028"] = "È per quello che hai abbandonato la Chiesa e sei diventato una Guida?",
		["551344"] = "L'amore... conduce solo alla follia.",
		["221164"] = "Ma è... Hai incontrato la mamma! È viva!",
		["9953035"] = "Vattene",
		["111232"] = "Ah ah ah, umano sempliciotto.",
		["441192"] = "Dov'è adesso?",
		["663803"] = "Sì",
		["665190"] = "È l'unica via... per la Terra benedetta...",
		["661318"] = "Hai avuto la tua occasione...",
		["334002"] = "Non posso andarmene proprio adesso...",
		["991057"] = "Sapevo che avresti ceduto all'ingordigia.",
		["111118"] = "Capelli rossi, abito di lino e strani disegni sulla sua pelle.",
		["553310"] = "Sì",
		["117021"] = "Terrence, aspetta.",
		["553330"] = "Offri Vino di miele splendente",
		["221181"] = "Non posso abbandonarla in questa situazione, devo rimanere al suo fianco...",
		["774147"] = "/t0",
		["113112"] = "<color=#DBBA7FFF>Sfrega la pietra contro il guscio</color>",
		["9961016"] = "Certo che anche io voglio sopravvivere, ma non voglio neanche vederti morire davanti ai miei occhi. Non c'è proprio altro modo?",
		["552365"] = "Costo: 1 Miele di condensazione e 150 ossa di Anthozoan. Vuoi davvero procedere?",
		["100510052201"] = "Consegna le Ossa di Anthozoan.",
		["4994993023"] = "Consegna materiali",
		["771226"] = "Sono solo preoccupata che tu non sia pronto...",
		["331095"] = "Certo non accuso lei per questo... Senza la stella rossa tutto ciò non sarebbe accaduto... diventare un mostro o conservare la dignità: la scelta è ardua.",
		["551068"] = "Oh... non sapevo che questo posto fosse così... vivo.",
		["774138"] = "/t0",
		["4994996014"] = "",
		["114149"] = "/t0",
		["9921026"] = "È la punizione che mi merito. Cosa dice il diario?",
		["441214"] = "No, anche lei è stata vittima della stessa menzogna.",
		["221066"] = "Non riuscirei mai a scappare, quindi mi sono nascosta qui.",
		["551399"] = "Ah, Guida, hai cambiato idea?",
		["771211"] = "Mi spiace, Viola, ma ora devo andare.",
		["9941019"] = "In seguito, i Demiurghi furono esiliati a Edith e persero la protezione della luce, vivendo come non-essenti.",
		["557170"] = "La Pietra del presente... Cosa significa?",
		["667085"] = "Che battaglia intensa... Spero che Elena non c'infastidisca più.",
		["661805"] = "Dio, non c'è nulla che possa fare. Chiunque merita la possibilità di vivere meglio di me.",
		["771231"] = "Pensi che adesso il tuo Dio mi darebbe la sua benedizione?",
		["554172"] = "/t0",
		["662601"] = "Aprire la porta?",
		["113908"] = "No",
		["227121"] = "Questo tuo nuovo amico ha certamente un'aria misteriosa.",
		["9966023"] = "",
		["331072"] = "Non è cambiato nulla... Sono passati così tanti anni...",
		["997200"] = "A quanto pare sono bloccati in una specie di ciclo di memoria. Proprio come questo Fiume delle anime che non scorre più... è più crudele della morte.",
		["553350"] = "Sì",
		["551094"] = "Perché ti importa tanto della clessidra?",
		["553282"] = "Vattene",
		["9921031"] = "Mostrami dove vuoi andare.",
		["551422"] = "Occultare la verità è coltivare ignoranza e intenzioni malvagie.",
		["4994993011"] = "Offri 100 funghi spettrali",
		["1006100080"] = "Senti ancora il dolore nel petto.",
		["551332"] = "I piccoli e deboli non dubitano neanche delle bugie più ovvie.",
		["991419"] = "Ci sono cinque scanalature sulla porta. Inserisci le gemme nelle scanalature in ordine.",
		["775110"] = "Sì! In alto! In alto! Ci sei quasi... Ah ah ah... Riconquisteremo la libertà!",
		["776241"] = "",
		["558303"] = "",
		["221217"] = "Posso aiutarti in qualche modo?",
		["773105"] = "Vattene",
		["9961053"] = "Certo, a me non interessa. Per me il caos è solo latore di maggiori opportunità di guadagno.",
		["661324"] = "Andiamo, ogni secondo che passa qui è un tormento.",
		["771074"] = "Ero sicuro che non avresti fatto ritorno.",
		["551184"] = "Non sono affari miei, ma ti consiglio di stare alla larga dalla Torre sacra, se ci tieni alla vita. Quei pazzi non hanno idea di quello che stanno facendo.",
		["774194"] = "/t0",
		["661166"] = "Stessa iscrizione dell'elsa... Il che conferma le mie supposizioni... Il Procuratore non può allontanarsi con l'indebolimento dei Pilastri sacri.",
		["991221"] = "Hai cambiato idea?",
		["663262"] = "Vattene",
		["661173"] = "Nessuno lo sa, solo Vaillant e il Procuratore tornarono vivi. È alquanto strano che entrambi non abbiano parlato di ciò che avvenne laggiù e tu già sai il resto... Ma ciò che nascondono ha sicuramente a che fare coi Marcioni.",
		["551291"] = "Oh, sei tu, amico mio. Come va? Hai trovato la clessidra?",
		["112130"] = "",
		["331196"] = "Aspetta, Terrence... Portami con te... Visto che Teresa è ancora viva, non ha più senso che io rimanga qui.",
		["443336"] = "Potrei aver bisogno di un po' più di tempo.",
		["448314"] = "",
		["221476"] = "Grazie per la gentilezza, signora Guida, ma voglio rimanere qui. Adamina è la mia casa, dopotutto.",
		["555252"] = "Fallito! Abbiamo fallito! Il Supervisore non mi perdonerà mai...",
		["337104"] = "La nobiltà della Terra benedetta! Non dovrebbero essere qui.",
		["441234"] = "La fede ci ha accordato un potere immenso, ma ci ha anche offuscato la vista di fronte alla vera luce. Attraverso l'oscurità, la nostra ardua ricerca, le emozioni represse, l'umiliazione, il sudore e il sangue si trasformeranno in una marea che metterà fine alla tirannia dell'idolo.",
		["441140"] = "Non ho alcuna intenzione di ucciderti, quindi torna da Elena e dille che sta sprecando il suo tempo.",
		["551372"] = "La ricerca dell'eccelso è la minaccia definitiva dell'umanità.",
		["9972294"] = "",
		["663903"] = "Sì",
		["774034"] = "I miei piani non prevedono margine d'errore... Mi dispiace, dovrai restare qui per un po'.",
		["113083"] = "Non ho tempo per queste cose. Non posso restare qui.",
		["993270"] = "Sì",
		["227185"] = "La Tomba colossale? Quindi il Santo Padre continua ad avere figli per poter trovare la Tomba colossale?",
		["662801"] = "Aprire la porta?",
		["771263"] = "La mia bancarella non riceve visite da tempo, forse posso farti un buon prezzo.",
		["227075"] = "Chi è questo Santo Padre di cui parlavano?",
		["4994991006"] = "Oh... Certo, capisco. Il brivido di superare la paura dell'oscurità può dare alla testa... Ma non puoi addentrarti ancora di più nella Foschia oscura con quella tua lanterna. Devi trovare un modo per rendere la sua luce più forte.",
		["771257"] = "Forse questo viaggio non ha niente a che vedere con la vendetta, forse è solo una deviazione necessaria per permetterti di ricongiungerti con Teresa...",
		["773189"] = "Mi dispiace...",
		["662107"] = "",
		["551353"] = "I mediocri sono sempre ossessionati dall'essere al di sopra degli altri.",
		["9971322"] = "In effetti, sta diventando sempre più difficile mantenere il controllo... Mi chiedo quanto tempo abbiamo.",
		["9941032"] = "Credo ancora che non sia diversa dalla vecchia buona Teresa che conosco.",
		["551402"] = "...",
		["661222"] = "Mi dispiace... è successo tutto così in fretta...",
		["991211"] = "È troppo pericoloso. Non dovresti stare qui.",
		["995279"] = "Che peccato, non l'avevo ancora realizzato: il vostro capo è un mostro!",
		["221259"] = "Dove mi trovo? Terrence, quella ragazzina... Ci hanno ingannati!",
		["221283"] = "Ah... signora Guida... Sto alleviando le sofferenze delle Madri... Dopo tutto, una volta anche io ero una di loro...",
		["552117"] = "",
		["663606"] = "No",
		["112901"] = "Vuoi tornare alla carrozza?",
		["221302"] = "Ma i discepoli sembrano aver perso il controllo, di recente...",
		["661705"] = "Dio, non c'è nulla che possa fare. Chiunque merita la possibilità di vivere meglio di me.",
		["9923042"] = "Vediamo cosa offri.",
		["551405"] = "...",
		["333116"] = "<color=#DBBA7FFF>No, non lo è.</color>",
		["221317"] = "Tutte le madri di Adamina ti ringrazieranno. È un segno di stima, ti prego di accettarlo. Spero che possa lenire il tuo dolore durante il viaggio.",
		["993058"] = "Prendi",
		["221110"] = "Qualcuno ha scoperto come iniettare una piccola quantità di Sendril nei corpi dei discepoli di Adamina. È questo che impedisce la loro crescita, insieme alla luce del Colosso.",
		["771082"] = "Quindi tu lo sapevi dall'inizio?",
		["771195"] = "Eckhart ha sacrificato Teresa per aprire il cancello della Tomba colossale! Ora è troppo tardi.",
		["9971290"] = "Cosa ti succede?",
		["221147"] = "Non preoccuparti signora Guida, te lo dirò. Ma prima trova Elmar, ti prego. Voglio che mi faccia uscire, sono molto preoccupata per la mia mamma.",
		["773004"] = "No",
		["114033"] = "Non è un buon segno.",
		["221152"] = "È nella Culla dei neonati, in attesa della nascita del bambino di Adamina... Mi ha dato il permesso di dirti che tornerà molto presto.",
		["4994994888"] = "Al momento non si può aprire.",
		["551105"] = "Era questa che stavi cercando?",
		["9951037"] = "Il contrario, piuttosto. Non ti permetterò di scappare, dobbiamo agire in segreto nella Terra benedetta...",
		["773245"] = "Vattene",
		["661229"] = "Se conosco il Procuratore, teneva nascosto qualcos'altro... Ho infatti trovato questa.",
		["773392"] = "<color=#DBBA7FFF>Sì</color>",
		["552415"] = "Costo: 1 Miele di condensazione e 240 ossa di Anthozoan. Vuoi davvero procedere?",
		["9921019"] = "Calmati, Terrence.",
		["773205"] = "Vattene",
		["331061"] = "A cosa stai pensando?",
		["551125"] = "È tutto pronto, possiamo partire. La fortuna ci sorride, troveremo sicuramente il Sigillo di Yekub sotto la Torre sacra.",
		["991506"] = "Se solo potessimo riavere l'acqua, potremmo trovare di nuovo il significato dell'esistenza... La nave che continua a comparire nei miei sogni... Da dove arriva?",
		["884004"] = "Alla ricerca di ciò in cui credi... Hai paura?",
		["663804"] = "No",
		["665191"] = "Ah... Non puoi tornare indietro... Si insospettirebbero... Seppellirò il segreto per sempre.",
		["661319"] = "Hai avuto la tua occasione...",
		["791330"] = "Dove andrai adesso?",
		["9961017"] = "No...",
		["551166"] = "Sembra che tu abbia imparato i segreti dei Marcioni. Aspetta un attimo, saggia Guida...",
		["441066"] = "Anni fa ti avrei dato ragione. Ma ora, Guida, i Demiurghi hanno trovato una forza incredibile per combattere la Chiesa.",
		["551235"] = "Via di qui, presto! Questo non è posto per una Guida!",
		["227321"] = "Il suo odore pungente assomiglia sempre di più a quello dei Sendril.",
		["661178"] = "È come ho detto, i ciechi sono molto più sensibili...",
		["791299"] = "Temo di no.",
		["661150"] = "Ma fare dei cambiamenti non significa che devi morire...",
		["100610062203"] = "Sì",
		["441193"] = "Ha lasciato Edith per la Terra benedetta.",
		["541001"] = "Dimmi, perché Teresa vuole distruggere la Terra benedetta?",
		["551159"] = "Quindi sei una persona normale... Solo le persone normali possono esplorare l'origine dei sogni.",
		["771075"] = "Terrence... Io...",
		["661169"] = "Quegli stupidi Demiurghi hanno tentato di condurre l'ira vendicativa qui e sembra che tu abbia a che fare con questa storia...",
		["662195"] = "In questo viaggio, alcuni personaggi non saranno disponibili, vuoi continuare?",
		["791376"] = "Hai trovato dell'altra argilla?",
		["791341"] = "È inciso in uno strano alfabeto...",
		["791365"] = "I nostri sforzi verranno ripagati, e anche tu ritroverai i tuoi ricordi.",
		["991059"] = "Lo senti? È il battito cardiaco di Ichthyosauria... Che piacevole melodia...",
		["4994993022"] = "Consegna materiali",
		["554130"] = "/t0",
		["111102"] = "Chi sei?!",
		["441173"] = "Salvarli. Capirai quando avrò suonato tutte le campane. Almeno sai perché Elena ha sigillato Edith?",
		["441235"] = "Oggi, l'impegno dei Demiurghi verrà finalmente ripagato. Questi nomi appartengono ai nuovi dèi, coloro che hanno scelto di prendere controllo del proprio destino, garantendo lo stesso anche alle generazioni future.",
		["331092"] = "Non conosco molte persone che oserebbero inchiodare al muro un cavaliere della Chiesa.",
		["9921027"] = "Mi pare di capire che la sua prossima destinazione sia un Colosso di nome Adamina.",
		["9921035"] = "La figura che ho visto... era reale, oppure solo un sogno?",
		["793275"] = "Contratto",
		["9971346"] = "Questa lastra narra le leggende dei Samarosiani... \"Il totem sacro dei Samarosa... ci guida verso la dimora di Dio...\"",
		["557171"] = "La Pietra del futuro? Serve a predire il futuro?",
		["114022"] = "Ma cosa diavolo?!",
		["791383"] = "...",
		["441219"] = "Allora non c'è nient'altro da dire. Ti garantisco che questa sarà l'ultima decisione che prenderai nella tua vita.",
		["221266"] = "Non è sicuro qui, torniamo alla carrozza.",
		["884055"] = "/t2",
		["9926037"] = "",
		["337174"] = "Anche io riesco a sentire la profonda tristezza di questa canzone.",
		["443070"] = "Allora dimmi che accidenti è successo qui!",
		["994482"] = "/t0",
		["793324"] = "Mi dispiace.",
		["334157"] = "/t0",
		["551130"] = "Hai sentito? I fantasmi dei Marcioni... Vagano qui attorno, sento i loro sussurri!",
		["100610062204"] = "No",
		["771232"] = "Non posso darti alcuna risposta, io stessa non sono certa dell'esistenza di Dio. Ma dopotutto, continuiamo a scegliere in cosa credere e andiamo avanti senza esitazione, non è così? Hai già trovato ciò che cercavi, quindi segui il tuo cuore e metti fine a questo viaggio.",
		["441122"] = "Tu sei...",
		["993413"] = "Vattene",
		["221198"] = "È un'unione sacra, una benedizione divina. Mia splendida figlia, capirai quando sarai grande e diventerai anche tu una sposa. Ah, la mia pancia!",
		["441072"] = "Dobbiamo solo fermare la campana, è l'ultimo ostacolo che rimane.",
		["9971370"] = "Non ti preoccupare, terrò a mente tutti i tuoi insegnamenti. Non sono più la Viola che hai conosciuto.",
		["991043"] = "A quanto pare non hai intenzione di mollare. Beh, allora vai a dare un'occhiata lassù, alla fonte del Fiume delle anime... Forse ti aspetta una piacevole sorpresa.",
		["331073"] = "Ha ancora addosso il fetore della morte.",
		["661500"] = "La maggior parte di loro si sono diretti alla Sala della Trinità per cercare la protezione del Procuratore.",
		["994275"] = "Stai ancora dormendo? Lord Ebbinghaus ha sconfitto il tuo schifoso capo molto tempo fa.",
		["9966032"] = "",
		["991198"] = "...",
		["227074"] = "Ho l'impressione che non siamo esattamente i benvenuti qui... o forse lo siamo fin troppo.",
		["553123"] = "Trovata.",
		["771100"] = "Sei... quello che mi è apparso in visione... Chi sei?",
		["663904"] = "No",
		["552328"] = "",
		["441279"] = "Bisogna fermarla, o non ci sarà scampo per i Demiurghi che si sono sottoposti al rituale.",
		["777041"] = "Questa luce rossa... L'ho già percepita altrove...",
		["791317"] = "Sei un pazzo.",
		["331190"] = "Teresa portò via da Adamina la stessa cosa.",
		["224052"] = "/t0",
		["663195"] = "<color=#DBBA7FFF>Sì</color>",
		["553394"] = "Accetta",
		["551380"] = "La ricerca dell'eccelso è la minaccia definitiva dell'umanità.",
		["9961030"] = "Non riesco a credere che sia tu a dire una cosa del genere.",
		["664097"] = "/t0",
		["111132"] = "Ah ah ah, umano sempliciotto.",
		["1006100091"] = "Lo percepisco...",
		["551412"] = "...",
		["222903"] = "Vuoi tornare alla carrozza?",
		["661174"] = "Vuoi dire che quella razza antica e in pericolo è coinvolta?",
		["9973389"] = "Commercia",
		["994004"] = "/t0",
		["9941028"] = "Il tuo comportamento nei confronti della Terra benedetta mi sorprende. Non mi aspettavo che tu, una Guida, avessi qualcosa che ti tenesse legato quaggiù.",
		["113084"] = "<color=#DBBA7FFF>Vedrò cosa posso fare.</color>",
		["551421"] = "Vendetta indulgente... convergenza ardente... tentativo rischioso...",
		["338231"] = "",
		["443301"] = "Commercia",
		["881073"] = "O ti sei esiliato nuovamente come prima? Questo luogo accoglie solo i più tenaci, se sei troppo vicino alla luce finirai con l'essere divorato...",
		["558304"] = "",
		["337105"] = "Non riusciremo a sconfiggerli da soli, forse possiamo entrare da un altro cancello?",
		["991203"] = "Ah ah... Questo è il destino di chi calpesta la benevolenza di Lord Ebbinghaus...",
		["774029"] = "Viola, per quanto astuta tu possa essere, hai preso proprio una decisione stupida.",
		["221242"] = "Non puoi uccidermi...",
		["773256"] = "No",
		["441078"] = "Oh, non è nulla... Nulla in confronto al dolore inflitto ai Demiurghi dalla Terra benedetta.",
		["793289"] = "Ho trovato dell'argilla.",
		["9971323"] = "L'odio che pervade questo braccio non si è placato, anzi, diventa sempre più forte...",
		["9952024"] = "",
		["556284"] = "",
		["667130"] = "Ha perso completamente il senno? La furia vendicatrice l'ha trasformata in un'invasata.",
		["664069"] = "/t0",
		["553416"] = "Sì",
		["221284"] = "Cosa stai mescolando?",
		["771192"] = "...Svegliami da questo sogno... quelle scimmie stanno arrivando... non farle avvicinare a me... non farle avvicinare a me... Mi esplode la testa... La luce è abbacinante…",
		["995198"] = "Ho dimenticato di chiederti: hai buttato la \"spazzatura\"?",
		["557086"] = "È troppo pulito... Neanche un cadavere in vista…",
		["221214"] = "Perché... Perché non hai messo fine alle sue sofferenze prima che partorisse quella mostruosità?",
		["551406"] = "...",
		["227076"] = "E quella roba che cos'è?",
		["4994991009"] = "Oh... Capisco perfettamente voi umani. Credete davvero che maggiore sia il rischio, maggiore è la ricompensa... Adesso sbrigati a entrare nella Foschia oscura!",
		["771248"] = "Conosce la verità, ed è molto pentita.",
		["9971315"] = "La Chiesa si è abbandonata all'eresia, ma tale scelta potrebbe ritorcerglisi contro.",
		["771083"] = "Norwood è sempre stato taciturno e riservato. Non sapevo che si stesse prendendo gioco di me.",
		["993462"] = "Vattene",
		["9942040"] = "",
		["664154"] = "Ce l'ha messa tutta... Ma serve ben altro per un cambiamento...",
		["221140"] = "Il Santo Padre mi ha detto che se avessi continuato ad attirare stranieri ad Adamina e concludere la caccia, avrei potuto rivedere la mia mamma.",
		["773005"] = "Sì",
		["551377"] = "I mediocri sono sempre ossessionati dall'essere al di sopra degli altri.",
		["991453"] = "Pietra della Natura umana",
		["111234"] = "Ah ah ah, umano furbetto.",
		["1004100068"] = "Ti stavamo aspettando...",
		["551106"] = "Ah ah... Sapevo che era il mio giorno fortunato! E mi sono fatto un amico!",
		["991235"] = "Ho già aperto il Muro del silenzio.",
		["331185"] = "Ora basta! Continuerò a cercare finché non troverò la verità.",
		["661332"] = "L'attuale Terra benedetta è costruita su menzogne. L'antica gloria suprema è deturpata dalle masse.",
		["554198"] = "/t0",
		["221285"] = "Non hai sentito le grida di dolore prevenienti dalla Culla dei neonati? Le madri di Adamina hanno bisogno di questo per sopravvivere.",
		["444162"] = "/t0",
		["221153"] = "Non posso abbandonarla in questa situazione, devo rimanere al suo fianco...",
		["661316"] = "Prendi questa chiave, torna alla Sala della Trinità e aspetta la protezione del Procuratore. Non starà immobile a guardare la distruzione della Terra benedetta, si paleserà di nuovo!",
		["221298"] = "Non sono sicuro che sia utile per alleviare il dolore.",
		["111042"] = "Sono solo storie, nessuno hai mai assistito alla morte di uno di loro, né scoperto la Tomba.",
		["9973362"] = "Non ho ancora trovato niente.",
		["991030"] = "Forse c'è qualcosa che disturba la tua testa... Forse l'hai sbattuta, o forse è il Sendril.",
		["884005"] = "Paura di perderla? O di qualcosa... di peggiore... un oscuro segreto? Devo confessarti la verità... la scioccante verità...",
		["551283"] = "La mia bancarella non riceve visite da tempo, forse posso farti un buon prezzo.",
		["882100"] = "Bevi...",
		["331060"] = "Forse solo la morte può liberarli.",
		["664137"] = "Più in là non possiamo andare...",
		["100110019999"] = "/t0",
		["994060"] = "/t0",
		["995138"] = "Eravamo così?",
		["9961018"] = "Terrence, sai che non c'è alcuna speranza per Teresa, ma ce n'è per te.",
		["113131"] = "Commercia",
		["992257"] = "Umpf... Metti giù quelle manacce! Gli umani non sono benvenuti qui, senza lettera d'invito.",
		["664777"] = "Impossibile aprire da questo lato.",
		["333224"] = "Vattene",
		["993224"] = "Ho trovato solo una delle due.",
		["551259"] = "Quella Strega è mia moglie, devo trovarla...",
		["791342"] = "È la lingua dei Samarosiani.",
		["663805"] = "Sì",
		["117063"] = "Vedo una donna poco più avanti. Forse ci può essere d'aiuto.",
		["991146"] = "Lord Ebbinghaus... È lui il nostro obiettivo.",
		["4994993017"] = "Vattene",
		["441190"] = "Come fa a saperlo?",
		["771218"] = "È probabilmente la bugia peggiore che ho mai sentito in questo mondo...",
		["9921028"] = "Menziona anche una Strega dai capelli rossi che si trovava a Heggem.",
		["111103"] = "Finalmente è arrivato qualcuno! Aiutatemi! Lord Ramones è il mio signore. Quella strana Strega ha fatto ammalare Heggem, poi tutti i discepoli si sono tramutati in mostri e il tesoro nella miniera è stato rubato. Lord Ramones ha deciso di inviarmi a cercare aiuto, non posso rimanere qui in trappola.",
		["1006100089"] = "No... Non è finita... Devo trovare Vaillant...",
		["1003100044"] = "Non sa nemmeno chi siano i suoi genitori...",
		["111116"] = "È giunta a Heggem alcuni mesi fa. Lord Ramones e io ci siamo intrattenuti con lei per diverso tempo. Nessuno sa perché sia venuta qui, l'unica cosa certa è che Heggem si è ammalato poco dopo la sua partenza. Dev'essere stata la sua stregoneria, non c'è altra spiegazione!",
		["771097"] = "In questa situazione, cosa credi che gli permetta di sopravvivere così a lungo?",
		["115125"] = "Hai lo stesso odore della Strega! Sei dalla sua parte!",
		["793329"] = "Contratto",
		["442338"] = "Lo senti il vento che ulula? Sta per arrivare una tempesta...",
		["9971347"] = "Conosco questa leggenda, si trattava di un popolo che è tramontato con il sole, e di cui non si conosce più nulla.",
		["9951046"] = "Una Guida anziana mi ha raccontato un po' di dettagli... dettagli orribili... Non dimenticherò mai di come tremava di paura mentre parlava.",
		["111054"] = "Basta domande.",
		["771091"] = "So solo che è sempre stato imprigionato qui e che devo trovarlo, vivo o morto. So bene che stai cercando Teresa e capisco come tu ti senta. Mi dispiace ma questa volta ho le mani legate...",
		["441174"] = "Aveva paura... Paura di affrontare la realtà! Quella donna non ha idea del perché Vaillant volesse così tanto confessare. I Demiurghi sono in questa situazione per colpa di una decisione stupida dietro l'altra.",
		["551435"] = "...",
		["1001100018"] = "Sconfiggilo... Non possiamo farci fermare...",
		["551359"] = "Ah... una vorace ossessione... Ci ha accecati tutti, proprio così.",
		["662264"] = "",
		["441059"] = "Aaah... quella maledetta campana. Sei... una Guida? Vieni da Edith fino alla Terra benedetta?",
		["9971338"] = "Ho un obiettivo.",
		["661321"] = "Rimanere in silenzio non ti aiuterà... straniero...",
		["221177"] = "È davvero questo che vuoi?",
		["443097"] = "Sì",
		["451075"] = "Chi è là? Non è l'ora... Non è ancora l'ora... Alla fine tutte queste immagini illusorie saranno sostituite... (ride) E diventeremo una cosa sola.",
		["551096"] = "Il Supervisore ha arruolato un gruppo di Guide con il compito di trovare il Sigillo di Yekub sotto la Torre sacra, ma sembra che quest'ultima sia stata maledetta dai Marcioni.",
		["991028"] = "Mh, volevo farti la stessa domanda.",
		["661605"] = "Dio, non c'è nulla che possa fare. Chiunque merita la possibilità di vivere meglio di me.",
		["441262"] = "Sì, ma non hanno intenzione di cambiare idea. Incontriamoci alla porta della città, così che possa aprire il cancello per la Terra benedetta.",
		["111120"] = "Se n'è già andata, si sta dirigendo verso un altro Colosso.",
		["883100"] = "Bevi",
		["441079"] = "Siamo stati circondati dall'oscurità per troppo tempo. Tutto ciò che blocca i Demiurghi dal riconquistare la luce deve essere distrutto, quindi stanne fuori. Occupati degli affari tuoi.",
		["661241"] = "Quindi questa è una tragedia infinita? La Terra benedetta sta cadendo a pezzi. Se vuoi fuggire, la violenza non è la scelta migliore, comunque Glenn conosce sempre una scappatoia.",
		["551114"] = "Aspettami Chloe, manca pochissimo! Davvero pochissimo.",
		["777042"] = "Sembra che Viola abbia perso conoscenza, potrebbe avere a che fare con la luce rossa?",
		["224001"] = "Impossibile entrare.",
		["997276"] = "Il Cavaliere di Handel? La prima Guida? Vengono forse dalla nave dei folli?",
		["100710072201"] = "………",
		["221244"] = "Ti dirò tutto, ma non uccidermi.",
		["221219"] = "Ho tanta paura... Mamma... Santo Padre... Elmar... Non ho più nessuno... Ho tanta paura...",
		["9961031"] = "Sembra che tu non mi conosca affatto.",
		["771384"] = "Naturalmente non sono un bastardo senza cuore, quindi se vuoi andare da qualche altra parte prima di entrare in quel postaccio, il mio contratto è ancora valido.",
		["661156"] = "C'era da aspettarselo, è proprio come dicono, siete tutti delle schifose bestie dannate, andate al diavolo!",
		["9921044"] = "Vediamo cosa offri.",
		["331170"] = "Se l'avessi trovato prima, tutto ciò non sarebbe successo.",
		["771207"] = "Tu sei quella che ha sempre voluto proteggere, fino alla fine... e questo è abbastanza.",
		["443077"] = "Sembra che la campana ti disturbi.",
		["775116"] = "Non guardare a te stesso, hai ancora molti segreti! Norwood, portalo all'altare.",
		["9931036"] = "Ho sentito alcune voci riguardo un Colosso, laggiù. Quel luogo è stato colpito da una meteora rossa ed è diventato una terra di morte, infestata da mostri e strani canti.",
		["551378"] = "Vendetta indulgente... convergenza ardente... tentativo rischioso...",
		["551135"] = "Forse è già stata trovata da qualcun altro...",
		["1006100090"] = "Si trova nelle profondità della Terra benedetta...",
		["771244"] = "Teresa non voleva che tu venissi coinvolto, ma tu ti sei presentato e hai mandato all'aria tutti i suoi piani. Mi ha chiesto di rimanere con te per garantire la tua sicurezza.",
		["554888"] = "Al momento non si può aprire.",
		["337106"] = "Qualcosa protegge questo luogo, visto che la forza dei Sendril qui è indebolita.",
		["337175"] = "È impazzita. Prendi questo da Benita o la canzone ti farà ammattire.",
		["997077"] = "Guarda, c'è una grotta laggiù. Forse possiamo salire da qui.",
		["991521"] = "Non c'è l'acqua del Fiume delle anime laggiù.",
		["662603"] = "Aprire la porta?",
		["331191"] = "Quindi voleva aprire la Tomba colossale.",
		["661501"] = "No, ti prego! Cerca rifugio dal Procuratore nella Sala della Trinità, non ha senso stare qui!",
		["9971312"] = "Ah... Quindi le voci erano vere, la reliquia sembra funzionare.",
		["791314"] = "Era la mano di un potente soldato Marcione. Poco prima che spirasse, qualcuno ha usato un rituale proibito per fonderla con i Sendril e trasformarla in un'arma.",
		["771076"] = "Dimmi, dov'è Teresa?",
		["662803"] = "Aprire la porta?",
		["663264"] = "Commercia",
		["338232"] = "",
		["661213"] = "Terrence! Calmati! L'interruttore è stato distrutto. Era tutto pianificato.",
		["118152"] = "",
		["9921041"] = "Ho un po' di cose interessanti. Dacci un'occhiata, ma non sono gratis.",
		["551088"] = "Oh... un volto nuovo... Anche tu sei stato reclutato dal Supervisore?",
		["791295"] = "Quasi finito?",
		["221243"] = "Sei un suo amico? L'ho salvata a Katib! Senza il mio aiuto non si troverebbe qui adesso!  ",
		["114035"] = "/t0",
		["791283"] = "Grazie... Sono contenta che te ne sia ricordato.",
		["771251"] = "Spero che un giorno ti toglierai quella maschera e potrai ripetermi quelle stesse parole. Siamo tutti intrappolati nel ciclo della reincarnazione, proprio come credete voi Marcioni.",
		["663154"] = "Vattene",
		["9933012"] = "Parla",
		["1005100054"] = "La gente di fede commette sempre crimini assurdi.",
		["441216"] = "Ah ah... Ho parlato troppo... Vai per la tua strada, so che non sei venuto qui per ascoltare la mia storia.",
		["991065"] = "Era il lamento di Ichthyosauria, infuriata a causa della nave dei pazzi... Anche se la mia memoria è da lungo tempo offuscata, non dimenticherò mai quel suono... Mi riecheggia ancora nelle orecchie...",
		["443321"] = "No",
		["221253"] = "泰伦斯，看来你也有不少秘密……",
		["771197"] = "Eckhart non si è mai rivelato prima: non avevamo altra scelta per poterlo scovare.",
		["444152"] = "/t0",
		["221304"] = "Il Santo Padre, è così che lo chiamano i discepoli.",
		["227077"] = "Strano... La luce di Adamina è così intensa che questi mostri non dovrebbero essere qui.",
		["771084"] = "Ora le cose iniziano ad avere un senso.",
		["441199"] = "Non abbiamo altra scelta... Vaillant è l'anima dei Demiurghi. Per proteggerci si è caricato di un peccato che non può più essere mondato.",
		["771241"] = "E come sempre, che Dio ti benedica, mio vecchio amico.",
		["441073"] = "Abbiamo qualche indizio?",
		["661098"] = "Tu sei...",
		["799313"] = "Come possiamo accogliere un nuovo futuro quando il passato deve ancora morire?",
		["662505"] = "Aprire la porta?",
		["551107"] = "È tutto pronto, possiamo partire. La fortuna ci sorride, troveremo sicuramente il Sigillo di Yekub sotto la Torre sacra.",
		["663169"] = "Vattene",
		["991410"] = "L'armatura abbandonata di un saccheggiatore. Solo una calda forza di volontà può guidare quest'armatura ghiacciata.",
		["117096"] = "L'intera miniera è stata corrotta. I discepoli di Heggem ormai non possono che fare parte dei Deturpati.",
		["991124"] = "Sfortunatamente no. Se vuoi avvicinarti al Muro del silenzio devi passare dal covo dei saccheggiatori.",
		["1004100067"] = "Elena... Elena...",
		["9951039"] = "Sei un mercenario?",
		["117090"] = "È il tesoro dei discepoli di Heggem! Ho già visto questo raro minerale. Uhm... Fammi pensare... ho un attimo di amnesia, forse ho respirato troppa Foschia oscura... Ma certo! Il Cristallo del Colosso! Può dissipare la Foschia oscura per un breve tempo.",
		["9971460"] = "Forse è una verità che ti vuole risparmiare? Sai bene che è pronta ad attraversare l'inferno stesso per proteggerti, per quanto duro possa essere.",
		["221166"] = "Non posso abbandonarla in questa situazione, devo rimanere al suo fianco...",
		["994038"] = "/t0",
		["667066"] = "Tutto è cambiato e sta peggiorando.",
		["553349"] = "Riempi flacone di Melos.",
		["441171"] = "Fermarmi non è la soluzione migliore per i Demiurghi, sai... Certo, la decisione spetta a te.",
		["771234"] = "Preferisco il presente, più veritiero e senza più niente da nascondere.",
		["667153"] = "La Spada sacra sembra essere inutile. Ora è sotto l'influsso dei Sendril...",
		["2001100120"] = "Sua Altezza. Non mi aspettavo di incontrarla qui. Mi viene la nostalgia per i bei vecchi tempi.",
		["991089"] = "Anche se potrebbe essere un'attesa folle, bisogna pur credere nei miracoli... Ne ho avuto abbastanza della prigionia in questa gabbia oscura…",
		["9971292"] = "In quanto Inquisitore della Terra benedetta, devi aver sofferto molto, costretta ad agire senza avere il controllo della tua vita...",
		["117024"] = "Certo, a meno che tu non faccia qualche stupidaggine.",
		["221268"] = "Povera Guida, ho un affare da proporti, che ne dici?",
		["995139"] = "Ah ah ah... Acqua... Altra acqua!",
		["221178"] = "Ormai ho deciso. Questo non mi serve più.",
		["227323"] = "Queste madri folli non respirano.",
		["9973294"] = "Contratto",
		["112903"] = "Vuoi tornare alla carrozza?",
		["223291"] = "Rifiuta",
		["331075"] = "Chissà cosa significa la morte per quei mostri...",
		["991236"] = "Coff, coff…",
		["551140"] = "È tutto pronto, possiamo partire. La fortuna ci sorride, troveremo sicuramente il Sigillo di Yekub sotto la Torre sacra.",
		["791327"] = "Ho portato via tutto a quell'uomo, e ho cominciato a indossare la sua maschera... Era da tanto tempo che non provavo una sensazione simile.",
		["9971336"] = "Perché?",
		["991471"] = "Mi dispiace, niente sconti, ma tutti i miei prodotti valgono il loro prezzo pieno.",
		["228234"] = "",
		["791361"] = "Dovrebbe essere l'ultima. Cosa dice il braccialetto?",
		["771148"] = "Terrence, non voglio più vedere altri Marcioni morire proprio davanti ai miei occhi!",
		["551419"] = "L'amore... conduce solo alla follia.",
		["541003"] = "Viola... Non puoi capire...",
		["111078"] = "L'ho fatto per cercare Jerry. Era uscito dalla miniera da solo. Qui fuori è così pericoloso.",
		["551284"] = "Oh, munifica Guida, ti auguro ogni successo.",
		["100110012202"] = "Sì",
		["661317"] = "Ho fatto del mio meglio per proteggere tutto, ma hai vanificato tutti i miei sforzi. Avida Guida, prendi questa chiave e vattene da qui! Che i Sendril possano trascinarti verso l'abisso eterno!",
		["1006100088"] = "È finita... Riposate in pace...",
		["553292"] = "Trovata.",
		["552394"] = "",
		["221286"] = "Ma quest'odore...",
		["100510052206"] = "/t0",
		["111104"] = "Come posso aiutarti?",
		["661176"] = "Ha mantenuto il segreto della Tomba colossale per rafforzare il suo potere. Il Procuratore deve biasimare solo se stesso per come finirà...",
		["791305"] = "Grazie... È la prima volta che ricevo qualcosa...",
		["9971320"] = "Ah... Mi dispiace, non era mia intenzione mancarti di rispetto. Loro hanno fatto la loro scelta per cupidigia, tu sei stato costretto...",
		["4994993016"] = "Aumenta la luminosità della lanterna della Foschia",
		["9921037"] = "Spero solo che non sia un incubo senza fine.",
		["791323"] = "L'odio che pervade questo braccio non si è placato, anzi, diventa sempre più forte...",
		["551115"] = "...",
		["9971344"] = "Neanche io so di preciso cosa significhi, ma se trovi qualcosa legato a Samarosa, o sei riesci a venire a capo di una parte di quest'enigma, vieni da me.",
		["114148"] = "/t0",
		["661601"] = "No, ti prego! Cerca rifugio dal Procuratore nella Sala della Trinità, non ha senso stare qui!",
		["995265"] = "Tesori meravigliosi, tesori preziosi... Non lascerò che tu li faccia annerire!",
		["551434"] = "La ricerca dell'eccelso è la minaccia definitiva dell'umanità.",
		["221150"] = "Ti piacerà.",
		["451076"] = "Ordine... Sto aspettando un ordine... L'ordine finale...",
		["771094"] = "Mi dispiace Terrence, per Teresa intendo... Ho preso la decisione sbagliata.",
		["991270"] = "Lord Ebbinghaus... Ora ricordo. Ricordo la promessa che avrei dovuto mantenere tempo fa.",
		["9943041"] = "Vattene",
		["791367"] = "Adesso che abbiamo trovato la Tomba colossale, le Guide non hanno più motivo di esistere.",
		["9971339"] = "E sarebbe?",
		["4994993204"] = "Vattene",
		["551342"] = "I piccoli e deboli non dubitano neanche delle bugie più ovvie.",
		["1004100070"] = "Finché combatterai...",
		["30134225"] = "/t0",
		["551436"] = "...",
		["30135236"] = "Cosa stiamo sbagliando?",
		["331147"] = "Un frammento della meteora rossa. Ha poteri simili a quelli del Colosso, può purificare i Sendril.",
		["995137"] = "Ho visto l'acqua uscire dalla sua ferita... Acqua nera... Calda...",
		["553373"] = "Riempi flacone di Melos.",
		["331152"] = "Se troviamo il resto dei pezzi, potremmo riuscire a capire a cosa punti Teresa, ma è anche come cercare un ago in un pagliaio.",
		["447086"] = "Attenzione! Sebbene non si ancora successo nulla, la polvere bianca che aleggia nell'aria è assolutamente inconsueta.",
		["554171"] = "/t0",
		["778303"] = "",
		["334185"] = "/t0",
		["991433"] = "Vattene",
		["551389"] = "Materiale insufficiente",
		["9931037"] = "Ci sono sopravvissuti?",
		["9941018"] = "Più di un decennio fa, il pontefice dei Demiurghi, Vaillant, fu condannato a morte per il suo tradimento.",
		["100410042206"] = "……",
		["553333"] = "No",
		["556268"] = "Sbrigati, vai alla Terra benedetta e fai ciò che devi... Io ho bisogno di riposare... Non preoccuparti... Spero che andrà tutto bene.",
		["441101"] = "Il mio figlio più piccolo, Andrew... Non so dove sia. Potresti andare a cercarlo per me? Si è intristito da quando i suoi fratelli sono partiti.",
		["777043"] = "Cosa facciamo adesso? Cerchiamo di salvarla? Potrebbe sapere dove sia Teresa, ma potrebbe anche essere una trappola.",
		["771201"] = "Terrence, se questo è davvero quello che vuoi, prendi la meteora rossa di Exilium con te. È l'unico modo per entrare nella Tomba colossale. Cosa accadrà dopo, nessuno lo sa... Potresti non uscirne vivo.",
		["771208"] = "Non volevo che finisse così.",
		["771228"] = "Terrence, questo viaggio ti ha cambiato.",
		["667125"] = "Ricordi i pezzi che abbiamo trovato dagli altri pilastri? Sembrano essere tutti connessi.",
		["775117"] = "Certo, maestro.",
		["9961032"] = "Fidati di me, vai avanti e tieni duro, perché, per la mia esperienza, arrendersi a metà strada porta sempre a conseguenze terribili.",
		["8100102"] = "Per tanto tempo... Hai cercato le ombre per la mia luce... Eppure ti rifiuti ancora di arrenderti?",
		["9921045"] = "Grazie degli acquisti.",
		["221216"] = "Nessuno si salva...",
		["771149"] = "Sono passati così tanti anni e io sono ancora troppo debole per salvare qualcuno...",
		["555171"] = "/t0",
		["661210"] = "Accidenti! Dove hanno portato Teresa?",
		["553158"] = "Eri un essere umano?",
		["221169"] = "Spero che la mia mamma sia fiera di me anche se... ormai non c'è più.",
		["9946023"] = "",
		["9921042"] = "Non ti ho già pagato abbastanza?",
		["774137"] = "/t0",
		["1002100035"] = "Non abbiamo tempo di esitare! Uccidilo!",
		["791284"] = "Sono curioso, chi stai cercando di ricreare con l'argilla?",
		["991518"] = "Comunque, finché ci sarà il guardiano della soglia, a nessuno verrà in mente di andare lassù.",
		["9973320"] = "Mi dispiace.",
		["557081"] = "Sebbene abbia viaggiato per lunghi anni tra tutte le genti di Solas, la fragilità della civiltà continua a stupirmi…",
		["444272"] = "/t0",
		["997046"] = "Umpf... Che soggetto. Che cosa c'è in quei contenitori? L'odore acido è nauseante...",
		["442301"] = "",
		["9972450"] = "",
		["221058"] = "La riporteremo indietro, dobbiamo solo sapere dove si trova.",
		["771102"] = "Devo dirti una cosa strana... una parte del mio corpo... sembra essersi dissolta... C'è questa cosa che si nutre della mia anima... Tra non molto sarò un altro di quei cadaveri ambulanti...",
		["3013014002"] = "Non avere paura, segui la Chiesa, segui la luce!",
		["661502"] = "Vattene, non troverai nulla che ti serva.",
		["221305"] = "In seguito divenne noto come il Santo Padre, con l'aiuto dei Sendril... Ironico, non trovi?",
		["665134"] = "Vaillant sarà così fiero di noi. Sarà fiero dei Demiurghi!",
		["663261"] = "Commercia",
		["551423"] = "La ricerca dell'eccelso è la minaccia definitiva dell'umanità.",
		["791293"] = "Non compatirmi, il mio passato peccaminoso non potrà mai essere perdonato... Avevo un'ossessione per il punire in nome della fede, ma ora so che è stata la mia crudeltà a trasformare quegli uomini in eretici e a privarli della loro ultima speranza di un atto divino.",
		["4994993020"] = "Consegna materiali",
		["771185"] = "Oh... Hai seguito Lord Eckhart fino a qui? Hai fatto anche tu quegli strani sogni? Nei sogni, un gruppo di scimmie... guidano una carrozza... mi costringono a mangiare cose strane... luccicanti... Ahh... Sebbene mi avessero obbligato il sapore mi piaceva e ora ne voglio ancora.",
		["881075"] = "Rimarrò per sempre qui ad aspettare, anche se non ricordo più chi sto aspettando, anche quando il tempo non scorrerà più e non ci saranno più lacrime da asciugare. Liberami da questa ossessione, concedimi il dolce sollievo della morte.",
		["441335"] = "Allora lasciaci salpare subito.",
		["9911019"] = "在这沉寂黑夜的之中我们可能离彼岸永远都那么遥远……",
		["772001"] = "Vuoi tornare alla carrozza?",
		["444001"] = "Impossibile entrare.",
		["9973312"] = "Ho trovato delle reliquie antiche.",
		["221192"] = "Davvero? Che gioia! Dille che mamma sente la sua mancanza e che tornerò da lei prestissimo.",
		["771194"] = "La mia gola brucia... Per favore datemene un altro pochino... questo sapore squisito, questa leccornia...",
		["441112"] = "È un vero miracolo! Chi avrebbe mai potuto pensare che il Colosso di Edith sarebbe tornato a splendere... Avevo quasi dimenticato come brillasse la luce. In quest'oscurità non ero nient'altro che un verme inutile.",
		["991067"] = "Un gruppo di pirati malvagi, pazzi e senza scrupoli. Più di dieci anni fa, Lord Ebbinghaus li ha gentilmente accolti, ma non avrebbe mai immaginato che avrebbero causato tali disastri.",
		["111204"] = "Il mondo diventa leggero.",
		["221125"] = "Calmati e dimmi cos'è successo. Ma basta menzogne.",
		["4994991007"] = "Oh... Certo, capisco. Il brivido di superare la paura dell'oscurità può dare alla testa... Ma non puoi addentrarti ancora di più nella Foschia oscura con quella tua lanterna. Devi trovare un modo per rendere la sua luce più forte.",
		["9941046"] = "Fammi vedere, che cos'hai?",
		["551288"] = "Mi dispiace, niente sconti, ma tutti i miei prodotti valgono il loro prezzo pieno.",
		["551108"] = "Finalmente potrò tornare alla Terra benedetta...",
		["772392"] = "Non potrai più tornare a Solas dopo essere entrato, l'ultimo salvataggio sarà alle rovine dei discepoli. Continuare?",
		["2004100124"] = "Percepisco un odore familiare da lui.",
		["884038"] = "Come osi minacciare la Chiesa!",
		["799310"] = "Ho qualche scelta?",
		["551089"] = "...",
		["661097"] = "Chi è là? Marcioni, Demiurghi e Guide; è da molto tempo che nella Terra benedetta non si vede un tale viavai.",
		["113068"] = "Qualcun altro è passato di qui?",
		["552357"] = "Costo: 1 Miele di condensazione e 120 ossa di Anthozoan. Vuoi davvero procedere?",
		["333166"] = "Tira fuori l'anello.",
		["114036"] = "/t0",
		["331100"] = "Oh Jenny... se solo potessi rivederti!",
		["991400"] = "Proverbi di Ebbinghaus: I",
		["441273"] = "Temo di non poterlo fare...",
		["662805"] = "Aprire la porta?",
		["9971461"] = "A volte ho l'impressione di non conoscerla per niente.",
		["553381"] = "Riempi flacone di Melos.",
		["441172"] = "Cosa vuoi fare esattamente?",
		["224051"] = "Impossibile aprire da questo lato.",
		["791324"] = "Se perdi il controllo, non ci andrò piano con te.",
		["551186"] = "Intendi quella Strega furibonda? Immagino che tu abbia incontrato questi fantasmi dannati, un tempo erano come noi... ma erano avidi e così la Strega ha usato le sue fiamme vendicative per punirli.",
		["554289"] = "/t0",
		["332901"] = "Vuoi tornare alla carrozza?",
		["9971293"] = "Non compatirmi, il mio passato peccaminoso non potrà mai essere perdonato... Avevo un'ossessione per il punire in nome della fede, ma ora so che è stata la mia crudeltà a trasformare quegli uomini in eretici e a privarli della loro ultima speranza di un atto divino.",
		["117091"] = "Sembra che quelli siano i fratelli John... Sono dei Deturpati adesso...",
		["221175"] = "Se mai deciderai di scappare da qui, potrai sempre contare su di me.",
		["9971337"] = "La maggior parte delle persone preferirebbe la morte all'essere una Guida.",
		["661314"] = "Le tue ferite sono messe male.",
		["554170"] = "/t0",
		["221133"] = "Hai la chiave, usala per aprire la porta della cella superiore.",
		["991509"] = "Condannate i pazzi a essere torturati per l'eternità! Se Ichthyosauria non può risorgere, allora noi cadremo con lei...",
		["541004"] = "È stata colpa mia...",
		["991448"] = "Pietra dell'Heggie",
		["30274003"] = "/t0",
		["775114"] = "Fidati, rimpiangerai i bei vecchi tempi andati.",
		["773255"] = "Sì",
		["551327"] = "Forse avrai una minuscola possibilità di cambiare il destino già scritto.",
		["791254"] = "Questa moneta apparteneva al mio defunto fratello. Prendila, forse ti porterà fortuna.",
		["442901"] = "Vuoi tornare alla carrozza?",
		["9973456"] = "Riguardo Teresa",
		["993048"] = "Prendi",
		["661600"] = "La maggior parte di loro si sono diretti alla Sala della Trinità per cercare la protezione del Procuratore.",
		["1006100087"] = "Non puoi fallire... Non finché i Demiurghi prospereranno nuovamente...",
		["441184"] = "È un'impressione mia, o Edith si sta risvegliando?",
		["111079"] = "Jerry? È tuo figlio?",
		["991070"] = "La cosa non deve sorprenderti. Dopo il rumore forte, è diventato tutto sfocato...",
		["443127"] = "Dagli il testamento insanguinato.",
		["221287"] = "Sono erbe di Adamina, ti fanno dimenticare il dolore... Ultimamente, sempre più madri sono vicine al parto... Sarà dura, senza abbastanza erbe.",
		["661112"] = "Forse, ma non ho tempo da perdere.",
		["553256"] = "No",
		["661209"] = "Non muoverti, le ferite sono ancora aperte e tu non ti sei ripreso del tutto.",
		["551295"] = "Finalmente potrò tornare alla Terra benedetta...",
		["9971345"] = "Guarda cosa ho trovato.",
		["553417"] = "Sì",
		["551324"] = "Chi è malato di ossessione può spezzare il ciclo di luce e ombra solo quando è morto e sepolto.",
		["9942026"] = "",
		["441152"] = "Sono arrivata tardi? Sembra che avessi ragione...",
		["551269"] = "Hai sentito? I fantasmi dei Marcioni... Vagano qui attorno, sento i loro sussurri!",
		["991271"] = "Perché vuoi farlo? Ciò che è stato appartiene al passato e il diritto di redimerti ti è stato strappato via da molto tempo ormai.",
		["994417"] = "/t0",
		["773391"] = "Aspetta.",
		["221312"] = "Oh no... Anche lei...",
		["444240"] = "/t0",
		["661168"] = "Shh, aspetta... Hai sentito? Quei sussurri folli provengono dal luogo del riposo. I Sendril devono essere entrati in Chiesa. Questo posto puzza sempre più di Sendril, non c'è modo di sfuggire loro.",
		["9923037"] = "Commercia",
		["442903"] = "Vuoi tornare alla carrozza?",
		["225319"] = "Oh... Al Santo Padre piacerà... Quei mostri... La stirpe del Santo Padre prospererà.",
		["791356"] = "No, non è ancora abbastanza per decifrare il codice.",
		["994258"] = "/t0",
		["9936031"] = "",
		["883102"] = "Bevi",
		["443334"] = "<color=#DBBA7FFF>Ci sono.</color>",
		["331076"] = "È un dono... di Dio.",
		["661602"] = "Vattene, non troverai nulla che ti serva.",
		["223105"] = "Perché ti trovi qui?",
		["884041"] = "Dammela subito! Bevila e non dovrai mai più temere la Foschia oscura!",
		["663501"] = "Sì",
		["441218"] = "Mi dispiace, ho bisogno di raggiungere la Terra benedetta.",
		["661323"] = "Oh... ah ah... Condividi questa gioia con noi...",
		["114021"] = "/t0",
		["776254"] = "",
		["662261"] = "",
		["9963035"] = "Parla",
		["777044"] = "Dopo tutto quello che è successo, non posso fidarmi così facilmente di lei.",
		["662605"] = "Aprire la porta?",
		["9921046"] = "Di risparmiar smetti se vuoi più oggetti!",
		["441102"] = "Hai già trovato mio figlio?",
		["331153"] = "Questo ago non è difficile da trovare. Ne ho avvertito il potere in cima alla montagna. La meteora rossa deve alimentare la flebile luce del Colosso.",
		["771215"] = "Credo che troverai un modo per guarirle.",
		["221207"] = "Stirpe benedetta? Mi pare più vero il contrario.",
		["551441"] = "So che hai ancora dei dubbi. Troverai tutte le risposte che cerchi se consegni il frammento di meteora rossa a Lord Ebbinghaus, il mio maestro, e risvegli il mio paese natale ormai da molto tempo assopito.",
		["9931038"] = "Alcuni dicono che è il lamento dei morti a Exilium, mentre altri ritengono che non sia altro che lo struggimento dei mostri.",
		["771093"] = "Grazie Terrence... Prego che tu trovi Teresa e possa finalmente lasciare questo dannatissimo posto.",
		["221265"] = "Non c'è tempo per litigare, il morbo arriverà presto ad Adamina.",
		["331188"] = "Potrebbe volerci un po'...",
		["442241"] = "Il cancello per la Terra benedetta è aperto, come promesso.",
		["661315"] = "Sono stato ferito da un terribile mostro Deturpato, non riuscirò a resistere per molto, ancora...",
		["995280"] = "Oh, non temere, ho preparato un'armatura per te. Tra poco potrai dare la caccia ai tuoi compatrioti e ucciderli.",
		["553374"] = "Sì",
		["661503"] = "Fermo! Non aprirla!",
		["441121"] = "Ah... Quei Demiurghi se ne sono andati? Se non fosse stato per loro, non sarei bloccato qui.",
		["223269"] = "Commercia",
		["778304"] = "",
		["994475"] = "/t0",
		["9921043"] = "Ah! Sono due lavori completamente diversi. Dopotutto, vi sto facendo da guida gratis.",
		["331200"] = "Mi dispiace... È stata contaminata dai Sendril, ho messo fine al suo dolore.",
		["331220"] = "Nessun reso, nessun rimborso. Ti auguro un buon viaggio.",
		["9931001"] = "Testa: andiamo a Exilium assieme. Croce: me ne vado subito.",
		["661215"] = "È stato distrutto! È un vicolo cieco. Dobbiamo andarcene da qui, adesso...",
		["336220"] = "",
		["661145"] = "Ah, hai trovato la Spada sacra. Grazie, Guida. Ora posso morire in pace.",
		["991079"] = "Ah... Rochette si è quasi addormentata... Un consiglio, viandante: non ti addentrare.",
		["793323"] = "Ho trovato delle reliquie antiche.",
		["771103"] = "Per favore, apri la gabbia, da lassù, fammi trovare un'anima... Vuoi andartene anche tu vero? Prendi una decisione prima che perda conoscenza.",
		["441280"] = "È questo il potere di cui parlava Elena?",
		["772075"] = "",
		["551319"] = "...",
		["3013014001"] = "Difendi la gloria della Terra benedetta con cieca lealtà. Come Procuratore, agisco per conto di Dio per perdonare i tuoi peccati. Non farti confondere.",
		["441336"] = "Ora non mi sembra il momento giusto.",
		["665135"] = "Non importa quanto duri il giorno, la notte prevale sempre.",
		["451077"] = "Il mio corpo... i miei pensieri... Offro tutto quello che ho... per il grande viaggio... Dobbiamo essere pronti... Non possiamo venire disturbati... Finché sono qui...",
		["551420"] = "I mediocri sono sempre ossessionati dall'essere al di sopra degli altri.",
		["9943026"] = "Parla",
		["223270"] = "Vattene",
		["991010"] = "La tempesta non è ancora cessata. Andarcene ora sarebbe un suicidio.",
		["991466"] = "La mia bancarella non riceve visite da tempo, forse posso farti un buon prezzo.",
		["793361"] = "Ho trovato una porzione di Lastra rotta.",
		["111036"] = "Grazie tante.",
		["441153"] = "Sapevi già chi fosse?",
		["9923045"] = "Inizia nuovo viaggio",
		["991238"] = "Non sei di qui, non è vero?",
		["221156"] = "Mi dispiace, ha perso la ragione. Mi ha attaccato prima che avessi il tempo di spiegarle la situazione.",
		["771186"] = "La mia gola brucia... Per favore datemene un altro pochino... questo sapore squisito, questa leccornia...",
		["881076"] = "Mi dispiace così tanto per quella ragazza Marciona, è stata tradita dal mondo di cui faceva parte, trasformata in una fantasma che infesta questi luoghi.",
		["441274"] = "Speravo che non saremmo arrivati a questo... Ma, ahimè, hai scelto di schierarti contro di noi.",
		["791329"] = "Non ho più bisogno di questo braccio... Senza odio, non ci può più essere vendetta.",
		["772005"] = "Vai alle Reliquie degli antenati?",
		["9973313"] = "Mi dispiace.",
		["441113"] = "Oh... Che bella questa luce. Per favore non fatela spegnere di nuovo...",
		["100310032201"] = "……",
		["661225"] = "Non finché decidi di rimanere.",
		["991110"] = "Hai già dimenticato? La gente di Ichthyosauria è maledetta. Non riesce a trattenere nessun ricordo.",
		["111208"] = "Il mondo diventa pesante.",
		["441075"] = "Meglio che ti trovi un posto in cui nasconderti se non vuoi finire come questo cadavere.",
		["991452"] = "Pietra del Colosso",
		["448313"] = "",
		["9973288"] = "Contratto",
		["4994991002"] = "Materiali insufficienti",
		["9971342"] = "È la lingua dei Samarosiani.",
		["221114"] = "È arrivato ad Adamina per caso, diversi anni fa, uno straniero come lo sei tu adesso.  ",
		["991101"] = "Che cos'erano?",
		["331118"] = "E dovrei stare ancora qui? Dopo la caduta del Colosso, chi ascolterà le preghiere delle nullità?",
		["9971334"] = "La tua mano sinistra è abbastanza inquietante.",
		["338234"] = "",
		["661212"] = "Devo raggiungerli, apri i cancelli, adesso!",
		["991142"] = "Sst... Cerco nuova acqua del Fiume delle anime, ovviamente.",
		["551409"] = "...",
		["114037"] = "/t0",
		["9971310"] = "Se trovi qualcosa di interessante, consegnamela pure.",
		["773224"] = "Parla",
		["992282"] = "",
		["30274002"] = "/t0",
		["773265"] = "Vattene",
		["112905"] = "Vuoi tornare alla carrozza?",
		["553382"] = "Sì",
		["4994993014"] = "Commercia",
		["441200"] = "Ma è troppo ingenuo, pensava che la Chiesa avrebbe lasciato andare i Demiurghi... Mentre la sete di segreti della Chiesa riguardo la Tomba colossale ha fatto sì che esagerassero.",
		["333223"] = "Commercia",
		["117092"] = "Ma che sorpresa. E non di buon auspicio per quella povera mamma.",
		["555169"] = "/t0",
		["552426"] = "Costo: 1 Miele di condensazione e 270 ossa di Anthozoan. Vuoi davvero procedere?",
		["551292"] = "Era questa che stavi cercando?",
		["1004100063"] = "Non posso brandire la mia spada contro di loro...",
		["4994993002"] = "Interno della Foschia oscura",
		["9951057"] = "Dai un'occhiata a tutto questo ben di dio, tutto a buon mercato, vedi?",
		["661149"] = "Sono certa che non comprenderai la mia scelta visto che il mio stupido padre l'ha sempre disprezzata, ma non ho rimpianti. La Terra benedetta è senza speranza. Invece di sprecare la mia vita, ho preferito cambiare...",
		["442216"] = "",
		["227084"] = "Poverette...",
		["662165"] = "",
		["991215"] = "Farò del mio meglio.",
		["331121"] = "Dopo la caduta del Colosso, chi ascolterà le preghiere delle nullità?",
		["9973290"] = "Mi dispiace.",
		["775109"] = "Non importa quanto duri il giorno, la notte prevale sempre... Quando la Foschia oscura penetra nel corpo, l'anima si dissolverà.",
		["662330"] = "C'è una scanalatura a forma di spada nella statua che custodisce la Spada sacra...",
		["661603"] = "Fermo! Non aprirla!",
		["791364"] = "Ma nonostante la luce della Terra benedetta, non dissiperanno la nebbia che alberga nel mio cuore.",
		["223289"] = "Accetta",
		["111122"] = "Un inflessibile Anacoreta, il salvatore che ci sta conducendo verso la pace. Liberami, così che ti possa guidare da lui! Ha molte più informazioni sulla Strega.",
		["111127"] = "Guarda laggiù! Ci sono dei Sifler che stanno saccheggiando il villaggio. Sembra che abbiano già ammassato un bel gruzzolo.",
		["331141"] = "Perché la Chiesa ha condannato a morte Teresa?",
		["991187"] = "Poni fine alla loro infelicità. Spezza la maledizione di Ebbinghaus!",
		["221313"] = "Questo apparteneva a Elmar. Puoi usarlo per raggiungere l'oracolo. Il Santo Padre è sicuramente là.",
		["661333"] = "Solo la fede pura può riaccendere la luce della Terra benedetta. Questo Procuratore debole ha perso il favore di Dio!",
		["221176"] = "Spero che la mia mamma sia fiera di me anche se... ormai non c'è più.",
		["991503"] = "Compirono ogni sorta di azione malvagia per rubare la saggezza di Ichthyosauria.",
		["997011"] = "A quanto pare non siamo soli.",
		["551086"] = "E quello cos'è? Un uomo, o forse un fantasma?",
		["1003100048"] = "Chi rimane ancorato al passato finisce col diventare il burattino di qualcuno. Non vorrai mica fare quella fine, vero?",
		["774023"] = "/t0",
		["1006100086"] = "Non puoi fallire nuovamente...",
		["9931039"] = "Quando eri via ho comprato qualcosa di nuovo. Come di consueto, dovrò fare un po' di soldi.",
		["551161"] = "La Foschia qui è diversa dai Sendril.",
		["991441"] = "Vattene",
		["331077"] = "Che le loro anime possano essere libere.",
		["663701"] = "Sì",
		["555249"] = "Dimmi che non è vero.",
		["551287"] = "Oh, munifica Guida, ti auguro ogni successo.",
		["9951020"] = "Nemmeno Dio è capace di cambiare il passato.",
		["221208"] = "Santo Padre! Lascia che ti segua in eterno!",
		["221218"] = "Nessuno si salva... Ci trasformeremo tutti... ahhh...",
		["661312"] = "Cos'è stato quel suono, dietro la porta?",
		["114026"] = "/t2",
		["221139"] = "Calmati e dimmi cos'è successo. Ma basta menzogne.",
		["771184"] = "Oh... è lei... Lord Eckhart? Ci guiderà alla scoperta della grande essenza, non è vero?",
		["777045"] = "Sei sicuro che sia questo quello che vuoi? Non posso prometterti che tornerai indietro vivo...",
		["553428"] = "Sì",
		["442242"] = "Vai, dunque... Hai avuto la fortuna di assistere al miracolo dei Demiurghi.",
		["771212"] = "Alla Tomba colossale? Terrence... Quindi mi abbandoni anche tu?",
		["991468"] = "Mi dispiace, niente sconti, ma tutti i miei prodotti valgono il loro prezzo pieno.",
		["661320"] = "Sei qui per il piacevole aroma? Non sembri un Demiurgo, ma se sei curioso abbastanza... Bene, dimmi il codice, fammi sapere quale odio ti spinge contro la Terra benedetta.",
		["771090"] = "Hai qualche idea su dove possa essere?",
		["441126"] = "Albertus, Kassandra, quand'è che torneranno quei due idioti?",
		["997023"] = "Hai visto una via d'uscita? Io odio la luce, ma questa grotta la odio ancora di più.",
		["554999"] = "È impossibile da aprire senza una chiave.",
		["771088"] = "È il maestro di Norwood, si dice che sia il Supervisore più anziano, testimone di tre Procuratori della Chiesa, incluso Higgins.",
		["441241"] = "Sei stato all'altezza della mia fiducia. Il rituale è completo, finalmente.",
		["557083"] = "Di fronte al potere assoluto, tutto il resto non è altro che polvere nel vento.",
		["4994991005"] = "Oh... Certo, capisco. Il brivido di superare la paura dell'oscurità può dare alla testa... Ma non puoi addentrarti ancora di più nella Foschia oscura con quella tua lanterna. Devi trovare un modo per rendere la sua luce più forte.",
		["775115"] = "Cosa vuoi farne esattamente di questo vecchio corpo?",
		["991189"] = "Avendo seguito Lord Handel, non ho rimpianti in questa vita.",
		["331161"] = "Mi dispiace Terrence, nessuno può lasciare la Foschia oscura da vivo.",
		["991520"] = "Ti consiglio anche ti interrompere questa avventura suicida. Nessuno ha idea di come sia lassù...",
		["661146"] = "Sei così giovane... Non ti spaventa la morte?",
		["991061"] = "...",
		["666261"] = "",
		["9936017"] = "",
		["771104"] = "Grazie straniero... Sei una persona gentile... Ma dovresti fare attenzione, le persone gentili non sempre vengono trattate con rispetto, non lasciare che la tua gentilezza ti metta in pericolo.",
		["112082"] = "",
		["442115"] = "Rimasti a nutrirsi di ricordi per colpa di quel tradimento…",
		["991457"] = "Vattene",
		["9931002"] = "Non girarci attorno, nascondendoti dietro la tua maschera.",
		["9941036"] = "Dio non rifiuta nessuno, se si ha fede in lui.",
		["551384"] = "L'amore... conduce solo alla follia.",
		["774183"] = "Ah... Aiuto! ... C'è nessuno qui? Qualcuno mi aiuti!",
		["551316"] = "Per guidare le anime con un'ossessione alla loro ultima dimora.",
		["221127"] = "Adamina mi fa sempre più paura. Voglio solo scappare il prima possibile con la mia mamma...",
		["441281"] = "Dopo la sua rinascita, Edith raccoglierà le anime di tutti i Demiurghi che hanno partecipato al rituale. Sarà la fine dei Demiurghi.",
		["441291"] = "L'accusa di apostasia è stata solo una menzogna della Chiesa per nascondere una verità ancor più terribile. Dopo la tragedia dei Marcioni, Vaillant ha assassinato il Procuratore.",
		["333096"] = "<color=#DBBA7FFF>Che cosa ti ha fatto?</color>",
		["771096"] = "Ero un cavaliere della Chiesa allora, non so perché non lo ricordi.",
		["331149"] = "Sembra proprio il Sacrum che Teresa ha preso da Adamina!",
		["992048"] = "",
		["9971294"] = "Se fosse stata lei a sopravvivere, forse ora non dovrei fare i conti con tutto questo.",
		["111045"] = "Hai pensato di scalare la montagna? La vetta è il punto più vicino al Colosso, forse nasconde qualche indizio.",
		["991064"] = "Che cosa intendi per \"rumore forte\"?",
		["661113"] = "Davvero? Che peccato... Ma la Terra benedetta è segnata. Per molti, anche sapere la verità non cambierebbe nulla.",
		["117107"] = "Heggem indebolito, il villaggio corrotto e adesso un uovo che parla... Non mi piace per niente.",
		["9972378"] = "",
		["791357"] = "Ho ancora tempo per trovare il resto...",
		["443305"] = "Vattene",
		["663401"] = "Sì",
		["994027"] = "/t0",
		["443320"] = "Sì",
		["221144"] = "È stato il Santo Padre a rinchiuderti qui?",
		["771105"] = "Lo capisco... Deve essere davvero imbarazzante aiutare qualcuno come me... Ma quasi non riesco a ricordare come mi abbia preso questo senso di colpa incombente... Dovresti lasciarmi andare così potrai andartene anche tu.",
		["553180"] = "Vattene",
		["111230"] = "Umano, ho un affare da proporti, che ne dici? Possiedo molti oggetti preziosi.",
		["100610064999"] = "È impossibile da aprire senza una chiave.",
		["9931020"] = "Stai bene?",
		["881077"] = "Quell'innocente ha aperto i cancelli per la luce, ed è diventata tutt'uno con essa.",
		["1004100116"] = "La mia campana rintocca solo i Demiurghi, ma tu per chi combatti?",
		["118151"] = "",
		["441305"] = "Ah ah ah, umano furbetto.",
		["9941021"] = "Tra loro e la Chiesa ci sono anni e anni di risentimento e, sai, i nemici dei miei nemici potrebbero diventare nostri amici.",
		["9973289"] = "Ho trovato dell'argilla.",
		["991422"] = "Pietra della Camera del tesoro",
		["772255"] = "Vuoi andare a Katib?",
		["665069"] = "Anche se riuscissi a distruggere Edith, non potrai mai fermare la vendetta dei Demiurghi.",
		["991102"] = "Non lo so, ma sembrava volessero dirci qualcosa.",
		["9973334"] = "Contratto",
		["9971343"] = "Il leggendario primo popolo?",
		["117093"] = "È questa la miniera? Non c'è anima viva.",
		["221173"] = "Mi dispiace tanto.",
		["551333"] = "Ah... una vorace ossessione... Ci ha accecati tutti, proprio così.",
		["9971335"] = "Ti sei pentita?",
		["200120019990"] = "/t0",
		["551366"] = "I piccoli e deboli non dubitano neanche delle bugie più ovvie.",
		["884053"] = "A te la scelta... Spezza con veemenza il giogo che ti opprime...",
		["441275"] = "Ma prima, ti prego di ingerire questi cristalli di polvere bianca e unirti alla nostra importante missione.",
		["9971311"] = "Come va? Hai trovato qualcosa di interessante?",
		["114030"] = "Impossibile aprire da questo lato.",
		["447209"] = "Strano, come fa Teresa a sapere che Vaillant è ancora vivo?",
		["791277"] = "Sono passati così tanti anni... E i tuoi occhi...",
		["773262"] = "Vattene",
		["771143"] = "Il sangue dei Marcioni non scorre solo nelle vene di Teresa, ma anche nelle mie.",
		["461001"] = "E allora sei ancora qui...",
		["448310"] = "",
		["992151"] = "",
		["446301"] = "",
		["221188"] = "Se solo mia figlia fosse qui!",
		["665192"] = "Per il bene supremo...",
		["221310"] = "Sia lode ad Adamina! Elmar non ti ha scoperto, vero?",
		["9973450"] = "Vattene",
		["2001100123"] = "Ho cercato di dargli la salvezza, ma... Su, diamogli la redenzione finale.",
		["117027"] = "Dei Deturpati... non dovrebbero essere qui.",
		["773006"] = "No",
		["995317"] = "Non illuderti. Tanto varrebbe ucciderti ora, ti risparmieresti ulteriori sofferenze.",
		["663108"] = "<color=#DBBA7FFF>Accetta</color>",
		["793294"] = "Contratto",
		["334052"] = "Arcieri della Chiesa. Sono coperti da strane piante. Sembra che siano stati morti per lungo tempo, ma i loro corpi non si sono ancora putrefatti.",
		["441201"] = "La tua stirpe è stata cancellata.",
		["661265"] = "Ah ah ah, Guida furbetta.",
		["9951058"] = "Fammi vedere, che cos'hai?",
		["991123"] = "C'è un altro modo per proseguire verso l'alto?",
		["771188"] = "Ah... Grazie per la tua abnegazione... Non è un sogno... Oh che cibo delizioso! È tutto qui? Ho ancora fame... Non darmi dell'ingordo, in fondo sono state quelle scimmie a farci cose terribili, un taglio... due tagli... la maggior parte della gente non è sopravvissuta a quell'incubo...",
		["332903"] = "Vuoi tornare alla carrozza?",
		["551361"] = "I mediocri sono sempre ossessionati dall'essere al di sopra degli altri.",
		["991161"] = "Posso solo guardarlo mentre il suo io svanisce... per diventare una macchina assassina. Questo maledetto Muro del silenzio, questa maledetta Ichthyosauria... Non torneremo mai alla Terra benedetta... Mai...",
		["993082"] = "E che mi dici di questo demone spaventoso?",
		["883104"] = "Inserisci il Sigillo di Yekub",
		["555250"] = "Chi sono?",
		["553396"] = "Accetta",
		["792378"] = "",
		["661171"] = "Il ben noto tradimento non era altro che una farsa organizzata dal Procuratore per coprire i segreti scovati nella Terra benedetta, segreti che avrebbero potuto portare alla caduta della Chiesa.",
		["225184"] = "I nostri discendenti accederanno finalmente alla Tomba colossale.",
		["774038"] = "Che cosa volete farmi? Fermati, Norwood, basta!",
		["791310"] = "Se trovi qualcosa di interessante, consegnamela pure.",
		["551087"] = "Dev'essere vicina... Pazienza... devo avere pazienza... Di solito non sono così sfortunato...",
		["9951021"] = "Abbiamo cercato entrambi di custodire al meglio quel segreto, ma quel che è fatto è fatto.",
		["4994993025"] = "Offri 50 funghi spettrali",
		["771237"] = "Lo farò...Benita, prima di dirti addio, vorrei chiederti un ultimo favore.",
		["442905"] = "Vuoi tornare alla carrozza?",
		["9976452"] = "",
		["9921048"] = "Oh, tutto esaurito? Bisogna fare scorta di nuovo.",
		["441104"] = "Fa lo stesso, il cancello per la Terra benedetta è ancora chiuso. Dev'essere da qualche parte lì vicino.",
		["663702"] = "No",
		["663141"] = "Rifiuta",
		["553312"] = "Sì",
		["551443"] = "Ah... Quindi l'hai incontrato... Quel rimpianto e quell'ossessione sono stati il prezzo che ho pagato per liberarmi dalle catene del fato... E tuttavia lui non può fare lo stesso. Posso soltanto abbandonarlo a galleggiare nell'abisso infinito.",
		["663806"] = "No",
		["9972288"] = "",
		["991111"] = "a causa del Muro del silenzio, il corso inferiore del fiume si è da tempo prosciugato.",
		["771227"] = "Mi basta sapere che mi sta aspettando.",
		["991430"] = "Pietra del Sifler",
		["553166"] = "<color=#DBBA7FFF>Restituisci le Pietre del tempo.</color>",
		["663170"] = "Che diamine è successo quindici anni fa?",
		["9924030"] = "/t0",
		["331143"] = "Katib? Non era stato distrutto quel posto?",
		["884042"] = "Dammelo, presto...",
		["225320"] = "Per il Santo Padre! Per la discendenza di Adamina!",
		["113233"] = "Vattene",
		["551439"] = "La mia terra natia, te ne ho parlato. La terra dei sogni, che fluttua in un mare senza confini.",
		["551098"] = "Perché non chiedi semplicemente un'altra clessidra alla Chiesa?",
		["221052"] = "Oh, gentili signori! Aiutatemi, vi prego!",
		["551189"] = "Come va la ferita?",
		["114025"] = "/t2",
		["553365"] = "Riempi flacone di Melos.",
		["553341"] = "Riempi flacone di Melos.",
		["9931003"] = "Croce.",
		["4994993101"] = "No",
		["111038"] = "La luce di Heggem si sta affievolendo più velocemente del previsto, e l'intera area è stata contaminata dai Sendril. Il villaggio è popolato solo da Deturpati... quello che resta dei discepoli di Heggem, temo.",
		["661147"] = "Certamente. Ma mi spaventa ancora di più perdere il controllo del mio destino e non riuscire a seguire il mio cuore... a causa di questa paura ho rinunciato al mio titolo e mi sono unita ai Demiurghi.",
		["991049"] = "Prego. Sii consapevole dei limiti.",
		["9961046"] = "La Terra benedetta è protetta da quattro Pilastri sacri e da formidabili cavalieri. Ma quanto scompiglio potranno mai portare i Demiurghi?",
		["551400"] = "Il frammento della meteora rossa in tuo possesso è la chiave di tutto. Ne avrai bisogno quando arriverà il momento giusto. Ti ringrazio per aver accettato, ma ti chiedo di aspettare. Ripagherò la tua fiducia.",
		["997024"] = "Finalmente! Abbiamo compagnia... Ma non sembrano amichevoli.",
		["442304"] = "",
		["441185"] = "Dev'essere stata la campana. Ironia della sorte... Se non fosse per quel traditore di Frollo, chi avrebbe potuto immaginare che la campana usata per avvertire la gente di un'invasione Sendril, sarebbe stata anche foriera di morte per i Demiurghi?",
		["117123"] = "Terrence, è una trappola! Te l'avevo detto, non mi ascolti mai!",
		["9961043"] = "Il mio aiuto? Non è incluso nel contratto.",
		["771255"] = "Terrence, non ti ho mai mentito...",
		["441123"] = "Non fraintendermi, non sono un traditore e non sono assolutamente un Demiurgo. Mi chiamo Vincent. Un tempo ero un nobile nella Terra benedetta.",
		["444271"] = "/t0",
		["441242"] = "Cosa vuole fermare Frollo con la sua campana?",
		["991093"] = "Sento la stanchezza, ma non riesco a dormire. Ho paura... Paura che al risveglio avrò dimenticato tutto.",
		["551152"] = "Un girasole che parla? Sono forse impazzito?",
		["551413"] = "...",
		["551158"] = "È difficile credere che tu mi stia raccontando la verità...",
		["331195"] = "A ogni modo non abbiamo altra scelta che tentare la nostra sorte laggiù.",
		["792381"] = "",
		["557084"] = "La fine dei Marcioni è la dimostrazione della crudele legge che vige a Solas.",
		["995059"] = "Dimmelo... Ah... Dimmelo, maledetto bastardo!",
		["9943033"] = "Vattene",
		["791278"] = "È qualcosa che voglio finire, quindi se hai dell'argilla in più, portamela... Senza fretta naturalmente, ci sono cose più importanti.",
		["9971332"] = "Ma qualcuno terrà viva la tua memoria, forse qualcuno della tua terra natia.",
		["9972379"] = "",
		["661217"] = "Come puoi dire una cosa simile?!",
		["111070"] = "Sta delirando. Prova a farle una domanda diversa.",
		["661110"] = "Uhm...",
		["221186"] = "È meraviglioso essere diventata la sposa del Santo Padre!",
		["553181"] = "Chi sei?",
		["221291"] = "Pensi davvero che questa sia la benedizione del Santo Padre? Non si può mettere fine al dolore di Adamina con delle semplici erbe.",
		["441282"] = "Fermare un Colosso? È davvero possibile?",
		["991426"] = "Ci sono cinque scanalature sulla porta. Inserisci le gemme nelle scanalature in ordine.",
		["776221"] = "",
		["792279"] = "",
		["9971296"] = "Sì, ma mi chiedo sempre... Se fossi in lei, approverei ancora la \"fede\"?",
		["663402"] = "No",
		["9972387"] = "",
		["991258"] = "Maledizione! Non so come tu abbia avuto la lettera d'invito, ma le regole sono regole. Hai l'opportunità di entrare nella stanza del tesoro,",
		["331194"] = "Sono solo voci. Su questo continente girano molte dicerie sull'ingresso della Tomba colossale e molte di loro non sono altro che menzogne.",
		["9971463"] = "Il tunnel segreto collega la Terra benedetta a Katib, come facevi a saperlo?",
		["777119"] = "Quindi... il maestro di Norwood sembra essere il prete cieco della Terra benedetta... Siamo stati gabbati in tutto e per tutto.",
		["221124"] = "Signora Guida? Sei vivo? No, no, no... Dovresti essere morto! Non posso vedere la mia mamma se non sei morto! Dov'è Elmar? Dov'è?",
		["793334"] = "Contratto",
		["884050"] = "Forza, trasformiamo questa ossessione in fonte di vita...",
		["441276"] = "I tuoi segreti moriranno qui con te a Edith, ma vivrai per sempre nella memoria dei Demiurghi.",
		["553287"] = "Commercia",
		["774032"] = "Solo con vera pazienza otterrai ciò che desideri di più. Tuttavia, i tuoi impulsi hanno dimostrato di rovinare tutti i tuoi sforzi.",
		["552349"] = "Costo: 1 Miele di condensazione e 90 ossa di Anthozoan. Vuoi davvero procedere?",
		["221307"] = "Visto che entrambi vogliamo trovare il Santo Padre, e visto il caos che regna Adamina, conviene fare squadra.",
		["991403"] = "Non rinunciare alla ragione portata dall'ordine. La pazienza fa miracoli. Impara a controllarti e sarai in grado di controllare tutto il resto.",
		["992081"] = "",
		["661227"] = "Ricordi a Katib? C'era quel cancello chiuso dal Procuratore. Questo tunnel segreto è un'altra entrata per aggirare il cancello.",
		["9973319"] = "Ho trovato delle reliquie antiche.",
		["100410042203"] = "Sì",
		["221174"] = "Non è colpa tua, signora Guida. Hai fatto quello che hai potuto.",
		["791378"] = "Come va? Hai trovato qualcosa di interessante?",
		["441194"] = "Accidenti! Apri il cancello, ora! Altrimenti...",
		["461002"] = "Non mettere alla prova la mia pazienza.",
		["221189"] = "Fa così freddo... dovrei cucirle qualche vestito nuovo. Il suo mantello verde non tiene abbastanza caldo.",
		["100310035999"] = "Impossibile aprire da questo lato.",
		["665193"] = "Se Dio muore, tutto si fermerà e la Terra benedetta perirà.",
		["441163"] = "Ancora tu... Ho abbassato per un attimo la guardia e sono stata attaccata da quella talpa È proprio lì nella torre, non può andare lontano.",
		["771219"] = "Puoi scegliere di crederci, Viola, e allora anche la bugia peggiore diventerà verità, un giorno. Prima o poi ci incontreremo di nuovo, in questo mondo in malora.",
		["222104"] = "",
		["791309"] = "Quando il mio Maestro mi ha dato questa mano, mi ha detto che la bara originale conteneva altre tre reliquie antiche, che però sono andate perdute per qualche ragione. Il mio braccio si calmerà solo quando avrò trovato le altre reliquie, oppure entrerà in frenesia, se non sto attento.",
		["661313"] = "Non ho sentito nulla.",
		["9973351"] = "Ho trovato una porzione di Lastra rotta.",
		["111072"] = "La luce di Heggem si sta affievolendo. È troppo debole per combattere contro i Sendril e i discepoli presenti si sono trasformati in mostri.",
		["9971464"] = "Era solo una supposizione, con Teresa e il Sigillo di Yekub catturati, la destinazione poteva essere solo una...",
		["661100"] = "Sebbene tu non possa vedere, dovresti aver avvertito il pericolo.",
		["111100"] = "Che uovo disgustoso... Attenzione, potrebbe essere una trappola!",
		["661262"] = "Ah ah ah, Guida furbetta.",
		["4994993100"] = "Sì",
		["441334"] = "Spero che questo viaggio non sarà troppo lungo.",
		["443198"] = "Vaillant...",
		["447083"] = "A volte mi chiedo veramente se non siamo stati maledetti...",
		["223295"] = "Consegna le erbe di Adamina.",
		["1003100045"] = "Un'anima esiliata lasciata in balia degli elementi...",
		["221311"] = "Mi dispiace, ha perso la ragione. Mi ha attaccato prima che avessi il tempo di spiegarle la situazione.",
		["995048"] = "Acqua del Fiume delle anime! Saporita, rinfrescante! Ne vuoi un po'?",
		["221154"] = "Sei libera....",
		["447303"] = "Smetti di perdere tempo, e dirigiti verso il campanile. Il Demiurgo traditore non può essere andato lontano.",
		["663109"] = "<color=#DBBA7FFF>Rifiuta</color>",
		["555272"] = "Se solo l'avessi saputo prima... Forse avrei potuto essere come lui... Anzi, migliore di lui... Ho infranto tutti i tabù, perso la dignità della fede... E per cosa?",
		["994014"] = "Guarda! È Jerold!",
		["1001100014"] = "Basta!",
		["991504"] = "Ma non importa, alla fine vinceremo e riporteremo Ichthyosauria alla sua antica gloria...",
		["441202"] = "Nessuno può davvero comprendere la completa umiliazione che i Demiurghi hanno dovuto subire. Sono stati lasciati indietro come bestiame in una città senza luce, a combattere i Sendril da soli.",
		["991090"] = "...",
		["228230"] = "",
		["223111"] = "Il Santo Padre?",
		["994480"] = "/t0",
		["221149"] = "Guarda cosa ho trovato.",
		["4994993024"] = "Offri 25 funghi spettrali",
		["551339"] = "Forse avrai una minuscola possibilità di cambiare il destino già scritto.",
		["661172"] = "Il Procuratore è stato alla Tomba colossale? Cosa ci ha trovato?",
		["991075"] = "Rochette... Rochette... Non devo dimenticare questo nome...",
		["663179"] = "Il Procuratore non si è fatto vedere?",
		["993411"] = "Inserisci l'ombra dei tempi antichi",
		["551119"] = "Ancora nulla."
	},
	StoryTutorial = {
		["1036DescriptionTouch"] = "Quando combatti contro un boss, se il personaggio perde tutta la ragione entra in stato lunatico. In questo stato, il boss diventerà più forte e potrà sferrare nuovi attacchi.\nQuesto stato si può rimuovere solo sconfiggendo il boss. Gli Elisir della ragione non possono essere utilizzati.",
		["1998Title"] = "Aumento di livello",
		["9104DescriptionKeyboard"] = "Gli Ossessi dell'Incubo sono sorti da qualche parte.",
		["9116Title"] = "",
