		["80032Description"] = "",
		["10052Description"] = "Um bracelete de prata com palavas samarosianas inscritas, deixado por uma mulher misteriosa no túmulo dos colossos. Infelizmente, ela desistiu de encontrar o dono.",
		["71048"] = "Prece da uma mãe",
		["71039"] = "Diário do açougueiro",
		["71065"] = "Bilhetes do mestre ferreiro",
		["10070Description"] = "Uma pedra preciosa extraordinária, ornamentada com uma runa que se assemelha a uma porta. Parece encaixar em algum lugar.",
		["71103"] = "Oração ictiosauriana",
		["10059"] = "Óleo de baleia rançoso",
