	StoryDialog = {
		["663703"] = "Sim",
		["994281"] = "Hahaha, meus catadores não vão parar de perseguir até que os Cavaleiros de Handel sejam eliminados de Ictiosauria...",
		["661152"] = "Depressa, estou perdendo o controle!",
		["111121"] = "Quem é Lorde Ramones?",
		["771198"] = "Como assim não tivemos escolha? Você entregou Teresa por nada? Eu tenho que entrar neste maldito túmulo dos colossos para trazê-la de volta!",
		["111134"] = "Ha-ha, um humano astuto.",
		["661322"] = "Sim... O código... Se você não sabe nada sobre isso, sugiro que vá embora agora mesmo!",
		["100210022202"] = "Sim",
		["997013"] = "Eu ainda consigo sentir o sopro da vida em algum lugar nesta ilha, embora seja extremamente fraco.",
		["551268"] = "Depressa, vá para as Terras Abençoadas e faça o que precisa ser feito... Agora eu preciso descansar... Não se preocupe... Espero que tudo acabe bem.",
