		["664999"] = "Não pode ser aberta sem uma chave.",
		["9951054"] = "Nunca se sabe o que a igreja está escondendo...",
		["554068"] = "/t0",
		["9931017"] = "Predestinada ou não, foi uma escolha difícil para ela fazer... Mas ela já pagou seu doloroso preço por isso.",
		["998302"] = "",
		["111055"] = "Rápido, minha carruagem não vai esperar muito.",
		["222314"] = "",
		["661185"] = "Conte-me tudo que você sabe...",
		["667084"] = "Droga, ela enlouqueceu completamente.",
