		["773187"] = "Entregar o broto rosa luminescente.",
		["994479"] = "/t0",
		["113907"] = "Sim",
		["991444"] = "Pedra da natureza humana",
		["221064"] = "O que aconteceu aqui?",
		["114020"] = "/t2",
		["4994994003"] = "/t0",
		["551294"] = "Podemos partir, tudo está pronto. Com a ajuda da boa sorte de hoje, certamente encontraremos o selo de Yekub sob esta Torre Sagrada.",
		["777039"] = "Parece o mestre do Norwood, a voz dele soa estranhamente familiar.",
		["791370"] = "Não se preocupe, eu me lembrarei de tudo que você me ensinou, não sou a velha Viola.",
		["441258"] = "Ele foi feito por Edith através do antigo ritual dos marcioneses.Transformado em cristais e fundido com humanos, ele é capaz de despertar um poder dormente.",
		["9934226"] = "/t0",
		["441097"] = "Já fizemos tanto por hoje...Tenho orgulho dos meus filhos. Se fosse mais jovem, eu me juntaria a eles.",
		["331171"] = "Eu sacrificaria minha cabeça novamente se pudesse salvá-la.",
		["111039"] = "Heggem parece... doente.",
		["9943029"] = "Negociar",
		["553189"] = "Como está seu ferimento?",
		["552291"] = "",
		["553163"] = "Onde estão as outras pessoas da igreja?",
		["663902"] = "Não",
		["4994992201"] = "Haha... é hora das recompensas！",
		["336223"] = "",
		["9971288"] = "Se você encontrar mais argila, por favor, traga-a para mim.",
		["9961052"] = "Terrence, você mais uma vez ultrapassou o limite do azar. Mas, às vezes, o caos não é algo ruim: pelo menos pode fazer as pessoas enxergarem a verdade.",
		["663265"] = "Sair",
		["551371"] = "A verdade oculta acata intenções malignas ignorantes.",
		["551368"] = "Amor... é isso que traz insanidade.",
		["221137"] = "Eu conto sim, senhor mensageiro. Mas, antes disso, encontre a Elmar e peça para ela me deixar sair daqui. Não consigo parar de me preocupar com a minha mãe.",
		["441147"] = "Pense bem antes de tomar uma decisão...",
		["9973459"] = "Voltar ao transporte?",
		["554271"] = "/t1",
		["227079"] = "Este fedor terrível... eles não são mais humanos!",
		["221071"] = "Por favor, ajude-a. Por favor...",
		["9941026"] = "Desculpe, tenho escondido coisas...",
		["551314"] = "Um coveiro.",
		["991465"] = "Desculpe, sem descontos. Mas saiba que todos os meus produtos valem o que eu cobro.",
		["994286"] = "/t0",
		["442102"] = "",
		["551352"] = "Amor... é isso que traz insanidade.",
		["663604"] = "Não",
		["663605"] = "Sim",
		["444083"] = "/t0",
		["112201"] = "Comer o ovo do portador?",
