		["71106Description"] = "",
		["10028"] = "Ключ к лифту",
		["78002"] = "Сундук мечты",
		["10046"] = "Сломанная скрижаль",
		["70071"] = "Священный кодекс",
		["71109Description"] = "",
		["71045"] = "Фрагмент дневника",
		["10011Description"] = "На плечах Курьеров лежат трудные задачи: исследование Мрачного тумана, поиск Кладбища Колоссов и ведение дневников. Однако ничего полезного о Кладбище Колоссов вы в них не найдете. \n«...Сияющие кристаллы... рассеивают мглу Сендрила... не нужно бояться, что свет Колоссов угаснет... Святая Земля, вечный свет четырех Святых Столпов... Рыжая ведьма... Запретное колдовство... Колоссы... Перерождение...»",
		["71111"] = "Тайный приказ",
		["72118"] = "Благословленный Эликсир прозрения",
		["71113Description"] = "",
		["71114"] = "Журнал путешествий Либа 3",
		["71114Description"] = "",
		["71115Description"] = "",
		["71064Description"] = "",
		["71116Description"] = "",
		["71117"] = "Незаконченный древний том",
		["71117Description"] = "",
		["71118"] = "Изречения Эббингхауса 1",
		["71118Description"] = "",
		["78001"] = "Сундук мечты",
		["71042"] = "Последнее слово Кассандры",
		["71014"] = "Записка кающегося",
		["78001Description"] = "Сундук из королевской сокровищницы. Даже Сифлер не знает, что внутри, но это определенно что-то привлекательное. Он даже может быть пустым. Хоть это недешево, но кто-то может рискнуть и купить его.",
		["10062"] = "Лазурное сердце",
		["10011"] = "Дневник Курьера",
		["71109"] = "Книга Гудериана",
		["71006"] = "Таинственная запись",
		["78004"] = "Сундук мечты",
		["78004Description"] = "Сундук из королевской сокровищницы. Даже Сифлер не знает, что внутри, но это определенно что-то привлекательное. Он даже может быть пустым. Хоть это недешево, но кто-то может рискнуть и купить его.",
		["78005"] = "Сундук мечты",
		["10053"] = "Окровавленное завещание",
		["78005Description"] = "Сундук из королевской сокровищницы. Даже Сифлер не знает, что внутри, но это определенно что-то привлекательное. Он даже может быть пустым. Хоть это недешево, но кто-то может рискнуть и купить его.",
		["99999Description"] = "为踏上未曾设想的道路之人献上的赠礼",
		["10038Description"] = "Ключ к Шубу, одному из четырех Святых Столпов Благословенной земли. Столпы расположены на запретных землях Церкви. Их ключи хранились в безопасности, доступ к ним имел только Посредник и несколько Наблюдателей. С тех пор, как свет четырех Святых Столпов начал угасать, Посредник запретил доступ к ключам. Поговаривают, что кто-то украл ключи, но ворота закрыты уже целое десятилетие. До сих пор никто не исследовал причину угасания четырех Святых Столпов.",
		["80021"] = "Черноводные осколки",
		["70049"] = "Стеклянный шар",
		["80022"] = "Черноводный камень",
		["71010Description"] = "",
		["80034Description"] = "",
		["10050Description"] = "Медная монета, подаренная Норвуду его умершим братом. С одной стороны изображен сигил Церкви, с другой — маркионская руна.",
		["10065Description"] = "Железное копье. На наконечнике копья можно с трудом разглядеть гравировку с эмблемой Святой Земли, но грубый дизайн делает копье непригодным для жарких битв. Внизу на сломанной рукояти есть надпись: «Рошетт».",
		["70037"] = "Жнец",
		["10060"] = "Загустевшее китовое масло",
		["10032"] = "Священный Василиск",
		["80025"] = "强化原石lv5",
		["71079"] = "Небрежная записка",
		["80025Description"] = "",
		["71050"] = "Официальный документ, подготовленный Еленой",
		["10015"] = "Камень Будущего",
		["71030Description"] = "",
		["80032"] = "重铸原石lv2",
		["80033"] = "重铸原石lv3",
		["10039Description"] = "Ключ к Хаде, одному из четырех Святых Столпов Благословенной земли. Столпы расположены на запретных землях Церкви. Их ключи хранились в безопасности, доступ к ним имел только Посредник и несколько Наблюдателей. С тех пор, как свет четырех Святых Столпов начал угасать, Посредник запретил доступ к ключам. Поговаривают, что кто-то украл ключи, но ворота закрыты уже целое десятилетие. До сих пор никто не исследовал причину угасания четырех Святых Столпов.",
		["80035Description"] = "",
		["20010Description"] = "Улучшает восстановление Выдержки на 30 секунд.\nНельзя использовать два или более вида эффектов для восстановления Выдержки одновременно.\nПеред трудными битвами Курьеры готовили несколько Эликсиров Выдержки на случай неизвестной опасности. Ведь в бою отсутствие Выдержки приведет к смерти.",
		["21010Description"] = "Улучшает восстановление Выдержки на 1 минуту.\nНельзя использовать два или более вида эффектов для восстановления Выдержки одновременно.\nБлагодаря благословению Бениты, этот эликсир остается эффективным дольше. Бенита беспокоится, чтобы ее товарищи не умерли от истощения.",
		["70056"] = "Окровавленная маска",
		["10070"] = "Камень Сокровищницы",
		["20011Description"] = "Количество костей увеличивается вдвое в течение 10 минут.\nБудучи побочным продуктом Эликсира Грубой силы, он не улучшит силу пользователя, а повысит его концентрацию. Этот Эликсир идеально подходит для тех, кто желает больше костей, поскольку усиленная концентрация предотвращает их повреждение.",
		["20012"] = "Эликсир здоровья",
		["20012Description"] = "Медленно восстанавливает Здоровье на 30 секунд.\nНельзя использовать два или более вида эффектов для восстановления Здоровья одновременно.\nИз-за опасности использования Зелий здоровья в бою Курьеры часто используют Эликсир здоровья. Это ускорит их самоисцеление, непрерывно и быстро восстанавливая здоровье. Такой эффект происходит из-за использования материалов, собранных с Нечестивых.",
		["71071Description"] = "",
		["21012Description"] = "Медленно восстанавливает Здоровье на 1 минуту.\nНельзя использовать два или более вида эффектов для восстановления Здоровья одновременно.\nЭликсир, благословленный Бенитой, действует дольше. Никто не знает, как Бенита благословила Эликсир, но в результате он приятнее на вкус, чем обычный вариант с привкусом Сендрила.",
		["20013"] = "Зелье полного восстановления",
		["70052"] = "Изношенная рукоять меча",
		["71063"] = "Записка",
		["20013Description"] = "Мгновенно восстанавливает Здоровье. Это зелье мечты любого искателя приключений. Никто не знает, как приготовить такой Эликсир, даже Церковь. Те, кому удалось его получить, держат способ добычи Эликсира в строжайшем секрете.",
		["10036"] = "Старая рукоять",
		["10058"] = "Вечно яркая туманная лампа",
		["80001Description"] = "Необработанные камни используются в алхимии. Эти загадочные камни появились в Соласе примерно в то же время, что и Сендрил. Их можно найти в оккупированных Сендрилом областях.",
		["10007Description"] = "Небеса озарил свет Алого Метеора, обрушившегося на Экзилиум. Ударная волна и облако пепла почти разрушили это место. Когда свет начал затухать, город наводнили туманы Сендрила.\nНа двух половинах Метеора были выгравированы странные узоры, якобы созданные человеком. Удивительно, но осколки Метеора могут в некоторой степени разогнать Сендрил.",
		["20014Description"] = "Улучшает атаку на 30 секунд.\nНельзя использовать два или более вида эффектов для улучшения атак одновременно.\nЭтот вид Эликсира дает принявшему его невероятную силу. Выпившие Эликсир Грубой силы говорят, что чувствовали в теле мощь, которая позволила бы им сломать кости Нечестивых. Другие считают, что битва с Нечестивыми все еще чрезвычайно опасна.",
		["79015"] = "Формула: Порошок оцепенения",
		["21014Description"] = "Улучшает атаку на 1 минуту.\nНельзя использовать два или более вида эффектов для улучшения атак одновременно.\nЭтот Эликсир был благословлен Бенитой. Даже с мощью Эликсира сражаться с Нечестивыми все еще чрезвычайно опасно. Благословение дарит принявшему его мужество.",
		["20015Description"] = "Улучшает Защиту на 30 секунд. \nНельзя использовать два или более вида эффектов для улучшения Защиты одновременно.\nЗараженные существа, имеющие твердую оболочку, очень популярны. Люди могут извлечь из оболочки полезные вещества и создать порошок, который ненадолго улучшит их Защиту.",
		["10014Description"] = "«Храбрые души зажгли Свет Колосса, дав начало жизни и ее процветанию».\nПророчество, переданное предками маркионов, подарило этому народу другое видение мира. Они не боятся тьмы и слепо не поклоняются свету. Они верят, что эта жизнь — лишь звено в цепи жизненных циклов. Пройдя через прошлое, настоящее и будущее, их души познают истину.\nУбеждения маркионов отражаются в их оккультных хитростях и приборах. Их предки умолчали, где искать истину. Если им хочется ее найти, сначала нужно пройти тест.",
		["71071"] = "Запись допроса",
		["50001"] = "Расколотый Иммундус",
		["70068"] = "Иссохший морской паук",
		["21015"] = "Благословленный Порошок Защиты",
		["21015Description"] = "Улучшает Защиту на 1 минуту.\nНельзя использовать два или более вида эффектов для улучшения Защиты одновременно.\nЭтот порошок освещен Бенитой. Его очищают и превращают в пилюли, которые продлевают его действие. Однако они не могут усилить Защиту, поскольку человеческое тело имеет пределы своих возможностей.",
		["20009"] = "Боеприпасы",
		["10047Description"] = "Кто-то оставил здесь ключ. Он открывает некую комнату в Священной башне.",
		["71102"] = "Старая бумага из рыбьей кожи",
		["80012Description"] = "Живые организмы, обнаруженные в трупах некоторых сильных Нечестивых. Они образуют единую структуру, которая распадается при касании других живых существ и моментально интегрируется с ними.",
		["20016Description"] = "Увеличивает сопротивление стихии на 30 секунд.\nНельзя использовать два или более вида эффектов для улучшения сопротивления одновременно.\nИзменения погоды сильно влияют на жизнь человека. В сравнении со стойкими Нечестивыми он более уязвим. Курьеры часто принимают этот Эликсир перед битвами в опасных зонах, чтобы уменьшить урон от стихий и ослабить боль.",
		["71033"] = "Дневник неудачных экспериментов, страница 78",
		["80023Description"] = "Кристаллы используются для усиления трофеев.\nПод воздействием света Колоссов телесные жидкости Нечестивых превращается в Черноводные кристаллы. Тем не менее, только после победы над сильнейшими Нечестивыми, насквозь пропитанными Сендрилом, можно получить такой кристалл.\nИспользуйте Черноводный кристалл на Алтаре, чтобы максимально усилить эффекты трофеев.",
		["21016"] = "Благословленный Эликсир инерции",
		["21016Description"] = "Увеличивает сопротивление стихии на 1 минуту.\nНельзя использовать два или более вида эффектов для улучшения Сопротивления одновременно.\nПосле того, как Бенита благословила Эликсир, его действие продлевается.",
		["50003Description"] = "Иммундус — вид кристалла, образующегося в теле носителя после того, как Сендрилы поглотят его энергию. Поток энергии создает странные узоры на его поверхности. Образовавшийся Кристалл Иммундус указывает на то, что энергия носителя была почти полностью поглощена. Использование Кристаллов Иммундус на Алтаре может разблокировать разные способности.",
		["70036Description"] = "Мирные жители Святой Земли больше всего ненавидят шутов. Эти паразиты живут под боком у дворян и зарабатывают на жизнь, развлекая их.\nЖивущие в темных уголках Святой Земли ни разу не видели дворян за всю свою жизнь. А вот шуты, как чума, постоянно их донимают, унижают и проклинают... Безумный смех, доносящийся из-под маски, вселяет в людей безнадежность. Почему Господь не очистит Землю от этой скверны?!",
		["70047Description"] = "Скалы, загрязненные Сендрилом.\nТакие неживые объекты, как скалы, наиболее подвержены загрязнению, их эрозия — верный признак воздействия Мрачного тумана. Однако концентрация Сендрила в почве низкая, поэтому ее часто используют в качестве алхимического катализатора. ",
		["20017"] = "Зелье противоядия",
		["79008Description"] = "Эта формула научит вас, как сделать Зелье противоядия.\nЗелье противоядия: увеличивает сопротивление ядам на 30 секунд.",
		["79013Description"] = "Эта формула научит вас, как сделать Эликсир восстановления.\nЭликсир восстановления: быстро восстанавливает очки Здоровья на 30 секунд.",
		["78003Description"] = "Сундук из королевской сокровищницы. Даже Сифлер не знает, что внутри, но это определенно что-то привлекательное. Он даже может быть пустым. Хоть это недешево, но кто-то может рискнуть и купить его.",
		["20017Description"] = "Увеличивает сопротивление ядам на 30 секунд.\nНельзя использовать два или более вида эффектов для улучшения сопротивления одновременно.\nЭликсир содержит в себе различные токсины. Курьеры используют его, чтобы ненадолго повысить устойчивость к ядам.",
		["21017"] = "Благословленное Зелье противоядия",
		["21017Description"] = "Увеличивает сопротивление ядам на 1 минуту.\nНельзя использовать два или более вида эффектов для улучшения сопротивления одновременно.\nБлагословение Бениты увеличивает число токсинов в Зелье, что усиливает его эффект и продлевает действие. ",
		["72001Description"] = "",
		["71075Description"] = "",
		["71080Description"] = "",
		["70072Description"] = "Железное копье. На наконечнике копья можно с трудом разглядеть гравировку с эмблемой Святой Земли, но грубый дизайн делает копье непригодным для жарких битв. Внизу на сломанной рукояти есть надпись: «Рошетт».",
		["70004"] = "Позвоночник хищника",
		["71039Description"] = "",
		["71044Description"] = "",
		["71057Description"] = "",
		["71066Description"] = "",
		["70050Description"] = "Раньше кабаны в лесу Катиба были любимой добычей маркионов.\nНикто не знает, когда кабаны начали терять плоть и кровь и стали вооружаться костями умерших. Как правило, на костях множество трещин и следы ожогов и коррозии. Да убережет Господь души хозяев этих костей от боли и страданий. ",
		["70061Description"] = "Говорят, Божественный меч Церкви может очистить Сендрилов. Посредник Хиггинс попросил своего близкого друга Израиля, мастера по изготовлению мечей, позаботиться об этой реликвии. Когда грудь Элеоноры пронзил меч Милость Тристана, случилось чудо — ее тело было очищено от Сендрила. \nОкровавленный Божественный меч потерял свою славу, осталось только проклятие лжи.",
		["70073"] = "Ящер из глубины",
		["70003Description"] = "Сильно зараженным Нечестивым суждено стать бездумными хищниками. Они окончательно теряют человеческий облик и одержимы лишь одной целью — быстро и свирепо сбить жертву с ног. Они убивают добычу своими невероятно сильными черепами.",
		["40001Description"] = "Осколки костей, которые были заражены. Они немного пополняют запас энергии, если преподнести их на Алтарь.",
		["71080"] = "Испорченная запись",
		["20033"] = "Сверкающий камень",
		["20019"] = "Антисендрильный Эликсир",
		["20019Description"] = "Улучшает Теневое сопротивление на 30 секунд.\nНельзя использовать два или более вида эффектов для улучшения сопротивления одновременно.\nНекоторые атаки Нечестивых призывают Сендрил. Этот Эликсир особенно полезен для Курьеров, которые часто бродят во Мрачном тумане.",
		["70059Description"] = "«Божья милость убережет нас от тьмы, исцелит страждущих и очистит Нечестивых».\nПоследние десять лет тело Посредника все больше слабело. Хорн стал его правой рукой. Он сопровождает Посредника на собрания, во Двор Святой Троицы и к четырем Святым столпам...\nПосредник редко беседует с Хорном. Несмотря на это, Хорн готов пожертвовать телом и душой ради Посредника.",
		["21019Description"] = "Улучшает Теневое сопротивление на 1 минуту.\nНельзя использовать два или более вида эффектов для улучшения сопротивления одновременно.\nПосле благословения Бениты Эликсир становится более насыщенным, и его действие продлевается.",
		["20020Description"] = "Медленно восстанавливает Здравый ум на 15 секунд.\nПосле столь продолжительного контакта с Сендрилом и Нечестивыми, большинство Курьеров видели вещи, способные свести с ума. Именно этот трудный опыт позволил им стать такими сильными. Тем не менее, никому не нравится бремя тяжелых мыслей, поэтому Курьеры пьют Эликсир Здравого Ума всякий раз, когда чувствуют тень подбирающегося безумия.",
		["20020"] = "Эликсир Здравого Ума",
		["72102"] = "Ключ к двери под Священной башней",
		["20022Description"] = "Дарит бесконечную Выдержку на 15 секунд.\nНельзя использовать два или более вида эффектов для восстановления Выдержки одновременно.\nПриняв Эликсир, вы ненадолго перестанете чувствовать усталость. Несмотря на сложность его приготовления, более агрессивные в бою Курьеры любят этот Эликсир.",
		["26001"] = "Сломанный бронзовый свисток",
		["70005"] = "Плеть аскета",
		["71111Description"] = "",
		["20023Description"] = "Быстро восстанавливает очки Здоровья на 30 секунд.\nНельзя использовать два или более вида эффектов для восстановления Здоровья одновременно.\nЭтот Эликсир иногда считают злом из-за его эффекта. Ведь он исцеляет тело больше, чем предполагалось природой. Только ведьмы, которые нарушают табу, делают такой Эликсир.",
		["21023"] = "Благословленный Эликсир восстановления",
		["21023Description"] = "Быстро восстанавливает очки Здоровья на 1 минуту.\nНельзя использовать два или более вида эффектов для восстановления Здоровья одновременно.\nБлагословение Бениты улучшает действие обычного Эликсира восстановления. Бенита считает, что использование Сендрила для продления эффекта не является табу, и поэтому готова нарушить любые правила, чтобы уберечь своих товарищей.",
		["10051"] = "Браслет",
		["71012"] = "Записная книжка Дхарама",
		["72110Description"] = "Камень бабочки",
		["71092"] = "Заметки 1",
		["21024"] = "Благословленный Эликсир прозрения",
		["21024Description"] = "Значительно усиливает атаку, но персонаж будет кровоточить в течение 1 минуту.\nНельзя использовать два или более вида эффектов для улучшения атак одновременно.\nБлагословение Бениты продлевает действие Эликсира. Те, кто использовал Эликсир, утверждали, что их свет способен рассеять Мрачный туман. Однако Бенита не верит, что люди могут сиять, как Колоссы. Только твердая вера может развеять их страх перед тьмой.",
		["10069"] = "Камень Человеческой природы",
		["20025Description"] = "Значительно улучшает Защиту на 30 секунд.\nНельзя использовать два или более вида эффектов для улучшения Защиты одновременно.\nПорошок обеспечивает еще большую защиту, но его действие непродолжительно. Некоторые Курьеры идут на риск и используют его, чтобы противостоять атакам сильнейших Нечестивых.",
		["71084"] = "Дневник Экхарта, часть вторая",
		["21025Description"] = "Значительно улучшает Защиту на 1 минуту.\nНельзя использовать два или более вида эффектов для улучшения Защиты одновременно.\nПорошок оцепенения превращается в пилюли благодаря благословению Бениты. Это продлевает его действие. Эти таблетки еще долго остаются в организме принявшего их человека. Те, кто зависит от таких пилюль, часто страдают от физической боли.",
		["20026"] = "Горькие хвойные иголки",
		["20026Description"] = "Исцеляет все дебаффы — отравление ядом, кровотечение, ожоги и т.д.\nКогда мир поглотил Мрачный туман, немногие растения выжили. Никто не знает, какому виду принадлежат горькие хвойные иголки, но они могут мгновенно исцелить любой дебафф, после чего боль персонажа полностью исчезает.",
		["21026Description"] = "Исцеляет все дебаффы — отравление ядом, кровотечение, ожоги и т.д. Также дарит иммунитет ко всем дебаффам на 30 секунд.\nСладкие корни редки. Чтобы бродить во Мрачном тумане, люди должны время от времени употреблять эти корни. Большинство корней имеют привкус Сендрила, что делает их совсем не аппетитными. Никто не знает, откуда берутся эти корни, поэтому люди тайно ими запасаются для будущего использования. ",
		["20027"] = "Древний серебряный ключ",
		["71077"] = "Дневник стража",
		["70033"] = "Сияющие оленьи рога",
		["20027Description"] = "Позволяет использовать персонажей, недоступных для Боя.\nНекоторые говорят, что это благословение Господа, доступное только для самых преданных его слуг. Другие верят, что это проклятие Сендрила, превращающее людей в бездушных марионеток. Однако для большинства это ключ, который существует только в сознании человека и открывает врата между жизнью и смертью.",
		["21027Description"] = "复活",
		["21026"] = "Сладкие корни",
		["10039"] = "Ключ Хады",
		["70044"] = "Фрагмент вероучения",
		["71068"] = "Протокол собрания 2",
		["20025"] = "Порошок оцепенения",
		["20028"] = "Сморщенный священный фрукт",
		["20028Description"] = "Создает Щит на 1 минуту.\nСморщенный священный фрукт создает загадочный щит. Он считается результатом древней таинственной магии. Щит, как воплощение света Колоссов, поглощает атаки Нечестивых.",
		["20029Description"] = "Сбрасывает настройки Атрибутов персонажа.\nВыдержанное вино, которое сияет светом галактик, когда его наливают в бокал. Выпив его, вы забываете обо всем.",
		["71087"] = "Анонимное письмо",
		["70020"] = "Грибы Хеггема",
		["20030"] = "Гвоздь в позвоночнике",
		["20030Description"] = "Используйте для быстрого восстановления Здравого ума.\nКаменный позвоночник, который изначально использовался экстремистами-верующими для практики самоистязания.\nКаждый гвоздь символизирует один грех, а любой человек с семью гвоздями в спине упадет замертво.\nНевообразимая боль от гвоздей может вернуть Здравый ум в Мрачном тумане, но только самые сильные Курьеры используют их для этой цели.",
		["79001"] = "Формула: Порошок Защиты",
		["20031Description"] = "Используйте для быстрого передвижения в течение 5 минут.\nДворяне Святой Земли верят, что надев этот амулет, они могут поймать любую добычу во время охоты в дикой местности.\nАмулеты набиты специями, которые стимулируют выработку адреналина, значительно повышая скорость их реакции.\nВ период стремительного развития Церкви, амулеты стали применяться в военном деле.",
		["21018Description"] = "Улучшает Скрытное сопротивление на 1 минуту.\nНельзя использовать два или более вида эффектов для улучшения сопротивления одновременно.\nБлагословение Бениты продлевает действие Эликсира, возможно, из-за ее непоколебимой веры.",
		["20033Description"] = "Используйте, чтобы видеть дальше во Мрачном тумане в течение 5 минут.\nРедкая руда, которую морские волны прибивают к берегу.\nОна излучает свет, рассеивающий Мрачный туман.\nНекоторые безумные верующие считают, что сверкающие камни – останки колоссов, и что Кладбище Колоссов находится где-то за глубоким морем.",
		["21022Description"] = "Дарит бесконечную Выдержку на 30 секунд.\nНельзя использовать два или более вида эффектов для восстановления Выдержки одновременно.\nБлагословение Бениты продлевает действие Эликсира. Тем не менее, Бенита всегда призывает соблюдать осторожность несмотря на бесконечную Выдержку.",
		["26001Description"] = "При помощи свистка можно вызвать Гленна, чтобы он перенес свистящего к повозке за некоторую цену. Гленн всегда прилетит на зов свистящего, где бы он ни был.",
		["10003"] = "Заметки ведьмы",
		["71054"] = "Посланник возмездия",
		["70017"] = "Зрелые споры",
		["26002"] = "Костяной свисток",
		["70055"] = "Ядро Колосса",
		["26002Description"] = "При помощи свистка можно вызвать Гленна, чтобы он перенес свистящего к повозке.\nЭтот редкий свисток сделан из кости и издает особый звонкий свист. К сожалению, он будет поврежден после использования. Из-за сложности его конструкции и уникальных материалов свисток сложно воссоздать.",
		["26003Description"] = "Согласно традиции, каждая бессмертная медуза символизирует душу умершего Курьера, который указывает путь своим коллегам-новичкам, бродящим во тьме Мрачного тумана.\nСтоя рядом с медузой, Здравый ум будет медленно восстанавливаться. Этот эффект не действует во Мрачном тумане.\nВ прозрачной банке плавают десять мерцающих бессмертных медуз. Это странное существо может выжить при любых условиях, поскольку у ее жизни нет конечной точки, есть только бесконечная реинкарнация. Говорят, медуза может быть источником легенды о реинкарнации души.",
		["26004"] = "Банка с лунными медузами",
		["26004Description"] = "Используйте, чтобы оставить лунную медузу на земле. Находясь рядом с медузой, разум останется ясным. Эффект продлится 1 минуту.\nВид бессмертной медузы, которая заразилась от Сендрила.\nПо сравнению с бессмертной медузой, она чрезвычайно слабая, так как не может выжить вне Мрачного тумана.\nЕсли оставаться рядом с лунной медузой, то Мрачный туман перестанет разрушать Здравый ум на некоторое время.",
		["70043"] = "Пустая курильница",
		["20029"] = "Звездное медовое вино",
		["72001"] = "减时间道具1",
		["20018Description"] = "Улучшает Скрытное сопротивление на 30 секунд.\nНельзя использовать два или более вида эффектов для улучшения сопротивления одновременно.\nНекоторые Нечестивые монстры уникальны тем, что светятся причудливым светом. Эта удивительная черта совсем не похожа на свечение Колоссов. Курьеры используют Эликсиры сокрытия, чтобы уменьшить вредное воздействие этого странного света.",
		["10008"] = "Голова рыцаря",
		["71105Description"] = "",
		["21025"] = "Благословленный Порошок оцепенения",
		["72002Description"] = "",
		["72003"] = "减时间道具3",
		["71073"] = "Секретная запись 2",
		["71110"] = "Треснувшая раковина",
		["72003Description"] = "",
		["72004Description"] = "",
		["10029Description"] = "Утерянный ключ Посредника открывает дверь в подземелья Священной башни Катиба.\nКонечно же, Посредник не хотел, чтобы кто-либо узнал о событиях, произошедших в подземелье Башни 15 лет назад. ",
		["72101"] = "Башенный ключ",
		["70040"] = "Дворянская шляпа",
		["70038"] = "Зараженная кровь оленя",
		["71022"] = "Письмо Ните",
		["72101Description"] = "Башенный ключ",
		["71094"] = "Предсмертная записка",
		["20021Description"] = "Мгновенная потеря Здравого ума.\nВо время битвы с Нечестивыми Курьеры могут потерять рассудок, а иногда даже видеть странные вещи. Аномальность не только вызывает физическую боль, но и обостряет чувства. Некоторым Курьерам так понравилось это состояние, что они создали Эликсир, воссоздающий такие эффекты.",
		["72103Description"] = "Ключ к внутренней двери зала Святой Троицы",
		["72104"] = "Ключ к боковой двери Алтаря возрождения",
		["72104Description"] = "Ключ к боковой двери Алтаря возрождения",
		["10059Description"] = "Жирное вещество используется в качестве топлива для освещения. У него густая консистенция, и оно излучает  голубоватое свечение.\nДобытое из разломов под землей, китовое масло образуется из мертвых морских организмов, но некоторые говорят, что оно в действительности состоит из разлагающихся трупов колоссов.\nОчень тяжело повысить его качество и очистить, что делает масло невероятно редким материалом.",
		["72105Description"] = "Меч-трость Экхарта",
		["20024Description"] = "Значительно усиливает атаку, но персонаж будет кровоточить в течение 30 секунд.\nНельзя использовать два или более вида эффектов для улучшения атак одновременно.\nПо словам принявшего Эликсир, его тело излучало прекрасный свет, а на ухо шептал голос: Великая сила, великий свет, рассей Туман, Сендрила нет...",
		["72111"] = "Амулет в виде собаки",
		["79008"] = "Формула: Зелье противоядия",
		["10045"] = "Гончарная глина",
		["10042"] = "Ключ к воротам",
		["10016"] = "Жировая свеча",
		["72106"] = "Ключ к исповедальне",
		["72106Description"] = "告解室的钥匙",
		["72113"] = "Благословленный Эликсир инерции",
		["72114"] = "Благословленное Зелье противоядия",
		["72114Description"] = "Увеличивает сопротивление ядам на 1 минуту.",
		["10041"] = "Мерцающий розовый бутон",
		["72115"] = "Эликсир иллюзий",
		["72115Description"] = "Мгновенная потеря Здравого ума.",
		["72116Description"] = "Дарит бесконечную Выдержку на 30 секунд.",
		["71026"] = "Письмо о Катибе",
		["72117"] = "Благословленный Эликсир восстановления",
