		["221289"] = "Я попробую.",
		["553329"] = "Наполнить бутыль Мелос.",
		["9921022"] = "Тереза... Тереза была моей женой. Я не видел ее уже очень давно.",
		["441108"] = "Но перед этим, странник, возьми это в награду.",
		["773104"] = "Согласиться",
		["773187"] = "Дай мне мерцающий розовый бутон.",
		["994479"] = "/t0",
		["113907"] = "Да",
		["991444"] = "Камень Человеческой природы",
		["221064"] = "Что же здесь произошло?",
		["114020"] = "/t2",
		["4994994003"] = "/t0",
		["551294"] = "Давай отправимся, когда все будет готово. С помощью сегодняшней удачи, мы точно найдем Печать Иекуба под Священной башней.",
		["777039"] = "Похоже, говорит хозяин Норвуда, его голос кого-то мне напоминает.",
		["791370"] = "Не волнуйся, я запомню то, чему ты меня научил, я уже не та Виола, что прежде.",
		["441258"] = "Она была создана Эдит с помощью маркионского ритуала. Потом пыль превратилась в кристаллы, которые внедрили в людей с активированной силой.",
		["9934226"] = "/t0",
		["441097"] = "Мы уже столько сделали для это дня... Я горжусь своими старшими сыновьями. Если бы я была несколькими годами моложе, я бы присоединилась к ним.",
		["331171"] = "Будь у меня шанс спасти ее, я бы вновь пожертвовал своей головой.",
		["111039"] = "Хеггем выглядит... больным.",
		["9943029"] = "Торговать",
		["553189"] = "Как твоя рана?",
		["552291"] = "",
		["553163"] = "Где другие люди Церкви?",
		["663902"] = "Нет",
		["4994992201"] = "Ха-ха…Время награды!",
		["336223"] = "",
		["9971288"] = "Если ты найдешь еще Белой глины, пожалуйста, принеси мне.",
		["9961052"] = "Терренс, ты еще раз превысил лимит на неудачи. Но иногда хаос не так плох, по крайней мере он может открыть людям глаза.",
		["663265"] = "Закончить разговор",
		["551371"] = "Слепая правда потворствует невежественным злым намерениям.",
		["551368"] = "Любовь... Вот что виной всему.",
		["221137"] = "Конечно скажу, господин Курьер. Но сначала найдите Элмар и уговорите отпустит меня. Я очень волнуюсь о маме.",
		["441147"] = "Подумай-ка об этом, пока принимаешь решение...",
		["9973459"] = "Назад к повозке?",
		["554271"] = "/t1",
		["227079"] = "Эта невыносимая вонь! Они больше не люди!",
		["221071"] = "Умоляю, спасите ее...",
		["9941026"] = "Прости, что не говорил всю правду.",
		["551314"] = "Могильщик.",
		["991465"] = "Извини, скидок я не даю, но все товары достойны своей цены.",
		["994286"] = "/t0",
		["442102"] = "",
		["551352"] = "Любовь... Вот что виной всему.",
