		["793294"] = "Контракт",
		["334052"] = "Церковные лучники. На них вьются странные растения. Кажется, они уже давно мертвы, но их тела еще не разложились.",
		["441201"] = "Ваша черта стерлась.",
		["661265"] = "Ха-ха, Курьер-хитрюга.",
		["9951058"] = "Дай-ка посмотреть, что у тебя есть?",
		["991123"] = "Есть ли другой способ подняться?",
		["771188"] = "О... Благодарю за твой альтруизм... Это не сон... О, какая вкусная еда! Это все? Я не наелся... Не вини меня за жадность, все-таки эти обезьяны сделали такие ужасные вещи с нами. Один порез... два пореза... большинство не пережило этот кошмар...",
		["332903"] = "Назад к повозке?",
		["551361"] = "Посредственные люди всегда одержимы своей значимостью.",
		["991161"] = "Я мог только наблюдать, как он медленно теряет свою человечность и становится бездумным убийцей. Эта проклятая Стена Тишины, эта проклятая Ихтиозаврия... Нам не вернуться на Святую Землю... Никогда...",
		["993082"] = "Что еще за страшный демон?",
		["883104"] = "Положить Печать Иекуба",
		["555250"] = "Кто я?",
		["553396"] = "Согласиться",
		["792378"] = "",
		["661171"] = "Всем известное предательство было лишь ширмой. Все было организовано Посредником, чтобы скрыть, обнаруженные в Святой Земле, секреты, из-за которых Церковь могли свергнуть.",
		["225184"] = "Наши потомки наконец войдут на Кладбище Колоссов.",
		["774038"] = "Как ты планируешь со мной поступить? Остановись, Норвуд, хватит!",
		["791310"] = "Если ты найдешь что-нибудь интересное, предоставь это мне.",
		["551087"] = "Это должно быть где-то рядом... терпение... терпение... Моя удача не так плоха...",
		["9951021"] = "Мы оба пытались защитить этот секрет, но что сделано уже сделано.",
		["4994993025"] = "Предложить 50 Призрачных грибов",
		["771237"] = "Скажу... Бенита, прежде чем я попрощаюсь,  надеюсь, ты сделаешь мне одолжение.",
		["442905"] = "Назад к повозке?",
		["9976452"] = "",
		["9921048"] = "Ох, уже закончилось? Время пополнить запасы.",
		["441104"] = "Все нормально, ворота Святой Земли еще закрыты. Он должен быть где-то поблизости.",
		["663702"] = "Нет",
		["663141"] = "Отказать",
		["553312"] = "Да",
		["551443"] = "Ах... Тебе тоже это знакомо... Чтобы сломать оковы судьбы, я заплатил свою цену — сожаление и одержимость стали моими спутниками... И все же не найти выхода. Впереди лишь ждет бездонная пропасть.",
		["663806"] = "Нет",
		["9972288"] = "",
		["991111"] = "Но из-за Стены Тишины низовья реки давно пересохли.",
		["771227"] = "Мне достаточно знать, что она ждет меня там.",
		["991430"] = "Камень Сифлера",
		["553166"] = "<color=#DBBA7FFF>Отдать Камни времени.</color>",
		["663170"] = "Что произошло пятнадцать лет назад?",
		["9924030"] = "/t0",
		["331143"] = "Катиба? Разве это место не разрушено?",
		["884042"] = "Быстро отдай...",
		["225320"] = "Славься, Святой Отец!За наследие Адамины!",
		["113233"] = "Закончить разговор",
		["551439"] = "Место, откуда я родом. Я тебе про него рассказывал. Страна чудес, плывущая по бескрайнему морю.",
		["551098"] = "Почему не попросишь Церковь дать другие?",
		["221052"] = "На помощь! Прошу, спасите!",
		["551189"] = "Как твоя рана?",
		["114025"] = "/t2",
		["553365"] = "Наполнить бутыль Мелос.",
		["553341"] = "Наполнить бутыль Мелос.",
		["9931003"] = "Решка.",
		["4994993101"] = "Нет",
		["111038"] = "Свет Хеггема гаснет быстрее, чем мы думали. Все вокруг заполонили Сендрилы. В поселении остались одни Нечестивые... Похоже, они когда-то были Последователями Хеггема.",
		["661147"] = "Конечно, боюсь. Но больше я страшусь потерять контроль над собственной судьбой и сердцем... из-за этого я отказалась от своего титула и присоединилась к Демиургам.",
		["991049"] = "Не за что. Просто знай меру.",
		["9961046"] = "Святая Земля находится под защитой четырех Святых Столпов и грозных рыцарей. Какой же жуткий хаос Демиурги могут создать?",
		["551400"] = "Осколок Алого Метеора, который у тебя есть, – ключ ко всему. Он понадобится тебе, когда придет время. Спасибо за обещание, но тебе придется немного подождать. Я оправдаю твое доверие.",
		["997024"] = "Наконец! У нас появилась компания... Но они не кажутся дружелюбными.",
		["442304"] = "",
		["441185"] = "Должно быть это колокол так влияет на него. Какая ирония... Если бы не этот предатель Фролло, который знал, что колокол использовали для предупреждения о нашествии Сендрилов, этот звон  стал бы бичом Демиургов.",
		["117123"] = "Это ловушка, Терренс! Почему никогда меня не слушаешь?!",
		["9961043"] = "Моя помощь? Это не оговорено нашим контрактом.",
		["771255"] = "Терренс, я никогда не лгал тебе...",
		["441123"] = "Не пойми меня неправильно, я не предатель и не Демиург. Меня зовут Винсент. Когда-то я был дворянином на Святой Земле.",
		["444271"] = "/t0",
		["441242"] = "А что же пытался остановить колокол Фролло?",
		["991093"] = "Я устала, но не могу уснуть. Мне страшно... Боюсь, стоит мне уснуть, и я все забуду.",
		["551152"] = "Говорящий подсолнух? Я брежу?",
		["551413"] = "...",
		["551158"] = "Сложно представить, что такое произошло с тобой...",
		["331195"] = "У нас нет выбора, кроме как попытаться найти его.",
		["792381"] = "",
		["557084"] = "Закат маркионов – лучшее подтверждение жестокого закона Соласа.",
		["995059"] = "Скажи мне.. Ах... Скажи мне, ты проклятый подонок!",
		["9943033"] = "Закончить разговор",
		["791278"] = "Но я все еще хочу закончить это, если у тебя есть Белая глина, пожалуйста, дай ее мне... Конечно, я не тороплю, есть вещи и поважнее.",
		["9971332"] = "Но кто-то будет помнить тебя и, возможно, твой клан предвидел это.",
		["9972379"] = "",
		["661217"] = "Как ты можешь говорить такое?!",
		["111070"] = "Кажется, она не в себе. Спроси ее о чем-нибудь другом.",
		["661110"] = "Хм...",
		["221186"] = "Нет большего счастья, чем стать невестой Святого Отца!",
		["553181"] = "Кто ты?",
		["221291"] = "Думаете, Святой Отец вас благословляет? Всю боль в Адамине не излечить одними травами.",
		["441282"] = "Остановить колосса? Это вообще возможно?",
		["991426"] = "На двери 5 отверстий. Вставить драгоценные камни в отверстия по порядку.",
		["776221"] = "",
		["792279"] = "",
		["9971296"] = "Да. Мне всегда было интересно, если бы я была ей, одобрила бы я «веру» сейчас?",
		["663402"] = "Нет",
		["9972387"] = "",
		["991258"] = "Проклятье! Не знаю, как тебе удалось раздобыть приглашение, но правила есть правила. У тебя есть возможность попасть в сокровищницу,",
		["331194"] = "Это лишь слух. На континенте ходят слухи о входе на Кладбище Колоссов. Большинство из них неверны.",
		["9971463"] = "Секретный туннель связывает Святую Землю и Катиб, откуда ты это знаешь?",
		["777119"] = "Так... хозяин Норвуда оказался слепым священником из Святой Земли... С самого начала нам морочили голову.",
		["221124"] = "Господин Курьер? Вы живы? Нет-нет-нет... Ты должен умереть! Если ты умрешь, я смогу увидеть маму. Где Элмар? Где она?!",
		["793334"] = "Контракт",
		["884050"] = "Давай превратим эту одержимость в наше пропитание...",
		["441276"] = "Демиурги не забудут тебя, оставь свои секреты здесь в Эдит навсегда.",
		["553287"] = "Торговать",
		["774032"] = "Только с помощью терпения можно получить то, что так желаешь. Однако твои действия сведут на нет все усилия.",
		["552349"] = "Требуется 1 баночка конденсированного меда и 90 Полипных костей. Хотите продолжить? ",
		["221307"] = "Учитывая желание найти этого Святого Отца и нынешний хаос в Адамине, работа в команде — лучший выбор.",
		["991403"] = "Не отказывайся от рациональности порядка. Терпение — залог успеха. Учись контролировать себя, и сможешь контролировать все вокруг.",
		["992081"] = "",
		["661227"] = "Помнишь тогда в Катибе? Там были ворота, которые закрыл Посредник.  Этот секретный туннель – другой вход туда, куда ведут ворота.",
		["9973319"] = "Я нашел какие-то древние реликвии",
		["100410042203"] = "Да",
		["221174"] = "Вы не виноваты, господин Курьер. Вы сделали все, что могли.",
		["791378"] = "Как обстоят дела? Нашел что-нибудь интересное?",
		["441194"] = "Черт! Открой сейчас же ворота! Или...",
		["461002"] = "Не испытывай мое терпение.",
		["221189"] = "Как холодно... Нужно сшить ей новую одежду. Ее зеленый плащ слишком тонкий.",
		["100310035999"] = "Нельзя открыть с этой стороны.",
		["665193"] = "Если Бог умрет, все исчезнет. Святая Земля канет в лету.",
		["441163"] = "Это ты опять... Я была немного не осторожна, и меня атаковала эта крыса. Он прямо на башне, он не мог уйти далеко.",
		["771219"] = "Если ты решишь верить, то даже самая отвратительная ложь однажды станет правдой, Виола. Рано или поздно мы встретимся вновь в этом проклятом мире.",
		["222104"] = "",
		["791309"] = "Когда я получил эту руку от хозяина, он сказал мне, что помимо нее в том гробу лежали еще три древние реликвии, но по какой-то причине их украли и они были утеряны. Только найдя их, она успокоится, если не обращаться с ней осторожно, уничтожит все живое.",
		["661313"] = "Я ничего не слышал.",
		["9973351"] = "Я нашел несколько сломанных скрижалей",
		["111072"] = "Свет Хеггема гаснет. Он слишком слаб, чтобы защитить нас от Сендрилов. Последователи превратились в монстров.",
		["9971464"] = "Это было лишь догадкой, Тереза и Печать Иекуба были похищены, их место нахождение может быть только...",
		["661100"] = "Хоть ты не видишь, ты должен ощущать опасность.",
		["111100"] = "Однако, какое мерзкое яйцо... Осторожно, это может быть ловушкой!",
		["661262"] = "Ха-ха, Курьер-хитрюга.",
		["4994993100"] = "Да",
		["441334"] = "Надеюсь это путешествие не займет много времени.",
		["443198"] = "Вэйлант...",
		["447083"] = "Иногда я задаюсь вопросом, действительно ли мы были прокляты....",
		["223295"] = "Отдайте ей траву Адамины.",
		["1003100045"] = "Изгнанная душа, оставленная на милость стихий...",
		["221311"] = "Прости, но она потеряла рассудок. Я хотел все ей объяснить, но не успел и слова вымолвить, как она напала на меня.",
		["995048"] = "Вода из реки души! Вкусная，освежающая! Хочешь попробовать?",
		["221154"] = "Ты свободна...",
		["447303"] = "Хватит ходить здесь, торопись к колокольне. Предатель Демиургов не должен был далеко уйти.",
		["663109"] = "<color=#DBBA7FFF>Отказать</color>",
		["555272"] = "Если бы только я раньше об этом знал... Может, он бы мне понравился... Нет, мне нужно быть лучше него... Я нарушил все табу, утратил достоинство... Ради чего?",
		["994014"] = "Смотри! Это Джерольд!",
		["1001100014"] = "Довольно!",
		["991504"] = "Но это не важно, мы одержим победу в конце концов и вернем Ихтиозаврии былую славу...",
		["441202"] = "Никто не может понять настоящее унижение, которое вытерпели Демиурги. Они остались позади, как скот в городе, лишенный света, трясясь от страха, они сражались с Сендирилами в одиночку.",
		["991090"] = "… ...",
		["228230"] = "",
		["223111"] = "Святым Отцом?",
		["994480"] = "/t0",
		["221149"] = "Смотри, что я нашел.",
		["4994993024"] = "Предложить 25 Призрачных грибов",
		["551339"] = "Возможно, у тебя появится небольшой шанс изменить предначертанную судьбу.",
		["661172"] = "Посредник был на Кладбище Колоссов? Что они там нашли?",
		["991075"] = "Рошетт... Рошетт... Я не забуду твоего имени...",
		["663179"] = "Посредник так и не показался?",
		["993411"] = "Вставить Былую тень",
		["551119"] = "Еще нет."
	},
	StoryTutorial = {
